import{_ as s}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as d,a,b as e,d as n,e as o,r as c}from"./app.37c9a343.js";const h={},l=o('<h1 id="_003-🐂-新一代-kubernetes、反脆弱" tabindex="-1"><a class="header-anchor" href="#_003-🐂-新一代-kubernetes、反脆弱" aria-hidden="true">#</a> 003 🐂 新一代 kubernetes、反脆弱</h1><h2 id="本期摘要" tabindex="-1"><a class="header-anchor" href="#本期摘要" aria-hidden="true">#</a> <strong>本期摘要</strong></h2><ul><li>wasmCloud，新一代 kubernetes？</li><li>《反脆弱：从不确定性中获益》</li></ul><h2 id="wasmcloud-新一代-kubernetes" tabindex="-1"><a class="header-anchor" href="#wasmcloud-新一代-kubernetes" aria-hidden="true">#</a> wasmCloud，新一代 kubernetes？</h2>',4),i={href:"https://github.com/GoogleChromeLabs/squoosh",target:"_blank",rel:"noopener noreferrer"},u=o('<p>wasm 可以由多种编程语言编译而成二进制格式，能够充分发挥硬件能力以达到近乎原生的执行效率，同时它是运行在可移植的沙箱环境中，从 <code>可移植性</code>和 <code>沙箱</code> 两个要素来看，我们不难发现其与 <code>docker</code> 有一定的相似之处，先知先觉的技术布道师其实早已洞穿一切，不光是有相似之处，使用 wasm 还有着性能方面的优势，过去我们总担心程序运行在虚拟机里性能会受影响，虽说以虚拟机为主的容器运行时早已成为主流，但之前的担心并无道理，在技术的不断创新下，wasmCloud 的出现可以说是为了解决现有容器的一系列问题。</p><p>原本我们认为 WebAssembly 是属于前端领域的技术，现在它也走到了后端，前后端的技术又一次产生了交集。</p><p>wasmCloud 目前被广泛看好，一些云计算厂商已经将其应用在 <code>Serverless</code> 无服务器函数领域，未来会不会在云原生架构上占有一席之地，让我们拭目以待。</p><h2 id="《反脆弱-从不确定性中获益》" tabindex="-1"><a class="header-anchor" href="#《反脆弱-从不确定性中获益》" aria-hidden="true">#</a> 《反脆弱：从不确定性中获益》</h2><p>《原则》一书中提到作者（Paypal 创始人）在面试的时候总会问面试者一个问题：你在什么问题上与其他人有着不同的看法？只有面试者回答的不错时才能应聘上。《反脆弱》这本书的书名其实也是个很好的回答，我们谈到脆弱时会想到它的反义词坚强，但脆弱有时候是不可避免的，比如黑天鹅事件，比如玻璃杯从桌上掉下，如何通过反脆弱在不确定性中获益是值得思考的，在面临随机的危机事件中，如何去面对，想更系统的了解的话，不防看看这本书。</p><h2 id="我的一周" tabindex="-1"><a class="header-anchor" href="#我的一周" aria-hidden="true">#</a> 我的一周</h2><p>过年期间没有保持每天阅读，但也阅读到一些价值较大的信息，这里推荐大家订阅 <code>湾区日报</code>。</p><h2 id="本周引用" tabindex="-1"><a class="header-anchor" href="#本周引用" aria-hidden="true">#</a> 本周引用</h2><p>为什么你需要习惯而不是激情？</p><blockquote><p><em>如果你有一个习惯模式，你头脑中更理智的部分可以专注于你的工作，你可以得到想法并做这些事情，其余的部分在后台就会自己解决。</em></p><p>—— 大卫林奇</p></blockquote><p>（完）</p>',11);function p(m,b){const r=c("ExternalLinkIcon");return t(),d("div",null,[l,a("p",null,[e("WebAssembly/wasm 是一个低层级的编程语言，可以让开发者运用自己熟悉的编程语言进行编译，再借由虚拟机引擎在浏览器内执行，Google 的 "),a("a",i,[e("https://github.com/GoogleChromeLabs/squoosh"),n(r)]),e(" 项目很大程度上就是在展示 wasm 的厉害之处（它所展示的图片压缩技术是在浏览器本地执行的）。")]),u])}const k=s(h,[["render",p],["__file","003.html.vue"]]);export{k as default};
