import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c,a as e,b as p,d as t,f as s,r}from"./app.c3058df1.js";const i={},d=s('<blockquote><p>\u6D45\u8C08\u5171\u8BC6\u7B97\u6CD5|DPOS(\u59D4\u6258\u80A1\u6743\u8BC1\u660E)\u7B97\u6CD5</p><p>\u914D\u5408\u4EE5\u4E0B\u4EE3\u7801\u8FDB\u884C\u9605\u8BFB\uFF1A&lt; /&gt;</p><p>\u5199\u6587\u4E0D\u6613\uFF0C\u7ED9\u4E2A\u5C0F\u5173\u6CE8\uFF0C\u6709\u4EC0\u4E48\u95EE\u9898\u53EF\u4EE5\u6307\u51FA\uFF0C\u4FBF\u4E8E\u5927\u5BB6\u4EA4\u6D41\u5B66\u4E60\u3002</p></blockquote><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gna2p4ti6pj31c00u0ahv.jpg" alt="src=http___img.pconline.com.cn_images_upload_upc_tx_wallpaper_1207_09_c2_12276725_1341818508711.jpg&amp;refer=http___img.pconline.com"></p><h2 id="dpos\u8BE6\u89E3" tabindex="-1"><a class="header-anchor" href="#dpos\u8BE6\u89E3" aria-hidden="true">#</a> DPOS\u8BE6\u89E3</h2><p>DPoS\u5171\u8BC6\u7B97\u6CD5\u5C31\u662F\u5C06PoS\u5171\u8BC6\u7B97\u6CD5\u4E2D\u7684\u8BB0\u8D26\u8005\u8F6C\u6362\u4E3A\u6307\u5B9A\u8282\u70B9\u6570\u7EC4\u6210\u7684\u5C0F\u5708\u5B50\uFF0C\u800C\u4E0D\u662F\u6240\u6709\u4EBA\u90FD\u53EF\u4EE5\u53C2\u4E0E\u8BB0\u8D26\uFF0C\u8FD9\u4E2A\u5708\u5B50\u53EF\u80FD\u662F21\u4E2A\u8282\u70B9\uFF0C\u4E5F\u6709\u53EF\u80FD\u662F101\u4E2A\u8282\u70B9\uFF0C\u53EA\u6709\u5708\u5B50\u5185\u7684\u8282\u70B9\u624D\u80FD\u83B7\u5F97\u8BB0\u8D26\u6743\u3002\u8FD9\u5C06\u6781\u5927\u5730\u63D0\u9AD8\u7CFB\u7EDF\u7684\u541E\u5410\u91CF\uFF0C\u56E0\u4E3A\u66F4\u5C11\u7684\u8282\u70B9\u4E5F\u5C31\u610F\u5473\u7740\u7F51\u7EDC\u548C\u8282\u70B9\u7684\u53EF\u63A7\u3002</p><p>DPOS\u7684\u80A1\u4E1C\u9009\u4E3E\u673A\u5236\uFF1A</p><ul><li>DPoS\u673A\u5236\u4E2D\u7684\u80A1\u6C11\uFF08\u8282\u70B9\uFF09\u6839\u636E\u81EA\u5DF1\u6301\u6709\u7684\u52A0\u5BC6\u8D27\u5E01\u6570\u91CF\u5360\u603B\u91CF\u7684\u767E\u5206\u6BD4\uFF08\u5360\u80A1\u6BD4\u4F8B\uFF09\u6765\u6295\u7968\uFF0C\u4E0D\u662F\u4E00\u4EBA\u4E00\u7968\uFF1B</li><li>\u9009\u4E3E\u51FA\u7684\u80A1\u4E1C\u4EE3\u8868\uFF08\u53EF\u4FE1\u8282\u70B9\uFF09\u5B8C\u5168\u5BF9\u7B49\uFF0C\u53EF\u7406\u89E3\u4E3A\u5177\u6709\u540C\u7B49\u7B97\u529B\u7684101\u4E2A\u77FF\u6C60\uFF1B</li><li>\u80A1\u4E1C\u4EE3\u8868\u4E00\u65E6\u65E0\u80FD\u3001\u4E0D\u4F5C\u4E3A\u3001\u80E1\u4F5C\u4E3A\uFF08\u63D0\u4F9B\u7684\u7B97\u529B\u4E0D\u7A33\u5B9A\uFF0C\u8BA1\u7B97\u673A\u5B95\u673A\u3001\u6216\u8005\u8BD5\u56FE\u5229\u7528\u624B\u4E2D\u7684\u6743\u529B\u4F5C\u6076\uFF09\uFF0C\u5C06\u7ACB\u523B\u88AB\u80A1\u6C11\u8E22\u51FA\u6574\u4E2A\u7CFB\u7EDF\uFF0C\u7136\u540E\u7531\u5176\u4ED6\u540E\u5907\u4EE3\u8868\u9876\u4E0A\u53BB\uFF1B</li><li>\u51B3\u7B56\u5B8C\u516C\u53F8\u5927\u4E8B\uFF08\u8BB0\u5B8C\u8D26\u3001\u51FA\u5B8C\u5757\uFF09\u6709\u94B1\u5206\uFF0C\u6839\u636E\u5360\u80A1\u6BD4\u4F8B\u3002</li></ul><h2 id="dpos\u7B97\u6CD5\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#dpos\u7B97\u6CD5\u5206\u6790" aria-hidden="true">#</a> DPOS\u7B97\u6CD5\u5206\u6790</h2><p>\u5728DPoS\u5171\u8BC6\u7B97\u6CD5\u4E2D\uFF0C\u533A\u5757\u94FE\u7684\u6B63\u5E38\u8FD0\u8F6C\u4F9D\u8D56\u4E8E\u89C1\u8BC1\u4EBA(Delegates)\uFF0C\u89C1\u8BC1\u4EBA\u662F\u7531\u5168\u7F51\u8282\u70B9\u6295\u7968\u4EA7\u751F\u7684\uFF0C\u89C1\u8BC1\u4EBA\u4E5F\u662F\u8BB0\u8D26\u8282\u70B9\u7684\u5B9E\u9645\u63A7\u5236\u4EBA\uFF0C\u76F8\u5F53\u4E8E\u54B1\u4EEC\u9009\u8BFE\u4EE3\u8868\uFF0C\u8BFE\u4EE3\u8868\u5E2E\u6211\u4EEC\u6574\u7406\u4F5C\u4E1A</p><p>\u89C1\u8BC1\u4EBA\u5728\u5B8C\u6210\u6253\u5305\u4EA4\u6613\u7684\u540C\u65F6\u53EF\u4EE5\u9886\u53D6\u533A\u5757\u5956\u52B1\u548C\u4EA4\u6613\u7684\u624B\u7EED\u8D39\uFF0C\u5E76\u4E14\u53EF\u4EE5\u6267\u884C\u793E\u533A\u6295\u7968\u7684\u63D0\u6848\uFF0C\u6240\u4EE5DPoS\u5171\u8BC6\u7B97\u6CD5\u4E0D\u4EC5\u4EC5\u662F\u7B97\u6CD5\uFF0C\u800C\u662F\u4E00\u4E2A\u5305\u542B\u4E86\u534F\u4F5C\u6CBB\u7406\u5173\u7CFB\u7684\u5171\u8BC6\u673A\u5236\u3002</p><p>DPoS\u4E3A\u4E86\u5C3D\u5FEB\u786E\u5B9A\u4EA4\u6613\u987A\u5E8F\uFF0C\u8FC7\u6EE4\u65E0\u6548\u4EA4\u6613\uFF0C\u6240\u4EE5\u89C4\u5B9A\u4E86\u5728\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF0C\u6240\u6709\u8BB0\u8D26\u8282\u70B9\u8F6E\u6D41\u6BCF3\u79D2\u4EA7\u751F\u4E00\u4E2A\u533A\u5757\uFF0C\u8F6E\u5230\u4E86\u67D0\u4E2A\u8BB0\u8D26\u8282\u70B9\u51FA\u5757\u65F6\uFF0C\u5FC5\u987B\u57282\u79D2\u5185\u63D0\u4EA4\u533A\u5757\uFF0C\u5426\u5219\u5C31\u4F1A\u9519\u5757\u3002</p><p>\u5047\u8BBE\u4E00\u76F4\u6CA1\u6709\u8BB0\u8D26\u8282\u70B9\u9519\u8FC7\u81EA\u5DF1\u987A\u5E8F\uFF0C\u90A3\u4E48\u4ED6\u4EEC\u751F\u4EA7\u7684\u94FE\u6761\u52BF\u5FC5\u662F\u6700\u957F\u7684\u94FE\u6761\uFF0C\u5982\u679C\u8BB0\u8D26\u8282\u70B9\u5728\u975E\u6307\u5B9A\u65F6\u95F4\u751F\u4EA7\u533A\u5757\u88AB\u8BA4\u4E3A\u662F\u65E0\u6548\u7684\uFF0C\u6BCF\u7ECF\u8FC7\u4E00\u8F6E\uFF0C\u6240\u6709\u8282\u70B9\u8F6E\u6D41\u51FA\u5757\u7684\u987A\u5E8F\u5C31\u4F1A\u53D1\u751F\u91CD\u65B0\u6D17\u724C\u3002</p><p>\u4E0B\u56FE\u5C31\u662F\u4E00\u4E2A\u7406\u60F3\u7684\u8F6E\u6D41\u8BB0\u8D26\u72B6\u6001\uFF1A</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnade58srjj316o05yju3.jpg" alt="image-20210203151915698"></p><p>DPoS\u7B97\u6CD5\u767D\u76AE\u4E66\u8FD8\u4ECB\u7ECD\u4E86\u4EE5\u4E0B\u51E0\u79CD\u4E0D\u6B63\u5E38\u7684\u60C5\u51B5\uFF1A</p><p>\u2460\uFF1A<strong>\u5C11\u6570\u8BB0\u8D26\u8282\u70B9\u53D1\u8D77\u6076\u610F\u5206\u53C9\u6216\u8005\u53D1\u751F\u6545\u969C</strong></p><p>\u53EF\u4EE5\u5141\u8BB8\u6700\u591A1/3\u7684\u8282\u70B9\u662F\u6076\u610F\u6216\u6545\u969C\uFF0C\u4ECE\u800C\u5BFC\u81F4\u51FA\u73B0\u5206\u53C9\u3002\u5728\u8FD9\u79CD\u60C5\u5F62\u4E0B\uFF0C\u5C11\u6570\u5206\u652F\u5C06\u53EA\u80FD\u57289\u79D2\u5185\u751F\u4EA71\u4E2A\u5757\uFF0C\u800C\u5927\u591A\u6570\u5206\u652F\uFF0C\u7531\u4E8E\u6570\u91CF\u591A\u4E00\u500D\uFF0C\u5C06\u9884\u671F\u80FD\u57289\u79D2\u5185\u751F\u4EA72\u4E2A\u5757\u3002\u518D\u4E00\u6B21\uFF0C\u8BDA\u5B9E\u76842/3\u7684\u5927\u591A\u6570\u53EF\u4EE5\u6BD4\u5C0F\u7684\u90A3\u4E00\u90E8\u5206\u521B\u5EFA\u4E00\u4E2A\u66F4\u957F\u7684\u94FE\u6761\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadefucijj317u076tb7.jpg" alt="image-20210203151934597"></p><p>\u2461\uFF1A<strong>\u9694\u79BB\u73AF\u5883\u4E0B\u7684\u91CD\u590D\u5757\u751F\u4EA7</strong></p><p>\u5C11\u6570\u7FA4\u4F53\u53EF\u80FD\u5C1D\u8BD5\u521B\u5EFA\u4E00\u4E2A\u65E0\u9650\u6570\u91CF\u7684\u5206\u53C9\uFF0C\u4F46\u6240\u6709\u5206\u652F\u90FD\u5C06\u6BD4\u4E3B\u94FE\u77ED\uFF0C\u56E0\u4E3A\u5C11\u6570\u7FA4\u4F53\u5728\u94FE\u7684\u6210\u957F\u4E0A\u66F4\u6162\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadihh19bj316a096mxr.jpg" alt="image-20210203152323992"></p><p>\u2462\uFF1A<strong>\u7F51\u7EDC\u788E\u7247</strong></p><p>\u7F51\u7EDC\u975E\u5E38\u6709\u53EF\u80FD\u788E\u7247\u5230\uFF0C\u6CA1\u6709\u54EA\u4E00\u4E2A\u94FE\u4E0A\u7684\u533A\u5757\u751F\u4EA7\u8005\u5360\u5230\u4E86\u6240\u6709\u533A\u5757\u751F\u4EA7\u8005\u4E2D\u7684\u5927\u591A\u6570\u3002\u5728\u6B64\u60C5\u666F\u4E0B\uFF0C\u6700\u957F\u7684\u90A3\u4E2A\u94FE\u5C06\u53D8\u6210\u6700\u5927\u7684\u4E00\u4E2A\u5C11\u6570\u7FA4\u4F53\u3002\u5F53\u7F51\u7EDC\u8FDE\u63A5\u6062\u590D\u6B63\u5E38\u540E\uFF0C\u76F8\u5BF9\u8F83\u5C0F\u7684\u90A3\u4E9B\u7FA4\u4F53\u5C06\u81EA\u7136\u7684\u5207\u6362\u5230\u6700\u957F\u7684\u94FE\uFF0C\u4ECE\u800C\u5C06\u6062\u590D\u660E\u786E\u7684\u5171\u8BC6\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadj14g6vj31600a0tc7.jpg" alt="image-20210203152359294"></p><p>\u8FD8\u6709\u4E00\u79CD\u975E\u5E38\u53EF\u80FD\u7684\u60C5\u51B5\u662F\uFF0C\u4E09\u4E2A\u5206\u652F\u4E2D\uFF0C\u6700\u5927\u7684\u4E24\u4E2A\u5206\u652F\u4E00\u6837\u5927\u3002\u6B64\u65F6\uFF0C\u5C06\u7531\u76F8\u5BF9\u66F4\u5C0F\u7684\u7B2C\u4E09\u4E2A\u5206\u652F\u52A0\u5165\u7F51\u7EDC\u65F6\u6765\u6253\u7834\u50F5\u5C40\u3002\u5B58\u5728\u5947\u6570\u4E2A\u533A\u5757\u751F\u4EA7\u8005\uFF0C\u6240\u4EE5\u50F5\u5C40\u4E00\u822C\u4E0D\u4F1A\u6301\u7EED\u5F88\u4E45\u3002\u540E\u9762\u6211\u4EEC\u8FD8\u5C06\u4ECB\u7ECD\u533A\u5757\u751F\u4EA7\u8005\u7684\u6E05\u6D17\uFF0C\u4F1A\u5C06\u751F\u4EA7\u8005\u968F\u673A\u751F\u6210\u987A\u5E8F\uFF0C\u4EE5\u786E\u4FDD\u5373\u4F7F\u4E24\u4E2A\u5206\u652F\u5177\u6709\u76F8\u540C\u6570\u91CF\u7684\u751F\u4EA7\u8005\uFF0C\u5206\u652F\u4E5F\u5C06\u4EE5\u4E0D\u540C\u7684\u957F\u5EA6\u7206\u53D1\u589E\u957F\uFF0C\u5BFC\u81F4\u4E00\u4E2A\u5206\u652F\u6700\u7EC8\u63A5\u7BA1\u53E6\u4E00\u4E2A\u5206\u652F\u3002</p><p>\u2463\uFF1A<strong>\u5C11\u6570\u7FA4\u4F53\u91CD\u590D\u751F\u4EA7</strong></p><p>\u5728\u8FD9\u79CD\u60C5\u666F\u4E0B\uFF0C\u5C11\u6570\u7FA4\u4F53B\u5728\u81EA\u5DF1\u53EF\u4EE5\u751F\u4EA7\u7684\u65F6\u95F4\u8282\u70B9\uFF0C\u540C\u65F6\u521B\u5EFA\u4E24\u6761\uFF0C\u6216\u591A\u6761\u7684\u533A\u5757\u94FE\u3002\u4E0B\u4E00\u4E2A\u6267\u884C\u7684\u751F\u4EA7\u8005C\uFF0C\u5C06\u9009\u62E9B\u521B\u5EFA\u7684\u53EF\u9009\u94FE\u4E2D\u7684\u4EFB\u4E00\u6761\u3002C\u9009\u4E2D\u7684\u8FD9\u6761\u94FE\u5C06\u6210\u4E3A\u6700\u957F\u7684\u94FE\uFF0C\u5F53\u8FD9\u53D1\u751F\u662F\uFF0C\u6240\u4EE5\u5982\u4E0B\u56FE\u6240\u793A\u7684B1\u94FE\u6761\u4E0A\u7684\u7ED3\u70B9\u90FD\u4F1A\u8F6C\u8FC7\u6765\u3002\u6240\u4EE5\uFF0C\u65E0\u8BBA\u5C11\u6570\u505A\u6076\u7ED3\u70B9\u5236\u9020\u591A\u5C11\u7684\u94FE\uFF0C\u4ED6\u4EEC\u5728\u4E0B\u4E00\u8F6E\u4E2D\uFF0C\u80AF\u5B9A\u4E0D\u4F1A\u662F\u6700\u957F\u7684\u90A3\u4E2A\u94FE\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadjozhpyj314y07ggo9.jpg" alt="image-20210203152437439"></p><p>\u2464\uFF1A<strong>\u6700\u540E\u7684\u4E0D\u53EF\u9006\u533A\u5757</strong></p><p>\u5728\u7F51\u7EDC\u788E\u7247\u7684\u60C5\u51B5\u4E0B\uFF0C\u591A\u4E2A\u5206\u53C9\u53EF\u80FD\u6301\u7EED\u8F83\u957F\u65F6\u95F4\u7684\u9694\u79BB\u3002\u957F\u8FDC\u6765\u770B\uFF0C\u6700\u957F\u7684\u94FE\u5C06\u6700\u7EC8\u53D7\u5230\u8BA4\u53EF\u3002\u4F46\u89C2\u5BDF\u8005\u9700\u8981\u4E00\u79CD\u624B\u6BB5\u6765\u786E\u5B9A\u67D0\u4E2A\u5757\u662F\u5426\u662F\u5728\u6700\u957F\u94FE\u6761\u7684\u4E00\u90E8\u5206\uFF08\u786E\u8BA4\u5171\u8BC6\uFF09\u3002\u8FD9\u53EF\u4EE5\u901A\u8FC72/3 + 1\u4E2A\u533A\u5757\u751F\u4EA7\u8005\u662F\u5426\u5BF9\u67D0\u4E2A\u5757\u6709\u786E\u8BA4\u3002</p><p>\u4E0B\u56FE\u4E2D\uFF0C\u5757B\u88ABA\u3001C\u786E\u8BA4\u4E86\uFF0C\u8FD9\u610F\u5473\u77402/3 + 1\u90FD\u5DF2\u7ECF\u786E\u8BA4\u4E86\u3002\u7531\u6B64\u6211\u4EEC\u53EF\u4EE5\u4E3A\u4E0D\u53EF\u80FD\u5B58\u5728\u66F4\u957F\u7684\u94FE\u4E86\uFF0C\u56E0\u4E3A2/3\u7684\u533A\u5757\u94FE\u662F\u8BDA\u5B9E\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadk4gx1dj314y07amyy.jpg" alt="image-20210203152502221"></p><p>\u9700\u8981\u6CE8\u610F\u7684\u662F\u8FD9\u4E2A\u89C4\u5219\u4E0E\u6BD4\u7279\u5E01\u76846\u4E2A\u533A\u5757\u786E\u8BA4\u7C7B\u4F3C\u3002\u4E00\u4E9B\u806A\u660E\u7684\u4EBA\u53EF\u4EE5\u8BBE\u8BA1\u4E00\u7CFB\u5217\u4E8B\u4EF6\uFF0C\u5176\u4E2D\u4E24\u4E2A\u8282\u70B9\u53EF\u80FD\u4F1A\u5728\u4E0D\u540C\u7684\u6700\u540E\u4E0D\u53EF\u9006\u5757\u4E0A\u7ED3\u675F\u3002\u8FD9\u79CD\u6781\u7AEF\u60C5\u51B5\u9700\u8981\u4E00\u4E2A\u653B\u51FB\u8005\uFF0C\u7CBE\u786E\u63A7\u5236\u901A\u4FE1\u5EF6\u8FDF\uFF0C\u5E76\u9700\u8981\u5728\u51E0\u5206\u949F\u5185\u5B9E\u65BD\u4E0D\u6B62\u4E00\u6B21\uFF0C\u800C\u662F\u4E8C\u6B21\u653B\u51FB\u3002\u5982\u679C\u53D1\u751F\u8FD9\u79CD\u60C5\u51B5\uFF0C\u90A3\u4E48\u6700\u957F\u94FE\u6761\u8FD9\u4E00\u957F\u671F\u89C4\u5219\u4ECD\u7136\u9002\u7528\u3002 \u6211\u4EEC\u4F30\u8BA1\u8FD9\u79CD\u653B\u51FB\u7684\u53EF\u80FD\u6027\u8DB3\u591F\u63A5\u8FD10\uFF0C\u7ECF\u6D4E\u540E\u679C\u4E5F\u5FAE\u4E0D\u8DB3\u9053\uFF0C\u4E0D\u503C\u5F97\u62C5\u5FC3\u3002</p><p>\u2465\uFF1A<strong>\u4E0D\u8DB3\u6CD5\u5B9A\u533A\u5757\u751F\u4EA7\u8005</strong></p><p>\u5728\u4E00\u4E9B\u4E0D\u592A\u53EF\u80FD\u7684\u60C5\u51B5\u4E0B\uFF0C\u751F\u4EA7\u8005\u6CA1\u6709\u660E\u786E\u8FBE\u5230\u6CD5\u5B9A\u4EBA\u6570\uFF0C\u5C11\u6570\u4EBA\u53EF\u80FD\u7EE7\u7EED\u751F\u4EA7\u5757\u3002\u5728\u7EE7\u7EED\u751F\u4EA7\u7684\u533A\u5757\u4E2D\uFF0C\u5229\u76CA\u76F8\u5173\u8005\u53EF\u4EE5\u5305\u542B\u4E00\u4E9B\u6539\u53D8\u6295\u7968\u7684\u4EA4\u6613\u3002\u8FD9\u4E9B\u6295\u7968\u4F1A\u9009\u4E3E\u4E00\u7EC4\u65B0\u7684\u533A\u5757\u751F\u4EA7\u8005\uFF0C\u5E76\u5C06\u533A\u5757\u751F\u4EA7\u53C2\u4E0E\u5EA6\u6062\u590D\u5230100%\u3002\u4E00\u65E6\u53D1\u751F\u8FD9\u79CD\u60C5\u51B5\uFF0C\u5C11\u6570\u4EBA\u94FE\u6700\u7EC8\u4F1A\u8D85\u8FC7\u5176\u5B83\u4F4E\u4E8E100%\u53C2\u4E0E\u94FE\u3002</p><p>\u5728\u8FD9\u4E2A\u6D41\u7A0B\u53D1\u751F\u65F6\uFF0C\u6240\u6709\u7684\u89C2\u5BDF\u8005\u5FC5\u987B\u8981\u660E\u767D\u6574\u4E2A\u7F51\u7EDC\u5904\u4E8E\u4E0D\u7A33\u5B9A\u7684\u72B6\u6001\uFF0C\u76F4\u5230\u591A\u4E8E67%\u53C2\u4E0E\u8005\u51FA\u73B0\u540E\u624D\u4F1A\u7A33\u5B9A\u4E0B\u6765\u3002\u54EA\u4E9B\u9009\u62E9\u5728\u8FD9\u79CD\u60C5\u666F\u4E0B\u53D1\u8D77\u4EA4\u6613\u7684\uFF0C\u4E0E\u90A3\u4E9B\u5728\u6BD4\u7279\u5E01\u4E2D\u63A5\u53D7\u4F4E\u4E8E6\u5757\u5C31\u786E\u8BA4\u4EA4\u6613\u6210\u529F\u90A3\u6837\uFF0C\u5192\u7740\u7C7B\u4F3C\u7684\u98CE\u9669\u3002\u4ED6\u4EEC\u5FC5\u987B\u660E\u767D\uFF0C\u5B58\u5728\u67D0\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u5171\u8BC6\u6700\u7EC8\u4F1A\u4EE5\u53E6\u4E00\u4E2A\u94FE\u4E3A\u51C6\u3002\u5728\u5B9E\u8DF5\u4E2D\uFF0C\u8FD9\u79CD\u60C5\u5F62\u6BD4\u5728\u6BD4\u7279\u5E01\u4E2D\u63A5\u53D7\u5C11\u4E8E3\u4E2A\u5757\u5C31\u786E\u8BA4\u66F4\u52A0\u5B89\u5168\u3002</p><p>\u2466\uFF1A<strong>\u5927\u591A\u6570\u636E\u533A\u5757\u751F\u4EA7\u8005\u7684\u8150\u8D25</strong></p><p>\u5982\u679C\u5927\u591A\u6570\u533A\u5757\u751F\u4EA7\u8005\u5408\u8C0B\u53D8\u5F97\u8150\u8D25\uFF0C\u4ED6\u4EEC\u5236\u9020\u65E0\u9650\u6570\u91CF\u7684\u5206\u652F\uFF0C\u6BCF\u4E00\u4E2A\u5206\u652F\u90FD\u6709\u591A\u4E8E2/3\u7684\u5927\u591A\u6570\u7684\u7B7E\u540D\u3002\u5728\u8FD9\u6837\u7684\u573A\u666F\u65E9\uFF0C\u6700\u540E\u4E0D\u53EF\u9006\u8F6C\u5757\u7B97\u6CD5\u9000\u5316\u4E3A\u6700\u957F\u94FE\u7B97\u6CD5\u3002\u6B64\u65F6\u6700\u957F\u7684\uFF0C\u83B7\u5F97\u4E86\u6700\u5927\u7684\u7FA4\u4F53\u8BA4\u8BC1\u7684\uFF0C\u5C06\u7531\u5C11\u6570\u7684\u8BDA\u5B9E\u8282\u70B9\u7684\u52A0\u5165\u6765\u786E\u5B9A\u3002\u8FD9\u6837\u7684\u60C5\u5F62\u4E0D\u4F1A\u6301\u7EED\u5F88\u4E45\uFF0C\u56E0\u4E3A\u5229\u76CA\u76F8\u5173\u8005\u4F1A\u6700\u7EC8\u6295\u7968\u66FF\u6362\u6389\u8FD9\u4E9B\u533A\u5757\u751F\u4EA7\u8005\u3002</p><p><img src="https://tva1.sinaimg.cn/large/008eGmZEgy1gnadl0aqk8j314k08s44j.jpg" alt="image-20210203152553558"></p><h2 id="dpos\u8981\u89E3\u51B3\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#dpos\u8981\u89E3\u51B3\u7684\u95EE\u9898" aria-hidden="true">#</a> DPOS\u8981\u89E3\u51B3\u7684\u95EE\u9898</h2><p>\u4ECE\u540D\u79F0\u4E0A\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u5224\u65AD\u51FA<code>DPoS</code>\u4E0E<code>PoS</code>\u5171\u8BC6\u662F\u76F4\u63A5\u5173\u8054\u7684\u3002<code>DPoS</code>\u7B97\u6CD5\u662FBM\u6839\u636E\u5F53\u65F6PoW\u3001PoS\u7684\u4E0D\u8DB3\u800C\u6539\u8FDB\u7684\u5171\u8BC6\u7B97\u6CD5\uFF0C\u5B83\u7684\u76EE\u7684\u5C31\u662F\u4E3A\u4E86\u63D0\u9AD8\u6027\u80FD\uFF0C\u4E5F\u5C31\u662F\u4EA4\u6613\u786E\u8BA4\u65F6\u95F4\u77ED\u3002</p><p>\u5728<code>PoS</code>\u5171\u8BC6\u4E2D\uFF0C\u4EBA\u4EEC\u4F7F\u7528\u8D22\u4EA7\u8BC1\u660E\u6765\u201C\u6316\u77FF\u201D\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u8FD9\u662F\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u53C2\u4E0E\u7684\uFF0C\u53EA\u8981\u4F60\u6301\u6709\u5E01\uFF0C\u4F60\u5C31\u53EF\u4EE5\u53C2\u4E0E\u6316\u77FF\u3002\u4F46\u662F<code>PoS</code>\u5E76\u6CA1\u6709\u89E3\u51B3\u6027\u80FD\u95EE\u9898\uFF0C\u5728\u8FD9\u91CC\u6211\u4EEC\u76F4\u63A5\u8BA4\u4E3A\u63D0\u9AD8\u6027\u80FD\u5C31\u662F\u63D0\u9AD8<code>TPS</code>\uFF0C\u5982\u4E0B\uFF1A</p><blockquote><p><strong>TPS = transactions / block_time</strong></p></blockquote><p>TPS\u8868\u793A\u533A\u5757\u94FE\u6BCF\u79D2\u80FD\u786E\u8BA4\u7684\u4EA4\u6613\u6570\uFF0C <code>transactions</code> \u662F\u7531\u533A\u5757\u5927\u5C0F<code>block_size</code>\u548C\u5E73\u5747\u6BCF\u7B14\u4EA4\u6613\u5927\u5C0F\u51B3\u5B9A\u7684\uFF0C\u800C\u533A\u5757\u5927\u5C0F\u53D7\u5168\u7F51\u7F51\u7EDC\u72B6\u6001<code>network_bandwidth</code> \u9650\u5236\uFF0C\u4E5F\u662F\u7531\u8BB0\u8D26\u8282\u70B9\u4E4B\u95F4\u7269\u7406\u5E26\u5BBD<code>witness_performance</code>\u51B3\u5B9A\u7684\u3002</p><p>\u8BB0\u8D26\u8282\u70B9\u7684\u4E2A\u6570<code>witness_count</code>\u76F4\u63A5\u51B3\u5B9A\u4E86\u7269\u7406\u5E26\u5BBD\u7684\u4E0A\u9650\uFF0C\u56E0\u4E3A\u8BB0\u8D26\u8282\u70B9\u6570\u91CF\u8D8A\u591A\uFF0C\u5219\u5BF9\u7269\u7406\u5E26\u5BBD\u8981\u6C42\u8D8A\u9AD8\uFF0C\u5BF9\u7F51\u7EDC\u7684\u7A33\u5B9A\u6027\u8981\u6C42\u4E5F\u8D8A\u9AD8\u3002</p><p>\u8981\u6CE8\u610F\u7684\u4E00\u70B9\u662F\u5728<code>DPoS</code>\u4E2D\uFF0C\u8BB0\u8D26\u8282\u70B9\u4E0D\u53EB\u505A\u77FF\u5DE5\uFF0C\u800C\u662F\u6539\u79F0\u4E3A\u89C1\u8BC1\u4EBA\uFF0C<code>Witness</code>\u3002\u6240\u4EE5\u8FD9\u4E2A\u516C\u5F0F\u53D8\u6210\u4E86\u4E0B\u9762\u7684\u6837\u5B50\uFF1A</p><blockquote><p>TPS = (block_size_network_bandwidth witness_performance)/(block_time * witness_count)</p></blockquote><p>\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0C\u8981\u63D0\u9AD8TPS\uFF0C\u53EF\u4EE5\u589E\u5927\u533A\u5757\u5927\u5C0F<code>block_size</code>\u3001\u63D0\u5347\u8BB0\u8D26\u8282\u70B9\u7F51\u7EDC\u5E26\u5BBD<code>network_bandwidth</code>\u3001\u63D0\u5347\u8BB0\u8D26\u8282\u70B9\u5904\u7406\u6027\u80FDwitness_performance\uFF0C\u51CF\u5C0F\u533A\u5757\u65F6\u95F4<code>block_time</code>\u3001\u51CF\u5C0F\u8BB0\u8D26\u8282\u70B9\u6570\u91CF<code>witness_count</code>\u3002</p><p>\u5206\u5B50\u9879\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0C\u5B83\u57FA\u672C\u53D7\u9650\u4E8E\u7269\u7406\u8D44\u6E90\u7684\u4E0A\u9650\uFF0C\u76EE\u524D\u5DE5\u4E1A\u6C34\u5E73\u5236\u9020\u7684\u7269\u7406\u8D44\u6E90\u7684\u4F7F\u7528\u4E0A\u9650\u57FA\u672C\u5C31\u662F\u6574\u4E2A\u9879\u7684\u4E0A\u9650\u4E86\uFF0C\u6240\u4EE5\u53EF\u64CD\u4F5C\u6027\u4E0D\u5927\u3002</p><p>\u800C\u5206\u6BCD\u9879\u662F\u7531\u5171\u8BC6\u7B97\u6CD5\u51B3\u5B9A\u7684\uFF0C\u6240\u4EE5\u6211\u4EEC\u4ECE\u533A\u5757\u65F6\u95F4\uFF0C\u4EE5\u53CA\u8BB0\u8D26\u8282\u70B9\u6570\u5165\u624B\uFF0CDPoS\u7B97\u6CD5\u4FBF\u6B63\u662F\u4ECE\u8FD9\u4E24\u9879\u7740\u624B\u7684\u3002</p><p>\u9996\u5148\u6539\u52A8\u7684\u4FBF\u662F\u9650\u5236\u8BB0\u8D26\u8282\u70B9\u7684\u6570\u91CF\uFF0C\u4E5F\u5C31\u662F\u89C1\u8BC1\u4EBA\u7684\u6570\u91CF\u3002</p><p>\u6211\u4EEC\u5728PoW\u548CPoS\u4E2D\u53EF\u4EE5\u770B\u5230\uFF0C\u6210\u4E3A\u8BB0\u8D26\u8282\u70B9\u662F\u65E0\u9700\u95E8\u69DB\u7684\uFF0C\u4F60\u53EF\u4EE5\u968F\u65F6\u53C2\u4E0E\u6316\u77FF\uFF0C\u968F\u65F6\u9000\u51FA\u3002</p><p>\u90A3\u8FD9\u4F1A\u5E26\u6765\u4EC0\u4E48\u95EE\u9898\u5462\uFF0C\u9996\u5148\u65E0\u6CD5\u786E\u5B9A\u8BB0\u8D26\u8282\u70B9\u7684\u6570\u91CF\uFF0C\u5176\u6B21\u65E0\u6CD5\u786E\u5B9A\u8BB0\u8D26\u8282\u70B9\u4E4B\u95F4\u7684\u7F51\u7EDC\u73AF\u5883\uFF0C\u8BB0\u8D26\u8282\u70B9\u6570\u8D8A\u591A\u7F51\u7EDC\u73AF\u5883\u8D8A\u590D\u6742\uFF0C\u8FD9\u4E9B\u4E0D\u786E\u5B9A\u6027\u4F1A\u589E\u5927\u7F51\u7EDC\u5206\u533A\u7684\u6982\u7387\uFF0C\u4ECE\u800C\u5BFC\u81F4\u533A\u5757\u94FE\u5206\u53C9\u3002</p><p>\u5982\u679C\u6211\u4EEC\u4E8B\u5148\u89C4\u5B9A\u597D\u8BB0\u8D26\u8282\u70B9\u7684\u6570\u91CF\uFF0C\u63A5\u7740\u8BA9\u5168\u7F51\u6240\u6709\u8282\u70B9\u53EF\u4EE5\u6295\u7968\u51B3\u5B9A\u54EA\u4E9B\u8282\u70B9\u53EF\u4EE5\u6210\u4E3A\u8BB0\u8D26\u8282\u70B9\uFF0C\u8FD9\u6837\u5C31\u9650\u5236\u5E76\u51CF\u5C0F\u4E86\u5206\u6BCD\u9879<code>witness_count</code>\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u6211\u4EEC\u4E5F\u79F0\u4F5C\u6295\u7968\u9009\u4E3E\u3002</p><p>\u56E0\u4E3A\u8BB0\u8D26\u8282\u70B9\u6570\u91CF\u4E0D\u591A\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EF\u4EE5\u5728\u5171\u8BC6\u7B97\u6CD5\u4E2D\u53EF\u4EE5<strong>\u89C4\u5B9A\u51FA\u5757\u65F6\u95F4</strong>\u4E3A\u4E00\u4E2A\u56FA\u5B9A\u503C\uFF0C\u8FD9\u4E2A\u503C\u53EF\u4EE5\u5F88\u5C0F\uFF0C\u901A\u8FC7\u8F6E\u6D41\u51FA\u5757\u7684\u65B9\u5F0F\u6765\u8FDB\u884C\u8BB0\u8D26\u3002</p><p>\u4EE5\u4E0A\u601D\u8DEF\u57FA\u672C\u5C31\u662FDPoS\u7684\u57FA\u672C\u8BBE\u8BA1\u601D\u8DEF\uFF0CBM\u8FD8\u4E3ADPoS\u7B97\u6CD5\u786E\u7ACB\u4E24\u4E2A\u539F\u5219\uFF1A</p><ul><li>\u6295\u7968\u9009\u4E3E\u8FC7\u7A0B\u4E00\u5B9A\u8981\u4FDD\u8BC1\u6700\u5927\u6743\u76CA\u6240\u6709\u8005\u6700\u7EC8\u80FD\u63A7\u5236\u5168\u7F51\uFF0C\u56E0\u4E3A\u4E00\u65E6\u51FA\u4E86\u95EE\u9898\uFF0C\u4ED6\u4EEC\u7684\u635F\u5931\u6700\u5927\uFF1B</li><li>\u4E0EPoW\u3001PoS\u4E00\u6837\uFF0C\u6240\u6709\u8282\u70B9\u4EC5\u627F\u8BA4\u201C\u6700\u957F\u201D\u94FE\u3002</li></ul><p>\u8FD9\u4E24\u4E2A\u539F\u5219\u786E\u7ACB\u4E86DPoS\u5171\u8BC6\u7684\u57FA\u672C\u7279\u6027\uFF0C\u7B2C\u4E00\u6761\u653E\u5927\u4E86PoS\u5171\u8BC6\u4F7F\u7528\u8005\u5C31\u662F\u8BB0\u8D26\u8005\u7684\u4F18\u70B9\uFF0C\u7B2C\u4E8C\u70B9\u5219\u89C4\u5B9A\u4E86\u5206\u53C9\u65F6\u7CFB\u7EDF\u5E94\u8BE5\u8868\u73B0\u7684\u884C\u4E3A\u3002</p><hr><h2 id="\u53C2\u8003" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a> \u53C2\u8003</h2>',59),g=e("p",null,"< >",-1),l={href:"https://eth.wiki/en/concepts/casper-proof-of-stake-compendium",target:"_blank",rel:"noopener noreferrer"},_={href:"https://eth.wiki/en/concepts/casper-proof-of-stake-compendium",target:"_blank",rel:"noopener noreferrer"},h={href:"https://eth.wiki/en/concepts/proof-of-stake-faqs",target:"_blank",rel:"noopener noreferrer"};function m(u,f){const o=r("ExternalLinkIcon");return a(),c("div",null,[d,e("blockquote",null,[g,e("p",null,[e("a",l,[p("https://eth.wiki/en/concepts/casper-proof-of-stake-compendium"),t(o)])]),e("p",null,[e("a",_,[p("https://eth.wiki/en/concepts/casper-proof-of-stake-compendium"),t(o)])]),e("p",null,[e("a",h,[p("https://eth.wiki/en/concepts/proof-of-stake-faqs"),t(o)])])])])}const S=n(i,[["render",m],["__file","5.EOS_DPOS_BFT.html.vue"]]);export{S as default};
