import{_ as i}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as n,c as o,a as e,b as a,d as r,e as l,r as c}from"./app.c2a49a29.js";const d={},s=e("h1",{id:"安装集成开发环境intellij-idea",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#安装集成开发环境intellij-idea","aria-hidden":"true"},"#"),a(" 安装集成开发环境Intellij IDEA")],-1),p=e("p",null,"IntelliJ IDEA 简称 IDEA，是业界公认为最好的 Java 集成开发工具，尤其是在代码自动提示、代码重构、代码版本管理、单元测试、代码分析等方面有着亮眼的发挥。",-1),b=e("p",null,"IDEA 产于捷克，开发人员以严谨著称的东欧程序员为主，分为社区版和付费版两个版本。如果只是学习 Java SE 社区版就足够用了。想要更多功能的话，比如说 Spring initializr 功能，需要下载付费版。",-1),g=e("p",null,"回想起我最初学 Java 的时候，老师要求我们在记事本上敲代码，在命令行中编译和执行 Java 代码，搞得全班三分之二的同学都做好了放弃学习 Java 的打算。",-1),v=e("p",null,"鉴于此，我强烈推荐大家使用集成开发工具，比如说 IntelliJ IDEA 来学习。",-1),h=e("h2",{id:"_01、下载-idea",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_01、下载-idea","aria-hidden":"true"},"#"),a(" 01、下载 IDEA")],-1),m={href:"https://www.jetbrains.com/idea/download",target:"_blank",rel:"noopener noreferrer"},f=l('<p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-d7ac2335-4c65-442c-931e-994e00db4235.png" alt="" loading="lazy"></p><p>UItimate 为付费版，可以免费试用，主要针对的是 Web 和企业开发用户；Community 为免费版，可以免费使用，主要针对的是 Java 初学者和安卓开发用户。</p><p>功能上的差别如下图所示。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-43e0ec45-acee-4c18-b0ff-ac7f4fc054f9.png" alt="" loading="lazy"></p><p>本篇教程主要针对的是 Java 初学者，所以选择免费版为例，点击「Download」进行下载。</p><p>稍等一分钟时间，大概 580M。</p><h2 id="_02、安装-idea" tabindex="-1"><a class="header-anchor" href="#_02、安装-idea" aria-hidden="true">#</a> 02、安装 IDEA</h2><p>双击运行 IDEA 安装程序，一步步傻瓜式的下一步就行了。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-8d0b11b3-99da-45c5-b9c3-a5d4e26077b5.png" alt="" loading="lazy"></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-3747b308-9b27-4068-9c47-46bc7098f8d4.png" alt="" loading="lazy"></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-5765a40b-e3c1-4021-b1de-73ae27774008.png" alt="" loading="lazy"></p><p>为了方便启动 IDEA，可以勾选【64-bit launcher】复选框。为了关联 Java 源文件，可以勾选【.java】复选框。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-15b6f6f4-308d-41d8-869a-4eb625f65eb0.png" alt="" loading="lazy"></p><p>点击【Install】后，需要静静地等待一会，大概一分钟的时间，趁机休息一下眼睛。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-2a80c17a-dbb6-4411-b88b-5bf1398db411.png" alt="" loading="lazy"></p><p>安装完成后的界面如下图所示。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-2afe2860-e3ef-4370-a0a5-f0075487f159.png" alt="" loading="lazy"></p><h2 id="_03、启动-idea" tabindex="-1"><a class="header-anchor" href="#_03、启动-idea" aria-hidden="true">#</a> 03、启动 IDEA</h2><p>回到桌面，双击运行 IDEA 的快捷方式，启动 IDEA。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-937da9b9-56e3-4970-ab50-e24f4b3549da.png" alt="" loading="lazy"></p><p>假装阅读完条款后，勾选同意复选框，点击【Continue】</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-0426d0d2-26eb-4376-bcaa-cede00fc2622.png" alt="" loading="lazy"></p><p>如果想要帮助 IDEA 收集改进信息，可以点击【Send Usage Statistics】；否则点击【Don&#39;t send】。</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/IDEA-install-config-b880a884-dbf5-4ce8-b0c1-345d60c72eff.png" alt="" loading="lazy"></p><p>到此，Intellij IDEA 的安装就完成了，很简单。</p><hr>',26);function I(_,E){const t=c("ExternalLinkIcon");return n(),o("div",null,[s,p,b,g,v,h,e("p",null,[a("IntelliJ IDEA 的官方下载地址为："),e("a",m,[a("https://www.jetbrains.com/idea/download/"),r(t)])]),f])}const A=i(d,[["render",I],["__file","IDEA-install-config.html.vue"]]);export{A as default};
