import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as r,c as s,a as e,b as i,d as o,f as t,r as g}from"./app.f90ce353.js";const l={},p=t('<h1 id="\u9879\u76EE\u7BA1\u7406-\u5982\u4F55\u505A\u597D\u9879\u76EE\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7BA1\u7406-\u5982\u4F55\u505A\u597D\u9879\u76EE\u7BA1\u7406" aria-hidden="true">#</a> \u9879\u76EE\u7BA1\u7406 - \u5982\u4F55\u505A\u597D\u9879\u76EE\u7BA1\u7406</h1><h2 id="_1-\u9879\u76EE\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_1-\u9879\u76EE\u6D41\u7A0B" aria-hidden="true">#</a> 1. \u9879\u76EE\u6D41\u7A0B</h2><p>\u6211\u628A\u9879\u76EE\u7BA1\u7406\u5206\u4E3A4\u4E2A\u9636\u6BB5\uFF0C\u5206\u522B\u4E3A\u9700\u6C42\u9636\u6BB5\u3001\u7814\u53D1\u9636\u6BB5\u3001\u6D4B\u8BD5\u9636\u6BB5\u548C\u4E0A\u7EBF\u9636\u6BB5\u3002</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220908225635605.png" alt="image-20220908225635605"></p><p>\u9879\u76EE\u7BA1\u7406\u8FC7\u7A0B\u4E2D\uFF0C\u9700\u8981\u501F\u52A9\u9879\u76EE\u7BA1\u7406\u5DE5\u5177\uFF0C\u6211\u4EEC\u4EE5TB\uFF08Teambition\uFF09\u4E3A\u4F8B\u3002</p><h3 id="_1-1-\u4EA7\u54C1\u89C4\u5212" tabindex="-1"><a class="header-anchor" href="#_1-1-\u4EA7\u54C1\u89C4\u5212" aria-hidden="true">#</a> 1.1 \u4EA7\u54C1\u89C4\u5212</h3><p>\u4E3A\u4EC0\u4E48\u6211\u9700\u8981\u5C06\u201C\u4EA7\u54C1\u89C4\u5212\u201D\u5355\u72EC\u5217\u51FA\u6765\uFF0C\u56E0\u4E3A\u8FD9\u4E2A\u771F\u7684\u592A\u91CD\u8981\u4E86\uFF0C<strong>\u4EA7\u54C1\u89C4\u5212\u5C31\u50CF\u662F\u6D77\u4E0A\u7684\u706F\u5854\uFF0C\u6307\u5F15\u4F60\u524D\u8FDB\u7684\u65B9\u5411\u3002</strong></p><p>\u6709\u4E86\u4EA7\u54C1\u89C4\u5212\uFF0C\u4EA7\u54C1\u53EF\u4EE5\u5C06\u9700\u6C42\u63D0\u524D\u653E\u5165\u9700\u6C42\u6C60\uFF0C\u6BCF\u4E2A\u8FED\u4EE3\u53EA\u505A\u9AD8\u4F18\u9700\u6C42\u3002</p><p>\u4E0B\u9762\u662F\u4EA7\u54C1\u89C4\u5212\u8981\u6C42\uFF1A</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220908225741223.png" alt="image-20220908225741223"></p><h3 id="_1-2-\u9700\u6C42\u8BC4\u5BA1" tabindex="-1"><a class="header-anchor" href="#_1-2-\u9700\u6C42\u8BC4\u5BA1" aria-hidden="true">#</a> 1.2 \u9700\u6C42\u8BC4\u5BA1</h3><p>\u9700\u6C42\u8BC4\u5BA1\u9636\u6BB5\uFF0C\u4E00\u5B9A\u662F\u9700\u8981\u4EA7\u54C1\u7ED9\u51FA\u5B8C\u6574\u7684\u9700\u6C42\u6587\u6863\uFF0C\u5982\u679C\u6709UI\u4EA4\u4E92\u7684\u5730\u65B9\uFF0C\u9700\u8981\u63D0\u524D\u7ED9\u51FA\u539F\u578B\u56FE\uFF0C<strong>\u62D2\u7EDD\u6CA1\u6709\u539F\u578B\u56FE\u7684\u9700\u6C42\u8BC4\u5BA1\u3002</strong></p><p><strong>\u9700\u6C42\u4E00\u5B9A\u662F\u8981\u975E\u5E38\u660E\u786E\uFF0C\u6700\u597D\u662F\u80FD\u7EC6\u5316\u5230\u5177\u4F53\u7684\u529F\u80FD\u70B9\uFF0C\u62D2\u7EDD\u6A21\u7CCA\u9700\u6C42\uFF08\u6BD4\u5982\u4E00\u53E5\u8BDD\u9700\u6C42\uFF09\u3002</strong></p><p>\u4E0B\u9762\u662F\u9700\u6C42\u8BC4\u5BA1\u8981\u6C42\uFF1A</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220908225858507.png" alt="image-20220908225858507"></p><p>\u4E3A\u4E86\u63D0\u9AD8\u9700\u6C42\u8BC4\u5BA1\u7684\u6548\u7387\uFF0C\u6211\u4EEC\u5728\u9700\u6C42\u8BC4\u5BA1\u524D\uFF0C\u9700\u8981\u505A\u5F88\u591A\u51C6\u5907\u5DE5\u4F5C\uFF0C\u6BD4\u5982\u9700\u6C42\u524D\u671F\u6C9F\u901A\u3001\u7814\u53D1\u521D\u6B65\u8BC4\u4F30\uFF0C<strong>\u524D\u671F\u5DE5\u4F5C\u51C6\u5907\u5230\u4F4D\u540E\uFF0C\u9700\u6C42\u8BC4\u5BA1\u671F\u95F4\u5C31\u53EF\u4EE5\u4E3B\u8981\u8BA8\u8BBA\u95EE\u9898\uFF0C\u907F\u514D\u4E00\u4E2A\u9700\u6C42\u53CD\u590D\u6C9F\u901A\u7684\u60C5\u51B5\u3002</strong></p><p>\u4E0B\u9762\u662F\u9700\u6C42\u9636\u6BB5\u6D41\u7A0B\uFF08\u6709\u4E9B\u73AF\u8282\u53EF\u4EE5\u5220\u6389\uFF0C\u4EC5\u4F9B\u53C2\u8003\uFF09\uFF1A</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220908225954889.png" alt="image-20220908225954889"><h3 id="_1-3-\u7814\u53D1\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_1-3-\u7814\u53D1\u9636\u6BB5" aria-hidden="true">#</a> 1.3 \u7814\u53D1\u9636\u6BB5</h3><p><strong>\u6280\u672F\u65B9\u6848\u662F\u6574\u4E2A\u9879\u76EE\u7684\u7075\u9B42\uFF0C\u5F88\u591A\u9879\u76EE\u5230\u540E\u671F\u51FA\u73B0\u95EE\u9898\uFF0C\u5F88\u5927\u539F\u56E0\u5C31\u662F\u6280\u672F\u65B9\u6848\u6CA1\u505A\u597D\u3002</strong></p><p><strong>\u9879\u76EE\u6392\u671F\u7528\u4E8E\u63A7\u5236\u6574\u4F53\u9879\u76EE\u7684\u8282\u594F</strong>\uFF0C\u6709\u4EE5\u4E0B\u51E0\u70B9\u7ECF\u9A8C\u4E4B\u8C08\uFF1A</p><ol><li>\u9879\u76EE\u6392\u671F\u6BCF\u5929\u4E0D\u8981\u6392\u6EE1\uFF0C<strong>\u5EFA\u8BAE\u9884\u7559 20% buffer</strong>;</li><li>\u5982\u679C\u9879\u76EE\u65F6\u95F4\u7D27\uFF0C\u53EF\u4EE5\u91C7\u7528<strong>\u5206\u6279\u63D0\u6D4B</strong>\u7684\u65B9\u5F0F\uFF1B</li><li>\u6392\u671F\u8981\u6709<strong>\u91CC\u7A0B\u7891</strong>\uFF0C\u5F00\u53D1\u3001\u8054\u8C03\u3001\u63D0\u6D4B\u3001\u4E0A\u7EBF\u3001\u9A8C\u6536\u7B49\uFF1B</li><li><strong>\u9879\u76EE\u6392\u671F\u4E0D\u80FD\u4EC5\u5230\u4E0A\u7EBF\u9636\u6BB5\uFF0C\u8FD8\u9700\u5305\u62EC\u7EBF\u4E0A\u7070\u5EA6\u548C\u9879\u76EE\u9A8C\u6536\uFF1B</strong></li><li>\u524D\u7AEF\u6392\u671F\u4F9D\u8D56UI\u8BBE\u8BA1\u3002</li></ol><p>\u4E0B\u9762\u662F\u9879\u76EE\u6392\u671F\u8981\u6C42\uFF1A</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220908230217459.png" alt="image-20220908230217459"></p><p>\u4E0B\u9762\u662F\u7814\u53D1\u9636\u6BB5\u6D41\u7A0B\uFF08\u6709\u4E9B\u73AF\u8282\u53EF\u4EE5\u5220\u6389\uFF0C\u4EC5\u4F9B\u53C2\u8003\uFF09\uFF1A</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220908230247545.png" alt="image-20220908230247545"><h3 id="_1-4-\u6D4B\u8BD5-\u4E0A\u7EBF\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_1-4-\u6D4B\u8BD5-\u4E0A\u7EBF\u9636\u6BB5" aria-hidden="true">#</a> 1.4 \u6D4B\u8BD5&amp;\u4E0A\u7EBF\u9636\u6BB5</h3><p><strong>\u4E0A\u7EBF\u65B9\u6848\u53EF\u4EE5\u4E3A\u7EBF\u4E0A\u7684\u7A33\u5B9A\u6027\u4FDD\u9A7E\u62A4\u822A\uFF0C\u91CD\u8981\u6027\u4E0D\u8A00\u800C\u55BB\u3002</strong> \u56E0\u4E3A\u4E0A\u7EBF\u5BFC\u81F4\u4E25\u91CD\u7684\u7EBF\u4E0A\u95EE\u9898\uFF0C\u8FD9\u4E2A\u9879\u76EE\u53EF\u80FD\u5C31\u767D\u5E72\u4E86\u3002</p><p>\u5173\u4E8E\u6D4B\u8BD5&amp;\u4E0A\u7EBF\u6D41\u7A0B\uFF0C\u6709\u4EE5\u4E0B\u51E0\u70B9\u7ECF\u9A8C\u4E4B\u8C08\uFF1A</p><ol><li>\u6D4B\u8BD5\u73AF\u8282\u9700\u8981\u5927\u5BB6\u4E00\u8D77\u8FC7<strong>\u6D4B\u8BD5Case</strong>\uFF1B</li><li>\u63D0\u6D4B\u524D\uFF0C\u6709\u7684\u9879\u76EE\u8FD8\u9700\u8981\u505A<strong>\u9879\u76EE\u6F14\u793A</strong>\uFF1B</li><li>\u4E0A\u7EBF\u524D\uFF0C\u9700\u8981\u4E00\u8D77\u8FC7<strong>\u4E0A\u7EBF\u65B9\u6848</strong>\uFF0C\u7A81\u51FA\u98CE\u9669\u70B9\uFF1B</li><li>\u4E0A\u7EBF\u540E\u53EF\u80FD\u9700\u8981<strong>\u5C0F\u6D41\u91CF\u9A8C\u8BC1\uFF0C\u6216\u8005\u7070\u5EA6</strong>\uFF1B</li><li>\u6709\u7684\u9879\u76EE\u8FD8\u6709<strong>\u9879\u76EE\u9A8C\u6536</strong>\u73AF\u8282\uFF0C\u6700\u540E\u624D\u5168\u5458\u5F00\u653E\u3002</li></ol><p>\u4E0B\u9762\u662F\u6D4B\u8BD5&amp;\u4E0A\u7EBF\u9636\u6BB5\u6D41\u7A0B\uFF08\u6709\u4E9B\u73AF\u8282\u53EF\u4EE5\u5220\u6389\uFF0C\u4EC5\u4F9B\u53C2\u8003\uFF09\uFF1A</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220908230417571.png" alt="image-20220908230417571"><h2 id="_2-\u9700\u6C42\u53D8\u66F4" tabindex="-1"><a class="header-anchor" href="#_2-\u9700\u6C42\u53D8\u66F4" aria-hidden="true">#</a> 2. \u9700\u6C42\u53D8\u66F4</h2><p>\u91CD\u70B9\u8BF4\u4E00\u4E0B\u9700\u6C42\u53D8\u66F4\uFF0C\u8FD9\u4E5F\u662F\u5F88\u591A\u7A0B\u5E8F\u5458\u5934\u75BC\u7684\u95EE\u9898\uFF0C\u53EA\u8981\u505A\u5230\u6B63\u786E\u628A\u63A7\uFF0C\u5176\u5B9E\u4E5F\u6CA1\u90A3\u4E48\u53EF\u6015\u3002</p><p>\u4E0B\u9762\u662F\u9700\u6C42\u53D8\u66F4\u8981\u6C42\uFF1A</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220908230523576.png" alt="image-20220908230523576"></p><p><strong>\u603B\u7ED3\u4E00\u4E0B\uFF1A\u9700\u6C42\u53D8\u66F4\u8D8A\u65E9\u8D8A\u597D\uFF0C\u53D8\u66F4\u9700\u6C42\uFF0C\u9700\u8981\u8C03\u6574\u6392\u671F\uFF0C\u4E34\u8FD1\u4E0A\u7EBF\uFF0C\u539F\u5219\u4E0A\u4E0D\u518D\u5141\u8BB8\u9700\u6C42\u53D8\u66F4\uFF0C\u5426\u5219\u9700\u8981\u9886\u5BFC\u5BA1\u6279\u3002</strong></p><h2 id="_3-\u6BCF\u65E5\u7AD9\u4F1A" tabindex="-1"><a class="header-anchor" href="#_3-\u6BCF\u65E5\u7AD9\u4F1A" aria-hidden="true">#</a> 3. \u6BCF\u65E5\u7AD9\u4F1A</h2><p>\u4E3A\u4EC0\u4E48\u8981\u63D0\u8FD9\u4E2A\u5462\uFF0C\u56E0\u4E3A\u6709\u7684\u540C\u5B66\u5E73\u65F6\u95F7\u58F0\u4E0D\u54CD\uFF0C\u6700\u540E\u7ED9\u4F60\u618B\u5927\u62DB\u3002<strong>\u6240\u4EE5\u4F60\u9700\u8981\u77E5\u9053\u5927\u5BB6\u6BCF\u5929\u7684\u5DE5\u4F5C\u8FDB\u5EA6\u3001\u95EE\u9898\u548C\u98CE\u9669\uFF0C\u65B9\u4FBF\u4F60\u63A8\u52A8\u548C\u534F\u8C03\u89E3\u51B3\uFF0C\u751A\u81F3\u4F1A\u5BF9\u9879\u76EE\u8282\u594F\u4E34\u65F6\u8C03\u6574\u3002</strong></p><p>\u7AD9\u4F1A\u600E\u4E48\u5F00\uFF0C\u8FD9\u4E2A\u4E5F\u6709\u8BB2\u7A76\uFF0C<strong>10-15\u5206\u949F\u6700\u4F73</strong>\uFF0C\u6BCF\u4F4D\u540C\u5B66\u90FD\u8981\u53C2\u4E0E\uFF1A</p><ol><li><strong>\u6628\u5929\u505A\u4E86\u4EC0\u4E48\uFF1F</strong></li><li><strong>\u4ECA\u5929\u6253\u7B97\u505A\u4EC0\u4E48\uFF1F</strong></li><li><strong>\u9047\u5230\u4EC0\u4E48\u95EE\u9898\uFF1F</strong></li></ol><p>\u5907\u6CE8\uFF1A\u7AD9\u4F1A\u65F6\u95F4\u65E9\u4E0A\u6216\u665A\u4E0A\u6700\u4F73\uFF0C\u65B9\u5F0F\u6BD4\u8F83\u7075\u6D3B\uFF0C\u524D\u671F\u53EF\u4EE5\u6BCF\u54682-3\u6B21\uFF0C\u540E\u671F\u5C31\u6BCF\u5929\u90FD\u5F00\u3002</p><h2 id="_4-\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#_4-\u603B\u7ED3" aria-hidden="true">#</a> 4. \u603B\u7ED3</h2><p>\u603B\u7ED3\u4E00\u4E0B\u9879\u76EE\u7BA1\u7406\u7684\u51E0\u4E2A\u91CD\u8981\u7684\u70B9\uFF1A</p><ol><li><strong>\u4EA7\u54C1\u89C4\u5212</strong>\u5C31\u50CF\u662F\u6D77\u4E0A\u7684\u706F\u5854\uFF0C\u4E0D\u80FD\u4E71\u6253\u4E00\u6C14\uFF1B</li><li><strong>\u9700\u6C42\u6587\u6863</strong>\u4E00\u5B9A\u8981\u5C3D\u53EF\u80FD\u8BE6\u7EC6\uFF0C\u62D2\u7EDD\u65E0\u539F\u578B\u7684\u9700\u6C42\u8BC4\u5BA1\uFF1B</li><li><strong>\u6280\u672F\u65B9\u6848</strong>\u662F\u6574\u4E2A\u9879\u76EE\u7684\u7075\u9B42\uFF0C\u8FD9\u5757\u591A\u6295\u5165\u65F6\u95F4\u7EDD\u5BF9\u4E0D\u4E8F\uFF1B</li><li><strong>\u9879\u76EE\u6392\u671F</strong>\u6709\u91CC\u7A0B\u7891\u3001\u6709buffer\uFF0C\u6392\u671F\u9700\u5305\u62EC\u7EBF\u4E0A\u7070\u5EA6\u548C\u9879\u76EE\u9A8C\u6536\u65F6\u95F4\uFF1B</li><li><strong>Code Review\u3001\u6D4B\u8BD5Case</strong>\u90FD\u4E0D\u80FD\u5C11\uFF1B</li><li><strong>\u4E0A\u7EBF\u65B9\u6848</strong>\u8981\u9884\u77E5\u98CE\u9669\uFF0C\u4E3A\u7EBF\u4E0A\u7684\u7A33\u5B9A\u6027\u4FDD\u9A7E\u62A4\u822A\u3002</li></ol><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',46),c={href:"https://juejin.cn/post/7053382185587310605",target:"_blank",rel:"noopener noreferrer"};function h(d,m){const a=g("ExternalLinkIcon");return r(),s("div",null,[p,e("p",null,[e("a",c,[i("2\u5E74\u7ECF\u9A8C\u603B\u7ED3\uFF0C\u544A\u8BC9\u4F60\u5982\u4F55\u505A\u597D\u9879\u76EE\u7BA1\u7406"),o(a)])])])}const u=n(l,[["render",h],["__file","Interface-design-too-many-dependencies.html.vue"]]);export{u as default};
