import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as n,c as t,a as e,b as a,d as p,e as s,r as h}from"./app.b2391a7a.js";const d={},i=s('<h2 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h2><p>\u8FD9\u4E48\u591A\u5E74\u6765\u6211\u6709\u4E00\u4EF6\u4E8B\u60C5\u4E00\u76F4\u5728\u575A\u6301\uFF0C\u90A3\u5C31\u662F\u505A\u7B14\u8BB0\u548C\u5199\u535A\u5BA2\u3002</p><p>\u505A\u7B14\u8BB0\uFF0C\u5C31\u662F\u8BB0\u5F55\u5B66\u4E60\u4E2D\u5927\u5927\u5C0F\u5C0F\u7684\u4E8B\u60C5\uFF0C\u53EF\u80FD\u662F\u9762\u8BD5\u95EE\u9898\uFF0C\u53EF\u80FD\u662F\u4E00\u5468\u7684\u5B66\u4E60\u8BA1\u5212\uFF0C\u4E5F\u53EF\u80FD\u77E5\u8BC6\u4E00\u4E2A\u77E5\u8BC6\u70B9\uFF0C\u603B\u5F52\u90FD\u662F\u503C\u5F97\u8BB0\u5F55\u7684\u4E1C\u897F\uFF0C\u5BF9\u6211\u6765\u8BF4\uFF0C\u5C31\u662F\u4E00\u79CD\u79EF\u7D2F\u3002\u800C\u5BF9\u4E8E\u535A\u5BA2\uFF0C\u6211\u4ECE\u4E00\u5F00\u59CB\u53EA\u7528\u4E8E\u8BB0\u5F55\u9879\u76EE\uFF0C\u5230\u540E\u6765\u505A\u8F6C\u8F7D\uFF0C\u518D\u5230\u540E\u6765\u5199\u539F\u521B\uFF0C\u6574\u7406\u7CFB\u5217\u6587\u7AE0\uFF0C\u5219\u66F4\u50CF\u662F\u4E00\u79CD\u6C89\u6DC0\u3002</p><p>\u4F46\u662F\u5728\u6625\u62DB\u521A\u521A\u7ED3\u675F\u7684\u8FD9\u6BB5\u65F6\u95F4\uFF0C\u6211\u53D1\u73B0\u4E00\u4E2A\u95EE\u9898\uFF0C\u4E4B\u524D\u5B66\u8FC7\u7684\u4E1C\u897F\u5FD8\u8BB0\u4E86\u5F88\u591A\uFF0C\u7279\u522B\u662F\u90A3\u4E9B\u7406\u89E3\u7684\u4E0D\u591F\u6DF1\u7684\u77E5\u8BC6\u70B9\uFF0C\u603B\u662F\u7279\u522B\u5BB9\u6613\u5FD8\u8BB0\u3002\u53E6\u5916\u6211\u53D1\u73B0\uFF0C\u867D\u7136\u6211\u5728\u7B14\u8BB0\u4E2D\u8BB0\u5F55\u4E86\u5F88\u591A\u7684\u77E5\u8BC6\u70B9\u548C\u9762\u8BD5\u9898\uFF0C\u4F46\u662F\u5F80\u5F80\u6211\u53EA\u770B\u8FC7\u4E00\u6B21\uFF0C\u4E0D\u4F1A\u518D\u53BB\u770B\u7B2C\u4E8C\u6B21\u3002</p><p><strong>\u8FD9\u4E5F\u610F\u5473\u7740\uFF0C\u867D\u7136\u8BB0\u5F55\u7684\u5185\u5BB9\u5F88\u591A\uFF0C\u4F46\u662F\u771F\u6B63\u6D88\u5316\u5438\u6536\u7684\u5185\u5BB9\u5F88\u5C11\uFF0C\u8111\u5B50\u91CC\u5145\u65A5\u7740\u603B\u662F\u90A3\u4E9B\u96F6\u788E\u7684\u77E5\u8BC6\u70B9\u548C\u9762\u8BD5\u95EE\u9898\uFF0C\u5BF9\u4E8E\u5B8C\u6574\u7684\u77E5\u8BC6\u4F53\u7CFB\u77E5\u4E4B\u751A\u5C11\u3002\u8FD9\u4E9B\u95EE\u9898\u5728\u6625\u62DB\u671F\u95F4\u4E5F\u4E0D\u65AD\u5730\u66B4\u9732\u51FA\u6765\uFF0C\u8BA9\u6211\u601D\u8003\u4E86\u5F88\u4E45\u3002</strong></p><p>\u9762\u5BF9\u5982\u6B64\u7A98\u5883\uFF0C\u6211\u60F3\u505A\u51FA\u6539\u53D8\uFF0C\u8D81\u7740\u73B0\u5728\u65F6\u95F4\u5145\u88D5\uFF0C\u6211\u60F3\u8981\u4E3A\u8FD9\u4E9B\u5185\u5BB9\u505A\u4E00\u6B21\u51CF\u6CD5\uFF0C\u5E76\u4E14\u501F\u6B64\u673A\u4F1A\uFF0C\u63A8\u7FFB\u81EA\u5DF1\u539F\u6709\u7684\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u91CD\u5EFA\u65B0\u7684\u77E5\u8BC6\u6846\u67B6\u3002\u7B80\u5355\u8BF4\u6765\uFF0C\u5C31\u662F\u91CD\u65B0\u5F00\u59CB\u5B66\u4E60Java\u540E\u7AEF\uFF0C\u8FD9\u6B21\u6211\u8981\u7528\u4E00\u79CD\u66F4\u9AD8\u6548\u7684\u65B9\u5F0F\uFF0C\u907F\u514D\u8D70\u4E4B\u524D\u8D70\u7684\u5F2F\u8DEF\uFF0C\u8981\u7528\u6700\u9AD8\u6548\uFF0C\u6700\u5408\u7406\u7684\u65B9\u5F0F\u53BB\u590D\u4E60\u3002\u7531\u4E8E\u6211\u4E4B\u524D\u5DF2\u7ECF\u6709\u57FA\u7840\uFF0C\u6240\u4EE5\u6211\u5BF9\u5B8C\u6210\u8FD9\u4E00\u76EE\u6807\u6709\u4FE1\u5FC3\uFF0C\u76F8\u5E94\u5730\u6211\u4E5F\u4E3A\u6B64\u505A\u51FA\u4E86\u660E\u786E\u4E14\u8BE6\u7EC6\u7684\u5B66\u4E60\u8BA1\u5212\u3002</p><h3 id="\u5177\u4F53\u53EF\u9760\u7684\u5B66\u4E60\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u5177\u4F53\u53EF\u9760\u7684\u5B66\u4E60\u8BA1\u5212" aria-hidden="true">#</a> <strong>\u201C\u5177\u4F53\u53EF\u9760\u7684\u5B66\u4E60\u8BA1\u5212\u201D</strong></h3><p>\u5728\u4E09\u4E2A\u6708\u7684\u65F6\u95F4\u91CC\uFF0C\u6211\u9996\u5148\u6309\u7167Java\u540E\u7AEF\u8DEF\u7EBF\u56FE\u5B89\u6392\u597D\u590D\u4E60\u8BA1\u5212\uFF0C\u6BCF\u4E2A\u77E5\u8BC6\u70B9\u90FD\u4F1A\u5BF9\u5E94\u5B89\u6392\u4E00\u6BB5\u65F6\u95F4\uFF0C\u6BD4\u5982\u6211\u53EF\u80FD\u82B1\u4E00\u5929\u65F6\u95F4\u590D\u4E60\u201CJava\u53CD\u5C04\u201D\uFF0C\u4E24\u5929\u65F6\u95F4\u590D\u4E60\u201C\u8BBE\u8BA1\u6A21\u5F0F\u201D\uFF0C\u4E00\u5468\u7684\u65F6\u95F4\u7528\u4E8E\u590D\u4E60&quot;JVM\u865A\u62DF\u673A\u201D\u3002\u6211\u4E00\u822C\u4F1A\u5728\u6708\u521D\u505A\u597D\u6574\u4E2A\u6708\u7684\u8BA1\u5212\uFF0C\u7136\u540E\u6839\u636E\u8FDB\u5EA6\u505A\u4E00\u4E9B\u5FAE\u8C03\uFF0C\u4F46\u662F\u57FA\u672C\u4E0A\u6211\u90FD\u53EF\u4EE5\u8DDF\u4E0A\u8FDB\u5EA6\uFF0C\u5E76\u4E14\u662F\u5728\u590D\u4E60\u5230\u4F4D\u7684\u524D\u63D0\u4E0B\u3002</p><p>\u6240\u4EE5\u6211\u89C9\u5F97\uFF0C\u5BF9\u4E8E\u79CB\u62DB\u8FD9\u4E00\u573A\u82E6\u6218\uFF0C\u6307\u5B9A\u8BA1\u5212\u5C24\u4E3A\u91CD\u8981\uFF0C\u4E00\u65E6\u8BA1\u5212\u5B9A\u4E0B\u6765\uFF0C\u6218\u7565\u76EE\u6807\u6E05\u6670\uFF0C\u5BF9\u5E94\u7684\u6218\u672F\u5236\u5B9A\u4E5F\u4F1A\u53D8\u5F97\u6E05\u6670\uFF0C\u6267\u884C\u529B\u4E5F\u4F1A\u968F\u4E4B\u53D8\u5F3A\u3002</p><h3 id="\u5199\u535A\u5BA2\u6574\u5408\u77E5\u8BC6\u70B9" tabindex="-1"><a class="header-anchor" href="#\u5199\u535A\u5BA2\u6574\u5408\u77E5\u8BC6\u70B9" aria-hidden="true">#</a> <strong>\u201C\u5199\u535A\u5BA2\u6574\u5408\u77E5\u8BC6\u70B9\u201D</strong></h3><p>\u81F3\u4E8E\u590D\u4E60\u65B9\u6CD5\uFF0C\u6211\u4E3B\u8981\u901A\u8FC7\u770B\u9AD8\u8D28\u91CF\u535A\u5BA2\uFF0C\u5E76\u4E14\u7ED3\u5408\u4EE3\u7801\u5B9E\u8DF5\u7684\u65B9\u5F0F\u5DE9\u56FA\u8FD9\u90E8\u5206\u77E5\u8BC6\u70B9\uFF0C\u6BD4\u5982\u4ECA\u5929\u5B66\u4E60\u201Cconcurrenthashmap\u201D\uFF0C\u6211\u4F1A\u53BB\u627E\u4E24\u4E09\u7BC7\u6BD4\u8F83\u597D\u7684\u535A\u5BA2\u5148\u770B\u770B\uFF0C\u4E3B\u8981\u662F\u6E90\u7801\u89E3\u8BFB\u65B9\u9762\u7684\uFF0C\u7136\u540E\u6211\u4F1A\u628A\u5B83\u4EEC\u8FDB\u884C\u6574\u5408\uFF0C\u5982\u679C\u6709\u9057\u6F0F\u7684\u77E5\u8BC6\u70B9\u6211\u4F1A\u518D\u8FDB\u884C\u8865\u5145\uFF0C\u6709\u65F6\u5019\u6211\u8FD8\u4F1A\u81EA\u5DF1\u53BB\u770B\u770BJDK\u6E90\u7801\uFF0C\u4EE5\u4FBF\u66F4\u597D\u5730\u7406\u89E3\u535A\u5BA2\u5185\u5BB9\uFF0C\u5B8C\u6210\u77E5\u8BC6\u6574\u5408\u4E4B\u540E\uFF0C\u6211\u5C31\u4F1A\u5BF9\u5E94\u5730\u6574\u7406\u51FA\u4E00\u7BC7\u535A\u5BA2\u51FA\u6765\uFF0C\u53D1\u5728\u6211\u7684\u4E2A\u4EBA\u535A\u5BA2\u4E0A\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u5F53\u6211\u5B8C\u6210\u4E86\u4E00\u6574\u4E2A\u4E13\u9898\u7684\u590D\u4E60\u4E4B\u540E\uFF0C\u6211\u4F1A\u628A\u8FD9\u4E9B\u6587\u7AE0\u6574\u7406\u6210\u4E00\u4E2A\u4E13\u9898\uFF0C\u6BD4\u5982\u4E0A\u9762\u8BF4\u7684\u201Cconcurrenthashmap\u201D\uFF0C\u5B9E\u9645\u4E0A\u5C5E\u4E8EJava\u5E76\u53D1\u5305\uFF0C\u6240\u4EE5\u6211\u4F1A\u4E13\u95E8\u505A\u4E00\u4E2A\u535A\u5BA2\u4E13\u680F\uFF0C\u7528\u6765\u5B8C\u6210Java\u5E76\u53D1\u7CFB\u5217\u7684\u6587\u7AE0\u4E13\u9898\u3002<strong>\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u6587\u7AE0\u4E13\u9898\uFF0C\u6211\u90FD\u4F1A\u5148\u7406\u6E05\u8FD9\u4E2A\u4E13\u9898\u4E00\u5171\u6709\u54EA\u4E9B\u5185\u5BB9\uFF0C\u7136\u540E\u518D\u5F00\u59CB\u6574\u7406\u3002\u6BD4\u5982\u5BF9\u4E8EJava\u5E76\u53D1\u5305\uFF0C\u6211\u4F1A\u5148\u5199Java\u591A\u7EBF\u7A0B\u57FA\u7840\u7684\u6587\u7AE0\uFF0C\u518D\u5199JMM\u5185\u5B58\u6A21\u578B\u7684\u6587\u7AE0\uFF0C\u63A5\u7740\u4E00\u6B65\u6B65\u7740\u624B\u5199Java\u7EBF\u7A0B\u6C60\uFF0C\u963B\u585E\u961F\u5217\uFF0C\u5DE5\u5177\u7C7B\uFF0C\u539F\u5B50\u7C7B\u7B49\u7B49\u3002\u8FD9\u6837\u4E00\u6765\u8FD9\u90E8\u5206\u5185\u5BB9\u5C31\u590D\u4E60\u5B8C\u6BD5\u4E86\uFF0C\u5199\u7CFB\u5217\u6587\u7AE0\u7684\u597D\u5904\u5C31\u5728\u4E8E\uFF0C\u6211\u53EF\u4EE5\u4ECE\u5934\u5230\u5C3E\u7406\u6E05\u8109\u7EDC\uFF0C\u5E76\u4E14\u5BF9\u4E8E\u6BCF\u4E00\u90E8\u5206\u7684\u77E5\u8BC6\u70B9\u90FD\u505A\u4E86\u6BD4\u8F83\u597D\u7684\u603B\u7ED3\u3002</strong></p><p>\u5BF9\u4E8E\u535A\u5BA2\u7684\u9009\u62E9\uFF0C\u6211\u5438\u53D6\u4E86\u4E4B\u524D\u7684\u6559\u8BAD\uFF0C\u5B81\u613F\u82B1\u534A\u5C0F\u65F6\u770B\u4E00\u7BC7\u9AD8\u8D28\u91CF\u6587\u7AE0\uFF0C\u4E5F\u4E0D\u82B110\u5206\u949F\u770B5\u7BC7\u70C2\u6587\u7AE0\u3002\u6DF1\u5EA6\u9605\u8BFB\u7684\u597D\u5904\uFF0C\u5C31\u662F\u53EF\u4EE5\u8BA9\u8FD9\u90E8\u5206\u5185\u5BB9\u66F4\u597D\u5730\u878D\u5165\u4F60\u8111\u5185\u7684\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u800C\u4E0D\u662F\u50CF\u5176\u4ED6\u5FEB\u9910\u6587\u7AE0\u4E00\u6837\u8F6C\u77AC\u5373\u901D\u3002</p><h3 id="\u505A\u9879\u76EE\u5DE9\u56FA\u5B9E\u8DF5\u80FD\u529B" tabindex="-1"><a class="header-anchor" href="#\u505A\u9879\u76EE\u5DE9\u56FA\u5B9E\u8DF5\u80FD\u529B" aria-hidden="true">#</a> <strong>\u201C\u505A\u9879\u76EE\u5DE9\u56FA\u5B9E\u8DF5\u80FD\u529B\u201D</strong></h3><p>\u7531\u4E8E\u4E4B\u524D\u5728\u5B9E\u4E60\u671F\u95F4\u53C2\u52A0\u7684\u9879\u76EE\u90FD\u6BD4\u8F83\u5927\uFF0C\u6211\u63A5\u89E6\u7684\u6A21\u5757\u4E5F\u6BD4\u8F83\u5355\u4E00\uFF0C\u6CA1\u6709\u5BF9\u6574\u4F53\u9879\u76EE\u6709\u4E00\u4E2A\u5F88\u597D\u7684\u628A\u63E1\uFF0C\u6240\u4EE5\u6211\u51B3\u5B9A\u8D81\u8FD9\u6BB5\u65F6\u95F4\u518D\u5DE9\u56FA\u4E00\u4E0B\u6211\u7684\u9879\u76EE\u5B9E\u8DF5\u80FD\u529B\uFF0C\u8FD9\u91CC\u7684\u80FD\u529B\u4E3B\u8981\u662F\u6307\u7684\u662F\u5BF9\u9879\u76EE\u67B6\u6784\u7684\u628A\u63E1\u80FD\u529B\uFF0C\u4EE5\u53CA\u5BF9\u4E1A\u52A1\u5F00\u53D1\u7684\u719F\u7EC3\u5EA6\uFF0C\u5F53\u7136\u4E5F\u5305\u62EC\u5BF9\u5404\u79CD\u5E38\u7528\u540E\u7AEF\u6280\u672F\u7684\u719F\u6089\u7A0B\u5EA6\u3002</p><p><strong>\u6211\u82B1\u4E86\u5927\u6982\u4E00\u4E2A\u6708\u7684\u65F6\u95F4\u5B8C\u6210\u4E86\u4E24\u4E2A\u9879\u76EE\u7684\u5F00\u53D1\uFF0C\u5F53\u7136\u4E3B\u8981\u4E5F\u662F\u6A21\u4EFF\u4E24\u4E2A\u5F00\u6E90\u9879\u76EE\u505A\u4E86\uFF0C\u8FD9\u4E24\u4E2A\u9879\u76EE\u90FD\u4F7F\u7528SpringBoot\u5FEB\u901F\u5F00\u53D1\uFF0C\u5E76\u4E14\u7528\u5230\u4E00\u4E9B\u5E38\u7528\u7684\u540E\u7AEF\u6280\u672F\u6BD4\u5982redis\uFF0C\u4E91\u5B58\u50A8\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u5E38\u89C1Web\u6846\u67B6\uFF0C\u9664\u6B64\u4E4B\u5916\u8FD8\u6D89\u53CA\u5230\u4E86solr\uFF0C\u722C\u866B\u7B49\u6280\u672F\u3002\u867D\u7136\u9879\u76EE\u4E0D\u7B97\u5F88\u96BE\uFF0C\u4F46\u662F\u6211\u5728\u8FD9\u6BB5\u65F6\u95F4\u91CC\u5F88\u5FEB\u5730\u719F\u6089\u4E86\u5B8C\u6574\u9879\u76EE\u5F00\u53D1\u7684\u6D41\u7A0B\uFF0C\u5E76\u4E14\u6BCF\u5929\u505A\u8FED\u4EE3\uFF0C\u901A\u8FC7Git\u6765\u8DDF\u8FDB\u7248\u672C\uFF0C\u6BCF\u4E2A\u7248\u672C\u90FD\u4F1A\u5199\u6E05\u6240\u505A\u7684\u5185\u5BB9\uFF0C\u8FD9\u4E5F\u8BA9\u6211\u5BF9\u9879\u76EE\u7684\u67B6\u6784\u975E\u5E38\u719F\u6089\u3002</strong></p><p>\u5728\u9879\u76EE\u4E4B\u4F59\uFF0C\u6211\u4E5F\u627E\u4E00\u4E9B\u5E38\u7528\u7684\u540E\u7AEF\u7EC4\u4EF6\u6765\u8DD1\u4E00\u8DD1demo\uFF0C\u4EE5\u4FBF\u8BA9\u6211\u5BF9\u8FD9\u4E9B\u6280\u672F\u6709\u4E00\u4E2A\u76F4\u89C2\u7684\u4E86\u89E3\uFF0C\u6BD4\u5982\u9762\u8BD5\u5E38\u95EE\u7684dubbo\uFF0Czookeeper\uFF0C\u6D88\u606F\u961F\u5217\u7B49\u7EC4\u4EF6\u3002\u8FD9\u4E9B\u5C1D\u8BD5\u4E5F\u8BA9\u6211\u5728\u7406\u89E3\u5B83\u4EEC\u7684\u539F\u7406\u65F6\u66F4\u52A0\u5F97\u5FC3\u5E94\u624B\u4E86\u3002</p><h3 id="\u575A\u6301\u5237\u9898-\u6CE8\u91CD\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u575A\u6301\u5237\u9898-\u6CE8\u91CD\u65B9\u6CD5" aria-hidden="true">#</a> <strong>\u201C\u575A\u6301\u5237\u9898\uFF0C\u6CE8\u91CD\u65B9\u6CD5\u201D</strong></h3><p><strong>\u7B97\u6CD5\u9898\u662F\u79CB\u62DB\u7B14\u8BD5\u9762\u8BD5\u4E2D\u7684\u91CD\u5934\u620F\uFF0C\u6BCF\u4E2A\u7814\u53D1\u540C\u5B66\u90FD\u514D\u4E0D\u4E86\u7ECF\u5386\u7B97\u6CD5\u9898\u7684\u6467\u6B8B\uFF0C\u5BF9\u6211\u8FD9\u4E48\u4E00\u4E2A\u975E\u79D1\u73ED\u540C\u5B66\u6765\u8BF4\uFF0C\u66F4\u662F\u8BA9\u4EBA\u5934\u5927\u3002\u6B63\u56E0\u4E3A\u5982\u6B64\uFF0C\u6211\u653E\u5F03\u4E86\u5237\u5927\u91CFLeetCode\u9898\u76EE\u7684\u65B9\u6CD5\uFF0C\u9009\u62E9\u4E86\u66F4\u52A0\u884C\u4E4B\u6709\u6548\u7684\u5237\u9898\u65B9\u5F0F\u3002</strong></p><p><strong>\u9996\u5148\u6211\u91CD\u65B0\u5237\u4E86\u4E00\u904D\u5251\u6307offer\uFF0C\u5E76\u4E14\u5BF9\u6BCF\u9053\u9898\u76EE\u8FDB\u884C\u603B\u7ED3\uFF0C\u5C3D\u91CF\u4FDD\u8BC1\u6BCF\u4E00\u9053\u9898\u90FD\u53EF\u4EE5\u8BB0\u5728\u8111\u5B50\u91CC\uFF0C\u4F17\u6240\u5468\u77E5\u5251\u6307offer\u4E2D\u7684\u9898\u662F\u9762\u8BD5\u65F6\u975E\u5E38\u559C\u6B22\u8003\u7684\uFF0C\u6240\u4EE5\u5148\u641E\u5B9A\u8FD9\u90E8\u5206\u9898\u76EE\u662F\u6700\u4E3A\u5173\u952E\u7684\u3002</strong></p><p>\u641E\u5B9A\u5251\u6307offer\u4E4B\u540E\uFF0C\u5F53\u7136\u8FD8\u8981\u5237LeetCode\u4E86\uFF0CLeetCode\u9898\u76EE\u8FD9\u4E48\u591A\uFF0C\u600E\u4E48\u9009\u62E9\u5462\uFF0C\u6211\u6CA1\u6709\u6309\u7167tag\u5237\uFF0C\u4E5F\u6CA1\u6709\u6309\u7167\u987A\u5E8F\u5237\uFF0C\u800C\u662F\u53C2\u8003\u5F53\u65F6\u4E00\u4E2A\u5927\u4F6C\u7684LeetCode\u5237\u9898\u6307\u5357\u6765\u8FDB\u884C\u5237\u9898\u7684\uFF0C\u4ED6\u628A\u6BCF\u4E2A\u7C7B\u578B\u7684\u9898\u76EE\u90FD\u505A\u4E86\u5F52\u7EB3\uFF0C\u6BCF\u90E8\u5206\u53EA\u653E\u4E00\u4E9B\u6BD4\u8F83\u7ECF\u5178\u7684\u9898\u76EE\u3002\u6240\u4EE5\u6211\u524D\u540E\u5927\u6982\u5237\u4E86100\u591A\u9053LeetCode\u7684\u9898\u76EE\uFF0C\u5E76\u4E14\u5728\u7B2C\u4E8C\u904D\u5237\u9898\u590D\u4E60\u7684\u65F6\u5019\uFF0C\u6211\u4E5F\u5BF9\u8FD9\u4E9B\u9898\u76EE\u505A\u4E86\u4E00\u4EFD\u603B\u7ED3\u3002</p><p><strong>\u9664\u4E86\u4E0A\u9762\u4E24\u4E2A\u7ECF\u5178\u9898\u5E93\uFF0C\u6211\u8FD8\u7740\u91CD\u5237\u4E86\u5927\u5382\u7684\u5386\u5E74\u771F\u9898\uFF0C\u8FD9\u90E8\u5206\u6211\u4E3B\u8981\u662F\u901A\u8FC7\u725B\u5BA2\u7F51\u7684\u5386\u5E74\u771F\u9898\u9898\u5E93\u6765\u5B8C\u6210\u5237\u9898\u7684\u3002\u8BF4\u5B9E\u8BDD\uFF0C\u771F\u9898\u662F\u975E\u5E38\u91CD\u8981\u7684\uFF0C\u56E0\u4E3A\u516C\u53F8\u51FA\u7684\u9898\u76EE\u4E0D\u50CF\u5E73\u65F6\u7684\u90A3\u4E9B\u7B97\u6CD5\u9898\uFF0C\u53EF\u80FD\u4F1A\u51FA\u5F97\u975E\u5E38\u5947\u8469\uFF0C\u6240\u4EE5\u4F60\u5982\u679C\u4E0D\u63D0\u524D\u9002\u5E94\u7684\u8BDD\u4F1A\u6BD4\u8F83\u5403\u4E8F\u3002\u5B8C\u6210\u8FD9\u90E8\u5206\u9898\u76EE\u4E4B\u540E\uFF0C\u6211\u5BF9\u7B97\u6CD5\u9898\u7684\u590D\u4E60\u4E5F\u57FA\u672C\u544A\u4E00\u6BB5\u843D\u4E86\u3002</strong></p><p>\u5F53\u6211\u5B8C\u6210\u6240\u6709\u5185\u5BB9\u7684\u590D\u4E60\u65F6\uFF0C\u63D0\u524D\u6279\u5DF2\u7ECF\u5F00\u59CB\u4E86\u3002\u7EC8\u4E8E\u8981\u4E0A\u6218\u573A\u4E86\uFF0C\u56E0\u4E3A\u6218\u524D\u51C6\u5907\u6BD4\u8F83\u5145\u5206\uFF0C\u6240\u4EE5\u6211\u5BF9\u79CB\u62DB\u8FD8\u662F\u6BD4\u8F83\u4E50\u89C2\u7684\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u79CB\u62DB\u4E0D\u4EC5\u662F\u653B\u575A\u6218\uFF0C\u800C\u4E14\u662F\u6301\u4E45\u6218\uFF0C\u8981\u7B11\u5230\u6700\u540E\uFF0C\u786E\u5B9E\u4E5F\u4E0D\u662F\u90A3\u4E48\u5BB9\u6613\u7684\u4E8B\u60C5\u3002</p><h2 id="\u91CD\u5EFA\u77E5\u8BC6\u4F53\u7CFB-\u5BF9\u5B66\u8FC7\u7684\u4E1C\u897F\u505A\u51CF\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u91CD\u5EFA\u77E5\u8BC6\u4F53\u7CFB-\u5BF9\u5B66\u8FC7\u7684\u4E1C\u897F\u505A\u51CF\u6CD5" aria-hidden="true">#</a> \u91CD\u5EFA\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u5BF9\u5B66\u8FC7\u7684\u4E1C\u897F\u505A\u51CF\u6CD5</h2><p>\u524D\u9762\u63D0\u5230\u6211\u5728\u79CB\u62DB\u524D\u5B8C\u6210\u4E86\u77E5\u8BC6\u4F53\u7CFB\u91CD\u5EFA\uFF0C\u90A3\u5728\u8FD9\u91CC\u6211\u4E5F\u60F3\u8DDF\u5927\u5BB6\u5206\u4EAB\u4E00\u4E0B\u6211\u5F53\u65F6\u5927\u81F4\u7684\u77E5\u8BC6\u4F53\u7CFB\u6784\u6210\u3002\u5C31\u8DDF\u6211\u524D\u9762\u8BF4\u7684\u4E00\u6837\uFF0C\u6211\u9009\u62E9\u91CD\u65B0\u518D\u5B66\u4E00\u904DJava\u540E\u7AEF\u76F8\u5173\u7684\u6280\u672F\u5185\u5BB9\uFF0C\u56E0\u4E3A\u6211\u77E5\u9053\u5927\u81F4\u7684\u5B66\u4E60\u65B9\u5411\uFF0C\u5E76\u4E14\u6709\u4E00\u5B9A\u7684\u57FA\u7840\uFF0C\u6240\u4EE5\u770B\u5F88\u591A\u6587\u7AE0\u53D8\u5F97\u66F4\u52A0\u5F97\u5FC3\u5E94\u624B\uFF0C\u5199\u6587\u7AE0\u548C\u505A\u603B\u7ED3\u4E5F\u66F4\u52A0\u6709\u5E95\u6C14\u4E86\u3002</p><p>\u9996\u5148\u5728Java\u57FA\u7840\u65B9\u9762\uFF0C\u6211\u5199\u4E8620\u591A\u7BC7\u539F\u521B\u535A\u5BA2\uFF0C\u4E3B\u8981\u662F\u5BF9Java\u6838\u5FC3\u6280\u672F\u7684\u89E3\u6790\uFF0C\u6BD4\u5982\u201CJava\u53CD\u5C04\u201D\uFF0C\u201CJava\u5E8F\u5217\u5316\u548C\u53CD\u5E8F\u5217\u5316\u201D\uFF0C\u201CJava\u5F02\u5E38\u4F53\u7CFB\u201D\u7B49\u7B49\u3002</p><p>\u5728Java\u96C6\u5408\u7C7B\u65B9\u9762\uFF0C\u6211\u539F\u521B\u4E86\u90E8\u5206\u6587\u7AE0\uFF0C\u53E6\u5916\u6574\u5408\u4E86\u4E00\u4E9B\u6BD4\u8F83\u597D\u7684\u6280\u672F\u6587\u7AE0\uFF0C\u5176\u4E2D\u6700\u4E3B\u8981\u7684\u5C31\u662F\u5173\u4E8Ehashmap\u7684\u6587\u7AE0\uFF0C\u5F53\u65F6\u6211\u6574\u5408\u7684\u6587\u7AE0\u51E0\u4E4E\u6CA1\u6709\u9057\u6F0F\u4EFB\u4F55\u4E00\u4E2A\u77E5\u8BC6\u70B9\u3002</p><p><strong>\u5728Java\u5E76\u53D1\u7F16\u7A0B\u65B9\u9762\uFF0C\u6211\u4E3B\u8981\u53C2\u8003\u4E86\u5E76\u53D1\u7F16\u7A0B\u7F51\u4EE5\u53CA\u4E00\u4E9B\u4F18\u8D28\u535A\u5BA2\u7684\u6587\u7AE0\uFF0C\u5148\u641E\u61C2\u4E86Java\u5E76\u53D1\u539F\u7406\uFF0C\u518D\u4E00\u6B65\u6B65\u5B66\u4E60JUC\u5E76\u53D1\u5305\u7684\u7EC4\u4EF6\uFF0C\u5176\u4E2D\u91CD\u70B9\u770B\u4E86chm\uFF0C\u5E76\u53D1\u5DE5\u5177\u7C7B\u4EE5\u53CA\u963B\u585E\u961F\u5217\u7B49JDK\u6E90\u7801\u7684\u89E3\u6790\u6587\u7AE0\uFF0C\u9664\u6B64\u4E4B\u5916\uFF0C\u6211\u8FD8\u4F1A\u5728IDE\u4E2D\u8DD1JUC\u76F8\u5173\u7684emo\uFF0C\u6BD5\u7ADF\u8FD9\u65B9\u9762\u7684\u5185\u5BB9\u975E\u5E38\u9700\u8981\u5B9E\u8DF5\u3002</strong></p><p>\u5728Java\u7F51\u7EDC\u7F16\u7A0B\u65B9\u9762\uFF0C\u6211\u5148\u4ECE\u6700\u57FA\u7840\u7684socket\u5165\u624B\uFF0C\u518D\u8BB2\u5230NIO,AIO\uFF0C\u5E76\u4E14\u52A0\u5165\u4E86\u51E0\u7BC7\u5BF9Linux IO\u6A21\u578B\u89E3\u6790\u7684\u6587\u7AE0\uFF0C\u8BA9\u6574\u4E2A\u77E5\u8BC6\u4F53\u7CFB\u66F4\u52A0\u5B8C\u6574\uFF08\u56E0\u4E3ANIO\u662F\u57FA\u4E8ELinux Epoll\u5B9E\u73B0\u7684\uFF09\uFF0C\u63A5\u7740\u6211\u53C8\u52A0\u5165\u4E86\u5BF9Netty\u7684\u63A2\u8BA8\uFF0C\u4EE5\u53CATomcat\u4E2D\u5BF9NIO\u7684\u5E94\u7528\uFF0C\u53EF\u4EE5\u8BF4\u662F\u628AJava\u7F51\u7EDC\u7F16\u7A0B\u4E00\u4E9B\u6BD4\u8F83\u91CD\u8981\u7684\u90E8\u5206\u90FD\u56CA\u62EC\u8FDB\u6765\u4E86\u3002<strong>\u4E3A\u4E86\u66F4\u597D\u7406\u89E3\u8FD9\u90E8\u5206\u5185\u5BB9\uFF0C\u6211\u4E5F\u5728\u7F51\u4E0A\u53C2\u8003\u4E86\u5F88\u591A\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u901A\u4FE1\u7684demo\uFF0C\u6700\u540E\u6211\u5206\u522B\u7528Socket\uFF0CNIO,AIO\u4EE5\u53CANetty\u628AC/S \u901A\u4FE1\u7684demo\u90FD\u5199\u4E86\u4E00\u904D\u3002</strong></p><p>\u5728JVM\u865A\u62DF\u673A\u65B9\u9762\uFF0C\u6211\u5219\u6309\u7167\u300A\u6DF1\u5165\u7406\u89E3JVM\u865A\u62DF\u673A\u300B\u8FD9\u672C\u4E66\u7684\u884C\u6587\u8109\u7EDC\u8FDB\u884C\u6587\u7AE0\u7684\u6574\u7406\u3002<strong>\u5728\u641E\u5B9AJVM\u57FA\u672C\u539F\u7406\u4EE5\u540E\uFF0C\u6211\u7740\u91CD\u4E86\u89E3\u4E86JVM\u8C03\u4F18\u548C\u5B9E\u8DF5\u4E2D\u5E38\u9047\u5230\u7684\u95EE\u9898\uFF0C\u5E76\u4E14\u6574\u7406\u4E86\u5E38\u7528\u7684JVM\u8C03\u4F18\u5DE5\u5177\uFF0C\u573A\u666F\u95EE\u9898\u4EE5\u53CA\u8C03\u4F18\u5B9E\u8DF5\u7684\u6848\u4F8B\uFF0C\u8FD9\u4E5F\u662F\u56E0\u4E3A\u9762\u8BD5\u4E2D\u5BF9JVM\u8C03\u4F18\u5B9E\u8DF5\u8D8A\u6765\u8D8A\u91CD\u89C6\u4E86\u3002</strong></p><p>\u5728JavaWeb\u65B9\u9762\uFF0C\u6211\u4ECEJava Web\u76F8\u5173\u6280\u672F\u7684\u53D1\u5C55\u5165\u624B\uFF0C\u4E00\u6B65\u6B65\u4E86\u89E3\u4E86\u6BCF\u79CD\u6280\u672F\u5B58\u5728\u7684\u610F\u4E49\uFF0C\u6BD4\u5982JSP\uFF0CServlet\uFF0CJDBC\uFF0CSpring\u7B49\u7B49\uFF0C\u7136\u540E\u5BF9\u6BCF\u79CD\u6280\u672F\u8FDB\u884C\u4E86\u6BD4\u8F83\u5168\u9762\u7684\u4E86\u89E3\uFF0C\u5E76\u4E14\u7740\u91CD\u5730\u770B\u4E86Spring\u548CSpringMVC\u7684\u6E90\u7801\u5206\u6790\u6587\u7AE0\uFF0C\u53E6\u5916\u4E00\u65B9\u9762\uFF0C\u6211\u82B1\u4E86\u5F88\u591A\u65F6\u95F4\u53BB\u7814\u7A76Tomcat\u7684\u5DE5\u4F5C\u539F\u7406\u3002\u9664\u6B64\u4E4B\u5916\uFF0CJavaWeb\u9879\u76EE\u4E2D\u5E38\u7528\u7684maven\uFF0C\u65E5\u5FD7\u7EC4\u4EF6\uFF0C\u751A\u81F3\u662F\u5355\u6D4B\u8BD5\u7EC4\u4EF6\uFF0C\u4E5F\u7EB3\u5165\u4E86\u6211\u7684\u7CFB\u5217\u6587\u7AE0\u91CC\u3002</p><p><strong>\u5728\u6570\u636E\u5E93\u548C\u7F13\u5B58\u65B9\u9762\uFF0C\u6211\u4E3B\u8981\u5B66\u4E60\u4E86MySQL\u548CRedis\u8FD9\u4E24\u79CD\u6700\u5E38\u7528\u7684\u6570\u636E\u5E93\u3002\u5BF9\u4E8EMysql\uFF0C\u6211\u4ECE\u7B80\u5355\u7684sql\u5F00\u59CB\u4E86\u89E3\uFF0C\u7136\u540E\u5F00\u59CB\u4E86\u89E3sql\u4F18\u5316\uFF0CMySQL\u7684\u5B58\u50A8\u5F15\u64CE\u548C\u7D22\u5F15\uFF0C\u4E8B\u52A1\u53CA\u9501\uFF0C\u8FD8\u6709\u66F4\u590D\u6742\u7684\u4E3B\u4ECE\u590D\u5236\uFF0C\u5206\u5E93\u5206\u8868\u7B49\u5185\u5BB9\u3002\u5BF9\u4E8ERedis\uFF0C\u6211\u4E5F\u662F\u4ECE\u7B80\u5355\u7684api\u5165\u624B\uFF0C\u7136\u540E\u53BB\u4E86\u89E3\u6BCF\u4E00\u79CD\u6570\u636E\u7ED3\u6784\u7684\u5E95\u5C42\u5B9E\u73B0\u539F\u7406\uFF0C\u63A5\u7740\u5C1D\u8BD5\u53BB\u5B66\u4E60Redis\u7684\u6301\u4E45\u5316\u65B9\u5F0F\uFF0C\u4EE5\u53CA\u4F5C\u4E3A\u7F13\u5B58\u5E38\u9700\u8981\u8003\u8651\u7684\u6280\u672F\u70B9\uFF0C\u5F53\u7136\uFF0C\u4E5F\u5305\u62ECRedis\u7684\u5206\u5E03\u5F0F\u9501\u5B9E\u73B0\uFF0C\u4EE5\u53CA\u5B83\u7684\u5206\u5E03\u5F0F\u96C6\u7FA4\u65B9\u6848\u3002</strong></p><p>\u6700\u540E\u4E00\u90E8\u5206\u5C31\u662F\u5206\u5E03\u5F0F\u76F8\u5173\u7684\u7406\u8BBA\u548C\u6280\u672F\u4E86\uFF0C\u8FD9\u4E2A\u4E5F\u662F\u56F0\u6270\u6211\u5F88\u4E45\u7684\u4E00\u5757\u5185\u5BB9\uFF0C\u6211\u4E3B\u8981\u628A\u8FD9\u5757\u5185\u5BB9\u5206\u4E3A\u4E24\u4E2A\u90E8\u5206\uFF0C\u5206\u522B\u662F\u5206\u5E03\u5F0F\u7406\u8BBA\u548C\u5206\u5E03\u5F0F\u6280\u672F\uFF0C\u7406\u8BBA\u65B9\u9762\uFF0C\u6211\u5148\u4E86\u89E3CAP,BASE\u7B49\u57FA\u672C\u77E5\u8BC6\uFF0C\u7136\u540E\u5F00\u59CB\u5B66\u4E60\u4E00\u81F4\u6027\u534F\u8BAE\u548C\u7B97\u6CD5\uFF0C\u63A5\u7740\u63A2\u8BA8\u5206\u5E03\u5F0F\u4E8B\u52A1\u3002**\u5BF9\u4E8E\u5206\u5E03\u5F0F\u6280\u672F\uFF0C\u6D89\u53CA\u7684\u4E1C\u897F\u5C31\u66F4\u591A\u4E86\uFF0C\u4F8B\u5982\u5206\u5E03\u5F0Fsession\uFF0C\u8D1F\u8F7D\u5747\u8861\uFF0C\u5206\u5E03\u5F0F\u9501\u7B49\u5185\u5BB9\uFF0C\u8FD9\u4E9B\u77E5\u8BC6\u70B9\u6211\u90FD\u4F1A\u7528\u4E00\u5230\u4E24\u7BC7\u6587\u7AE0\u53BB\u603B\u7ED3\uFF0C\u5BF9\u4E8E\u5206\u5E03\u5F0F\u7F13\u5B58\uFF0C\u6D88\u606F\u961F\u5217\uFF0C\u4EE5\u53CA\u5206\u5E03\u5F0F\u670D\u52A1\u7B49\u5185\u5BB9\uFF0C\u6211\u4F1A\u82B1\u6BD4\u8F83\u591A\u7684\u65F6\u95F4\u53BB\u5168\u9762\u5B66\u4E60\uFF0C\u7136\u540E\u603B\u7ED3\u51FA\u4E00\u4E2A\u7CFB\u5217\u7684\u6587\u7AE0\u51FA\u6765\u3002**\u5F53\u7136\uFF0C\u5BF9\u4E8E\u8FD9\u4E9B\u6280\u672F\u7684\u5B66\u4E60\u4E3B\u8981\u8FD8\u662F\u505C\u7559\u5728\u7406\u8BBA\u65B9\u9762\uFF0C\u5728\u81EA\u5DF1\u7684\u9879\u76EE\u4E2D\u80FD\u7528\u5230\u7684\u6BD4\u8F83\u5C11\u3002</p><p>\u81F3\u6B64\uFF0C\u6211\u7684\u77E5\u8BC6\u4F53\u7CFB\u57FA\u672C\u6784\u5EFA\u5B8C\u6210\uFF0C\u8FD9\u4E5F\u662F\u6211\u5728\u79CB\u62DB\u4E2D\u80FD\u591F\u6210\u529F\u95EF\u8FC7\u90A3\u4E48\u591A\u9762\u8BD5\u7684\u539F\u56E0\u3002</p><h2 id="\u79CB\u62DB\u4E4B\u8DEF-\u7825\u783A\u524D\u884C" tabindex="-1"><a class="header-anchor" href="#\u79CB\u62DB\u4E4B\u8DEF-\u7825\u783A\u524D\u884C" aria-hidden="true">#</a> \u79CB\u62DB\u4E4B\u8DEF\uFF0C\u7825\u783A\u524D\u884C</h2><p>\u4E0D\u7BA1\u524D\u671F\u505A\u4E86\u591A\u5C11\u51C6\u5907\uFF0C\u5230\u79CB\u62DB\u7684\u65F6\u5019\u4E5F\u4E0D\u80FD\u6389\u4EE5\u8F7B\u5FC3\uFF0C\u4ECE\u4E03\u6708\u5E95\u7B2C\u4E00\u6B21\u9762\u8BD5\u52309\u6708\u57FA\u672C\u4F5B\u7CFB\uFF0C\u4E2D\u95F4\u7ECF\u5386\u4E86\u5927\u5927\u5C0F\u5C0F\u7684\u9762\u8BD5\u3002</p><p><strong>\u5728\u5B8C\u6210\u77E5\u8BC6\u4F53\u7CFB\u91CD\u5EFA\u4EE5\u540E\uFF0C\u6211\u628A\u91CD\u70B9\u8F6C\u5411\u4E86\u53E6\u5916\u51E0\u4EF6\u4E8B\uFF0C\u4E00\u662F\u5B8C\u5584\u548C\u719F\u6089\u6211\u7684\u7B80\u5386\uFF0C\u4EE5\u4FBF\u5728\u9762\u8BD5\u4E2D\u80FD\u591F\u6BD4\u8F83\u597D\u5730\u53D1\u6325\uFF0C\u4E8C\u662F\u6301\u7EED\u5237\u9898\uFF0C\u4FDD\u6301\u5BF9\u7B97\u6CD5\u9898\u548C\u7B14\u8BD5\u771F\u9898\u7684\u624B\u611F\u548C\u719F\u7EC3\u5EA6\uFF0C\u4E09\u5219\u662F\u770B\u9762\u7ECF\u67E5\u7F3A\u8865\u6F0F\uFF0C\u6211\u4E00\u76F4\u8BA4\u4E3A\u770B\u9762\u7ECF\u662F\u5F88\u91CD\u8981\u7684\u4E00\u9879\u590D\u4E60\u5185\u5BB9\u3002</strong></p><p><strong>\u5C31\u8FD9\u6837\uFF0C\u6211\u4E00\u8FB9\u7EE7\u7EED\u590D\u4E60\uFF0C\u4EE5\u4FBF\u5F00\u59CB\u4E86\u4E00\u573A\u63A5\u4E00\u573A\u7684\u9762\u8BD5\u63A5\u529B\u3002</strong></p><p>\u8D77\u521D\uFF0C\u6211\u9762\u4E86\u51E0\u5BB6\u5C0F\u516C\u53F8\u7EC3\u624B\uFF0C\u63A5\u7740\u963F\u91CC\u7684\u63D0\u524D\u6279\u63A5\u8E35\u800C\u81F3\uFF0C\u6211\u6218\u6218\u5162\u5162\u5730\u53C2\u52A0\u4E86\u963F\u91CC\u4E2D\u95F4\u4EF6\u90E8\u95E8\u7684\u9762\u8BD5\uFF0C\u9762\u96BE\u96BE\u5EA6\u8FD8\u7B97\u9002\u4E2D\uFF0C\u4E00\u5171\u56DB\u8F6E\u9762\u8BD5\uFF0C\u5F53\u65F6\u6211\u7684\u8868\u73B0\u4E5F\u8FD8\u4E0D\u9519\uFF0C\u95EE\u9898\u57FA\u672C\u90FD\u7B54\u4E0A\u6765\u4E86\u3002\u9762\u5B8C\u4E0D\u5230\u4E00\u5468\u4EE5\u540E\u6211\u5C31\u6536\u5230\u4E86\u901A\u8FC7\u7684\u6D88\u606F\uFF0C\u5F53\u65F6\u8FD8\u6709\u70B9\u61F5\u3002\u6CA1\u60F3\u5230\u7B2C\u4E00\u4E2Aoffer\u8FD9\u4E48\u5FEB\u5C31\u6765\u4E86\u3002</p><p>\u8FD9\u6BB5\u65F6\u95F4\u5185\uFF0C\u8682\u8681\u91D1\u670D\u7684\u4E24\u4E2A\u90E8\u95E8\u4E5F\u7ED9\u4E86\u6211\u9762\u8BD5\u673A\u4F1A\uFF0C\u6211\u90FD\u53C2\u52A0\u4E86\u5B83\u4EEC\u7684\u9762\u8BD5\uFF0C\u5E76\u4E14\u987A\u5229\u5730\u62FF\u5230\u4E86\u5176\u4E2D\u4E00\u4E2A\u90E8\u95E8\u7684offer\u3002\u7531\u4E8E\u6211\u5BF9\u8682\u8681\u8FD9\u8FB9\u7684\u4E1A\u52A1\u6BD4\u8F83\u611F\u5174\u8DA3\uFF0C\u6700\u7EC8\u9009\u62E9\u4E86\u8682\u8681\u91D1\u670D\u7684offer\u3002</p><p><strong>\u963F\u91CC\u63D0\u524D\u6279\u7684\u80DC\u5229\u786E\u5B9E\u662F\u610F\u5916\u4E4B\u559C\uFF0C\u4F46\u4E5F\u5927\u5927\u5730\u9F13\u821E\u4E86\u6211\uFF0C\u4E8E\u662F\u6211\u53C8\u53C2\u52A0\u4E86\u767E\u5EA6\u548C\u817E\u8BAF\u7684\u63D0\u524D\u6279\u9762\u8BD5\uFF0C\u7531\u4E8E\u767E\u5EA6\u7684\u63D0\u524D\u6279\u4E0D\u8D70\u6D41\u7A0B\uFF0C\u4E00\u5171\u6709\u56DB\u4E2A\u90E8\u95E8\u9762\u8BD5\u4E86\u6211\uFF0C\u6BCF\u4E2A\u90E8\u95E8\u90FD\u67092\u52303\u8F6E\u9762\u8BD5\uFF0C\u603B\u8BA1\u7EA6\u4E3A12\u6B21\u9762\u8BD5\uFF0C\u5230\u540E\u6765\u6211\u5DF2\u7ECF\u5FEB\u6655\u4E86\uFF0C\u770B\u5230\u767E\u5EA6\u7684\u7535\u8BDD\u5C31\u5BB3\u6015\uFF0C\u7531\u4E8E\u9762\u8BD5\u6B21\u6570\u592A\u591A\uFF0C\u6709\u65F6\u5019\u53D1\u6325\u786E\u5B9E\u4E5F\u4E0D\u662F\u5F88\u597D\uFF0C\u6211\u4E5F\u6CA1\u6709\u7279\u522B\u5728\u610F\uFF0C\u53EA\u5F53\u662F\u5728\u953B\u70BC\u81EA\u5DF1\u4E86\u3002</strong></p><p>\u767E\u5EA6\u7684\u9762\u8BD5\u96BE\u5EA6\u6BCF\u4E2A\u90E8\u95E8\u4E0D\u4E00\u6837\uFF0C\u4F46\u662F\u6BCF\u6B21\u9762\u8BD5\u5FC5\u5199\u7B97\u6CD5\u9898\uFF0C\u4E00\u5199\u7B97\u6CD5\u9898\uFF0C\u65F6\u95F4\u81F3\u5C11\u5C31\u662F\u4E00\u4E2A\u5C0F\u65F6\u4EE5\u4E0A\uFF0C\u6BCF\u6B21\u9762\u8BD5\u5B8C\u90FD\u6709\u4E00\u79CD\u8EAB\u4F53\u88AB\u638F\u7A7A\u7684\u611F\u89C9\u3002</p><p>\u7ECF\u5386\u4E86\u767E\u5EA6\u9762\u8BD5\u7684\u6467\u6B8B\u4EE5\u540E\uFF0C\u6211\u624B\u5199\u7B97\u6CD5\u7684\u901F\u5EA6\u4E5F\u53D8\u5FEB\u4E86\uFF0C\u5F88\u591A\u5751\u4E5F\u88AB\u6211\u586B\u4E0A\u4E86\u3002\u63A5\u4E0B\u6765\u9762\u5BF9\u817E\u8BAF\u7684\u9762\u8BD5\uFF0C\u6211\u4E5F\u662F\u65E2\u6FC0\u52A8\u53C8\u62C5\u5FC3\uFF0C\u817E\u8BAF\u7684\u9762\u8BD5\u96BE\u5EA6\u6BD4\u8F83\u5927\uFF0C\u5BF9\u4E8E\u64CD\u4F5C\u7CFB\u7EDF\u548C\u7F51\u7EDC\u7684\u77E5\u8BC6\u559C\u6B22\u6DF1\u6316\uFF0C\u95EE\u7684\u4E1C\u897F\u4E5F\u5F88\u6709\u6DF1\u5EA6\uFF0C\u9762\u5B8C\u524D\u4E09\u9762\u4EE5\u540E\uFF0C\u7B2C\u56DB\u9762\u62D6\u4E863\u5468\u624D\u8FDB\u884C\u3002\u5F53\u65F6\u4E09\u9762\u9762\u8BD5\u5B98\u5BF9\u6211\u7684\u8BC4\u4EF7\u6BD4\u8F83\u597D\uFF0C\u4E5F\u8BA9\u6211\u4FE1\u5FC3\u7206\u68DA\u4E86\u597D\u4E45\u3002</p><p>\u5728\u7B49\u5F85\u817E\u8BAF\u7EC8\u9762\u7684\u671F\u95F4\uFF0C\u6211\u53C2\u52A0\u4E86\u4ECA\u65E5\u5934\u6761\u7684\u9762\u8BD5\uFF0C\u5F53\u65F6\u6709\u5E78\u62FF\u5230\u4E86\u4E00\u4E2A\u767D\u91D1\u7801\uFF0C\u514D\u53BB\u7B14\u8BD5\uFF0C\u4E8B\u5B9E\u8BC1\u660E\u767D\u91D1\u7801\u4F5C\u7528\u771F\u7684\u5F88\u5927\u3002\u5934\u6761\u7684\u9762\u8BD5\u96BE\u5EA6\u548C\u817E\u8BAF\u5DEE\u4E0D\u591A\uFF0C\u4E09\u8F6E\u9762\u8BD5\uFF0C\u540C\u6837\u9700\u8981\u5199\u5404\u79CD\u7B97\u6CD5\uFF0C\u7531\u4E8E\u662F\u89C6\u9891\u9762\u8BD5\uFF0C\u6211\u53EF\u4EE5\u6E05\u695A\u5730\u770B\u5230\uFF0C\u5934\u6761\u7684\u9762\u8BD5\u5B98\u771F\u7684\u975E\u5E38\u9AD8\u51B7\u554A\u3002\u9762\u5B8C\u5934\u6761\u6211\u7684\u7B2C\u4E00\u611F\u89C9\u5C31\u662F\u5E94\u8BE5\u6302\u4E86\u5427\u3002\u6CA1\u60F3\u5230\u6700\u540E\u8FD8\u662F\u7ED9\u4E86offer\u3002</p><p><strong>\u7ED3\u675F\u8FD9\u51E0\u5BB6\u5927\u5382\u7684\u9762\u8BD5\u4E4B\u540E\uFF0C\u6211\u89C9\u5F97\u6211\u7684\u79CB\u62DB\u5DF2\u7ECF\u63A5\u8FD1\u5C3E\u58F0\u4E86\uFF0C\u4E0D\u8FC7\u7531\u4E8E\u4E4B\u524D\u6295\u7684\u6BD4\u8F83\u591A\uFF0C\u6240\u4EE5\u6211\u53C8\u9762\u4E86\u51E0\u5BB6\u5927\u516C\u53F8\uFF0C\u5982\u7F51\u6613\uFF0C\u534E\u4E3A\uFF0C\u5FEB\u624B\u7B49\u3002\u52309\u6708\u4E0A\u65EC\u7684\u65F6\u5019\uFF0C\u6211\u63A5\u8FDE\u6536\u5230\u4E86bat\u548C\u5934\u6761\uFF0C\u7F51\u6613\u7684\u610F\u5411\u4E66\uFF0C\u963F\u91CC\u6700\u65E9\uFF0C\u817E\u8BAF\u6700\u665A\uFF0C\u6BCF\u6536\u5230\u4E00\u5C01\u610F\u5411\u4E66\u6211\u90FD\u5F88\u5F00\u5FC3\uFF0C\u6CA1\u60F3\u5230\u6700\u540E\u6211\u771F\u7684\u53EF\u4EE5\u96C6\u9F50bat\u7B49\u5927\u5382\u7684offer\u3002</strong></p><p>9\u6708\u4EE5\u540E\uFF0C\u9664\u4E86\u5076\u5C14\u548C\u540C\u5B66\u505A\u51E0\u573A\u5927\u5382\u7684\u7B14\u8BD5\uFF0C\u6211\u57FA\u672C\u5C31\u4F5B\u7CFB\u4E86\u3002\u76F4\u5230\u540E\u6765\u4E00\u4E9B\u5916\u4F01\u4F8B\u5982\u4E9A\u9A6C\u900A\uFF0C\u5927\u6469\u5F00\u59CB\u7B14\u8BD5\u9762\u8BD5\uFF0C\u6211\u624D\u53C8\u91CD\u65B0\u56DE\u5230\u4E86\u72B6\u6001\u3002</p><p>\u622A\u6B62\u76EE\u524D\uFF0C\u6211\u57FA\u672C\u4E0A\u628A\u8BE5\u62D2\u7EDD\u7684offer\u90FD\u62D2\u7EDD\u4E86\uFF0C\u7EFC\u5408\u5404\u65B9\u9762\u56E0\u7D20\u7684\u8003\u8651\uFF0C\u6700\u540E\u5E94\u8BE5\u4F1A\u7B7E\u963F\u91CC\uFF0C\u539F\u56E0\u662F\u90E8\u95E8\u662F\u6211\u81EA\u5DF1\u559C\u6B22\u7684\uFF0C\u540C\u65F6\u7ED9\u7684\u8BC4\u7EA7\u4E5F\u6BD4\u8F83\u9AD8\u3002\u867D\u7136\u817E\u8BAF\u4E5F\u7ED9\u4E86sp\uFF0C\u4F46\u662F\u6700\u540E\u8FD8\u662F\u5FCD\u75DB\u5272\u7231\u5566\u3002\u81F3\u4E8E\u767E\u5EA6\u548C\u5934\u6761\uFF0C\u7ED9\u7684offer\u5E76\u4E0D\u662F\u5F88\u4EE4\u4EBA\u6EE1\u610F\uFF0C\u6240\u4EE5\u5C31\u6CA1\u6709\u8003\u8651\u4E86\u3002</p><p>\u81F3\u6B64\uFF0C\u6211\u7684\u79CB\u62DB\u4E4B\u65C5\u603B\u7B97\u5706\u6EE1\u7ED3\u675F\u3002</p><p>\u5E0C\u671B\u5404\u4F4D\u5728\u672A\u6765\u4E5F\u53EF\u4EE5\u62FF\u5230\u81EA\u5DF1\u7406\u60F3\u4E2D\u7684offer\uFF01</p>',49),c={href:"https://www.zhihu.com/question/26406260/answer/569976835",target:"_blank",rel:"noopener noreferrer"},g=e("hr",null,null,-1);function f(l,J){const r=h("ExternalLinkIcon");return n(),t("div",null,[i,e("blockquote",null,[e("p",null,[a("\u8F6C\u8F7D\u94FE\u63A5\uFF1A"),e("a",c,[a("https://www.zhihu.com/question/26406260/answer/569976835"),p(r)])])]),g])}const m=o(d,[["render",f],["__file","benkesheng-ali-tengxun.html.vue"]]);export{m as default};
