import{_ as g}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as l,c as n,a as p,b as t,d as e,e as a,r as s}from"./app.c2ac2075.js";const i={},r=a('<p><strong>\u9762\u5411\u5931\u8D25\u7F16\u7A0B</strong>\u662F\u7F16\u7A0B\u4E2D\u6700\u96BE\u7684\u4E8B\u60C5\u3002</p><p>\u8BDD\u8BF4\u7A0B\u5E8F\u5458\u5C0F\u6797\u7684\u67D0\u4E00\u5929\uFF1A\u8D77\u5E8A-&gt;\u5403\u996D-&gt;\u5750\u5730\u94C1-&gt;\u5230\u516C\u53F8-&gt;\u6572\u4EE3\u7801-&gt;\u56DE\u5BB6-&gt;\u73A9\u6E38\u620F-&gt;\u7761\u89C9\u3002</p><p>\u8FD9\u4E00\u5929\u7684\u53E6\u4E00\u4E2A\u7248\u672C\uFF1A\u8D77\u5E8A-&gt;\u5403\u996D-&gt;\u5750\u5730\u94C1-&gt;\u5230\u516C\u53F8-&gt;\u7A81\u7136\u8981 24 \u5C0F\u65F6\u5065\u5EB7\u7801-&gt;\u8FDB\u4E0D\u4E86\u516C\u53F8-&gt;\u5750\u5730\u94C1\u56DE\u53BB-&gt;\u5730\u94C1\u505C\u8FD0\u4E86-&gt;\u4E0A\u5395\u6240-&gt;\u8E29\u5230\u5C4E\u6ED1\u5012-&gt;\u6454\u6210\u8111\u9707\u8361\u3002</p><p>\u7B2C\u4E8C\u4E2A\u7248\u672C\u5145\u6EE1\u610F\u5916\uFF0C\u8C8C\u4F3C\u6709\u4E9B\u6781\u7AEF\uFF0C\u4F46\u4F60\u6211\u5929\u5929\u5728\u65B0\u95FB\u4E0A\u770B\u5230\u7C7B\u4F3C\u7684\u4E8B\u60C5\uFF0C\u8BF4\u660E\u5B83\u5176\u5B9E\u6BCF\u5929\u90FD\u5728\u53D1\u751F\u3002</p><p>\u7A0B\u5E8F\u4E5F\u662F\u5982\u6B64\u3002</p><p>\u7A0B\u5E8F\u5458\u5C0F\u6797\u7ED9\u516C\u53F8\u5F00\u53D1\u7684\u67D0\u4E2A\u7CFB\u7EDF\uFF0C\u7528\u6237\u91CF\u66B4\u6DA8\uFF1B\u4E09\u5E74\u540E\u516C\u53F8\u4E0A\u5E02\u4E86\uFF0C\u5C0F\u6797\u559C\u8FCE\u767D\u5BCC\u7F8E\u3002</p><p>\u53E6\u4E00\u4E2A\u7248\u672C\uFF1A\u4E0A\u7EBF\u540E\u7B2C\u4E8C\u5929\u88AB SQL \u6CE8\u5165\u5220\u5E93\u4E86\uFF0C\u9020\u6210\u5927\u91CF\u6295\u8BC9\uFF1B\u5C0F\u6797\u88AB\u8001\u677F\u75DB\u9A82\u4E00\u987F\u540E\uFF0C\u5377\u94FA\u76D6\u8D70\u4EBA\u4E86\u3002</p><p>\u7A0B\u5E8F\u7684\u4E16\u754C\u5145\u6EE1\u610F\u5916\uFF0C\u4F60\u6211\u7684\u6BCF\u4E00\u884C\u4EE3\u7801\u51E0\u4E4E\u90FD\u662F bug\u3002</p><p>\u5199\u51FA\u53EF\u7528\u7684\u7CFB\u7EDF\u5F88\u5BB9\u6613\uFF0C\u4F46\u5199\u51FA\u5065\u58EE\u7684\u7CFB\u7EDF\u5F88\u96BE\u3002</p><h3 id="\u4E00\u4E2A-\u7B80\u5355-\u7684\u4F8B\u5B50" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A-\u7B80\u5355-\u7684\u4F8B\u5B50" aria-hidden="true">#</a> \u4E00\u4E2A\u201D\u7B80\u5355\u201C\u7684\u4F8B\u5B50</h3><p>\u6211\u4EEC\u901A\u8FC7\u50A8\u503C\u5361\u6D88\u8D39\u8FD9\u4E2A\u4F8B\u5B50\u6765\u770B\u770B\u5982\u6B64\u201D\u7B80\u5355\u201C\u7684\u6848\u4F8B\u5230\u5E95\u5B58\u5728\u591A\u5C11\u8BA9\u4EBA\u773C\u82B1\u7F2D\u4E71\u7684\u5931\u8D25\u573A\u666F\u3002</p><p>\u5047\u8BBE\u6211\u4EEC\u7ED9\u67D0\u4E2A\u52A0\u6CB9\u7AD9\u5F00\u53D1\u4E2A\u50A8\u503C\u5361\u7CFB\u7EDF\uFF0C\u7528\u6237\u53EF\u4EE5\u5F80\u91CC\u9762\u5145\u94B1\uFF0C\u53EF\u4EE5\u7528\u50A8\u503C\u5361\u52A0\u6CB9\u6D88\u8D39\uFF0C\u7C7B\u4F3C\u4F60\u5728\u7406\u53D1\u5E97\u3001\u6D17\u811A\u5E97\u5F00\u7684\u90A3\u79CD\u5145\u503C\u5361\u3002</p><p>\u6211\u4EEC\u770B\u770B\u8F66\u4E3B\u52A0\u6CB9\u6D88\u8D39\u7684\u573A\u666F\u2014\u2014\u800C\u4E14\u53EA\u770B\u8FD9\u4E2A\u573A\u666F\u4E2D\u7684\u201D\u50A8\u503C\u5361\u6263\u6B3E\u201C\u8FD9\u4E00\u4E2A\u7ED3\u70B9\u3002</p><p>\u6B63\u5E38\u6D41\u7A0B\uFF08\u7B80\u5316\u7248\uFF09\u5927\u81F4\u662F\u8FD9\u6837\u7684\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032877-918840117.png" alt="image-20220420094234062"></p><p>\u6D41\u7A0B\u5F88\u7B80\u5355\uFF0C\u52A0\u6CB9\u5458\u52A0\u5B8C\u6CB9\u540E\uFF0C\u7528\u6237\u638F\u51FA\u624B\u673A\u626B\u7801\u8FDB\u5165\u4ED8\u6B3E\u9875\u9762\uFF0C\u8F93\u5165\u6CB9\u67AA\u3001\u91D1\u989D\uFF0C\u9009\u50A8\u503C\u5361\u652F\u4ED8\uFF0C\u8F93\u5B8C\u5BC6\u7801\u540E\u70B9\u63D0\u4EA4\uFF1B\u540E\u7AEF\u521B\u5EFA\u8BA2\u5355\u540E\u8C03\u5361\u670D\u52A1\u7684\u6263\u6B3E\u63A5\u53E3\u6267\u884C\u6263\u6B3E\uFF08\u4F20\u5165\u5361\u53F7\u3001\u8BA2\u5355\u53F7\u3001\u91D1\u989D\uFF09\uFF1B\u5361\u670D\u52A1\u6263\u6B3E\u6210\u529F\u540E\u8FD4\u56DE\u544A\u77E5\u7528\u6237\u4ED8\u6B3E\u6210\u529F\u3002</p><p>\u201D\u8FD9\u4E2A\u9700\u6C42\u5927\u6982\u8981\u51E0\u5929\u5F00\u53D1\uFF1F\u201C\u4EA7\u54C1\u7ECF\u7406\u95EE\u5C0F\u6797\u3002</p><p>\u201D\u4E94\u5929\u3002\u201C\u5C0F\u6797\u89C9\u5F97\u4E94\u5929\u7EF0\u7EF0\u6709\u4F59\u3002</p><p>\u201D\u4E09\u5929\u5427\uFF0C\u8FD9\u5468\u6211\u4EEC\u5C31\u8981\u4E0A\u7EBF\u3002\u201C</p><p>\u201D\u90A3\u5C31\u4E09\u5929\u3002\u201C\u5C0F\u6797\u89C9\u5F97\u5176\u5B9E\u4E09\u5929\u8DB3\u591F\u2014\u2014\u4E0D\u5C31\u4E00\u4E24\u4E2A\u63A5\u53E3\u8C03\u7528\u561B\uFF0C\u5361\u670D\u52A1\u662F\u73B0\u6210\u7684\u3002</p><p>\u4E8E\u662F\u5C0F\u6797\u64B8\u8D77\u8896\u5B50\u5F00\u59CB\u6572\u4EE3\u7801\u3002\u8FDB\u5C55\u6BD4\u9884\u60F3\u5F97\u8981\u987A\u5229\uFF0C\u4E24\u5929\u5C31\u6572\u5B8C\u4E86\uFF08\u591A\u5C11\u52A0\u4E86\u70B9\u73ED\uFF09\uFF0C\u4E00\u5929\u6D4B\u8BD5\u5B8C\u6210\uFF0C\u7B2C\u56DB\u5929\u5C31\u4E0A\u7EBF\u4E86\uFF01</p><p>\u67D0\u5929\u591C\u91CC\uFF0C\u5C0F\u6797\u6B63\u5728\u64B8\u732B\u65F6\uFF0C\u8FD0\u8425\u540C\u5B66\u6253\u6765\u7535\u8BDD\uFF1A\u67D0\u8F66\u4E3B\u7684\u5361\u88AB\u83AB\u540D\u5176\u5999\u6263\u6B3E\u4E86\uFF01</p><p>\u4E8B\u60C5\u662F\u8FD9\u6837\u7684\uFF1A\u8F66\u4E3B\u9C81\u67D0\u52A0\u4E86 3000 \u5143\u7684\u6CB9\uFF0C\u9009\u62E9\u7528\u50A8\u503C\u5361\u652F\u4ED8\uFF0C\u7ED3\u679C\u7CFB\u7EDF\u63D0\u793A\u6263\u6B3E\u5931\u8D25\uFF0C\u4E8E\u662F\u9C81\u67D0\u6362\u5FAE\u4FE1\u4ED8\u6B3E\u6210\u529F\uFF0C\u5F00\u8F66\u8D70\u4EBA\u4E86\u3002</p><p>\u8E4A\u8DF7\u7684\u662F\uFF1A\u9C81\u67D0\u5341\u5206\u949F\u540E\u6536\u5230\u6D88\u606F\u8BF4\u5361\u6263\u6389\u4E86 3000 \u5143\uFF01</p><p>\u660E\u660E\u8BF4\u652F\u4ED8\u5931\u8D25\uFF0C\u600E\u4E48\u6263\u4E86 3000\uFF1F\u4E8E\u662F\u9C81\u67D0\u6253\u7535\u8BDD\u627E\u6CB9\u7AD9\u95F9\u3002</p><p>\u5C0F\u6797\u8D76\u7D27\u6392\u67E5\u65E5\u5FD7\uFF0C\u53D1\u73B0<strong>\u4E0A\u56FE\u4E2D\u5730\u7B2C 3 \u6B65\uFF08\u8C03\u5361\u670D\u52A1\u7684\u6263\u6B3E\u63A5\u53E3\uFF09\u8D85\u65F6\u4E86\uFF0C\u4E8E\u662F\u4E1A\u52A1\u7CFB\u7EDF\u544A\u77E5\u524D\u7AEF\u6263\u6B3E\u5931\u8D25</strong>\u3002</p><p>\u8C03\u5361\u670D\u52A1\u6263\u6B3E\u63A5\u53E3\u8D85\u65F6\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u80FD\u76F4\u63A5\u8FD4\u56DE\u5931\u8D25\u7ED9\u524D\u7AEF\u5417\uFF1F</p><p>\u4E0D\u80FD\uFF01</p><p><strong>\u56E0\u4E3A\u63A5\u53E3\u8D85\u65F6\u5E76\u4E0D\u80FD\u8BF4\u660E\u5361\u670D\u52A1\u90A3\u8FB9\u5B9E\u9645\u4E0A\u5230\u5E95\u6709\u6CA1\u6709\u6263\u6210\u529F</strong>\uFF08\u6709\u53EF\u80FD\u5361\u670D\u52A1\u5904\u7406\u6210\u529F\u4E86\uFF0C\u4F46\u8FD4\u56DE\u7684\u65F6\u5019\u7F51\u7EDC\u51FA\u95EE\u9898\uFF1B\u4E5F\u6709\u53EF\u80FD\u5361\u7CFB\u7EDF\u8D1F\u8F7D\u9AD8\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u7B49\u5F85\u8D85\u65F6\u4ECE\u800C\u65AD\u5F00\u8FDE\u63A5\uFF09\u3002</p><p>\u6211\u4EEC\u770B\u770B\u4E0A\u9762\u7684\u5F02\u5E38\u662F\u600E\u4E48\u53D1\u751F\u7684\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032652-912788247.png" alt="image-20220420111711176"></p><p>\u7B2C\u56DB\u6B65\u8D85\u65F6\u540E\uFF0C\u4E1A\u52A1\u540E\u53F0\u76F4\u63A5\u544A\u77E5\u8F66\u4E3B\u652F\u4ED8\u5931\u8D25\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5361\u7CFB\u7EDF\u4ECD\u7136\u5728\u6263\u6B3E\uFF01</p><p>\u90A3\u600E\u4E48\u529E\uFF1F\u544A\u8BC9\u8F66\u4E3B\u201D\u8BF7\u60A8\u7A0D\u540E\u67E5\u770B\u652F\u4ED8\u7ED3\u679C\uFF1F\u201C</p><p>\u600E\u4E48\u53EF\u80FD\uFF01</p><p>\u4E00\u4E2A\u60F3\u6CD5\u662F\u8D85\u65F6\u540E\u4E1A\u52A1\u7CFB\u7EDF\u8C03\u5361\u670D\u52A1\u7684\u67E5\u8BE2\u63A5\u53E3\uFF0C\u770B\u770B\u8FD9\u7B14\u8BA2\u5355\u5B9E\u9645\u662F\u5426\u652F\u4ED8\u6210\u529F\u3002</p><p>\u95EE\u9898\u662F\uFF0C\u5982\u679C\u67E5\u8BE2\u63A5\u53E3\u8C03\u7528\u4E5F\u8D85\u65F6\u5462\uFF08\u5361\u7CFB\u7EDF\u8D1F\u8F7D\u9AD8\u7684\u60C5\u51B5\u4E0B\u8FD9\u4E2A\u6982\u7387\u5F88\u5927\uFF09\uFF1F</p><p>\u53E6\u5916\uFF0C\u67E5\u8BE2\u63A5\u53E3\u8FD4\u56DE\u6CA1\u6709\u6263\u6B3E\u6210\u529F\u5C31\u80FD\u76F4\u63A5\u544A\u8BC9\u7528\u6237\u6263\u6B3E\u5931\u8D25\u5417\uFF1F</p><p>\u4E0D\u80FD\uFF01</p><p>\u56E0\u4E3A\u67E5\u8BE2\u63A5\u53E3\u67E5\u6570\u636E\u5E93\u7684\u65F6\u5019\uFF0C\u6570\u636E\u5E93\u91CC\u9762\u6CA1\u6709\u8BB0\u5F55\uFF0C\u4F46\u6709\u53EF\u80FD\u524D\u9762\u53D1\u8D77\u7684\u90A3\u4E2A\u6263\u6B3E\u903B\u8F91\u4ECD\u7136\u5728\u6267\u884C\uFF0C\u7A0D\u540E\u4ECD\u7136\u4F1A\u53D1\u751F\u6263\u6B3E\u3002</p><p>\u65E2\u7136\u6015\u67E5\u8BE2\u7684\u65F6\u5019\u6263\u6B3E\u903B\u8F91\u4ECD\u7136\u5728\u6267\u884C\uFF0C\u90A3\u6211\u4EEC\u80FD\u4E0D\u80FD\u7B49\u4E00\u4F1A\uFF08\u6BD4\u5982\u4E94\u5206\u949F\uFF09\u518D\u67E5\u7ED3\u679C\u5462\uFF08\u7B49\u90A3\u4E2A\u53EF\u80FD\u7684\u6263\u6B3E\u6267\u884C\u6D41\u8DD1\u5B8C\uFF09\uFF1F</p><p>\u4E5F\u4E0D\u80FD\uFF01</p><p>\u56E0\u4E3A\u8F66\u4E3B\u5728\u90A3\u7B49\u7740\u5462\uFF01\u96BE\u9053\u624B\u673A\u4E0A\u4E00\u76F4\u5728\u90A3\u8F6C\u5708\uFF0C\u8DDF\u8F66\u4E3B\u8BF4\u73B0\u5728\u8D1F\u8F7D\u9AD8\uFF0C\u8BF7\u5148\u559D\u676F\u8336\uFF0C\u8BA9\u5B50\u5F39\u98DE\u4E00\u4F1A\uFF1F</p><p>\u56E0\u4E3A\u5FC5\u987B\u8981\u7ACB\u5373\u544A\u77E5\u7528\u6237\u5904\u7406\u7ED3\u679C\uFF0C\u6240\u4EE5\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF08\u6263\u6B3E\u8D85\u65F6\u4E14\u672A\u67E5\u5230\u6263\u6B3E\u8BB0\u5F55\uFF09\u53EA\u80FD\u544A\u8BC9\u7528\u6237\u6263\u6B3E\u5931\u8D25\u3002</p><p>\u53EA\u4E0D\u8FC7\uFF0C\u5728\u544A\u77E5\u7528\u6237\u4E4B\u524D\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u9700\u8981\u5148\u64A4\u9500\u672C\u6B21\u6263\u6B3E\u7533\u8BF7\uFF0C\u544A\u8BC9\u50A8\u503C\u5361\u7CFB\u7EDF\u672C\u6B21\u6263\u6B3E\u6D41\u7A0B\u4E0D\u80FD\u6267\u884C\u4E86\uFF08\u56DE\u6EDA\u672C\u6B21\u4E8B\u52A1\uFF09\u3002</p><p>\u4E8E\u662F\u5C0F\u6797\u505A\u4E86\u5982\u4E0B\u4F18\u5316\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032636-1770812664.png" alt="image-20220420114150613"></p><p>\u73B0\u5728\u7CFB\u7EDF\u5065\u58EE\u591A\u4E86\uFF0C\u5F88\u4E45\u6CA1\u51FA\u73B0\u4E0A\u6B21\u7684\u95EE\u9898\u4E86\uFF0C\u5C0F\u6797\u53C8\u8DD1\u53BB\u64B8\u732B\u4E86\u3002</p><p>\u67D0\u5929\u6DF1\u591C\uFF0C\u5C0F\u6797\u53C8\u63A5\u5230\u8FD0\u8425\u540C\u5B66\u7535\u8BDD\uFF1A\u4E0A\u6B21\u7684\u95EE\u9898\u91CD\u73B0\u4E86\uFF01</p><p>\u5C3C\u739B\uFF0C\u89C1\u9B3C\u4E86\uFF01</p><p>\u5C0F\u6797\u53C8\u8DD1\u53BB\u67E5\u65E5\u5FD7\uFF0C\u53D1\u73B0\u786E\u5B9E\u662F\u6263\u6B3E\u63A5\u53E3\u8D85\u65F6\u4E86\uFF0C\u4F46\u64A4\u9500\u63A5\u53E3\u8C03\u6210\u529F\u4E86\uFF08\u867D\u7136\u8C03\u4E86\u51E0\u6B21\u624D\u6210\u529F\uFF09\u2014\u2014\u90A3\u4E3A\u6BDB\u8FD8\u6263\u4E86\u94B1\u554A\uFF1F</p><p>\u60F3\u4E86\u534A\u5929\uFF0C\u5C0F\u6797\u7EC8\u4E8E\u53D1\u73B0\u4E86\u95EE\u9898\uFF1A\u548C\u524D\u9762\u63D0\u5230\u7684\u67E5\u8BE2\u95EE\u9898\u4E00\u6837\uFF0C\u64A4\u9500\u7684\u65F6\u5019\u540C\u6837\u65E0\u6CD5\u4FDD\u8BC1\u90A3\u4E2A\u8BE5\u6B7B\u7684\u6263\u6B3E\u6D41\u7A0B\u5DF2\u7ECF\u8DD1\u5B8C\u4E86\u554A\uFF01\u8FD9\u6B21\u662F\u56E0\u4E3A\u64A4\u9500\u903B\u8F91\u786E\u5B9E\u6267\u884C\u4E86\uFF0C\u4F46\u6267\u884C\u7684\u65F6\u5019\u6263\u6B3E\u903B\u8F91\u8FD8\u5728\u8DD1\uFF08\u8FD8\u6CA1\u5199\u5E93\uFF09\uFF01</p><p>\u6240\u4EE5\u64A4\u9500\u63A5\u53E3\u5FC5\u987B\u8003\u8651\u4E24\u79CD\u60C5\u51B5\uFF1A</p><ol><li>\u64A4\u9500\u7684\u65F6\u5019\uFF0C\u6263\u6B3E\u5DF2\u7ECF\u53D1\u751F\u4E86\u2014\u2014\u6B64\u65F6\u80FD\u6B63\u786E\u64A4\u9500\uFF1B</li><li>\u64A4\u9500\u7684\u65F6\u5019\uFF0C\u6263\u6B3E\u8FD8\u6CA1\u53D1\u751F\uFF0C\u4F46\u6263\u6B3E\u6D41\u7A0B\u6B63\u5728\u6267\u884C\u2014\u2014\u6B64\u65F6\u64A4\u9500\u4F1A\u5931\u8D25\uFF0C\u7A0D\u540E\u94B1\u4ECD\u7136\u4F1A\u88AB\u6263\u6389\uFF1B</li></ol><p>\u4E8E\u662F\u5C0F\u6797\u5C31\u60F3\uFF1A\u65E2\u7136\u6263\u6B3E\u8D85\u65F6\u540E\u7ACB\u5373\u8C03\u64A4\u9500\u63A5\u53E3\u6709\u53EF\u80FD\u56E0\u65F6\u5E8F\u95EE\u9898\u5BFC\u81F4\u64A4\u9500\u5931\u8D25\uFF0C\u90A3\u6211\u628A\u64A4\u9500\u64CD\u4F5C\u505A\u6210\u5F02\u6B65\u8C03\u5EA6\u4E0D\u5C31\u884C\u4E86\u561B\u2014\u2014\u5728\u4E00\u6BB5\u65F6\u95F4\u5185\uFF08\u6BD4\u5982\u4E94\u5206\u949F\uFF09\u5982\u679C\u56E0\u672A\u627E\u5230\u8BB0\u5F55\u800C\u64A4\u9500\u5931\u8D25\uFF0C\u5C31\u7A0D\u540E\u91CD\u8BD5\u3002</p><p>\u5C0F\u6797\u7684\u64A4\u9500\u903B\u8F91\u662F\u8FD9\u6837\u7684\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032651-1838918260.png" alt="image-20220420143256054"></p><p>\u539F\u672C\u7531\u4E1A\u52A1\u7CFB\u7EDF\u540C\u6B65\u8C03\u64A4\u9500\u63A5\u53E3\uFF0C\u73B0\u5728\u6539\u6210\u8D70\u8C03\u5EA6\u7CFB\u7EDF\u5F02\u6B65\u64A4\u9500\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u6295\u9012\u64A4\u9500\u4EFB\u52A1\u5B8C\u6210\u540E\u7ACB\u9A6C\u8FD4\u56DE\u7ED3\u679C\u7ED9\u5BA2\u6237\u7AEF\u3002</p><p>\u56E0\u4E3A\u6709\u5F02\u6B65\u91CD\u8BD5\u673A\u5236\uFF0C\u64A4\u9500\u603B\u662F\u80FD\u6210\u529F\uFF08\u9664\u4E86\u5B9E\u9645\u4E2D\u51E0\u4E4E\u4E0D\u4F1A\u53D1\u751F\u7684\u6781\u7AEF\u60C5\u51B5\uFF09\uFF0C\u56E0\u800C\u8FD9\u6B21\u4E00\u5B9A\u80FD\u4FDD\u8BC1\u4E0D\u4F1A\u610F\u5916\u6263\u94B1\uFF01</p><p>\u5C0F\u6797\u540C\u5B66\u62B1\u7740\u5982\u91CA\u91CD\u8D1F\u7684\u5FC3\u6001\u7EE7\u7EED\u64B8\u732B\u3002</p><p>\u7136\u800C\uFF0C\u5B89\u7A33\u65E5\u5B50\u6CA1\u8FC7\u51E0\u5929\uFF0C\u4E00\u4E2A\u96F7\u7535\u4EA4\u52A0\u7684\u591C\u665A\uFF0C\u624B\u673A\u518D\u6B21\u54CD\u8D77\uFF1A\u8F66\u4E3B\u50A8\u503C\u5361\u6D88\u8D39\u7684\u94B1\u83AB\u540D\u5176\u5999\u7ED9\u4EBA\u5BB6\u9000\u56DE\u53BB\u4E86\uFF01\u6CB9\u7AD9\u6253\u7535\u8BDD\u8981\u6211\u4EEC\u8D54\u507F\uFF01</p><p>\u5C0F\u6797\u8D76\u7D27\u67E5\u65E5\u5FD7\uFF0C\u53D1\u73B0\u573A\u666F\u662F\u8FD9\u6837\u7684\uFF1A\u8F66\u4E3B\u738B\u67D0\u7528\u50A8\u503C\u5361\u652F\u4ED8 1000 \u5143\u6CB9\u6B3E\uFF0C\u5931\u8D25\u4E86\uFF1B\u5341\u51E0\u79D2\u540E\u8F66\u4E3B\u518D\u6B21\u7528\u50A8\u503C\u5361\u53D1\u8D77\u652F\u4ED8\uFF0C\u6210\u529F\u4E86\u3002</p><p>\u652F\u4ED8\u6700\u7EC8\u6210\u529F\u4E86\uFF0C\u83AB\u975E\u4EBA\u5DE5\u9000\u94B1\u4E86\uFF1F\u6CA1\u770B\u5230\u4EFB\u4F55\u9000\u6B3E\u8BB0\u5F55\u554A\uFF1F</p><p>\u6293\u8033\u6320\u816E\uFF0C\u767E\u601D\u4E0D\u5F97\u5176\u89E3\u3002\u5C0F\u6797\u53EA\u80FD\u6253\u7535\u8BDD\u7ED9\u50A8\u503C\u5361\u7CFB\u7EDF\u8D1F\u8D23\u4EBA\u5C0F\u674E\u3002</p><p>\u5C0F\u674E\u4E00\u987F\u67E5\u65E5\u5FD7\uFF0C\u6700\u7EC8\u53D1\u73B0\u8FD9\u7B14\u94B1\u662F\u88AB\u8C03\u5EA6\u7CFB\u7EDF\u8C03\u64A4\u9500\u63A5\u53E3\u7ED9\u64A4\u9500\u4E86\uFF01</p><p>\u5C0F\u6797\u5982\u68A6\u65B9\u9192\uFF0C\u624D\u77E5\u9053\u4E4B\u524D\u81EA\u5DF1\u81EA\u9E23\u5F97\u610F\u5730\u72AF\u4E86\u4E2A\u5929\u5927\u7684\u9519\u8BEF\u3002</p><p>\u672C\u6B21\u6D88\u8D39\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u5171\u5411\u50A8\u503C\u5361\u7CFB\u7EDF\u53D1\u8D77\u4E86\u4E24\u6B21\u6263\u6B3E\u7533\u8BF7\u2014\u2014<strong>\u867D\u7136\u90FD\u662F\u540C\u4E00\u7B14\u8BA2\u5355\u7684\u6263\u6B3E\uFF0C\u5374\u662F\u4E24\u4E2A\u72EC\u7ACB\u7684\u4E8B\u52A1</strong>\u3002</p><p>\u5C0F\u6797\uFF08\u4EE5\u53CA\u50A8\u503C\u5361\u7CFB\u7EDF\uFF09\u7684\u9519\u8BEF\u5728\u4E8E\uFF0C\u64A4\u9500\u64CD\u4F5C\u662F\u4F5C\u7528\u5728\u8BA2\u5355\u4E0A\uFF0C\u800C\u4E0D\u662F\u4E8B\u52A1\u4E0A\u3002</p><p>\u5728\u672C\u6B21\u4E8B\u6545\u4E2D\uFF0C\u7B2C\u4E00\u6B21\u6263\u6B3E\u8D85\u65F6\u540E\uFF0C\u4E1A\u52A1\u7CFB\u7EDF\u6295\u9012\u4E86\u64A4\u9500\u4EFB\u52A1\uFF1B\u800C\u540E\u8F66\u4E3B\u53C8\u5BF9\u8BE5\u7B14\u8BA2\u5355\uFF08\u8BA2\u5355\u53F7\u76F8\u540C\uFF09\u53D1\u8D77\u4E86\u7B2C\u4E8C\u6B21\u6263\u6B3E\uFF0C\u6210\u529F\u4E86\uFF1B\u4E0E\u6B64\u540C\u65F6\uFF0C\u8C03\u5EA6\u7CFB\u7EDF\u7B2C\u4E00\u6B21\u64A4\u9500\u5931\u8D25\uFF08\u5361\u7CFB\u7EDF\u672A\u627E\u5230\u6D88\u8D39\u8BB0\u5F55\uFF0C\u6216\u8005\u63A5\u53E3\u8D85\u65F6\uFF09\uFF0C\u4E00\u6BB5\u65F6\u95F4\u540E\u53C8\u53D1\u8D77\u7B2C\u4E8C\u6B21\u64A4\u9500\u2014\u2014\u800C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u8F66\u4E3B\u5DF2\u7ECF\u5B8C\u6210\u4E86\u7B2C\u4E8C\u6B21\u6263\u6B3E\u4E14\u6210\u529F\u4E86\uFF0C\u4E8E\u662F\u8FD9\u6B21\u7684\u64A4\u9500\u4FBF\u4F5C\u7528\u5728\u8FD9\u4E2A\u6210\u529F\u7684\u6263\u6B3E\u4E0A\uFF08\u50A8\u503C\u5361\u7CFB\u7EDF\u7684\u6263\u6B3E\u548C\u64A4\u9500\u63A5\u53E3\u90FD\u662F\u6839\u636E\u8BA2\u5355\u53F7\u6765\u7684\uFF0C\u5B83\u80FD\u4FDD\u8BC1\u540C\u4E00\u7B14\u8BA2\u5355\u4E0D\u4F1A\u91CD\u590D\u6263\u6B3E\uFF0C\u4F46\u64A4\u9500\u7684\u65F6\u5019\u65E0\u6CD5\u533A\u5206\u6263\u6B3E\u662F\u54EA\u6B21\u53D1\u8D77\u7684\uFF09\u3002</p><p>\u6211\u4EEC\u753B\u4E0B\u6D41\u7A0B\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032630-1781175288.png" alt="image-20220420152519360"></p><p>\u5982\u56FE\uFF0C\u7B2C\u4E8C\u6B21\u7684\u6263\u6B3E\u88AB\u8C03\u5EA6\u7CFB\u7EDF\u64A4\u9500\u4E86\u3002</p><p>\u5C0F\u6797\u548C\u5C0F\u674E\u8FD9\u624D\u53D1\u73B0<strong>\u9700\u8981\u7ED9\u6263\u6B3E\u548C\u64A4\u9500\u63A5\u53E3\u589E\u52A0\u4E8B\u52A1\u7F16\u53F7</strong>\u3002</p><p>\u4E4B\u524D\u6263\u6B3E\u63A5\u53E3\u4E3B\u8981\u53C2\u6570\u662F card_no\u3001order_code\u3001amount\uFF0C\u73B0\u5728\u53D8\u6210 card_no\u3001order_code\u3001trans_id\u3001amount\u3002</p><p>\u4E4B\u524D\u64A4\u9500\u63A5\u53E3\u53C2\u6570\u662F order_code\uFF0C\u73B0\u5728\u53D8\u6210 order_code\u3001trans_id\u3002</p><p>\u901A\u8FC7 trans_id \u5C06\u6263\u6B3E\u548C\u64A4\u9500\u7ED1\u5B9A\u5230\u540C\u4E00\u4E2A\u64CD\u4F5C\u4E8B\u52A1\u4E0A\uFF0C\u53EA\u4F1A\u64A4\u9500\u76F8\u5E94 trans_id \u7684\u6263\u6B3E\u64CD\u4F5C\u3002</p><p>trans_id \u7531\u5BA2\u6237\u7AEF\u6839\u636E\u5F53\u524D\u65F6\u95F4\u6BEB\u79D2\u6570\u751F\u6210\uFF08\u540E\u9762\u4F1A\u8BF4\u4E3A\u5565\u53D6\u6BEB\u79D2\u65F6\u95F4\u6233\uFF09\uFF0C\u5B83\u4E0D\u4E00\u5B9A\u9700\u8981\u5168\u5C40\u552F\u4E00\uFF0C\u53EA\u9700\u8981\u9488\u5BF9\u540C\u4E00\u4E2A\u8BA2\u5355\u662F\u552F\u4E00\u7684\u5373\u53EF\u3002</p><p>\u52A0\u4E86\u4E8B\u52A1\u7684\u6982\u5FF5\u540E\uFF0C\u5C0F\u6797\u548C\u5C0F\u674E\u53D1\u73B0\u538B\u6839\u4E0D\u9700\u8981\u901A\u8FC7\u8C03\u5EA6\u7CFB\u7EDF\u4E0D\u65AD\u5C1D\u8BD5\uFF0C\u53EA\u8981\u4FDD\u8BC1\u64A4\u9500\u63A5\u53E3\u8C03\u6210\u529F\u5C31\u80FD\u4FDD\u8BC1\u5BF9\u5E94\u7684\u6263\u6B3E\u4E8B\u52A1\u4E00\u5B9A\u80FD\u591F\u88AB\u64A4\u9500\uFF08\u6216\u8005\u963B\u6B62\u6267\u884C\uFF09\u3002</p><p>\u73B0\u5728\u64A4\u9500\u63A5\u53E3\u505A\u4E24\u4EF6\u4E8B\uFF1A</p><ol><li>\u5199\u5165\u4E00\u6761\u64A4\u9500\u8BB0\u5F55\uFF1B</li><li>\u8BD5\u56FE\u64A4\u9500\u6389\u5DF2\u7ECF\u4EA7\u751F\u7684\u6263\u6B3E\uFF1B</li></ol><p>\u64A4\u9500\u903B\u8F91\u5982\u4E0B\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032593-203950863.png" alt="image-20220420205541356"></p><p>\u518D\u770B\u770B\u6263\u6B3E\u7684\u903B\u8F91\u3002</p><p>\u6263\u6B3E\u8BB0\u5F55\u8868\u5927\u81F4\u957F\u8FD9\u6837\u5B50\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032847-1446149878.png" alt="image-20220420184516421"></p><p>\u6263\u6B3E\u903B\u8F91\u5982\u4E0B\uFF1A</p><ol><li>\u5148\u68C0\u67E5\u662F\u5426\u5B58\u5728\u5BF9\u8BE5\u6263\u6B3E\u4E8B\u52A1\u7684\u64A4\u9500\u8BB0\u5F55\uFF0C\u5982\u679C\u5B58\u5728\uFF0C\u5219\u62D2\u7EDD\u5904\u7406\uFF1B\u4E0D\u5B58\u5728\u5219\u7EE7\u7EED\uFF1B</li><li>\u518D\u68C0\u67E5\u662F\u5426\u5B58\u5728\u8BE5\u8BA2\u5355\u7684\u6263\u6B3E\u8BB0\u5F55\uFF1A</li></ol><p>2.1 \u5982\u679C\u4E0D\u5B58\u5728\uFF0C\u5219\u8D70\u6B63\u5E38\u6263\u6B3E\u6D41\u7A0B\uFF1B</p><p>2.2 \u5982\u679C\u5B58\u5728\u8BB0\u5F55\uFF0C\u5219\u8981\u6BD4\u8F83\u4E8B\u52A1\u7F16\u53F7\uFF1A\u5982\u679C\u5DF2\u5B58\u5728\u7684\u90A3\u6761\u4E8B\u52A1\u7F16\u53F7\u5C0F\u4E8E\u5F53\u524D\u7684\uFF0C\u5219\u7528\u5F53\u524D\u7684\u4E8B\u52A1\u7F16\u53F7\u8986\u76D6\uFF0C\u5426\u5219\u4E0D\u505A\u4EFB\u4F55\u5904\u7406\uFF08\u540E\u9762\u4F1A\u89E3\u91CA\u8FD9\u4E48\u505A\u7684\u539F\u56E0\uFF09\uFF1B</p><p>\u6D41\u7A0B\u56FE\u5982\u4E0B\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220423192133909-244591209.png" alt=""></p><p>\u73B0\u5728\u6211\u4EEC\u770B\u770B\u5F53\u64A4\u9500\u6D41\u7A0B\u6267\u884C\u65F6\uFF0C\u88AB\u64A4\u9500\u7684\u6263\u6B3E\u4E8B\u52A1\u5904\u4E8E\u4E0D\u540C\u72B6\u6001\u4E0B\u7684\u60C5\u51B5\uFF1A</p><ol><li>\u6263\u6B3E\u4E8B\u52A1\u6267\u884C\u5931\u8D25\u3002\u6B64\u65F6\u538B\u6839\u4E0D\u4F1A\u4EA7\u751F\u6263\u6B3E\uFF1B</li><li>\u6263\u6B3E\u4E8B\u52A1\u5DF2\u7ECF\u6267\u884C\u5B8C\u6BD5\uFF0C\u4EA7\u751F\u4E86\u5B9E\u9645\u6263\u6B3E\u3002\u6B64\u65F6\u64A4\u9500\u6D41\u7A0B\u4F1A\u64A4\u9500\u6389\u8FD9\u7B14\u6263\u6B3E\uFF1B</li><li>\u6263\u6B3E\u4E8B\u52A1\u6B63\u5728\u6267\u884C\u4E2D\uFF0C\u8FD8\u6CA1\u6709\u5199\u5E93\uFF0C\u4F46\u7A0D\u540E\u4F1A\u5199\u5E93\u3002\u6263\u6B3E\u4E8B\u52A1\u5B9E\u9645\u5199\u5E93\u4E4B\u524D\uFF0C\u4F1A\u5148\u68C0\u67E5\u662F\u5426\u5B58\u5728\u5BF9\u8BE5\u4E8B\u52A1\u7684\u64A4\u9500\u8BB0\u5F55\uFF0C\u56E0\u4E3A\u5148\u524D\u64A4\u9500\u6D41\u7A0B\u5DF2\u7ECF\u5199\u5165\u4E86\u4E00\u6761\u5BF9\u8BE5\u4E8B\u52A1\u7684\u64A4\u9500\u8BB0\u5F55\uFF0C\u6263\u6B3E\u4E8B\u52A1\u6B64\u65F6\u4F1A\u67E5\u5230\u64A4\u9500\u8BB0\u5F55\uFF0C\u4ECE\u800C\u963B\u6B62\u672C\u6B21\u6263\u6B3E\u4E8B\u52A1\u5199\u5E93\uFF08\u672C\u6B21\u4E8B\u52A1\u4E3B\u52A8\u56DE\u6EDA\uFF09\u3002</li></ol><p>\u7531\u4E8E\u64A4\u9500\u7684\u65F6\u5019\u662F\u6309\u4E8B\u52A1\u7F16\u53F7\u6765\u7684\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u64A4\u9500\u522B\u7684\u4E8B\u52A1\u7684\u6263\u6B3E\u3002</p><p>\u73B0\u5728\u6211\u4EEC\u89E3\u91CA\u4E0B\u4E3A\u4F55\u8981\u7528\u5F53\u524D\u65F6\u95F4\u7684\u6BEB\u79D2\u65F6\u95F4\u6233\u4F5C\u4E3A\u4E8B\u52A1\u7F16\u53F7\u3002</p><p>\u56DE\u5230\u4E0A\u9762\u8F66\u4E3B\u738B\u67D0\u7684\u573A\u666F\u3002\u738B\u67D0\u7B2C\u4E00\u6B21\u7528\u5361\u652F\u4ED8\u8D85\u65F6\uFF0C\u4E8E\u662F\u4ED6\u51B3\u5B9A\u91CD\u8BD5\u3002\u8BE5\u573A\u666F\u4E2D\uFF0C\u5361\u7CFB\u7EDF\u63A5\u6536\u5230\u540C\u4E00\u7B14\u8BA2\u5355\u7684\u4E24\u6B21\u6263\u6B3E\u4E8B\u52A1\u4EE5\u53CA\u4E00\u6B21\u64A4\u9500\u4E8B\u52A1\u3002\u5047\u5982\u4E24\u6B21\u4E8B\u52A1\u90FD\u5C1D\u8BD5\u5199\u5E93\uFF0C\u90A3\u4E48\u5F53\u540E\u9762\u7684\u4E8B\u52A1\uFF08\u4E0D\u4E00\u5B9A\u662F\u7B2C\u4E8C\u6B21\u6263\u6B3E\u7684\u90A3\u4E2A\uFF09\u5C1D\u8BD5\u5199\u5E93\u65F6\uFF0C\u80AF\u5B9A\u5DF2\u7ECF\u5B58\u5728\u4E00\u6761\u6263\u6B3E\u8BB0\u5F55\uFF0C\u6B64\u65F6\u540E\u9762\u8FD9\u4E2A\u4E8B\u52A1\u8981\u5982\u4F55\u505A\uFF1F</p><ol><li>\u7528\u540E\u8005\u7684\u4E8B\u52A1\u7F16\u53F7\u66FF\u6362\u6389\u524D\u8005\u7684\u3002</li><li>\u4E0D\u505A\u4EFB\u4F55\u5904\u7406\u3002</li></ol><blockquote><p>\u4E24\u6B21\u4E8B\u52A1\u7684\u6267\u884C\u903B\u8F91\u5B8C\u5168\u76F8\u540C\uFF0C\u4EA7\u751F\u7684\u6263\u6B3E\u8BB0\u5F55\u6570\u636E\u4E5F\u662F\u5B8C\u5168\u76F8\u540C\u7684\u2014\u2014\u9664\u4E86\u4E8B\u52A1\u7F16\u53F7\u548C\u6263\u6B3E\u65F6\u95F4\u3002</p></blockquote><p>\u8FD9\u91CC\u7684\u5173\u952E\u662F\uFF0C<strong>\u6211\u4EEC\u65E0\u6CD5\u786E\u5B9A\u7B2C\u4E00\u6B21\u6263\u6B3E\u3001\u7B2C\u4E8C\u6B21\u6263\u6B3E\u3001\u5BF9\u7B2C\u4E00\u6B21\u6263\u6B3E\u7684\u64A4\u9500\u8FD9\u4E09\u4E2A\u8BF7\u6C42\u5199\u5E93\u7684\u5148\u540E\u987A\u5E8F</strong>\u3002</p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u91C7\u7528\u65B9\u6848 1\uFF0C\u66FF\u6362\u4E8B\u52A1\u7F16\u53F7\uFF0C\u90A3\u4E48\u5F53\u7B2C\u4E8C\u6B21\u7684\u63D0\u4EA4\u5148\u5199\u5E93\u65F6\uFF0C\u540E\u9762\u4E8B\u52A1\uFF08\u7B2C\u4E00\u6B21\u63D0\u4EA4\u7684\u6263\u6B3E\u8BF7\u6C42\uFF09\u7684\u66FF\u6362\u4F1A\u5BFC\u81F4\u4E8B\u52A1\u7F16\u53F7\u53D8\u6210\u4E86\u5F85\u64A4\u9500\u7684\u90A3\u4E2A\uFF0C\u56E0\u800C\u5F88\u53EF\u80FD\u4F1A\u88AB\u64A4\u9500\u6389\uFF0C\u8FD9\u5C31\u4F1A\u5BFC\u81F4\u7528\u6237\u4ED8\u7684\u94B1\u83AB\u540D\u5176\u5999\u88AB\u9000\u56DE\u4E86\u3002</p><p>\u5982\u679C\u91C7\u7528\u65B9\u6848 2\uFF0C\u4E0D\u505A\u4EFB\u4F55\u5904\u7406\uFF0C\u90A3\u4E48\u5F53\u7B2C\u4E00\u6B21\u7684\u63D0\u4EA4\u5148\u5199\u5E93\u65F6\uFF0C\u4E8B\u52A1\u7F16\u53F7\u5C31\u4E00\u76F4\u662F\u5F85\u64A4\u9500\u7684\u90A3\u4E2A\uFF0C\u4E5F\u4F1A\u88AB\u64A4\u9500\u6389\uFF0C\u5BFC\u81F4\u7528\u6237\u4ED8\u7684\u94B1\u83AB\u540D\u5176\u5999\u88AB\u9000\u56DE\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u65E0\u8111\u66FF\u6362\u6216\u4E0D\u66FF\u6362\u90FD\u662F\u6709\u95EE\u9898\u7684\u3002</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032842-1060919890.png" alt="image-20220420220641797"></p><p><em>\u7B2C\u4E00\u6B21\u6263\u6B3E\u4E8B\u52A1\u5148\u5199\u5E93\u7684\u60C5\u51B5</em></p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032649-874208200.png" alt="image-20220420221122078"></p><p><em>\u7B2C\u4E8C\u6B21\u6263\u6B3E\u4E8B\u52A1\u5148\u5199\u5E93\u7684\u60C5\u51B5</em></p><p>\u5B9E\u9645\u7684\u4E1A\u52A1\u573A\u666F\u662F\uFF0C\u5BF9\u4E8E\u540C\u4E00\u7B14\u8BA2\u5355\uFF0C\u65E0\u8BBA\u53D1\u51FA\u591A\u5C11\u6B21\u6263\u6B3E\u8BF7\u6C42\uFF0C\u53EA\u5141\u8BB8\u4E00\u6B21\u6210\u529F\uFF0C\u800C\u4E14\u8FD9\u6B21\u6210\u529F\u7684\u6263\u6B3E\u4E0D\u80FD\u88AB\u8BEF\u64A4\u9500\u3002\u6709\u5F88\u591A\u65B9\u6848\u53EF\u4EE5\u5B9E\u73B0\u8FD9\u4E00\u70B9\uFF0C\u4E0D\u8FC7\u6709\u4E9B\u65B9\u6848\u9700\u8981\u589E\u52A0\u989D\u5916\u8868\uFF0C\u6709\u4E9B\u5219\u9700\u8981\u4E3A\u540C\u4E00\u7B14\u8BA2\u5355\u7EF4\u62A4\u591A\u6761\u6263\u6B3E\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u90FD\u4F1A\u5E26\u6765\u989D\u5916\u7684\u590D\u6742\u6027\u3002</p><p>\u6211\u4EEC\u91C7\u53D6\u4E8B\u52A1\u5E8F\u5217\u53F7\uFF08\u6BEB\u79D2\u65F6\u95F4\u6233\uFF09\u7684\u65B9\u5F0F\u6765\u4FDD\u8BC1\u6263\u6B3E\u4E8B\u52A1\u7684\u65F6\u5E8F\u6027\uFF0C\u53EA\u5141\u8BB8\u540E\u9762\u8986\u76D6\u524D\u9762\u7684\uFF0C\u4E0D\u5141\u8BB8\u53CD\u8FC7\u6765\u8986\u76D6\u3002\u5176\u57FA\u4E8E\u8FD9\u6837\u7684\u4E8B\u5B9E\uFF1A\u7528\u6237\u5982\u679C\u5BF9\u540C\u4E00\u7B14\u8BA2\u5355\u53D1\u51FA\u591A\u6B21\u6263\u6B3E\u8BF7\u6C42\uFF0C\u90A3\u4E00\u5B9A\u662F\u524D\u9762\u6263\u6B3E\u5931\u8D25\u4E86\uFF0C\u56E0\u800C\u4E1A\u52A1\u7CFB\u7EDF\u4F1A\u4E3A\u524D\u9762\u90A3\u4E9B\u5931\u8D25\u7684\u6263\u6B3E\u53D1\u51FA\u64A4\u9500\u8BF7\u6C42\uFF0C\u6240\u4EE5\u53EA\u8981\u4FDD\u8BC1\u4EC5\u5141\u8BB8\u540E\u9762\u8986\u76D6\u524D\u9762\u7684\u4E8B\u52A1\uFF0C\u5C31\u4E0D\u4F1A\u9020\u6210\u8BEF\u64A4\u9500\uFF08\u56E0\u4E3A\u552F\u6709\u6700\u540E\u90A3\u4E2A\u6263\u6B3E\u4E8B\u52A1\u4E0D\u4F1A\u5B58\u5728\u64A4\u9500\u8BF7\u6C42\u3002\u611F\u5174\u8DA3\u7684\u53EF\u53C2\u7167\u4E0A\u9762\u7684\u56FE\u793A\u63A8\u6F14\u4E00\u4E0B\uFF09\u3002</p><blockquote><p>\u8FD9\u91CC\u8BF4\u7684\u4E8B\u52A1\u662F\u6307\u4E00\u6B21\u6263\u6B3E\u5904\u7406\u6D41\uFF0C\u4E0D\u662F\u6307\u6570\u636E\u5E93\u4E8B\u52A1\u3002</p></blockquote><h3 id="\u6240\u4EE5\u5462" tabindex="-1"><a class="header-anchor" href="#\u6240\u4EE5\u5462" aria-hidden="true">#</a> \u6240\u4EE5\u5462\uFF1F</h3><p>\u6211\u4E0D\u60F3\u7F16\u7A0B\u4E86\uFF0C\u8BF4\u771F\u7684\uFF0C\u8FD9\u4E48\u4E2A\u7B80\u5355\u7684\u6263\u6B3E\u573A\u666F\u5C31\u626F\u51FA\u8FD9\u4E48\u591A\u5E7A\u86FE\u5B50\uFF0C\u592A\u96BE\u4E86\uFF01</p><p>\u73B0\u5B9E\u4E2D\u6BD4\u8FD9\u590D\u6742\u7684\u573A\u666F\u591A\u5F97\u662F\u3002</p><p>\u7A0B\u5E8F\u5458\u5230\u5E95\u662F\u600E\u4E48\u6D3B\u4E0B\u6765\u7684\uFF1F</p><p>\u7B54\u6848\u662F\uFF0C\u4ED6\u4EEC\u7684\u4E00\u751F\u662F\u5728\u6CA1\u5B8C\u6CA1\u4E86\u7684 bug \u4E2D\u5EA6\u8FC7\u7684\u3002</p><p>90% \u4EE5\u4E0A\u7684 bug \u90FD\u662F\u56E0\u4E3A\u5BF9\u5931\u8D25\u573A\u666F\u8003\u8651\u4E0D\u5468\u5BFC\u81F4\u3002</p><p>\u5982\u679C\u628A\u73B0\u5B9E\u770B\u6210\u4E8B\u4EF6\u6D41\uFF0C\u90A3\u4E48\u4E8B\u4EF6\u6D41\u4E2D\u7684\u7EDD\u5927\u591A\u6570\u8282\u70B9\u90FD\u6709\u4E0D\u6B62\u4E00\u4E2A\u51FA\u53E3\u5206\u652F\uFF08\u5178\u578B\u7684\u662F\u201D\u6B63\u5E38\u201C\u548C\u201D\u5F02\u5E38\u201C\uFF09\u30022022 \u5E74 4 \u6708 30 \u65E5\u665A\uFF0C\u5C0F\u6797\u540C\u5B66\u53EF\u80FD\u8EBA\u5728\u5E8A\u4E0A\u73A9\u6E38\u620F\uFF0C\u4E5F\u53EF\u80FD\u8EBA\u5728 ICU\u3002</p><p>\u7CFB\u7EDF\uFF08\u7279\u522B\u662F\u4E1A\u52A1\u7CFB\u7EDF\uFF09\u662F\u5BF9\u73B0\u5B9E\u4E16\u754C\u4E1A\u52A1\u7684\u53CD\u6620\uFF0C\u6BCF\u4E2A\u8282\u70B9\u540C\u6837\u5B58\u5728\u591A\u79CD\u53EF\u80FD\u3002</p><p>\u5178\u578B\u7684\u4E1A\u52A1\u6D41\u5206\u6790\u6B65\u9AA4\u662F\u8FD9\u6837\u7684\uFF1A</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220421092032841-805463935.png" alt="image-20220420225002673"></p><p>\u51E0\u4E4E\u6240\u6709\u7684\u7ED3\u70B9\u90FD\u8981\u8003\u8651\u5931\u8D25\u573A\u666F\uFF0C\u800C\u5BF9\u4E8E\u4E00\u4E9B\u5931\u8D25\u573A\u666F\u7684\u8865\u507F\u63AA\u65BD\u4ECD\u7136\u53EF\u80FD\u5931\u8D25\uFF0C\u5982\u6B64\u9012\u5F52\uFF0C\u6700\u7EC8\u7531\u81EA\u52A8\u8865\u507F\u7CFB\u7EDF\uFF08\u5982\u6F0F\u5355\u68C0\u6D4B/\u8865\u507F\u7CFB\u7EDF\uFF09\u6216\u4EBA\u5DE5\u5904\u7406\u6765\u515C\u5E95\u3002</p><p>\u5931\u8D25\u7684\u4E00\u5927\u91CD\u8981\u6839\u6E90\u662F\u5206\u5E03\u5F0F\u3002</p><p>\u4E0D\u8981\u63D0\u4EC0\u4E48\u5355\u4F53\u67B6\u6784\uFF0C\u505A web \u5F00\u53D1\u7684\uFF0C\u81EA\u5165\u884C\u7B2C\u4E00\u5929\u8D77\u5C31\u9762\u5BF9\u5206\u5E03\u5F0F\u7CFB\u7EDF\u3002</p><p>\u5178\u578B\u7684\u5206\u5E03\u5F0F\u662F\u524D\u540E\u7AEF\u4EA4\u4E92\u3002\u81EA ajax \u51FA\u4E16\u4EE5\u6765\uFF0C\u524D\u540E\u7AEF\u63A5\u53E3\u4EA4\u4E92\u6210\u4E3A\u5E38\u6001\uFF0C\u63A5\u53E3\u5931\u8D25\u4E5F\u662F\u6BCF\u4E2A\u7A0B\u5E8F\u5458\u90FD\u4F1A\u9047\u5230\u7684\u95EE\u9898\u3002\u5F88\u5927\u90E8\u5206\u7684\u524D\u540E\u7AEF\u4EA4\u4E92\u5931\u8D25\u7684\u573A\u666F\u6CA1\u6709\u5F97\u5230\u5F88\u597D\u5730\u5904\u7406\uFF08\u7279\u522B\u662F\u8D85\u65F6\uFF09\uFF0C\u6BD4\u5982\u6CA1\u6709\u53BB\u91CD\uFF0C\u5BFC\u81F4\u91CD\u590D\u5199\u5165\u6570\u636E\u3002</p><p>\u81EA\u4ECE\u5FAE\u670D\u52A1\u6A2A\u884C\u4EE5\u6765\uFF0C\u540E\u7AEF\u5F00\u53D1\u4EBA\u5458\u65E0\u4E0D\u88AB\u5206\u5E03\u5F0F\u4E8B\u52A1\u641E\u5F97\u7126\u5934\u70C2\u989D\u3002\u4E1A\u754C\u4E5F\u603B\u7ED3\u4E86\u4E9B\u89E3\u51B3\u65B9\u6848\uFF0C\u6BD4\u5982\u4E24\u9636\u6BB5\u63D0\u4EA4\u3001SAGA\u3001TCC \u7B49\uFF0C\u4F46\u771F\u6B63\u5B9E\u73B0\u8D77\u6765\u90FD\u4E0D\u7B80\u5355\uFF0C\u4E00\u4E2A\u770B\u4F3C\u7B80\u5355\u7684\u4E1A\u52A1\u90FD\u4F1A\u641E\u5F97\u5F88\u590D\u6742\u3002\u6240\u4EE5\u4E1A\u754C\u53C8\u641E\u4E86\u4E9B\u73B0\u6210\u7684\u5F00\u6E90\u65B9\u6848\u5982 seata\u3001DTM\u3002</p><h3 id="\u8FD8\u6709\u6551\u5417" tabindex="-1"><a class="header-anchor" href="#\u8FD8\u6709\u6551\u5417" aria-hidden="true">#</a> \u8FD8\u6709\u6551\u5417\uFF1F</h3><p>\u597D\u6D88\u606F\u662F\uFF0C\u4E0D\u662F\u6240\u6709\u7684\u7CFB\u7EDF\u90FD\u9700\u8981\u90A3\u4E48\u9AD8\u7684\u53EF\u9760\u6027\u4FDD\u8BC1\uFF0C\u4E5F\u4E0D\u662F\u6240\u6709\u7684\u5931\u8D25\u573A\u666F\u90FD\u8981\u505A\u8865\u507F\u5904\u7406\u3002</p><p>\u4F60\u53EF\u80FD\u662F\u5728\u4E00\u5BB6\u521D\u521B\u516C\u53F8\uFF0C\u522B\u8BF4\u7CFB\u7EDF\u4E00\u5206\u949F\u4E0D\u53EF\u7528\u4E86\uFF0C\u5C31\u662F\u5E93\u88AB\u5220\u4E86\u4F30\u8BA1\u4E5F\u6CA1\u4E8B\u3002</p><p>\u4F60\u505A\u7684\u7CFB\u7EDF\u53EF\u80FD\u53EA\u662F\u7ED9\u5185\u90E8\u4EBA\u5458\u7528\u7528\uFF0C\u51E1\u662F\u9047\u5230\u5931\u8D25\u5C31\u629B\u5F02\u5E38\uFF0C\u5927\u4E0D\u4E86\u4EBA\u5DE5\u53BB\u4FEE\u590D\u6570\u636E\u4E5F\u662F\u53EF\u4EE5\u7684\u3002</p><p>\u8FD9\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u5F88\u53EF\u80FD\u4F60\u5E76\u4E0D\u9700\u8981\u53BB\u5F00\u53D1\u9AD8\u53EF\u7528\u7CFB\u7EDF\uFF0C\u4ED6\u4EEC\u66F4\u8BB2\u7A76\u6548\u7387\uFF0C\u628A\u6B63\u5E38\u6D41\u7A0B\u7801\u51FA\u6765\u57FA\u672C\u5C31\u5B8C\u4E8B\u4E86\u3002</p><p>\u8BB2\u7A76\u70B9\u53EF\u7528\u6027\u7684\uFF0C\u7A0D\u5FAE\u628A\u4EE3\u7801\u5199\u597D\u70B9\uFF0C\u670D\u52A1\u5668\u914D\u7F6E\u5806\u9AD8\u70B9\uFF0C\u4E1A\u52A1\u6D41\u7A0B\u8BBE\u8BA1\u4E0A\u6CE8\u610F\u70B9\uFF0C\u57FA\u672C\u4E5F\u80FD\u89C4\u907F\u5927\u90E8\u5206\u796D\u5929\u6027\u7684\u95EE\u9898\u3002</p><p>\u7B49\u4F60\u516C\u53F8\u771F\u7684\u53D1\u5C55\u6210 BAT \u90A3\u79CD\u4E86\uFF0C\u662F\u771F\u6B63\u62FC\u5200\u5DE5\u7684\u65F6\u5019\uFF0C\u4E07\u5206\u4E4B\u4E00\u6982\u7387\u7684\u5F02\u5E38\u573A\u666F\u53EF\u80FD\u5C31\u4F1A\u8BA9\u7CFB\u7EDF\u5929\u5929\u5B95\u673A\uFF0C\u8D26\u6237\u5929\u5929\u5C11\u94B1\u3002\u90A3\u65F6\u5019\u5404\u79CD\u65B9\u6848\u3001\u67B6\u6784\u3001\u5206\u6790\u90FD\u8981\u62FF\u5230\u684C\u9762\u4E0A\u6765\u4E86\u3002</p><p>\u6240\u4EE5\uFF0C\u9762\u5411\u5931\u8D25\u7F16\u7A0B\u8BDA\u7136\u5F88\u96BE\uFF0C\u4F46\u4E0D\u4EE3\u8868\u4F60\u5FC5\u987B\u5F97\u5929\u5929\u9762\u5BF9\u7740\u5931\u8D25\u6293\u8033\u6320\u816E\uFF0C<strong>\u4F60\u9700\u8981\u8BC4\u4F30\u4F60\u6240\u8D1F\u8D23\u7684\u7CFB\u7EDF\u5728\u6210\u672C\u3001\u6548\u7387\u3001\u5065\u58EE\u6027\u4E0A\u5E94\u505A\u600E\u6837\u7684\u53D6\u820D</strong>\u3002</p><p><img src="https://img2022.cnblogs.com/blog/1997761/202204/1997761-20220422101501383-874386105.png" alt=""></p>',132),c={href:"https://www.cnblogs.com/linvanda/p/16172767.html",target:"_blank",rel:"noopener noreferrer"};function m(d,b){const o=s("ExternalLinkIcon");return l(),n("div",null,[r,p("blockquote",null,[p("p",null,[t("\u8F6C\u8F7D\u94FE\u63A5\uFF1A"),p("a",c,[t("https://www.cnblogs.com/linvanda/p/16172767.html"),e(o)]),t("\uFF0C\u6574\u7406\uFF1Amusk")])])])}const u=g(i,[["render",m],["__file","biancwsmnmn.html.vue"]]);export{u as default};
