import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as n,c as t,a as e,b as i,d as r,e as o,r as d}from"./app.900d58e3.js";const s={},h=e("p",null,"之前面试总是把大量精力放在准备自我介绍和项目上面，却忽略了面试中一个最关键的环节，那就是面试最后一定会被问到的问题：“你有什么要问我的吗？”这其实是一个最好的了解公司的机会，即使自己没有走到下一步，也应该把握这几分钟尽可能的多提一些有价值的问题。",-1),c=e("p",null,[i("然而之前自己在"),e("a",{href:"/jump/super-jump/word?word=%E7%89%9B%E5%AE%A2"},"牛客"),i("上并没有看到太多这方面的经验帖，网上的资料也总觉得不够好，直到我在GitHub上面看到了一篇非常全面的反向面试问题总结，原文主要是翻译自GitHub的另一篇英文文档，所以我对译文又进行了一些删减，下面这些是我认为在面试最后几分钟内最应该提出的问题，可以按照个人兴趣和面试气氛选择几个向面试官或HR提问")],-1),u=e("p",null,"原文地址：",-1),_={href:"https://github.com/viraptor/reverse-interview",target:"_blank",rel:"noopener noreferrer"},p={href:"https://github.com/yifeikong/reverse-interview-zh",target:"_blank",rel:"noopener noreferrer"},b=o('<h1 id="职责" tabindex="-1"><a class="header-anchor" href="#职责" aria-hidden="true">#</a> 职责</h1><ul><li>值班或者遇到问题加班时候有加班费吗？</li><li>我的日常工作是什么？有给我设定的特定目标吗？</li><li>入职培训会是什么样的？</li><li>你期望我在最初的一个月 / 三个月能够完成什么？</li><li>试用期结束的时候，你会怎么样衡量我的绩效？</li><li>一个典型的一天或者一周的工作是怎样安排的？</li><li>在这份工作上，我将会和谁紧密合作？</li><li>我在这个岗位上应该如何发展？会有哪些机会？</li><li>每天预期 / 核心工作时间是多少小时？</li><li>我入职的岗位是新增还是接替之前离职的同事？（是否有技术债需要还）？</li><li>入职之后在哪个项目组，项目是新成立还是已有的？</li></ul><h1 id="技术" tabindex="-1"><a class="header-anchor" href="#技术" aria-hidden="true">#</a> 技术</h1><ul><li>公司常用的技术栈是什么？</li><li>你们怎么<a href="/jump/super-jump/word?word=%E6%B5%8B%E8%AF%95">测试</a>代码？怎么追踪 bug?</li><li>你们怎样监控项目？</li><li>从计划到完成一项任务的工作流是什么样的？</li><li>有标准的开发环境吗？是强制的吗？</li></ul><h1 id="团队" tabindex="-1"><a class="header-anchor" href="#团队" aria-hidden="true">#</a> 团队</h1><ul><li>工作是怎么组织的？团队有多大？公司技术团队的架构和人员组成？</li><li>团队内 / 团队间的交流通常是怎样的？如果遇到不同的意见怎样处理？</li><li>每周都会开什么类型的会议？</li><li>产品 / 服务的规划是什么样的？（几周一次发布 / 持续部署 / 多个发布流 / ...)</li><li>你们如何跟踪进度？</li><li>预期和目标是如何设定的？谁来设定？</li></ul><h1 id="公司" tabindex="-1"><a class="header-anchor" href="#公司" aria-hidden="true">#</a> 公司</h1><ul><li>公司为什么在招人？（产品发展 / 新产品 / 波动...)</li><li>晋升流程是怎样的？要求 / 预期是怎样沟通的？</li><li>绩效评估流程是怎样的？</li><li>公司的调薪制度是如何的？</li></ul><h1 id="办公室" tabindex="-1"><a class="header-anchor" href="#办公室" aria-hidden="true">#</a> 办公室</h1><ul><li>公司提供<a href="/jump/super-jump/word?word=%E7%A1%AC%E4%BB%B6">硬件</a>吗？更新计划如何？</li><li>办公室的布局如何？（开放的 / 小隔间 / 独立办公室）</li><li>有没有支持 / 市场 / 或者其他需要大量打电话的团队在我的团队旁边办公？</li></ul><h1 id="待遇" tabindex="-1"><a class="header-anchor" href="#待遇" aria-hidden="true">#</a> 待遇</h1><ul><li>如果有奖金计划的话，奖金如何分配？</li><li>如果有奖金计划的话，过去的几年里通常会发百分之多少的奖金？</li><li>有五险一金或者其他退休养老金等福利吗？</li><li>五险一金中，补充公积金一般交多少比例？我可以自己选择这一比例吗？</li><li>有什么医疗保险吗？如果有的话何时开始？</li></ul><h1 id="休假" tabindex="-1"><a class="header-anchor" href="#休假" aria-hidden="true">#</a> 休假</h1><ul><li>带薪休假时间有多久？</li><li>病假和事假是分开的还是一起算？</li><li>假期的更新策略是什么样的？也就是说未休的假期能否滚入下一周期？</li></ul>',14),f={href:"https://www.nowcoder.com/discuss/1045767?type=0&order=1&pos=2&page=2&source_id=discuss_center_0_nctrack&channel=-1&ncTraceId=24400bc74d23441b990bf6e436b5f65d.502.16636561253343843&gio_id=72C60591C52664CA1A03B999CC3E6600-1663656127912",target:"_blank",rel:"noopener noreferrer"};function m(w,g){const l=d("ExternalLinkIcon");return n(),t("div",null,[h,c,u,e("blockquote",null,[e("p",null,[i("英文版："),e("a",_,[i("https://github.com/viraptor/reverse-interview"),r(l)])]),e("p",null,[i("中文版："),e("a",p,[i("https://github.com/yifeikong/reverse-interview-zh"),r(l)])])]),b,e("blockquote",null,[e("p",null,[i("参考链接："),e("a",f,[i("https://www.nowcoder.com/discuss/1045767?type=0&order=1&pos=2&page=2&source_id=discuss_center_0_nctrack&channel=-1&ncTraceId=24400bc74d23441b990bf6e436b5f65d.502.16636561253343843&gio_id=72C60591C52664CA1A03B999CC3E6600-1663656127912"),r(l)]),i("，整理：musk")])])])}const x=a(s,[["render",m],["__file","dangmsgtwnysyywwdmgrhhdbjmjnkw.html.vue"]]);export{x as default};
