import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as r,a as e,d as s,f as o,r as l}from"./app.3728560f.js";const n={},c=o('<h1 id="\u5355\u5143\u6D4B\u8BD5-\u6D45\u8C08\u5355\u5143\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#\u5355\u5143\u6D4B\u8BD5-\u6D45\u8C08\u5355\u5143\u6D4B\u8BD5" aria-hidden="true">#</a> \u5355\u5143\u6D4B\u8BD5 - \u6D45\u8C08\u5355\u5143\u6D4B\u8BD5</h1><blockquote><p>\u5355\u5143\u6D4B\u8BD5\uFF08unit testing\uFF09\uFF0C\u662F\u6307\u5BF9\u8F6F\u4EF6\u4E2D\u7684\u6700\u5C0F\u53EF\u6D4B\u8BD5\u5355\u5143\u8FDB\u884C\u68C0\u67E5\u548C\u9A8C\u8BC1\u3002\u8FD9\u662F\u57FA\u7840\uFF0C\u6240\u4EE5\u56F4\u7ED5\u7740\u5355\u5143\u6D4B\u8BD5\uFF0C\u6211\u4ECE\u7F51\u4E0A\u641C\u96C6\u548C\u603B\u7ED3\u4E86\u76F8\u5173\u7684\u6982\u5FF5\uFF0C\u4EE5\u52A9\u4F60\u5B8C\u5584\u4F53\u7CFB\u3002</p></blockquote><h2 id="_1-\u4EC0\u4E48\u662F\u5355\u5143\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662F\u5355\u5143\u6D4B\u8BD5" aria-hidden="true">#</a> 1. \u4EC0\u4E48\u662F\u5355\u5143\u6D4B\u8BD5\uFF1F</h2><blockquote><p>\u6765\u81EA\u767E\u5EA6\u767E\u79D1\u3002</p></blockquote><p>\u5355\u5143\u6D4B\u8BD5\uFF08unit testing\uFF09\uFF0C\u662F\u6307\u5BF9\u8F6F\u4EF6\u4E2D\u7684\u6700\u5C0F\u53EF\u6D4B\u8BD5\u5355\u5143\u8FDB\u884C\u68C0\u67E5\u548C\u9A8C\u8BC1\u3002\u5BF9\u4E8E\u5355\u5143\u6D4B\u8BD5\u4E2D\u5355\u5143\u7684\u542B\u4E49\uFF0C\u4E00\u822C\u6765\u8BF4\uFF0C\u8981\u6839\u636E\u5B9E\u9645\u60C5\u51B5\u53BB\u5224\u5B9A\u5176\u5177\u4F53\u542B\u4E49\uFF0C\u5982C\u8BED\u8A00\u4E2D\u5355\u5143\u6307\u4E00\u4E2A\u51FD\u6570\uFF0CJava\u91CC\u5355\u5143\u6307\u4E00\u4E2A\u7C7B\uFF0C\u56FE\u5F62\u5316\u7684\u8F6F\u4EF6\u4E2D\u53EF\u4EE5\u6307\u4E00\u4E2A\u7A97\u53E3\u6216\u4E00\u4E2A\u83DC\u5355\u7B49\u3002\u603B\u7684\u6765\u8BF4\uFF0C\u5355\u5143\u5C31\u662F\u4EBA\u4E3A\u89C4\u5B9A\u7684\u6700\u5C0F\u7684\u88AB\u6D4B\u529F\u80FD\u6A21\u5757\u3002\u5355\u5143\u6D4B\u8BD5\u662F\u5728\u8F6F\u4EF6\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u8981\u8FDB\u884C\u7684\u6700\u4F4E\u7EA7\u522B\u7684\u6D4B\u8BD5\u6D3B\u52A8\uFF0C\u8F6F\u4EF6\u7684\u72EC\u7ACB\u5355\u5143\u5C06\u5728\u4E0E\u7A0B\u5E8F\u7684\u5176\u4ED6\u90E8\u5206\u76F8\u9694\u79BB\u7684\u60C5\u51B5\u4E0B\u8FDB\u884C\u6D4B\u8BD5\u3002</p><h2 id="_2-\u4E3A\u4EC0\u4E48\u8981\u5199\u5355\u5143\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#_2-\u4E3A\u4EC0\u4E48\u8981\u5199\u5355\u5143\u6D4B\u8BD5" aria-hidden="true">#</a> 2. \u4E3A\u4EC0\u4E48\u8981\u5199\u5355\u5143\u6D4B\u8BD5\uFF1F</h2><blockquote><p>\u4F7F\u7528\u5355\u5143\u6D4B\u8BD5\u53EF\u4EE5\u6709\u6548\u5730\u964D\u4F4E\u7A0B\u5E8F\u51FA\u9519\u7684\u673A\u7387\uFF0C\u63D0\u4F9B\u51C6\u786E\u7684\u6587\u6863\uFF0C\u5E76\u5E2E\u52A9\u6211\u4EEC\u6539\u8FDB\u8BBE\u8BA1\u65B9\u6848\u7B49\u7B49\u3002</p></blockquote><p>\u4EE5\u4E0B\u5217\u4E3E\u4E86\u4E00\u4E9B\u6211\u4E3A\u4EC0\u4E48\u4F7F\u7528\u5355\u5143\u6D4B\u8BD5\u7684\u597D\u5904\uFF1A</p><ul><li>\u5141\u8BB8\u4F60\u5BF9\u4EE3\u7801\u505A\u51FA\u8F83\u4EFB\u4F55\u6539\u53D8\uFF0C\u56E0\u4E3A\u4F60\u4E86\u89E3\u5355\u5143\u6D4B\u8BD5\u4F1A\u5728\u4F60\u7684\u9884\u671F\u4E4B\u4E2D\u3002</li><li>\u5355\u5143\u6D4B\u8BD5\u53EF\u4EE5\u6709\u6548\u5730\u964D\u4F4E\u7A0B\u5E8F\u51FA\u73B0BUG\u7684\u673A\u7387\uFF1B</li><li>\u5E2E\u52A9\u4F60\u66F4\u6DF1\u5165\u5730\u7406\u89E3\u4EE3\u7801--\u56E0\u4E3A\u5728\u5199\u5355\u5143\u6D4B\u8BD5\u7684\u65F6\u5019\uFF0C\u4F60\u9700\u8981\u660E\u786E\u7A0B\u5E8F\u6240\u6709\u7684\u6267\u884C\u6D41\u7A0B\u53CA\u5BF9\u5E94\u7684\u6267\u884C\u7ED3\u679C\u7B49\u7B49;</li><li>\u5141\u8BB8\u5728\u4EFB\u4F55\u65F6\u5019\u4EE3\u7801\u91CD\u6784\uFF0C\u800C\u4E0D\u5FC5\u62C5\u5FC3\u7834\u574F\u73B0\u6709\u7684\u4EE3\u7801\u3002\u8FD9\u4F7F\u5F97\u6211\u4EEC\u7F16\u5199\u7A0B\u5E8F\u66F4\u7075\u6D3B;</li><li>\u786E\u4FDD\u4F60\u7684\u4EE3\u7801\u7684\u5065\u58EE\u6027\uFF0C\u56E0\u4E3A\u6240\u6709\u7684\u6D4B\u8BD5\u90FD\u662F\u901A\u8FC7\u4E86\u7684\u3002</li><li>\u6587\u6863\u8BB0\u5F55\u3002\u5355\u5143\u6D4B\u8BD5\u5C31\u662F\u4E00\u79CD\u65E0\u4EF7\u7684\u6587\u6863\uFF0C\u5B83\u662F\u5C55\u793A\u51FD\u6570\u6216\u7C7B\u5982\u4F55\u4F7F\u7528\u7684\u6700\u4F73\u6587\u6863\uFF0C\u8FD9\u4EFD\u6587\u6863\u662F\u53EF\u7F16\u8BD1\u3001\u53EF\u8FD0\u884C\u7684\u3001\u5E76\u4E14\u5B83\u4FDD\u6301\u6700\u65B0\uFF0C\u6C38\u8FDC\u4E0E\u4EE3\u7801\u540C\u6B65\u3002</li><li><strong>\u5177\u6709\u56DE\u5F52\u6027</strong>\u3002\u81EA\u52A8\u5316\u7684\u5355\u5143\u6D4B\u8BD5\u907F\u514D\u4E86\u4EE3\u7801\u51FA\u73B0\u56DE\u5F52\uFF0C\u7F16\u5199\u5B8C\u6210\u4E4B\u540E\uFF0C\u53EF\u4EE5\u968F\u65F6\u968F\u5730\u5730\u5FEB\u901F\u8FD0\u884C\u6D4B\u8BD5\uFF0C\u800C\u4E0D\u662F\u5C06\u4EE3\u7801\u90E8\u7F72\u5230\u8BBE\u5907\u4E4B\u540E\uFF0C\u7136\u540E\u518D\u624B\u52A8\u5730\u8986\u76D6\u5404\u79CD\u6267\u884C\u8DEF\u5F84\uFF0C\u8FD9\u6837\u7684\u884C\u4E3A\u6548\u7387\u4F4E\u4E0B\uFF0C\u6D6A\u8D39\u65F6\u95F4\u3002</li></ul><h2 id="_3-\u4EC0\u4E48\u65F6\u5019\u5199\u5355\u5143\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#_3-\u4EC0\u4E48\u65F6\u5019\u5199\u5355\u5143\u6D4B\u8BD5" aria-hidden="true">#</a> 3. \u4EC0\u4E48\u65F6\u5019\u5199\u5355\u5143\u6D4B\u8BD5\uFF1F</h2><p>\u5199\u5355\u5143\u6D4B\u8BD5\u7684\u65F6\u673A\u4E0D\u5916\u4E4E\u4E09\u79CD\u60C5\u51B5\uFF1A</p><ul><li>\u4E00\u662F\u5728\u5177\u4F53\u5B9E\u73B0\u4EE3\u7801\u4E4B\u524D\uFF0C\u8FD9\u662F\u6D4B\u8BD5\u9A71\u52A8\u5F00\u53D1\uFF08TDD\uFF09\u6240\u63D0\u5021\u7684\uFF1B</li><li>\u4E8C\u662F\u4E0E\u5177\u4F53\u5B9E\u73B0\u4EE3\u7801\u540C\u6B65\u8FDB\u884C\u3002\u5148\u5199\u5C11\u91CF\u529F\u80FD\u4EE3\u7801\uFF0C\u7D27\u63A5\u7740\u5199\u5355\u5143\u6D4B\u8BD5\uFF08\u91CD\u590D\u8FD9\u4E24\u4E2A\u8FC7\u7A0B\uFF0C\u76F4\u5230\u5B8C\u6210\u529F\u80FD\u4EE3\u7801\u5F00\u53D1\uFF09\u3002\u5176\u5B9E\u8FD9\u79CD\u65B9\u6848\u8DDF\u7B2C\u4E00\u79CD\u5DF2\u7ECF\u5F88\u63A5\u8FD1\uFF0C\u57FA\u672C\u4E0A\u529F\u80FD\u4EE3\u7801\u5F00\u53D1\u5B8C\uFF0C\u5355\u5143\u6D4B\u8BD5\u4E5F\u5DEE\u4E0D\u591A\u5B8C\u6210\u4E86\u3002</li><li>\u4E09\u662F\u7F16\u5199\u5B8C\u529F\u80FD\u4EE3\u7801\u518D\u5199\u5355\u5143\u6D4B\u8BD5\u3002\u6211\u7684\u5B9E\u8DF5\u7ECF\u9A8C\u544A\u8BC9\u6211\uFF0C\u4E8B\u540E\u7F16\u5199\u7684\u5355\u5143\u6D4B\u8BD5\u201C\u7C92\u5EA6\u201D\u90FD\u6BD4\u8F83\u7C97\u3002\u5BF9\u540C\u6837\u7684\u529F\u80FD\u4EE3\u7801\uFF0C\u91C7\u53D6\u524D\u4E24\u79CD\u65B9\u6848\u7684\u7ED3\u679C\u53EF\u80FD\u662F\u752810\u4E2A\u201C\u5C0F\u201D\u7684\u5355\u6D4B\u6765\u8986\u76D6\uFF0C\u6BCF\u4E2A\u5355\u6D4B\u6BD4\u8F83\u7B80\u5355\u6613\u61C2\uFF0C\u53EF\u8BFB\u6027\u53EF\u7EF4\u62A4\u6027\u90FD\u6BD4\u8F83\u597D\uFF08\u91CD\u6784\u65F6\u5355\u6D4B\u7684\u6539\u52A8\u4E0D\u5927\uFF09\uFF1B\u800C\u7B2C\u4E09\u79CD\u65B9\u6848\u5199\u7684\u5355\u6D4B\uFF0C\u5F80\u5F80\u662F\u75281\u4E2A\u201C\u5927\u201D\u7684\u5355\u6D4B\u6765\u8986\u76D6\uFF0C\u8FD9\u4E2A\u5355\u6D4B\u903B\u8F91\u5C31\u6BD4\u8F83\u590D\u6742\uFF0C\u56E0\u4E3A\u5B83\u8981\u6D4B\u7684\u4E1C\u897F\u5F88\u591A\uFF0C\u53EF\u8BFB\u6027\u53EF\u7EF4\u62A4\u6027\u5C31\u6BD4\u8F83\u5DEE\u3002</li></ul><p>\u5EFA\u8BAE\uFF1A\u6211\u4E2A\u4EBA\u662F\u6BD4\u8F83\u63A8\u8350\u5355\u5143\u6D4B\u8BD5\u4E0E\u5177\u4F53\u5B9E\u73B0\u4EE3\u7801\u540C\u6B65\u8FDB\u884C\u8FD9\u4E2A\u65B9\u6848\u7684\u3002\u53EA\u6709\u5BF9\u9700\u6C42\u6709\u4E00\u5B9A\u7684\u7406\u89E3\u540E\u624D\u80FD\u77E5\u9053\u4EC0\u4E48\u662F\u4EE3\u7801\u7684\u6B63\u786E\u6027\uFF0C\u624D\u80FD\u5199\u51FA\u6709\u6548\u7684\u5355\u5143\u6D4B\u8BD5\u6765\u9A8C\u8BC1\u6B63\u786E\u6027\uFF0C\u800C\u80FD\u5199\u51FA\u4E00\u4E9B\u529F\u80FD\u4EE3\u7801\u5219\u8BF4\u660E\u5BF9\u9700\u6C42\u6709\u4E00\u5B9A\u7406\u89E3\u4E86\u3002</p><h2 id="_4-\u5355\u5143\u6D4B\u8BD5\u8981\u5199\u591A\u7EC6" tabindex="-1"><a class="header-anchor" href="#_4-\u5355\u5143\u6D4B\u8BD5\u8981\u5199\u591A\u7EC6" aria-hidden="true">#</a> 4. \u5355\u5143\u6D4B\u8BD5\u8981\u5199\u591A\u7EC6\uFF1F</h2><p>\u5355\u5143\u6D4B\u8BD5\u4E0D\u662F\u8D8A\u591A\u8D8A\u597D\uFF0C\u800C\u662F\u8D8A\u6709\u6548\u8D8A\u597D\uFF01\u8FDB\u4E00\u6B65\u89E3\u8BFB\u5C31\u662F\u54EA\u4E9B\u4EE3\u7801\u9700\u8981\u6709\u5355\u5143\u6D4B\u8BD5\u8986\u76D6\uFF1A</p><ul><li>\u903B\u8F91\u590D\u6742\u7684</li><li>\u5BB9\u6613\u51FA\u9519\u7684</li><li>\u4E0D\u6613\u7406\u89E3\u7684\uFF0C\u5373\u4F7F\u662F\u81EA\u5DF1\u8FC7\u6BB5\u65F6\u95F4\u4E5F\u4F1A\u9057\u5FD8\u7684\uFF0C\u770B\u4E0D\u61C2\u81EA\u5DF1\u7684\u4EE3\u7801\uFF0C\u5355\u5143\u6D4B\u8BD5\u4EE3\u7801\u6709\u52A9\u4E8E\u7406\u89E3\u4EE3\u7801\u7684\u529F\u80FD\u548C\u9700\u6C42</li><li>\u516C\u5171\u4EE3\u7801\u3002\u6BD4\u5982\u81EA\u5B9A\u4E49\u7684\u6240\u6709http\u8BF7\u6C42\u90FD\u4F1A\u7ECF\u8FC7\u7684\u62E6\u622A\u5668\uFF1B\u5DE5\u5177\u7C7B\u7B49\u3002</li><li>\u6838\u5FC3\u4E1A\u52A1\u4EE3\u7801\u3002\u4E00\u4E2A\u4EA7\u54C1\u91CC\u6700\u6838\u5FC3\u6700\u6709\u4E1A\u52A1\u4EF7\u503C\u7684\u4EE3\u7801\u5E94\u8BE5\u8981\u6709\u8F83\u9AD8\u7684\u5355\u5143\u6D4B\u8BD5\u8986\u76D6\u7387\u3002</li></ul><h2 id="_5-\u6709\u54EA\u4E9B\u5355\u5143\u6D4B\u8BD5\u76F8\u5173\u7684\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#_5-\u6709\u54EA\u4E9B\u5355\u5143\u6D4B\u8BD5\u76F8\u5173\u7684\u6982\u5FF5" aria-hidden="true">#</a> 5. \u6709\u54EA\u4E9B\u5355\u5143\u6D4B\u8BD5\u76F8\u5173\u7684\u6982\u5FF5\uFF1F</h2><h3 id="_5-1-\u88AB\u6D4B\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_5-1-\u88AB\u6D4B\u7CFB\u7EDF" aria-hidden="true">#</a> 5.1 \u88AB\u6D4B\u7CFB\u7EDF</h3><p>\u88AB\u6D4B\u7CFB\u7EDF(System under test, SUT)\u8868\u793A\u6B63\u5728\u88AB\u6D4B\u8BD5\u7684\u7CFB\u7EDF, \u76EE\u7684\u662F\u6D4B\u8BD5\u7CFB\u7EDF\u80FD\u5426\u6B63\u786E\u64CD\u4F5C. \u6839\u636E\u6D4B\u8BD5\u7C7B\u578B\u7684\u4E0D\u540C, SUT \u6307\u4EE3\u7684\u5185\u5BB9\u4E5F\u4E0D\u540C, \u4F8B\u5982 SUT \u53EF\u4EE5\u662F\u4E00\u4E2A\u7C7B\u751A\u81F3\u662F\u4E00\u6574\u4E2A\u7CFB\u7EDF.</p><h3 id="_5-2-\u6D4B\u8BD5\u4F9D\u8D56\u7EC4\u4EF6-doc" tabindex="-1"><a class="header-anchor" href="#_5-2-\u6D4B\u8BD5\u4F9D\u8D56\u7EC4\u4EF6-doc" aria-hidden="true">#</a> 5.2 \u6D4B\u8BD5\u4F9D\u8D56\u7EC4\u4EF6(DOC)</h3><p>\u88AB\u6D4B\u7CFB\u7EDF\u6240\u4F9D\u8D56\u7684\u7EC4\u4EF6, \u4F8B\u5982\u8FDB\u7A0B UserService \u7684\u5355\u5143\u6D4B\u8BD5\u65F6, UserService \u4F1A\u4F9D\u8D56 UserDao, \u56E0\u6B64 UserDao \u5C31\u662F DOC.</p><h3 id="_5-3-\u6D4B\u8BD5\u66FF\u8EAB-test-double" tabindex="-1"><a class="header-anchor" href="#_5-3-\u6D4B\u8BD5\u66FF\u8EAB-test-double" aria-hidden="true">#</a> 5.3 \u6D4B\u8BD5\u66FF\u8EAB(Test Double)</h3><p>\u4E00\u4E2A\u5B9E\u9645\u7684\u7CFB\u7EDF\u4F1A\u4F9D\u8D56\u591A\u4E2A\u5916\u90E8\u5BF9\u8C61, \u4F46\u662F\u5728\u8FDB\u884C\u5355\u5143\u6D4B\u8BD5\u65F6, \u6211\u4EEC\u4F1A\u7528\u4E00\u4E9B\u529F\u80FD\u8F83\u4E3A\u7B80\u5355\u7684\u5E76\u4E14\u5176\u884C\u4E3A\u548C\u5B9E\u9645\u5BF9\u8C61\u7C7B\u4F3C\u7684\u5047\u5BF9\u8C61\u6765\u4F5C\u4E3A SUT \u7684\u4F9D\u8D56\u5BF9\u8C61, \u4EE5\u6B64\u6765\u964D\u4F4E\u5355\u5143\u6D4B\u8BD5\u7684\u590D\u6742\u6027\u548C\u53EF\u5B9E\u73B0\u6027. \u5728\u8FD9\u91CC, \u8FD9\u4E9B\u5047\u5BF9\u8C61\u5C31\u88AB\u79F0\u4E3A \u6D4B\u8BD5\u66FF\u8EAB(Test Double). \u6D4B\u8BD5\u66FF\u8EAB\u6709\u5982\u4E0B 5 \u79CD\u7C7B\u578B:</p><ul><li><strong>Test stub</strong></li></ul><p>\u4E3A SUT \u63D0\u4F9B\u6570\u636E\u7684\u5047\u5BF9\u8C61\uFF0C\u6211\u4EEC\u4E3E\u4E00\u4E2A\u4F8B\u5B50\u6765\u5C55\u793A\u4EC0\u4E48\u662F Test stub.</p><p>\u5047\u8BBE\u6211\u4EEC\u7684\u4E00\u4E2A\u6A21\u5757\u9700\u8981\u4ECE HTTP \u63A5\u53E3\u4E2D\u83B7\u53D6\u5546\u54C1\u4EF7\u683C\u6570\u636E, \u8FD9\u4E2A\u83B7\u53D6\u6570\u636E\u7684\u63A5\u53E3\u88AB\u5C01\u88C5\u4E3A getPrice \u65B9\u6CD5. \u5728\u5BF9\u8FD9\u4E2A\u6A21\u5757\u8FDB\u884C\u6D4B\u8BD5\u65F6, \u6211\u4EEC\u663E\u7136\u4E0D\u592A\u53EF\u80FD\u4E13\u95E8\u5F00\u4E00\u4E2A HTTP \u670D\u52A1\u5668\u6765\u63D0\u4F9B\u6B64\u63A5\u53E3, \u800C\u662F\u63D0\u4F9B\u4E00\u4E2A\u5E26\u6709 getPrice \u65B9\u6CD5\u7684\u5047\u5BF9\u8C61, \u4ECE\u8FD9\u4E2A\u5047\u5BF9\u8C61\u4E2D\u83B7\u53D6\u6570\u636E. \u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D, \u63D0\u4F9B\u6570\u636E\u7684\u5047\u5BF9\u8C61\u5C31\u53EB\u505A Test stub.</p><ul><li><strong>Fake object</strong></li></ul><p>\u5B9E\u73B0\u4E86\u7B80\u5355\u529F\u80FD\u7684\u4E00\u4E2A\u5047\u5BF9\u8C61. Fake object \u548C Test stub \u7684\u4E3B\u8981\u533A\u522B\u5C31\u662F Test stub \u4FA7\u91CD\u4E8E\u7528\u4E8E\u63D0\u4F9B\u6570\u636E\u7684\u5047\u5BF9\u8C61, \u800C Fake object \u6CA1\u6709\u8FD9\u5C42\u542B\u4E49.</p><p>\u4F7F\u7528 Fake object \u7684\u6700\u4E3B\u8981\u7684\u539F\u56E0\u5C31\u662F\u5728\u6D4B\u8BD5\u65F6\u67D0\u4E9B\u7EC4\u4EF6\u4E0D\u53EF\u7528\u6216\u8FD0\u884C\u901F\u5EA6\u592A\u6162, \u56E0\u800C\u4F7F\u7528 Fake object \u6765\u4EE3\u66FF\u5B83\u4EEC.</p><ul><li><strong>Mock object</strong></li></ul><p>\u7528\u4E8E\u6A21\u62DF\u5B9E\u9645\u7684\u5BF9\u8C61, \u5E76\u4E14\u80FD\u591F\u6821\u9A8C\u5BF9\u8FD9\u4E2A Mock object \u7684\u65B9\u6CD5\u8C03\u7528\u662F\u5426\u7B26\u5408\u9884\u671F.</p><p>\u5B9E\u9645\u4E0A, Mock object \u662F Test stub \u6216 Fake object \u4E00\u79CD, \u4F46\u662F Mock object \u6709 Test stub/Fake object \u6CA1\u6709\u7684\u7279\u6027, Mock object \u53EF\u4EE5\u5F88\u7075\u6D3B\u5730\u914D\u7F6E\u6240\u8C03\u7528\u7684\u65B9\u6CD5\u6240\u4EA7\u751F\u7684\u884C\u4E3A, \u5E76\u4E14\u5B83\u53EF\u4EE5\u8FFD\u8E2A\u65B9\u6CD5\u8C03\u7528, \u4F8B\u5982\u4E00\u4E2A Mock Object \u65B9\u6CD5\u8C03\u7528\u65F6\u4F20\u9012\u4E86\u54EA\u4E9B\u53C2\u6570, \u65B9\u6CD5\u8C03\u7528\u4E86\u51E0\u6B21\u7B49.</p><ul><li><strong>Dummy object</strong></li></ul><p>\u5728\u6D4B\u8BD5\u4E2D\u5E76\u4E0D\u4F7F\u7528\u7684, \u4F46\u662F\u4E3A\u4E86\u6D4B\u8BD5\u4EE3\u7801\u80FD\u591F\u6B63\u5E38\u7F16\u8BD1/\u8FD0\u884C\u800C\u6DFB\u52A0\u7684\u5BF9\u8C61. \u4F8B\u5982\u6211\u4EEC\u8C03\u7528\u4E00\u4E2A Test Double \u5BF9\u8C61\u7684\u4E00\u4E2A\u65B9\u6CD5, \u8FD9\u4E2A\u65B9\u6CD5\u9700\u8981\u4F20\u9012\u51E0\u4E2A\u53C2\u6570, \u4F46\u662F\u5176\u4E2D\u67D0\u4E2A\u53C2\u6570\u65E0\u8BBA\u662F\u4EC0\u4E48\u503C\u90FD\u4E0D\u4F1A\u5F71\u54CD\u6D4B\u8BD5\u7684\u7ED3\u679C, \u90A3\u4E48\u8FD9\u4E2A\u53C2\u6570\u5C31\u662F\u4E00\u4E2A Dummy object. Dummy object \u53EF\u4EE5\u662F\u4E00\u4E2A\u7A7A\u5F15\u7528, \u4E00\u4E2A\u7A7A\u5BF9\u8C61\u6216\u8005\u662F\u4E00\u4E2A\u5E38\u91CF\u7B49.</p><p>\u7B80\u5355\u7684\u8BF4, Dummy object \u5C31\u662F\u90A3\u4E9B\u6CA1\u6709\u4F7F\u7528\u5230\u7684, \u4EC5\u4EC5\u662F\u4E3A\u4E86\u586B\u5145\u53C2\u6570\u5217\u8868\u7684\u5BF9\u8C61.</p><ul><li><strong>Test Spy</strong></li></ul><p>\u53EF\u4EE5\u5305\u88C5\u4E00\u4E2A\u771F\u5B9E\u7684 Java \u5BF9\u8C61, \u5E76\u8FD4\u56DE\u4E00\u4E2A\u5305\u88C5\u540E\u7684\u65B0\u5BF9\u8C61. \u82E5\u6CA1\u6709\u7279\u522B\u914D\u7F6E\u7684\u8BDD, \u5BF9\u8FD9\u4E2A\u65B0\u5BF9\u8C61\u7684\u6240\u6709\u65B9\u6CD5\u8C03\u7528, \u90FD\u4F1A\u59D4\u6D3E\u7ED9\u5B9E\u9645\u7684 Java \u5BF9\u8C61.</p><p>mock \u548C spy \u7684\u533A\u522B\u662F: mock \u662F\u65E0\u4E2D\u751F\u6709\u5730\u751F\u51FA\u4E00\u4E2A\u5B8C\u5168\u865A\u62DF\u7684\u5BF9\u8C61, \u5B83\u7684\u6240\u6709\u65B9\u6CD5\u90FD\u662F\u865A\u62DF\u7684; \u800C spy \u662F\u5728\u73B0\u6709\u7C7B\u7684\u57FA\u7840\u4E0A\u5305\u88C5\u4E86\u4E00\u4E2A\u5BF9\u8C61, \u5373\u5982\u679C\u6211\u4EEC\u6CA1\u6709\u91CD\u5199 spy \u7684\u65B9\u6CD5, \u90A3\u4E48\u8FD9\u4E9B\u65B9\u6CD5\u7684\u5B9E\u73B0\u5176\u5B9E\u90FD\u662F\u8C03\u7528\u7684\u88AB\u5305\u88C5\u7684\u5BF9\u8C61\u7684\u65B9\u6CD5.</p><h3 id="_5-4-test-fixture" tabindex="-1"><a class="header-anchor" href="#_5-4-test-fixture" aria-hidden="true">#</a> 5.4 Test fixture</h3><p>\u6240\u8C13 test fixture, \u5C31\u662F\u8FD0\u884C\u6D4B\u8BD5\u7A0B\u5E8F\u6240\u9700\u8981\u7684\u5148\u51B3\u6761\u4EF6(precondition). \u5373\u5BF9\u88AB\u6D4B\u5BF9\u8C61\u8FDB\u884C\u6D4B\u8BD5\u65F6\u9501\u9700\u8981\u7684\u4E00\u5207\u4E1C\u897F(The test fixture is everything we need to have in place to exercise the SUT). \u8FD9\u4E2A \u4E1C\u897F \u4E0D\u5355\u5355\u6307\u7684\u662F\u6570\u636E, \u540C\u65F6\u5305\u62EC\u5BF9\u88AB\u6D4B\u5BF9\u8C61\u7684\u914D\u7F6E, \u88AB\u6D4B\u5BF9\u8C61\u6240\u9700\u8981\u7684\u4F9D\u8D56\u5BF9\u8C61\u7B49. JUnit4 \u4E4B\u524D\u662F\u901A\u8FC7 setUp, TearDown \u65B9\u6CD5\u5B8C\u6210, \u5728 JUnit4\u8FD9, \u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528@Before \u4EE3\u66FF setUp \u65B9\u6CD5, @After \u4EE3\u66FF tearDown \u65B9\u6CD5.</p><p>\u6CE8\u610F, @Before \u5728\u6BCF\u4E2A\u6D4B\u8BD5\u65B9\u6CD5\u8FD0\u884C\u524D\u90FD\u4F1A\u88AB\u8C03\u7528, @After \u5728\u6BCF\u4E2A\u6D4B\u8BD5\u65B9\u6CD5\u8FD0\u884C\u540E\u90FD\u4F1A\u88AB\u8C03\u7528.</p><p>\u56E0\u4E3A @Before \u548C @After \u4F1A\u5728\u6BCF\u4E2A\u6D4B\u8BD5\u65B9\u6CD5\u524D\u540E\u90FD\u4F1A\u88AB\u8C03\u7528, \u800C\u6709\u65F6\u6211\u4EEC\u4EC5\u4EC5\u9700\u8981\u5728\u6D4B\u8BD5\u524D\u8FDB\u884C\u4E00\u6B21\u521D\u59CB\u5316, \u8FD9\u6837\u7684\u60C5\u51B5\u4E0B, \u53EF\u4EE5\u4F7F\u7528@BeforeClass \u548C@AfterClass \u6CE8\u89E3.</p><h3 id="_5-5-\u6D4B\u8BD5\u7528\u4F8B-test-case" tabindex="-1"><a class="header-anchor" href="#_5-5-\u6D4B\u8BD5\u7528\u4F8B-test-case" aria-hidden="true">#</a> 5.5 \u6D4B\u8BD5\u7528\u4F8B(Test case)</h3><p>\u5728 JUnit 3\u4E2D, \u6D4B\u8BD5\u65B9\u6CD5\u90FD\u5FC5\u987B\u4EE5 test \u4E3A\u524D\u7F00, \u4E14\u5FC5\u987B\u662F public void \u7684, JUnit 4\u4E4B\u540E, \u5C31\u6CA1\u6709\u8FD9\u4E2A\u9650\u5236\u4E86, \u53EA\u8981\u5728\u6BCF\u4E2A\u6D4B\u8BD5\u65B9\u6CD5\u6807\u6CE8 @Test \u6CE8\u89E3, \u65B9\u6CD5\u7B7E\u540D\u53EF\u4EE5\u662F\u4EFB\u610F\u7684.</p><h3 id="_5-6-\u6D4B\u8BD5\u5957\u4EF6" tabindex="-1"><a class="header-anchor" href="#_5-6-\u6D4B\u8BD5\u5957\u4EF6" aria-hidden="true">#</a> 5.6 \u6D4B\u8BD5\u5957\u4EF6</h3><p>\u901A\u8FC7 TestSuit \u5BF9\u8C61\u5C06\u591A\u4E2A\u6D4B\u8BD5\u7528\u4F8B\u7EC4\u88C5\u6210\u4E00\u4E2A\u6D4B\u8BD5\u5957\u4EF6, \u6D4B\u8BD5\u5957\u4EF6\u6279\u91CF\u8FD0\u884C.</p><p>\u901A\u8FC7@RunWith \u548C@SuteClass \u4E24\u4E2A\u6CE8\u89E3, \u6211\u4EEC\u53EF\u4EE5\u521B\u5EFA\u4E00\u4E2A\u6D4B\u8BD5\u5957\u4EF6. \u901A\u8FC7@RunWith \u6307\u5B9A\u4E00\u4E2A\u7279\u6B8A\u7684\u8FD0\u884C\u5668, \u51E0 Suite.class \u5957\u4EF6\u8FD0\u884C\u5668, \u5E76\u901A\u8FC7@SuiteClasses \u6CE8\u89E3, \u5C06\u9700\u8981\u8FDB\u884C\u6D4B\u8BD5\u7684\u7C7B\u5217\u8868\u4F5C\u4F5C\u4E3A\u53C2\u6570\u4F20\u5165.</p><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',48),h={href:"https://pdai.tech/md/develop/ut/dev-ut-unit-test.html",target:"_blank",rel:"noopener noreferrer"},d=e("strong",null,"\u5355\u5143\u6D4B\u8BD5 - \u6D45\u8C08\u5355\u5143\u6D4B\u8BD5",-1);function u(p,b){const t=l("ExternalLinkIcon");return i(),r("div",null,[c,e("p",null,[e("a",h,[d,s(t)])])])}const T=a(n,[["render",u],["__file","devut-overview.html.vue"]]);export{T as default};
