import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as r,c as l,a as e,b as t,d as i,f as s,r as c}from"./app.cb51073c.js";const n={},p=s('<h1 id="\u5206\u5E03\u5F0F\u7CFB\u7EDF-\u7406\u8BBA\u57FA\u7840\u53CA\u4E00\u81F4\u6027\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u7CFB\u7EDF-\u7406\u8BBA\u57FA\u7840\u53CA\u4E00\u81F4\u6027\u7B97\u6CD5" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u7CFB\u7EDF-\u7406\u8BBA\u57FA\u7840\u53CA\u4E00\u81F4\u6027\u7B97\u6CD5</h1><h2 id="_1-\u4EC0\u4E48\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF" aria-hidden="true">#</a> 1. \u4EC0\u4E48\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF\uFF1F</h2><blockquote><p>\u4E00\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u662F\u4E00\u4E9B\u72EC\u7ACB\u7684\u8BA1\u7B97\u673A\u96C6\u5408\uFF0C\u4F46\u662F\u5BF9\u8FD9\u4E2A\u7CFB\u7EDF\u7684\u7528\u6237\u6765\u8BF4\uFF0C\u7CFB\u7EDF\u5C31\u50CF\u4E00\u53F0\u8BA1\u7B97\u673A\u4E00\u6837\u3002</p></blockquote><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u662F\u4E00\u4E2A\u786C\u4EF6\u6216\u8F6F\u4EF6\u7EC4\u4EF6\u5206\u5E03\u5728\u4E0D\u540C\u7684\u7F51\u7EDC\u8BA1\u7B97\u673A\u4E0A\uFF0C\u5F7C\u6B64\u4E4B\u95F4\u4EC5\u4EC5\u901A\u8FC7\u6D88\u606F\u4F20\u9012\u8FDB\u884C\u901A\u4FE1\u548C\u534F\u8C03\u7684\u7CFB\u7EDF\u3002\u7B80\u5355\u6765\u8BF4\u5C31\u662F<strong>\u4E00\u7FA4\u72EC\u7ACB\u8BA1\u7B97\u673A\u96C6\u5408\u5171\u540C\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\uFF0C\u4F46\u662F\u5BF9\u4E8E\u7CFB\u7EDF\u7684\u7528\u6237\u6765\u8BF4\uFF0C\u5C31\u50CF\u662F\u4E00\u53F0\u8BA1\u7B97\u673A\u5728\u63D0\u4F9B\u670D\u52A1\u4E00\u6837</strong>\u3002\u5206\u5E03\u5F0F\u610F\u5473\u7740\u53EF\u4EE5\u91C7\u7528\u66F4\u591A\u7684\u666E\u901A\u8BA1\u7B97\u673A\uFF08\u76F8\u5BF9\u4E8E\u6602\u8D35\u7684\u5927\u578B\u673A\uFF09\u7EC4\u6210\u5206\u5E03\u5F0F\u96C6\u7FA4\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\u3002\u8BA1\u7B97\u673A\u8D8A\u591A\uFF0CCPU\u3001\u5185\u5B58\u3001\u5B58\u50A8\u8D44\u6E90\u7B49\u4E5F\u5C31\u8D8A\u591A\uFF0C\u80FD\u591F\u5904\u7406\u7684\u5E76\u53D1\u8BBF\u95EE\u91CF\u4E5F\u5C31\u8D8A\u5927\u3002</p><p>\u4ECE\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6982\u5FF5\u4E2D\u6211\u4EEC\u77E5\u9053\uFF0C\u5404\u4E2A\u4E3B\u673A\u4E4B\u95F4\u901A\u4FE1\u548C\u534F\u8C03\u4E3B\u8981\u901A\u8FC7\u7F51\u7EDC\u8FDB\u884C\uFF0C\u6240\u4EE5\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u8BA1\u7B97\u673A\u5728\u7A7A\u95F4\u4E0A\u51E0\u4E4E\u6CA1\u6709\u4EFB\u4F55\u9650\u5236\uFF0C\u8FD9\u4E9B\u8BA1\u7B97\u673A\u53EF\u80FD\u88AB\u653E\u5728\u4E0D\u540C\u7684\u673A\u67DC\u4E0A\uFF0C\u4E5F\u53EF\u80FD\u88AB\u90E8\u7F72\u5728\u4E0D\u540C\u7684\u673A\u623F\u4E2D\uFF0C\u8FD8\u53EF\u80FD\u5728\u4E0D\u540C\u7684\u57CE\u5E02\u4E2D\uFF0C\u5BF9\u4E8E\u5927\u578B\u7684\u7F51\u7AD9\u751A\u81F3\u53EF\u80FD\u5206\u5E03\u5728\u4E0D\u540C\u7684\u56FD\u5BB6\u548C\u5730\u533A\u3002</p><h3 id="_1-1-\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u4E3B\u8981\u7279\u5F81" tabindex="-1"><a class="header-anchor" href="#_1-1-\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u4E3B\u8981\u7279\u5F81" aria-hidden="true">#</a> 1.1 \u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u4E3B\u8981\u7279\u5F81</h3><blockquote><p>\u65E0\u8BBA\u7A7A\u95F4\u4E0A\u5982\u4F55\u5206\u5E03\uFF0C\u4E00\u4E2A\u6807\u51C6\u7684\u5206\u5E03\u5F0F\u7CFB\u7EDF\u5E94\u8BE5\u5177\u6709\u4EE5\u4E0B\u51E0\u4E2A\u4E3B\u8981\u7279\u5F81</p></blockquote><ul><li><strong>\u5206\u5E03\u6027</strong></li></ul><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u591A\u53F0\u8BA1\u7B97\u673A\u4E4B\u95F4\u5728\u7A7A\u95F4\u4F4D\u7F6E\u4E0A\u53EF\u4EE5\u968F\u610F\u5206\u5E03\uFF0C\u540C\u65F6\uFF0C\u673A\u5668\u7684\u5206\u5E03\u60C5\u51B5\u4E5F\u4F1A\u968F\u65F6\u53D8\u52A8\u3002</p><ul><li><strong>\u5BF9\u7B49\u6027</strong></li></ul><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u8BA1\u7B97\u673A\u6CA1\u6709\u4E3B\uFF0F\u4ECE\u4E4B\u5206\uFF0C\u5373\u6CA1\u6709\u63A7\u5236\u6574\u4E2A\u7CFB\u7EDF\u7684\u4E3B\u673A\uFF0C\u4E5F\u6CA1\u6709\u88AB\u63A7\u5236\u7684\u4ECE\u673A\uFF0C\u7EC4\u6210\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6240\u6709\u8BA1\u7B97\u673A\u8282\u70B9\u90FD\u662F\u5BF9\u7B49\u7684\u3002\u526F\u672C\uFF08Replica\uFF09\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF\u6700\u5E38\u89C1\u7684\u6982\u5FF5\u4E4B\u4E00\uFF0C\u6307\u7684\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF\u5BF9\u6570\u636E\u548C\u670D\u52A1\u63D0\u4F9B\u7684\u4E00\u79CD\u5197\u4F59\u65B9\u5F0F\u3002\u5728\u5E38\u89C1\u7684\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u4E3A\u4E86\u5BF9\u5916\u63D0\u4F9B\u9AD8\u53EF\u7528\u7684\u670D\u52A1\uFF0C\u6211\u4EEC\u5F80\u5F80\u4F1A\u5BF9\u6570\u636E\u548C\u670D\u52A1\u8FDB\u884C\u526F\u672C\u5904\u7406\u3002\u6570\u636E\u526F\u672C\u662F\u6307\u5728\u4E0D\u540C\u8282\u70B9\u4E0A\u6301\u4E45\u5316\u540C\u4E00\u4EFD\u6570\u636E\uFF0C\u5F53\u67D0\u4E00\u4E2A\u8282\u70B9\u4E0A\u5B58\u50A8\u7684\u6570\u636E\u4E22\u5931\u65F6\uFF0C\u53EF\u4EE5\u4ECE\u526F\u672C\u4E0A\u8BFB\u53D6\u8BE5\u6570\u636E\uFF0C\u8FD9\u662F\u89E3\u51B3\u5206\u5E03\u5F0F\u7CFB\u7EDF\u6570\u636E\u4E22\u5931\u95EE\u9898\u6700\u4E3A\u6709\u6548\u7684\u624B\u6BB5\u3002\u53E6\u4E00\u7C7B\u526F\u672C\u662F\u670D\u52A1\u526F\u672C\uFF0C\u6307\u591A\u4E2A\u8282\u70B9\u63D0\u4F9B\u540C\u6837\u7684\u670D\u52A1\uFF0C\u6BCF\u4E2A\u8282\u70B9\u90FD\u6709\u80FD\u529B\u63A5\u6536\u6765\u81EA\u5916\u90E8\u7684\u8BF7\u6C42\u5E76\u8FDB\u884C\u76F8\u5E94\u7684\u5904\u7406\u3002</p><ul><li><strong>\u81EA\u6CBB\u6027</strong></li></ul><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u5404\u4E2A\u8282\u70B9\u90FD\u5305\u542B\u81EA\u5DF1\u7684\u5904\u7406\u673A\u548C\u5185\u5B58\uFF0C\u5404\u81EA\u5177\u6709\u72EC\u7ACB\u7684\u5904\u7406\u6570\u636E\u7684\u529F\u80FD\u3002\u901A\u5E38\uFF0C\u5F7C\u6B64\u5728\u5730\u4F4D\u4E0A\u662F\u5E73\u7B49\u7684\uFF0C\u65E0\u4E3B\u6B21\u4E4B\u5206\uFF0C\u65E2\u80FD\u81EA\u6CBB\u5730\u8FDB\u884C\u5DE5\u4F5C\uFF0C\u53C8\u80FD\u5229\u7528\u5171\u4EAB\u7684\u901A\u4FE1\u7EBF\u8DEF\u6765\u4F20\u9001\u4FE1\u606F\uFF0C\u534F\u8C03\u4EFB\u52A1\u5904\u7406\u3002</p><ul><li><strong>\u5E76\u53D1\u6027</strong></li></ul><p>\u5728\u4E00\u4E2A\u8BA1\u7B97\u673A\u7F51\u7EDC\u4E2D\uFF0C\u7A0B\u5E8F\u8FD0\u884C\u8FC7\u7A0B\u7684\u5E76\u53D1\u6027\u64CD\u4F5C\u662F\u975E\u5E38\u5E38\u89C1\u7684\u884C\u4E3A\u3002\u4F8B\u5982\u540C\u4E00\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u591A\u4E2A\u8282\u70B9\uFF0C\u53EF\u80FD\u4F1A\u5E76\u53D1\u5730\u64CD\u4F5C\u4E00\u4E9B\u5171\u4EAB\u7684\u8D44\u6E90\uFF0C\u5982\u4F55\u51C6\u786E\u5E76\u9AD8\u6548\u5730\u534F\u8C03\u5206\u5E03\u5F0F\u5E76\u53D1\u64CD\u4F5C\u4E5F\u6210\u4E3A\u4E86\u5206\u5E03\u5F0F\u7CFB\u7EDF\u67B6\u6784\u4E0E\u8BBE\u8BA1\u4E2D\u6700\u5927\u7684\u6311\u6218\u4E4B\u4E00\u3002</p><h3 id="_1-2-\u5206\u5E03\u5F0F\u7CFB\u7EDF\u9762\u4E34\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_1-2-\u5206\u5E03\u5F0F\u7CFB\u7EDF\u9762\u4E34\u7684\u95EE\u9898" aria-hidden="true">#</a> 1.2 . \u5206\u5E03\u5F0F\u7CFB\u7EDF\u9762\u4E34\u7684\u95EE\u9898</h3><blockquote><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u9762\u4E34\u7684\u95EE\u9898\u6709\u54EA\u4E9B\uFF1F</p></blockquote><ul><li><strong>\u7F3A\u4E4F\u5168\u5C40\u65F6\u949F</strong></li></ul><p>\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u5F88\u96BE\u5B9A\u4E49\u4E24\u4E2A\u4E8B\u4EF6\u7A76\u7ADF\u8C01\u5148\u8C01\u540E\uFF0C\u539F\u56E0\u5C31\u662F\u56E0\u4E3A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7F3A\u4E4F\u4E00\u4E2A\u5168\u5C40\u7684\u65F6\u949F\u5E8F\u5217\u63A7\u5236\u3002</p><ul><li><strong>\u673A\u5668\u5B95\u673A</strong></li></ul><p>\u673A\u5668\u5B95\u673A\u662F\u6700\u5E38\u89C1\u7684\u5F02\u5E38\u4E4B\u4E00\u3002\u5728\u5927\u578B\u96C6\u7FA4\u4E2D\u6BCF\u65E5\u5B95\u673A\u53D1\u751F\u7684\u6982\u7387\u4E3A\u5343\u5206\u4E4B\u4E00\u5DE6\u53F3\uFF0C\u5728\u5B9E\u8DF5\u4E2D\uFF0C\u4E00\u53F0\u5B95\u673A\u7684\u673A\u5668\u6062\u590D\u7684\u65F6\u95F4\u901A\u5E38\u8BA4\u4E3A\u662F24 \u5C0F\u65F6\uFF0C\u4E00\u822C\u9700\u8981\u4EBA\u5DE5\u4ECB\u5165\u91CD\u542F\u673A\u5668\u3002</p><ul><li><strong>\u7F51\u7EDC\u5F02\u5E38</strong></li></ul><p>\u6D88\u606F\u4E22\u5931\uFF0C\u4E24\u7247\u8282\u70B9\u4E4B\u95F4\u5F7C\u6B64\u5B8C\u5168\u65E0\u6CD5\u901A\u4FE1\uFF0C\u5373\u51FA\u73B0\u4E86\u201C\u7F51\u7EDC\u5206\u5316\u201D\uFF1B\u6D88\u606F\u4E71\u5E8F\uFF0C\u6709\u4E00\u5B9A\u7684\u6982\u7387\u4E0D\u662F\u6309\u7167\u53D1\u9001\u65F6\u7684\u987A\u5E8F\u4F9D\u6B21\u5230\u8FBE\u76EE\u7684\u8282\u70B9\uFF0C\u8003\u8651\u4F7F\u7528\u5E8F\u5217\u53F7\u7B49\u673A\u5236\u5904\u7406\u7F51\u7EDC\u6D88\u606F\u7684\u4E71\u5E8F\u95EE\u9898\uFF0C\u4F7F\u5F97\u65E0\u6548\u7684\u3001\u8FC7\u671F\u7684\u7F51\u7EDC\u6D88\u606F\u4E0D\u5F71\u54CD\u7CFB\u7EDF\u7684\u6B63\u786E\u6027\uFF1B\u6570\u636E\u9519\u8BEF\uFF1B\u4E0D\u53EF\u9760\u7684TCP\uFF0CTCP \u534F\u8BAE\u4E3A\u5E94\u7528\u5C42\u63D0\u4F9B\u4E86\u53EF\u9760\u7684\u3001\u9762\u5411\u8FDE\u63A5\u7684\u4F20\u8F93\u670D\u52A1\uFF0C\u4F46\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u534F\u8BAE\u8BBE\u8BA1\u4E2D\u4E0D\u80FD\u8BA4\u4E3A\u6240\u6709\u7F51\u7EDC\u901A\u4FE1\u90FD\u57FA\u4E8ETCP \u534F\u8BAE\u5219\u901A\u4FE1\u5C31\u662F\u53EF\u9760\u7684\u3002TCP\u534F\u8BAE\u53EA\u80FD\u4FDD\u8BC1\u540C\u4E00\u4E2ATCP \u94FE\u63A5\u5185\u7684\u7F51\u7EDC\u6D88\u606F\u4E0D\u4E71\u5E8F\uFF0CTCP \u94FE\u63A5\u4E4B\u95F4\u7684\u7F51\u7EDC\u6D88\u606F\u987A\u5E8F\u5219\u65E0\u6CD5\u4FDD\u8BC1\u3002</p><ul><li><strong>\u5206\u5E03\u5F0F\u4E09\u6001</strong></li></ul><p>\u5982\u679C\u67D0\u4E2A\u8282\u70B9\u5411\u53E6\u4E00\u4E2A\u8282\u70B9\u53D1\u8D77RPC(Remote procedure call)\u8C03\u7528\uFF0C\u5373\u67D0\u4E2A\u8282\u70B9A \u5411\u53E6\u4E00\u4E2A\u8282\u70B9B \u53D1\u9001\u4E00\u4E2A\u6D88\u606F\uFF0C\u8282\u70B9B \u6839\u636E\u6536\u5230\u7684\u6D88\u606F\u5185\u5BB9\u5B8C\u6210\u67D0\u4E9B\u64CD\u4F5C\uFF0C\u5E76\u5C06\u64CD\u4F5C\u7684\u7ED3\u679C\u901A\u8FC7\u53E6\u4E00\u4E2A\u6D88\u606F\u8FD4\u56DE\u7ED9\u8282\u70B9A\uFF0C\u90A3\u4E48\u8FD9\u4E2ARPC \u6267\u884C\u7684\u7ED3\u679C\u6709\u4E09\u79CD\u72B6\u6001\uFF1A\u201C\u6210\u529F\u201D\u3001\u201C\u5931\u8D25\u201D\u3001\u201C\u8D85\u65F6\uFF08\u672A\u77E5\uFF09\u201D\uFF0C\u79F0\u4E4B\u4E3A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u4E09\u6001\u3002</p><ul><li><strong>\u5B58\u50A8\u6570\u636E\u4E22\u5931</strong></li></ul><p>\u5BF9\u4E8E\u6709\u72B6\u6001\u8282\u70B9\u6765\u8BF4\uFF0C\u6570\u636E\u4E22\u5931\u610F\u5473\u7740\u72B6\u6001\u4E22\u5931\uFF0C\u901A\u5E38\u53EA\u80FD\u4ECE\u5176\u4ED6\u8282\u70B9\u8BFB\u53D6\u3001\u6062\u590D\u5B58\u50A8\u7684\u72B6\u6001\u3002 <em>\u5F02\u5E38\u5904\u7406\u539F\u5219</em>\uFF1A\u88AB\u5927\u91CF\u5DE5\u7A0B\u5B9E\u8DF5\u6240\u68C0\u9A8C\u8FC7\u7684\u5F02\u5E38\u5904\u7406\u9EC4\u91D1\u539F\u5219\u662F\uFF1A\u4EFB\u4F55\u5728\u8BBE\u8BA1\u9636\u6BB5\u8003\u8651\u5230\u7684\u5F02\u5E38\u60C5\u51B5\u4E00\u5B9A\u4F1A\u5728\u7CFB\u7EDF\u5B9E\u9645\u8FD0\u884C\u4E2D\u53D1\u751F\uFF0C\u4F46\u5728\u7CFB\u7EDF\u5B9E\u9645\u8FD0\u884C\u9047\u5230\u7684\u5F02\u5E38\u5374\u5F88\u6709\u53EF\u80FD\u5728\u8BBE\u8BA1\u65F6\u672A\u80FD\u8003\u8651\uFF0C\u6240\u4EE5\uFF0C\u9664\u975E\u9700\u6C42\u6307\u6807\u5141\u8BB8\uFF0C\u5728\u7CFB\u7EDF\u8BBE\u8BA1\u65F6\u4E0D\u80FD\u653E\u8FC7\u4EFB\u4F55\u5F02\u5E38\u60C5\u51B5\u3002</p><h3 id="_1-3-\u8861\u91CF\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6307\u6807" tabindex="-1"><a class="header-anchor" href="#_1-3-\u8861\u91CF\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6307\u6807" aria-hidden="true">#</a> 1.3 \u8861\u91CF\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6307\u6807</h3><blockquote><p>\u8861\u91CF\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7684\u6307\u6807\u6709\u54EA\u4E9B\uFF1F</p></blockquote><ul><li><p><strong>\u6027\u80FD</strong></p><ul><li>\u7CFB\u7EDF\u7684\u541E\u5410\u80FD\u529B\uFF0C\u6307\u7CFB\u7EDF\u5728\u67D0\u4E00\u65F6\u95F4\u53EF\u4EE5\u5904\u7406\u7684\u6570\u636E\u603B\u91CF\uFF0C\u901A\u5E38\u53EF\u4EE5\u7528\u7CFB\u7EDF\u6BCF\u79D2\u5904\u7406\u7684\u603B\u7684\u6570\u636E\u91CF\u6765\u8861\u91CF\uFF1B</li><li>\u7CFB\u7EDF\u7684\u54CD\u5E94\u5EF6\u8FDF\uFF0C\u6307\u7CFB\u7EDF\u5B8C\u6210\u67D0\u4E00\u529F\u80FD\u9700\u8981\u4F7F\u7528\u7684\u65F6\u95F4\uFF1B</li><li>\u7CFB\u7EDF\u7684\u5E76\u53D1\u80FD\u529B\uFF0C\u6307\u7CFB\u7EDF\u53EF\u4EE5\u540C\u65F6\u5B8C\u6210\u67D0\u4E00\u529F\u80FD\u7684\u80FD\u529B\uFF0C\u901A\u5E38\u4E5F\u7528QPS(query per second)\u6765\u8861\u91CF\u3002</li></ul><p>\u4E0A\u8FF0\u4E09\u4E2A\u6027\u80FD\u6307\u6807\u5F80\u5F80\u4F1A\u76F8\u4E92\u5236\u7EA6\uFF0C\u8FFD\u6C42\u9AD8\u541E\u5410\u7684\u7CFB\u7EDF\uFF0C\u5F80\u5F80\u5F88\u96BE\u505A\u5230\u4F4E\u5EF6\u8FDF\uFF1B\u7CFB\u7EDF\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u8F83\u957F\u65F6\uFF0C\u4E5F\u5F88\u96BE\u63D0\u9AD8QPS\u3002</p></li><li><p><strong>\u53EF\u7528\u6027</strong></p></li></ul><p>\u7CFB\u7EDF\u7684\u53EF\u7528\u6027(availability)\u6307\u7CFB\u7EDF\u5728\u9762\u5BF9\u5404\u79CD\u5F02\u5E38\u65F6\u53EF\u4EE5\u6B63\u786E\u63D0\u4F9B\u670D\u52A1\u7684\u80FD\u529B\u3002\u7CFB\u7EDF\u7684\u53EF\u7528\u6027\u53EF\u4EE5\u7528\u7CFB\u7EDF\u505C\u670D\u52A1\u7684\u65F6\u95F4\u4E0E\u6B63\u5E38\u670D\u52A1\u7684\u65F6\u95F4\u7684\u6BD4\u4F8B\u6765\u8861\u91CF\uFF0C\u4E5F\u53EF\u4EE5\u7528\u67D0\u529F\u80FD\u7684\u5931\u8D25\u6B21\u6570\u4E0E\u6210\u529F\u6B21\u6570\u7684\u6BD4\u4F8B\u6765\u8861\u91CF\u3002\u53EF\u7528\u6027\u662F\u5206\u5E03\u5F0F\u7684\u91CD\u8981\u6307\u6807\uFF0C\u8861\u91CF\u4E86\u7CFB\u7EDF\u7684\u9C81\u68D2\u6027\uFF0C\u662F\u7CFB\u7EDF\u5BB9\u9519\u80FD\u529B\u7684\u4F53\u73B0\u3002</p><ul><li><strong>\u53EF\u6269\u5C55\u6027</strong></li></ul><p>\u7CFB\u7EDF\u7684\u53EF\u6269\u5C55\u6027(scalability)\u6307\u5206\u5E03\u5F0F\u7CFB\u7EDF\u901A\u8FC7\u6269\u5C55\u96C6\u7FA4\u673A\u5668\u89C4\u6A21\u63D0\u9AD8\u7CFB\u7EDF\u6027\u80FD\uFF08\u541E\u5410\u3001\u5EF6\u8FDF\u3001\u5E76\u53D1\uFF09\u3001\u5B58\u50A8\u5BB9\u91CF\u3001\u8BA1\u7B97\u80FD\u529B\u7684\u7279\u6027\u3002\u597D\u7684\u5206\u5E03\u5F0F\u7CFB\u7EDF\u603B\u5728\u8FFD\u6C42\u201C\u7EBF\u6027\u6269\u5C55\u6027\u201D\uFF0C\u4E5F\u5C31\u662F\u4F7F\u5F97\u7CFB\u7EDF\u7684\u67D0\u4E00\u6307\u6807\u53EF\u4EE5\u968F\u7740\u96C6\u7FA4\u4E2D\u7684\u673A\u5668\u6570\u91CF\u7EBF\u6027\u589E\u957F\u3002</p><ul><li><strong>\u4E00\u81F4\u6027</strong></li></ul><p>\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E3A\u4E86\u63D0\u9AD8\u53EF\u7528\u6027\uFF0C\u603B\u662F\u4E0D\u53EF\u907F\u514D\u7684\u4F7F\u7528\u526F\u672C\u7684\u673A\u5236\uFF0C\u4ECE\u800C\u5F15\u53D1\u526F\u672C\u4E00\u81F4\u6027\u7684\u95EE\u9898\u3002\u8D8A\u662F\u5F3A\u7684\u4E00\u81F4\u7684\u6027\u6A21\u578B\uFF0C\u5BF9\u4E8E\u7528\u6237\u4F7F\u7528\u6765\u8BF4\u4F7F\u7528\u8D77\u6765\u8D8A\u7B80\u5355\u3002</p><h2 id="_2-\u5206\u5E03\u5F0F\u7406\u8BBA\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#_2-\u5206\u5E03\u5F0F\u7406\u8BBA\u57FA\u7840" aria-hidden="true">#</a> 2. \u5206\u5E03\u5F0F\u7406\u8BBA\u57FA\u7840</h2><blockquote><p>\u4E3B\u8981\u5305\u542BCAP\u7406\u8BBA\u548CBASE\u7406\u8BBA\u3002</p></blockquote><h3 id="_2-1-cap\u7406\u8BBA" tabindex="-1"><a class="header-anchor" href="#_2-1-cap\u7406\u8BBA" aria-hidden="true">#</a> 2.1 CAP\u7406\u8BBA</h3><blockquote><p>CAP\u7406\u8BBA\u662F\u5206\u5E03\u5F0F\u7CFB\u7EDF\u3001\u7279\u522B\u662F\u5206\u5E03\u5F0F\u5B58\u50A8\u9886\u57DF\u4E2D\u88AB\u8BA8\u8BBA\u7684\u6700\u591A\u7684\u7406\u8BBA\u3002\u5176\u4E2DC\u4EE3\u8868\u4E00\u81F4\u6027 (Consistency)\uFF0CA\u4EE3\u8868\u53EF\u7528\u6027 (Availability)\uFF0CP\u4EE3\u8868\u5206\u533A\u5BB9\u9519\u6027 (Partition tolerance)\u3002CAP\u7406\u8BBA\u544A\u8BC9\u6211\u4EECC\u3001A\u3001P\u4E09\u8005\u4E0D\u80FD\u540C\u65F6\u6EE1\u8DB3\uFF0C\u6700\u591A\u53EA\u80FD\u6EE1\u8DB3\u5176\u4E2D\u4E24\u4E2A\u3002</p></blockquote><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220615223816600.png" alt="image-20220615223816600"></p><h3 id="_2-2-base\u7406\u8BBA" tabindex="-1"><a class="header-anchor" href="#_2-2-base\u7406\u8BBA" aria-hidden="true">#</a> 2.2 BASE\u7406\u8BBA</h3><blockquote><p>BASE\u662F\u201CBasically Available, Soft state, Eventually consistent(\u57FA\u672C\u53EF\u7528\u3001\u8F6F\u72B6\u6001\u3001\u6700\u7EC8\u4E00\u81F4\u6027)\u201D\u7684\u9996\u5B57\u6BCD\u7F29\u5199\u3002\u5176\u4E2D\u7684\u8F6F\u72B6\u6001\u548C\u6700\u7EC8\u4E00\u81F4\u6027\u8FD9\u4E24\u79CD\u6280\u5DE7\u64C5\u4E8E\u5BF9\u4ED8\u5B58\u5728\u5206\u533A\u7684\u573A\u5408\uFF0C\u5E76\u56E0\u6B64\u63D0\u9AD8\u4E86\u53EF\u7528\u6027\u3002</p></blockquote><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220615223858440.png" alt="image-20220615223858440"></p><h2 id="_3-\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_3-\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5" aria-hidden="true">#</a> 3. \u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5</h2><p>\u53EA\u8981\u8131\u79BB\u4E86\u5355\u673A\u7CFB\u7EDF\uFF0C\u5C31\u4F1A\u5B58\u5728\u591A\u673A\u4E4B\u95F4\u4E0D\u4E00\u81F4\u7684\u95EE\u9898\u3002**\u591A\u4E2A\u8282\u70B9\u4FDD\u8BC1\u5177\u6709\u76F8\u540C\u7684\u6570\u636E\uFF0C\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5C31\u9700\u8981\u4E00\u81F4\u6027\u7B97\u6CD5\u3002**\u4E00\u81F4\u6027\u4ECE\u5F3A\u5230\u5F31\u5206\u7C7B\u4E3A\uFF1A\u7EBF\u6027\u4E00\u81F4\u6027\u3001\u987A\u5E8F\u4E00\u81F4\u6027\u3001\u56E0\u679C\u4E00\u81F4\u6027\u3001\u5355\u8C03\u4E00\u81F4\u6027\u3001\u6700\u7EC8\u4E00\u81F4\u6027\u3002</p><blockquote><p>\u4E00\u81F4\u6027\u7B97\u6CD5\u7684\u76EE\u7684\u662F\u4FDD\u8BC1\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u591A\u6570\u636E\u526F\u672C\u8282\u70B9\u6570\u636E\u4E00\u81F4\u6027\u3002\u4E3B\u8981\u5305\u542B\u4E00\u81F4\u6027Hash\u7B97\u6CD5\uFF0CPaxos\u7B97\u6CD5\uFF0CRaft\u7B97\u6CD5\uFF0CZAB\u7B97\u6CD5\u7B49\u3002</p></blockquote><h3 id="_3-1-\u4E00\u81F4\u6027hash\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_3-1-\u4E00\u81F4\u6027hash\u7B97\u6CD5" aria-hidden="true">#</a> 3.1 \u4E00\u81F4\u6027Hash\u7B97\u6CD5</h3><ul><li>\u5206\u5E03\u5F0F\u7B97\u6CD5 - \u4E00\u81F4\u6027Hash\u7B97\u6CD5 <ul><li>\u4E00\u81F4\u6027Hash\u7B97\u6CD5\u662F\u4E2A\u7ECF\u5178\u7B97\u6CD5\uFF0CHash\u73AF\u7684\u5F15\u5165\u662F\u4E3A\u89E3\u51B3<code>\u5355\u8C03\u6027(Monotonicity)</code>\u7684\u95EE\u9898\uFF1B\u865A\u62DF\u8282\u70B9\u7684\u5F15\u5165\u662F\u4E3A\u4E86\u89E3\u51B3<code>\u5E73\u8861\u6027(Balance)</code>\u95EE\u9898</li></ul></li></ul><h3 id="_3-2-paxos\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_3-2-paxos\u7B97\u6CD5" aria-hidden="true">#</a> 3.2 Paxos\u7B97\u6CD5</h3><ul><li>\u5206\u5E03\u5F0F\u7B97\u6CD5 - Paxos\u7B97\u6CD5 <ul><li>Paxos\u7B97\u6CD5\u662FLamport\u5B97\u5E08\u63D0\u51FA\u7684\u4E00\u79CD\u57FA\u4E8E\u6D88\u606F\u4F20\u9012\u7684\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5\uFF0C\u4F7F\u5176\u83B7\u5F972013\u5E74\u56FE\u7075\u5956\u3002\u81EAPaxos\u95EE\u4E16\u4EE5\u6765\u5C31\u6301\u7EED\u5784\u65AD\u4E86\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5\uFF0CPaxos\u8FD9\u4E2A\u540D\u8BCD\u51E0\u4E4E\u7B49\u540C\u4E8E\u5206\u5E03\u5F0F\u4E00\u81F4\u6027, \u5F88\u591A\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5\u90FD\u7531Paxos\u6F14\u53D8\u800C\u6765</li></ul></li></ul><blockquote><p>\u5305\u542B\u4E09\u4E2A\u89D2\u8272\uFF1AProposer, Acceptor, Learner</p><ol><li><p>Prepare \u9636\u6BB5\uFF0C Proposer \u5411 Acceptors \u53D1\u51FA Prepare \u8BF7\u6C42\uFF0CAcceptors \u8FDB\u884C Promise \u627F\u8BFA\uFF1B</p></li><li><p>Accept \u9636\u6BB5\uFF0C Proposer \u6536\u5230\u591A\u6570 Acceptors \u627F\u8BFA\u7684 Promise \u540E\uFF0C\u5411 Acceptors \u53D1\u51FA Propose \u8BF7\u6C42\uFF0C\u8FDB\u884C accept \u5904\u7406\uFF1B</p></li><li><p>Learn \u9636\u6BB5\uFF0CProposer \u5728\u6536\u5230\u591A\u6570 Acceptors \u7684 accept \u4E4B\u540E\uFF0C\u8868\u793A\u672C\u6B21 accept \u6210\u529F\uFF0C\u51B3\u8BAE\u5F62\u6210\uFF0C\u5C06\u51B3\u8BAE\u53D1\u9001\u7ED9\u6240\u6709 Learners;</p></li></ol></blockquote><h3 id="_3-3-raft\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_3-3-raft\u7B97\u6CD5" aria-hidden="true">#</a> 3.3 Raft\u7B97\u6CD5</h3><ul><li>\u5206\u5E03\u5F0F\u7B97\u6CD5 - Raft\u7B97\u6CD5 <ul><li>Paxos\u662F\u51FA\u4E86\u540D\u7684\u96BE\u61C2\uFF0C\u800CRaft\u6B63\u662F\u4E3A\u4E86\u63A2\u7D22\u4E00\u79CD\u66F4\u6613\u4E8E\u7406\u89E3\u7684\u4E00\u81F4\u6027\u7B97\u6CD5\u800C\u4EA7\u751F\u7684\u3002\u5B83\u7684\u9996\u8981\u8BBE\u8BA1\u76EE\u7684\u5C31\u662F\u6613\u4E8E\u7406\u89E3\uFF0C\u6240\u4EE5\u5728\u9009\u4E3B\u7684\u51B2\u7A81\u5904\u7406\u7B49\u65B9\u5F0F\u4E0A\u5B83\u90FD\u9009\u62E9\u4E86\u975E\u5E38\u7B80\u5355\u660E\u4E86\u7684\u89E3\u51B3\u65B9\u6848</li></ul></li></ul><blockquote><p>\u591A\u6570\u6D3E\u6295\u7968\u9009\u4E3E\u7B97\u6CD5, \u5305\u542B\u89D2\u8272\uFF1ALeader, Candidate, Follower</p></blockquote><h3 id="_3-4-zab\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_3-4-zab\u7B97\u6CD5" aria-hidden="true">#</a> 3.4 ZAB\u7B97\u6CD5</h3><ul><li>\u5206\u5E03\u5F0F\u7B97\u6CD5 - ZAB\u7B97\u6CD5 <ul><li>ZAB \u534F\u8BAE\u5168\u79F0\uFF1AZookeeper Atomic Broadcast\uFF08Zookeeper \u539F\u5B50\u5E7F\u64AD\u534F\u8BAE\uFF09, \u5B83\u5E94\u8BE5\u662F\u6240\u6709\u4E00\u81F4\u6027\u534F\u8BAE\u4E2D\u751F\u4EA7\u73AF\u5883\u4E2D\u5E94\u7528\u6700\u591A\u7684\u4E86\u3002\u4E3A\u4EC0\u4E48\u5462\uFF1F\u56E0\u4E3A\u4ED6\u662F\u4E3A Zookeeper \u8BBE\u8BA1\u7684\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u534F\u8BAE\uFF01</li></ul></li></ul><blockquote><p>\u5177\u6709\u4F18\u5148\u7EA7\u7684\u6C11\u4E3B\u6295\u7968\uFF0C\u5305\u542B\u89D2\u8272\uFF1ALeader, Follower, Observer</p></blockquote><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',58),h={href:"https://pdai.tech/md/arch/arch-z-theory.html",target:"_blank",rel:"noopener noreferrer"};function d(u,b){const a=c("ExternalLinkIcon");return r(),l("div",null,[p,e("p",null,[e("a",h,[t("\u5206\u5E03\u5F0F\u7CFB\u7EDF - \u7406\u8BBA\u57FA\u7840,\u7406\u8BBA\u53CA\u4E00\u81F4\u6027\u7B97\u6CD5"),i(a)])])])}const P=o(n,[["render",d],["__file","distributed-theoretical-basis.html.vue"]]);export{P as default};
