import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,e as t}from"./app.dfd1789b.js";const i={},r=t('<h1 id="java\u7684\u7279\u70B9-java\u80FD\u505A\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#java\u7684\u7279\u70B9-java\u80FD\u505A\u4EC0\u4E48" aria-hidden="true">#</a> Java\u7684\u7279\u70B9\uFF0CJava\u80FD\u505A\u4EC0\u4E48</h1><p>\u5C3D\u7BA1 Java \u5DF2\u7ECF 25 \u5C81\u4E86\uFF0C\u4F46\u4ECD\u7136\u201C\u5B9D\u5200\u672A\u8001\u201D\u3002\u5728 Stack Overflow 2019 \u5E74\u6D41\u884C\u7F16\u7A0B\u8BED\u8A00\u8C03\u67E5\u62A5\u544A\u4E2D\uFF0CJava \u4F4D\u5C45\u7B2C 5 \u4F4D\uFF0C\u6709 41% \u7684\u53D7\u8C03\u5F00\u53D1\u8005\u8BA4\u4E3A Java \u4ECD\u7136\u662F\u4E00\u95E8\u53D7\u6B22\u8FCE\u7684\u7F16\u7A0B\u8BED\u8A00\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/three-01.png" alt=""></p><p>\u5F88\u591A\u5927\u578B\u7684\u4E92\u8054\u7F51\u516C\u53F8\u90FD\u5728\u4F7F\u7528 Java\uFF0C\u56FD\u5185\u6700\u6709\u540D\u7684\u5F53\u5C5E\u963F\u91CC\u5DF4\u5DF4\uFF0C\u56FD\u5916\u6700\u6709\u540D\u7684\u5F53\u5C5E\u8C37\u6B4C\u3002\u90A3\u4E3A\u4EC0\u4E48 Java \u5982\u6B64\u6D41\u884C\u5462\uFF1F</p><p><strong>1\uFF09\u7B80\u5355\u6027</strong></p><p>Java \u4E3A\u5F00\u53D1\u8005\u63D0\u4F9B\u4E86\u7B80\u5355\u6613\u7528\u7684\u7528\u6237\u4F53\u9A8C\uFF0C\u4E0E\u5176\u4ED6\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u8BED\u8A00\u76F8\u6BD4\uFF0CJava \u7684\u8BBE\u8BA1\u548C\u751F\u6001\u5E93\u5177\u6709\u5DE8\u5927\u7684\u4F18\u52BF\u3002Java \u5254\u9664\u4E86 C++ \u4E2D\u5F88\u5C11\u4F7F\u7528\u3001\u96BE\u4EE5\u7406\u89E3\u3001\u6613\u6DF7\u6DC6\u7684\u7279\u522B\uFF0C\u6BD4\u5982\u8BF4\u6307\u9488\u8FD0\u7B97\u3001\u64CD\u4F5C\u7B26\u91CD\u8F7D\uFF0C\u5185\u5B58\u7BA1\u7406\u7B49\u3002</p><p>Java \u53EF\u4EE5\u505A\u5230\u5806\u6808\u5206\u914D\u3001\u5783\u573E\u56DE\u6536\u548C\u81EA\u52A8\u5185\u5B58\u7BA1\u7406\uFF0C\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u4E3A\u5F00\u53D1\u8005\u51CF\u8F7B\u4E86\u5165\u95E8\u7684\u96BE\u5EA6\u3002</p><p><strong>2\uFF09\u53EF\u79FB\u690D\u6027</strong></p><p>\u5982\u679C Java \u76F4\u63A5\u7F16\u8BD1\u6210\u64CD\u4F5C\u7CFB\u7EDF\u80FD\u8BC6\u7684\u4E8C\u8FDB\u5236\u7801\uFF0C\u53EF\u80FD\u4E00\u4E2A\u6807\u8BC6\u5728 Windows \u64CD\u4F5C\u7CFB\u7EDF\u4E0B\u662F1100\uFF0C\u800C Linux \u4E0B\u662F 1001\uFF0C\u8FD9\u6837\u7684\u8BDD\uFF0C\u5728 Windows \u64CD\u4F5C\u7CFB\u7EDF\u4E0B\u53EF\u4EE5\u8FD0\u884C\u7684\u7A0B\u5E8F\u5230\u4E86 Linux \u73AF\u5883\u4E0B\u5C31\u65E0\u6CD5\u8FD0\u884C\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0CJava \u5148\u7F16\u8BD1\u751F\u6210\u5B57\u8282\u7801\uFF0C\u518D\u7531 JVM\uFF08Java \u865A\u62DF\u673A\uFF09\u6765\u89E3\u91CA\u6267\u884C\uFF0C\u76EE\u7684\u5C31\u662F\u5C06\u7EDF\u4E00\u7684\u5B57\u8282\u7801\u8F6C\u6210\u64CD\u4F5C\u7CFB\u7EDF\u53EF\u4EE5\u8BC6\u522B\u7684\u4E8C\u8FDB\u5236\u7801\uFF0C\u7136\u540E\u6267\u884C\u3002\u800C\u9488\u5BF9\u4E0D\u540C\u7684\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u90FD\u6709\u76F8\u5E94\u7248\u672C\u7684 JVM\uFF0C\u6240\u4EE5 Java \u5C31\u5B9E\u73B0\u4E86\u53EF\u79FB\u690D\u6027\u3002</p><p><strong>3\uFF09\u5B89\u5168\u6027</strong></p><p>Java \u9002\u7528\u4E8E\u7F51\u7EDC/\u5206\u5E03\u5F0F\u73AF\u5883\uFF0C\u4E3A\u4E86\u8FBE\u5230\u8FD9\u4E2A\u76EE\u6807\uFF0C\u5728\u5B89\u5168\u65B9\u9762\u6295\u5165\u4E86\u5DE8\u5927\u7684\u7CBE\u529B\u3002\u4F7F\u7528 Java \u53EF\u4EE5\u6784\u5EFA\u9632\u75C5\u6BD2\u3001\u9632\u7BE1\u6539\u7684\u7A0B\u5E8F\u3002</p><p>\u4ECE\u4E00\u5F00\u59CB\uFF0CJava \u5C31\u8BBE\u8BA1\u4E86\u5F88\u591A\u53EF\u4EE5\u9632\u8303\u653B\u51FB\u7684\u673A\u5236\uFF0C\u6BD4\u5982\u8BF4\uFF1A</p><ul><li>\u8FD0\u884C\u65F6\u5806\u6808\u6EA2\u51FA\uFF0C\u8FD9\u662F\u8815\u866B\u75C5\u6BD2\u5E38\u7528\u7684\u653B\u51FB\u624B\u6BB5\u3002</li><li>\u5B57\u8282\u7801\u9A8C\u8BC1\uFF0C\u53EF\u4EE5\u786E\u4FDD\u4EE3\u7801\u7B26\u5408 JVM \u89C4\u8303\u5E76\u9632\u6B62\u6076\u610F\u4EE3\u7801\u7834\u574F\u8FD0\u884C\u65F6\u73AF\u5883\u3002</li><li>\u5B89\u5168\u7684\u7C7B\u52A0\u8F7D\uFF0C\u53EF\u4EE5\u9632\u6B62\u4E0D\u53D7\u4FE1\u4EFB\u7684\u4EE3\u7801\u5E72\u6270 Java \u7A0B\u5E8F\u7684\u8FD0\u884C\u3002</li><li>\u5168\u9762\u7684 API \u652F\u6301\u5E7F\u6CDB\u7684\u52A0\u5BC6\u670D\u52A1\uFF0C\u5305\u62EC\u6570\u5B57\u7B7E\u540D\u3001\u6D88\u606F\u6458\u8981\u3001\uFF08\u5BF9\u79F0\u3001\u975E\u5BF9\u79F0\uFF09\u5BC6\u7801\u3001\u5BC6\u94A5\u751F\u6210\u5668\u3002</li><li>\u5B89\u5168\u901A\u4FE1\uFF0C\u652F\u6301 HTTPS\u3001SSL\uFF0C\u4FDD\u62A4\u4F20\u8F93\u7684\u6570\u636E\u5B8C\u6574\u6027\u548C\u9690\u79C1\u6027\u3002</li></ul><p><strong>4\uFF09\u5E76\u53D1\u6027</strong></p><p>Java \u5728\u591A\u7EBF\u7A0B\u65B9\u9762\u505A\u5F97\u975E\u5E38\u7A81\u51FA\uFF0C\u53EA\u8981\u64CD\u4F5C\u7CFB\u7EDF\u652F\u6301\uFF0CJava \u4E2D\u7684\u7EBF\u7A0B\u5C31\u53EF\u4EE5\u5229\u7528\u591A\u4E2A\u5904\u7406\u5668\uFF0C\u5E26\u6765\u4E86\u66F4\u597D\u7684\u4EA4\u4E92\u54CD\u5E94\u548C\u5B9E\u65F6\u884C\u4E3A\u3002</p><p>\u201Cmusk\uFF0C\u90A3 Java \u8FD8\u4F1A\u7EE7\u7EED\u6D41\u884C\u4E0B\u53BB\u5417\uFF1F\u201D\u4E09\u59B9\u7728\u4E86\u7728\u5979\u7684\u957F\u776B\u6BDB\uFF0C\u5BF9\u6211\u8BF4\u3002</p><p>\u201C\u5F53\u7136\u3002\u201D\u6211\u65A9\u9489\u622A\u94C1\u5730\u56DE\u7B54\u5230\u3002</p><p><strong>\u5927\u6570\u636E\u9886\u57DF\uFF1A</strong></p><p>\u4E0E Python \u4E00\u6837\uFF0CJava \u5728\u5927\u6570\u636E\u9886\u57DF\u5360\u636E\u7740\u4E3B\u5BFC\u5730\u4F4D\uFF0C\u5F88\u591A\u7528\u4E8E\u5904\u7406\u5927\u89C4\u6A21\u6570\u636E\u7684\u6846\u67B6\u90FD\u662F\u57FA\u4E8E Java \u5F00\u53D1\u7684\u3002</p><ul><li><p>Apache Hadoop\uFF0C\u7528\u4E8E\u5728\u5206\u5E03\u5F0F\u73AF\u5883\u4E2D\u5904\u7406\u5927\u89C4\u6A21\u6570\u636E\u96C6\u3002Hadoop \u91C7\u7528\u4E86\u4E3B\u526F\u67B6\u6784\u6A21\u5F0F\uFF0C\u5176\u4E2D\u4E3B\u8282\u70B9\u8D1F\u8D23\u63A7\u5236\u6574\u4E2A\u5206\u5E03\u5F0F\u8BA1\u7B97\u6808\u3002Hadoop \u5728\u9700\u8981\u5904\u7406\u548C\u5206\u6790\u5927\u89C4\u6A21\u6570\u636E\u7684\u516C\u53F8\u5F53\u4E2D\u5F88\u6D41\u884C\u3002</p></li><li><p>Apache Spark\uFF0C\u5927\u578B\u7684 ETL\uFF08\u6570\u636E\u4ED3\u5E93\u6280\u672F\uFF09\u3001\u9884\u6D4B\u5206\u6790\u548C\u62A5\u8868\u7A0B\u5E8F\u7ECF\u5E38\u4F7F\u7528\u5230 Spark\u3002</p></li><li><p>Apache Mahout\uFF0C\u7528\u4E8E\u673A\u5668\u5B66\u4E60\uFF0C\u6BD4\u5982\u5206\u7C7B\u3001\u805A\u7C7B\u548C\u63A8\u8350\u3002</p></li><li><p>JFreechart\uFF0C\u7528\u4E8E\u53EF\u89C6\u5316\u6570\u636E\uFF0C\u53EF\u4EE5\u7528\u5B83\u5236\u4F5C\u5404\u79CD\u56FE\u8868\uFF0C\u6BD4\u5982\u997C\u56FE\u3001\u67F1\u72B6\u56FE\u3001\u7EBF\u56FE\u3001\u6563\u70B9\u56FE\u3001\u76D2\u72B6\u56FE\u3001\u76F4\u65B9\u56FE\u7B49\u7B49\u3002</p></li><li><p>Deeplearning4j\uFF0C\u7528\u4E8E\u6784\u5EFA\u5404\u79CD\u7C7B\u578B\u7684\u795E\u7ECF\u7F51\u7EDC\uFF0C\u53EF\u4EE5\u4E0E Spark \u96C6\u6210\uFF0C\u8FD0\u884C\u5728 GPU\uFF08\u56FE\u5F62\u5904\u7406\u5668\uFF09\u4E0A\u3002</p></li><li><p>Apache Storm\uFF0C\u7528\u4E8E\u5904\u7406\u5B9E\u65F6\u6570\u636E\u6D41\uFF0C\u4E00\u4E2A Storm \u8282\u70B9\u53EF\u4EE5\u5728\u79D2\u7EA7\u5904\u7406\u6570\u767E\u4E07\u4E2A\u4F5C\u4E1A\u3002</p></li></ul><p><strong>\u7269\u8054\u7F51\uFF08IoT\uFF09\u9886\u57DF\uFF1A</strong></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/three-02.png" alt=""></p><p>Oracle \u8868\u793A\uFF0C\u7075\u6D3B\u6027\u548C\u6D41\u884C\u5EA6\u662F IoT \u7A0B\u5E8F\u5458\u9009\u62E9 Java \u7684\u4E3B\u8981\u539F\u56E0\u3002Java \u63D0\u4F9B\u4E86\u5927\u91CF\u7684 API \u5E93\uFF0C\u53EF\u4EE5\u5F88\u5BB9\u6613\u5E94\u7528\u5230\u5D4C\u5165\u5F0F\u5E94\u7528\u7A0B\u5E8F\u4E2D\u3002\u76F8\u6BD4\u5176\u4ED6\u7F16\u7A0B\u8BED\u8A00\uFF0C\u6BD4\u5982 C \u8BED\u8A00\uFF0CJava \u5728\u5207\u6362\u5E73\u53F0\u65F6\u66F4\u52A0\u987A\u7545\uFF0C\u4E0D\u5BB9\u6613\u51FA\u9519\u3002</p><p><strong>\u91D1\u878D\u670D\u52A1\u9886\u57DF\uFF1A</strong></p><ul><li><p>\u804A\u5929\u673A\u5668\u4EBA\uFF0C\u7531\u4E8E\u53EF\u79FB\u690D\u6027\u3001\u53EF\u7EF4\u62A4\u6027\u3001\u53EF\u89C6\u5316\u7B49\u8BF8\u591A\u65B9\u9762\u7684\u56E0\u7D20\uFF0CJava \u6210\u4E86\u5F00\u53D1\u804A\u5929\u673A\u5668\u4EBA\u6700\u597D\u7684\u5DE5\u5177\u3002</p></li><li><p>\u6B3A\u8BC8\u68C0\u6D4B\u548C\u7BA1\u7406\uFF0C\u94F6\u884C\u548C\u91D1\u878D\u516C\u53F8\u4F7F\u7528 AI\uFF08\u4EBA\u5DE5\u667A\u80FD\uFF09\u5DE5\u5177\u6765\u8FDB\u884C\u91D1\u878D\u6B3A\u8BC8\u548C\u4FE1\u7528\u5361\u6B3A\u8BC8\u68C0\u6D4B\uFF0C\u800C Java \u5E38\u7528\u6765\u5F00\u53D1\u8FD9\u4E9B AI \u5DE5\u5177\u3002</p></li><li><p>\u4EA4\u6613\u7CFB\u7EDF\uFF0CJava \u865A\u62DF\u673A\u63D0\u4F9B\u7684\u52A8\u6001\u8FD0\u884C\u65F6\u7F16\u8BD1\u4F18\u5316\u5728\u5F88\u591A\u60C5\u51B5\u4E0B\u6BD4\u7F16\u8BD1\u578B\u8BED\u8A00\uFF08\u5982 C++\uFF09\u5177\u6709\u66F4\u597D\u7684\u6027\u80FD\uFF0C\u8BA9\u4EA4\u6613\u7CFB\u7EDF\u8FD0\u884C\u5F97\u66F4\u987A\u7545\u3002</p></li><li><p>\u79FB\u52A8\u94B1\u5305\uFF0C\u57FA\u4E8E AI \u548C Java \u7B97\u6CD5\u5F00\u53D1\u7684\u79FB\u52A8\u94B1\u5305\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u7528\u6237\u5728\u82B1\u94B1\u65F6\u505A\u51FA\u66F4\u667A\u80FD\u7684\u51B3\u7B56\u3002</p></li></ul><p><strong>Web \u9886\u57DF\uFF1A</strong></p><p>Java \u6280\u672F\u5BF9 Web \u9886\u57DF\u7684\u53D1\u5C55\u6CE8\u5165\u4E86\u5F3A\u5927\u7684\u52A8\u529B\uFF0C\u4E3B\u6D41\u7684 Java Web \u5F00\u53D1\u6846\u67B6\u6709\u5F88\u591A\uFF1A</p><ul><li><p>Spring \u6846\u67B6\uFF0C\u4E00\u4E2A\u8F7B\u91CF\u7EA7\u7684\u63A7\u5236\u53CD\u8F6C\uFF08IoC\uFF09\u548C\u9762\u5411\u5207\u9762\uFF08AOP\uFF09\u7684\u5BB9\u5668\u6846\u67B6\uFF0C\u6E17\u900F\u4E86 Java EE \u6280\u672F\u7684\u65B9\u65B9\u9762\u9762\uFF0C\u7EDD\u5927\u90E8\u5206 Java \u5E94\u7528\u90FD\u53EF\u4EE5\u4ECE Spring \u6846\u67B6\u4E2D\u53D7\u76CA\u3002</p></li><li><p>Spring MVC \u6846\u67B6\uFF0C\u662F\u4E00\u79CD\u57FA\u4E8E Java \u5B9E\u73B0\u7684 MVC\uFF08Model-View-Controller\uFF09\u8BBE\u8BA1\u6A21\u5F0F\u7684\u8BF7\u6C42\u9A71\u52A8\u7C7B\u578B\u7684\u8F7B\u91CF\u7EA7 Web \u6846\u67B6\u3002</p></li><li><p>MyBatis \u6846\u67B6\uFF0C\u4E00\u4E2A\u4F18\u79C0\u7684\u6570\u636E\u6301\u4E45\u5C42\u6846\u67B6\uFF0C\u53EF\u5728\u5B9E\u4F53\u7C7B\u548C SQL \u8BED\u53E5\u4E4B\u95F4\u5EFA\u7ACB\u6620\u5C04\u5173\u7CFB\uFF0C\u662F\u4E00\u79CD\u534A\u81EA\u52A8\u5316\u7684 ORM\uFF08Object Relational Mapping\uFF0C\u5BF9\u8C61\u5173\u7CFB\u6620\u5C04\uFF09\u5B9E\u73B0\u3002</p></li><li><p>JavaServer Faces \u6846\u67B6\uFF0C\u7531 Oracle \u5F00\u53D1\uFF0C\u80FD\u591F\u5C06\u8868\u793A\u5C42\u4E0E\u5E94\u7528\u7A0B\u5E8F\u4EE3\u7801\u8F7B\u677E\u8FDE\u63A5\uFF0C\u5B83\u63D0\u4F9B\u4E86\u4E00\u4E2A API \u96C6\uFF0C\u7528\u4E8E\u8868\u793A\u548C\u7BA1\u7406 UI \u7EC4\u4EF6\u3002</p></li></ul><p>\u603B\u4E4B\uFF0COracle \u5BA3\u79F0\uFF0CJava \u6B63\u8FD0\u884C\u5728 97% \u7684\u4F01\u4E1A\u8BA1\u7B97\u673A\u4E0A\u2014\u2014\u6709\u70B9\u5389\u5BB3\u7684\u6837\u5B50\u3002</p><hr>',31),l=[r];function o(v,n){return p(),e("div",null,l)}const c=a(i,[["render",o],["__file","java-can-do-what.html.vue"]]);export{c as default};
