import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,e as J}from"./app.dfd1789b.js";const t={},r=J('<h1 id="java\u53D1\u5C55\u7B80\u53F2" tabindex="-1"><a class="header-anchor" href="#java\u53D1\u5C55\u7B80\u53F2" aria-hidden="true">#</a> Java\u53D1\u5C55\u7B80\u53F2</h1><p>20 \u4E16\u7EAA 90 \u5E74\u4EE3\uFF0C\u5355\u7247\u5F0F\u8BA1\u7B97\u673A\u7CFB\u7EDF\u8BDE\u751F\u3002\u5355\u7247\u5F0F\u8BA1\u7B97\u673A\u7CFB\u7EDF\u4E0D\u4EC5\u5EC9\u4EF7\uFF08\u4E4B\u524D\u7684\u8BA1\u7B97\u673A\u975E\u5E38\u5E9E\u5927\uFF0C\u5E76\u4E14\u6602\u8D35\uFF09\uFF0C\u800C\u4E14\u529F\u80FD\u5F3A\u5927\uFF0C\u53EF\u4EE5\u5927\u5E45\u5EA6\u63D0\u5347\u6D88\u8D39\u6027\u7535\u5B50\u4EA7\u54C1\u7684\u667A\u80FD\u5316\u7A0B\u5EA6\u3002</p><p>Sun \u516C\u53F8\u4E3A\u4E86\u62A2\u5360\u5E02\u573A\u5148\u673A\uFF0C\u5728 1991 \u5E74\u6210\u7ACB\u4E86\u4E00\u4E2A\u7531\u8A79\u59C6\u65AF\xB7\u9AD8\u65AF\u6797\uFF08James Gosling\uFF09\u9886\u5BFC\u7684\uFF0C\u540D\u4E3A\u201CGreen\u201D\u7684\u9879\u76EE\u7EC4\uFF0C\u76EE\u7684\u662F\u5F00\u53D1\u4E00\u79CD\u80FD\u591F\u5728\u5404\u79CD\u6D88\u8D39\u6027\u7535\u5B50\u4EA7\u54C1\u4E0A\u8FD0\u884C\u7684\u7A0B\u5E8F\u67B6\u6784\u3002</p><p>\u9879\u76EE\u7EC4\u9996\u5148\u8003\u8651\u7684\u662F\u91C7\u7528 C++ \u6765\u7F16\u5199\u7A0B\u5E8F\uFF0C\u4F46 C++ \u8FC7\u4E8E\u590D\u6742\u548C\u5E9E\u5927\uFF0C\u518D\u52A0\u4E0A\u6D88\u8D39\u7535\u5B50\u4EA7\u54C1\u6240\u91C7\u7528\u7684\u5D4C\u5165\u5F0F\u5904\u7406\u5668\u82AF\u7247\u7684\u79CD\u7C7B\u7E41\u6742\uFF0C\u9700\u8981\u8BA9\u7F16\u5199\u7684\u7A0B\u5E8F\u80FD\u591F\u8DE8\u5E73\u53F0\u8FD0\u884C\u5E76\u4E0D\u5BB9\u6613\u2014\u2014C++ \u5728\u8DE8\u5E73\u53F0\u65B9\u9762\u505A\u5F97\u5E76\u4E0D\u597D\u3002</p><p>\u601D\u524D\u60F3\u540E\uFF0C\u9879\u76EE\u7EC4\u6700\u540E\u51B3\u5B9A\uFF1A\u5728 C++ \u7684\u57FA\u7840\u4E0A\u521B\u5EFA\u4E00\u79CD\u65B0\u7684\u7F16\u7A0B\u8BED\u8A00\uFF0C\u65E2\u80FD\u591F\u5254\u9664 C++ \u590D\u6742\u7684\u6307\u9488\u548C\u5185\u5B58\u7BA1\u7406\uFF0C\u8FD8\u80FD\u591F\u517C\u5BB9\u5404\u79CD\u8BBE\u5907\u3002\u8FD9\u8BED\u8A00\u6700\u521D\u7684\u540D\u5B57\u53EB\u505A <strong>Greentalk</strong>\uFF0C\u6587\u4EF6\u6269\u5C55\u540D\u4E3A <code>.gt</code>\u3002\u8FD9\u4E2A\u540D\u5B57\u53EB\u7684\u6BD4\u8F83\u968F\u610F\uFF0C\u5C31\u56E0\u4E3A\u9879\u76EE\u7EC4\u53EB Green\uFF0C\u6CA1\u4EC0\u4E48\u7279\u6B8A\u7684\u5BD3\u610F\u3002</p><p><strong>Oak</strong> \u662F\u201CJava\u201D\u7684\u7B2C\u4E8C\u4E2A\u540D\u5B57\uFF0C\u8FD9\u6B21\u5C31\u6709\u70B9\u610F\u4E49\u4E86\u3002Oak\uFF08\u6A61\u6811\uFF09\u662F\u529B\u91CF\u7684\u8C61\u5F81\uFF0C\u88AB\u7F8E\u56FD\u3001\u6CD5\u56FD\u3001\u5FB7\u56FD\u7B49\u8BB8\u591A\u6B27\u7F8E\u56FD\u5BB6\u9009\u4E3A\u56FD\u6811\u3002\u6A61\u6811\u957F\u4E0B\u9762\u8FD9\u6837\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/two-01.png" alt=""></p><p>1992 \u5E74\uFF0COak \u7684\u96CF\u5F62\u6709\u4E86\uFF0C\u4F46\u9879\u76EE\u7EC4\u5728\u5411\u786C\u4EF6\u751F\u4EA7\u5546\u8FDB\u884C\u5546\u6F14\u7684\u65F6\u5019\uFF0C\u5E76\u6CA1\u6709\u83B7\u5F97\u8BA4\u53EF\uFF0C\u4E8E\u662F Oak \u5C31\u88AB\u6401\u7F6E\u4E00\u65C1\u4E86\u3002</p><p>1994 \u5E74\uFF0C\u9879\u76EE\u7EC4\u53D1\u73B0 Java \u66F4\u9002\u5408\u8FDB\u884C Internet \u7F16\u7A0B\u3002\u968F\u540E\uFF0C\u9879\u76EE\u7EC4\u7528 Oak \u8BED\u8A00\u7814\u53D1\u4E86\u4E00\u79CD\u80FD\u5C06\u5C0F\u7A0B\u5E8F\u5D4C\u5165\u5230\u7F51\u9875\u4E2D\u6267\u884C\u7684\u6280\u672F\u2014\u2014Applet\u3002Applet \u4E0D\u4EC5\u80FD\u5D4C\u5165\u7F51\u9875\uFF0C\u8FD8\u80FD\u591F\u968F\u540C\u7F51\u9875\u5728\u7F51\u7EDC\u4E0A\u8FDB\u884C\u4F20\u8F93\u3002</p><p>\u4E0D\u5F97\u4E0D\u611F\u6168\u4E00\u4E0B\uFF0C\u6280\u672F\u7684\u66F4\u65B0\u8FED\u4EE3\u662F\u771F\u7684\u5FEB\uFF0CApplet \u62EF\u6551\u4E86 Oak\uFF0C\u5E76\u4F7F\u5176\u8715\u53D8\u6210\u9876\u5929\u7ACB\u5730\u7684 Java\uFF0C\u4F46 Applet \u5F88\u65E9\u4E4B\u524D\u5C31\u88AB\u65E0\u60C5\u5730\u62CD\u6B7B\u5728\u4E86\u6C99\u6EE9\u4E0A\u3002\u662F\u4E0D\u662F\u5F88\u6B8B\u9177\uFF1F</p><p>1995 \u5E74\uFF0COak \u88AB\u91CD\u65B0\u547D\u540D\u4E3A\u201CJava\u201D\uFF0C\u56E0\u4E3A Oak \u88AB\u522B\u7684\u516C\u53F8\u6CE8\u518C\u8FC7\u4E86\u3002\u65B0\u7684\u540D\u5B57\u6700\u597D\u80FD\u591F\u8868\u8FBE\u51FA\u6280\u672F\u7684\u672C\u8D28\uFF1Adynamic\uFF08\u52A8\u6001\u7684\uFF09\u3001revolutionary\uFF08\u9769\u547D\u6027\u7684\uFF09\u3001Silk\uFF08\u50CF\u4E1D\u7EF8\u4E00\u6837\u67D4\u8F6F\u7684\uFF09\u3001Cool\uFF08\u70AB\u9177\u7684\uFF09\u7B49\u7B49\u3002\u53E6\u5916\uFF0C\u540D\u5B57\u4E00\u5B9A\u8981\u5BB9\u6613\u62FC\u5199\uFF0C\u5FF5\u8D77\u6765\u4E5F\u6BD4\u8F83\u6709\u8DA3\u3002</p><p>\u9009\u6765\u9009\u53BB\uFF0C\u9879\u76EE\u7EC4\u6700\u540E\u9009\u62E9\u4E86\u201CJava\u201D\uFF0C\u4E2D\u6587\u53EB\u201C\u722A\u54C7\u201D\u3002\u7EC6\u5FC3\u7684\u5C0F\u4F19\u4F34\u53EF\u80FD\u4F1A\u53D1\u73B0\uFF0CJava \u8FD9\u4E2A\u5355\u8BCD\u91CC\u6709\u4E00\u4E2A\u654F\u611F\u8BCD\uFF0C\u6240\u4EE5\u6709\u6BB5\u65F6\u95F4\u5FAE\u4FE1\uFF08\u6587\u7AE0\u4E13\u8F91\u540D\u8FD9\u5757\uFF09\u4E3A\u4E86\u7981\u654F\u611F\u8BCD\uFF0C\u7ADF\u7136\u628A Java \u90FD\u7981\u4E86\uFF0C\u6211\u5F53\u65F6\u5C31\u53EA\u80FD\u7528\u722A\u54C7\u6765\u4EE3\u66FF Java\uFF0C\u624B\u52A8\u72D7\u5934\u3002</p><p>\u201CJava\u201D\u662F\u5370\u5EA6\u5C3C\u897F\u4E9A\u722A\u54C7\u5C9B\u7684\u82F1\u6587\u540D\uFF0C\u56E0\u751F\u4EA7\u5496\u5561\u800C\u95FB\u540D\uFF0C\u6240\u4EE5\uFF0C\u5C0F\u4F19\u4F34\u4E5F\u770B\u5230\u4E86\uFF0CJava \u8FD9\u4E2A\u5355\u8BCD\u7ECF\u5E38\u548C\u4E00\u676F\u5192\u7740\u70ED\u6C14\u7684\u5496\u5561\u4E00\u8D77\u51FA\u73B0\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/overview/two-02.png" alt=""></p><p>\u540C\u5E74\uFF0CSun \u516C\u53F8\u5728 SunWorld \u5927\u4F1A\u4E0A\u6B63\u5F0F\u53D1\u5E03\u4E86 Java 1.0 \u7248\u672C\uFF0C\u7B2C\u4E00\u6B21\u63D0\u51FA\u4E86\u201CWrite Once, Run anywhere\u201D\u7684\u53E3\u53F7\u3002\u300A\u65F6\u4EE3\u300B\u6742\u5FD7\u5C06 Java \u8BC4\u4E3A 1995 \u5E74\u5341\u5927\u6700\u4F73\u4EA7\u54C1\u4E4B\u4E00\u3002</p><p>1996 \u5E74 1 \u6708 23 \u65E5\uFF0CJDK 1.0 \u53D1\u5E03\uFF0CJava \u8BED\u8A00\u6709\u4E86\u7B2C\u4E00\u4E2A\u6B63\u5F0F\u7248\u672C\u7684\u8FD0\u884C\u73AF\u5883\u3002JDK 1.0 \u662F\u4E00\u4E2A\u7EAF\u89E3\u91CA\u6267\u884C\u7684 Java \u865A\u62DF\u673A\uFF0C\u4EE3\u8868\u6280\u672F\u6709\uFF1AJava \u865A\u62DF\u673A\u3001AWT\uFF08\u56FE\u5F62\u5316\u754C\u9762\uFF09\u3001Applet\u3002</p><p>4 \u6708\uFF0C\u5341\u4E2A\u4E3B\u8981\u7684\u64CD\u4F5C\u7CFB\u7EDF\u548C\u8BA1\u7B97\u673A\u4F9B\u5E94\u5546\u5BA3\u79F0\u5C06\u5728\u4EA7\u54C1\u4E2D\u5D4C\u5165 Java \u6280\u672F\u30029 \u6708\uFF0C\u5DF2\u6709\u5927\u7EA6 8.3 \u4E07\u7F51\u9875\u5E94\u7528\u91C7\u7528\u4E86 Java \u6765\u5236\u4F5C\u30025 \u6708\u5E95\uFF0C\u7B2C\u4E00\u5C4A JavaOne \u5927\u4F1A\u5728\u65E7\u91D1\u5C71\u4E3E\u884C\uFF0C\u4ECE\u6B64\uFF0CJavaOne \u6210\u4E3A\u5168\u4E16\u754C\u6570\u767E\u4E07 Java \u8BED\u8A00\u5F00\u53D1\u8005\u7684\u6280\u672F\u76DB\u4F1A\u3002</p><p>1997 \u5E74 2 \u6708 19 \u65E5\uFF0CJDK 1.1 \u53D1\u5E03\uFF0C\u4EE3\u8868\u6280\u672F\u6709\uFF1AJAR \u6587\u4EF6\u683C\u5F0F\u3001JDBC\u3001JavaBeans\u3001RMI\uFF08\u8FDC\u7A0B\u65B9\u6CD5\u8C03\u7528\uFF09\u3002</p><p>1998 \u5E74 12 \u6708 4 \u65E5\uFF0CJDK 1.2 \u53D1\u5E03\uFF0C\u8FD9\u662F\u4E00\u4E2A\u91CC\u7A0B\u7891\u5F0F\u7684\u7248\u672C\u3002Sun \u5728\u8FD9\u4E2A\u7248\u672C\u4E2D\u628A Java \u62C6\u5206\u4E3A\u4E09\u4E2A\u65B9\u5411\uFF1A\u9762\u5411\u684C\u9762\u5F00\u53D1\u7684 J2SE\u3001\u9762\u5411\u4F01\u4E1A\u5F00\u53D1\u7684 J2EE\uFF0C\u9762\u5411\u79FB\u52A8\u5F00\u53D1\u7684 J2ME\u3002\u4EE3\u8868\u6280\u672F\u6709\uFF1AEJB\u3001Swing\u3002</p><p>2000 \u5E74 5 \u6708 8 \u65E5\uFF0CJDK 1.3 \u53D1\u5E03\uFF0C\u5BF9 Java 2D \u505A\u4E86\u5927\u5E45\u4FEE\u6539\u3002</p><p>2002 \u5E74 2 \u6708 13 \u65E5\uFF0CJDK 1.4 \u53D1\u5E03\uFF0C\u8FD9\u662F Java \u771F\u6B63\u8D70\u5411\u6210\u719F\u7684\u4E00\u4E2A\u7248\u672C\uFF0CIBM\u3001\u5BCC\u58EB\u901A\uFF08musk\u66FE\u5728\u8FD9\u5BB6\u4E16\u754C 500 \u5F3A\u7684\u65E5\u4F01\u5DE5\u4F5C\u8FC7\u4E09\u5E74\u534A\u65F6\u95F4\uFF09\u7B49\u8457\u540D\u516C\u53F8\u90FD\u6709\u53C2\u4E0E\u3002\u4EE3\u8868\u6280\u672F\u6709\uFF1A\u6B63\u5219\u8868\u8FBE\u5F0F\u3001NIO\u3002</p><p>2004 \u5E74 9 \u6708 30 \u65E5\uFF0CJDK 5 \u53D1\u5E03\uFF0C\u6CE8\u610F Sun \u628A\u201C1.x\u201D\u7684\u547D\u540D\u65B9\u5F0F\u629B\u5F03\u4E86\u3002JDK 5 \u5728 Java \u8BED\u6CD5\u7684\u6613\u7528\u6027\u4E0A\u505A\u51FA\u4E86\u975E\u5E38\u5927\u7684\u6539\u8FDB\uFF0C\u6BD4\u5982\u8BF4\uFF1A\u81EA\u52A8\u88C5\u7BB1\u3001\u6CDB\u578B\u3001\u52A8\u6001\u6CE8\u89E3\u3001\u679A\u4E3E\u3001\u53EF\u53D8\u53C2\u6570\u3001foreach \u5FAA\u73AF\u3002</p><p>2006 \u5E74 12 \u6708 11 \u65E5\uFF0CJDK 6 \u53D1\u5E03\uFF0CJ2SE \u53D8\u6210\u4E86 Java SE 6\uFF0CJ2EE \u53D8\u6210\u4E86 Java EE 6\uFF0CJ2ME \u53D8\u6210\u4E86 Java ME 6\u3002JDK 6 \u6050\u6015\u662F Java \u5386\u53F2\u4E0A\u4F7F\u7528\u5BFF\u547D\u6700\u957F\u7684\u4E00\u4E2A\u7248\u672C\u4E86\u3002\u4E3B\u8981\u7684\u539F\u56E0\u6709\uFF1A\u4EE3\u7801\u590D\u6742\u6027\u7684\u589E\u52A0\u3001\u4E16\u754C\u7ECF\u6D4E\u5371\u673A\u3001Oracle \u5BF9 Sun \u7684\u6536\u8D2D\u3002</p><p>JDK 6 \u7684\u6700\u540E\u4E00\u4E2A\u5347\u7EA7\u8865\u4E01\u4E3A Java SE 6 Update 211\uFF0C \u4E8E 2018 \u5E74 10 \u6708 18 \u65E5\u53D1\u5E03\u2014\u201412 \u5E74\u7684\u8DE8\u5EA6\u554A\uFF01</p><p>2009 \u5E74 2 \u6708 19 \u65E5\uFF0CJDK 7 \u53D1\u5E03\uFF0C\u4F46\u529F\u80FD\u662F\u9609\u5272\u7684\u3002\u5F88\u591A\u7FD8\u9996\u4EE5\u76FC\u7684\u529F\u80FD\u90FD\u6CA1\u6709\u5B8C\u6210\uFF0C\u6BD4\u5982\u8BF4 Lambda \u8868\u8FBE\u5F0F\u3002\u4E3B\u8981\u662F\u56E0\u4E3A Sun \u516C\u53F8\u5728\u5546\u4E1A\u4E0A\u9677\u5165\u4E86\u6CE5\u6CBC\uFF0C\u5DF2\u7ECF\u65E0\u529B\u63A8\u52A8 JDK 7 \u7684\u7814\u53D1\u5DE5\u4F5C\u3002</p><p>2009 \u5E74 4 \u6708 20 \u65E5\uFF0COracle \u4EE5 74 \u4EBF\u7F8E\u5143\u7684\u4EF7\u683C\u6536\u8D2D\u4E86\u5E02\u503C\u66FE\u8D85\u8FC7 2000 \u4EBF\u7F8E\u5143\u7684 Sun \u516C\u53F8\u2014\u2014\u592A\u9633\u7EC8\u7A76\u8FD8\u662F\u843D\u5C71\u4E86\u3002\u5BF9\u4E8E Java \u8BED\u8A00\u8FD9\u4E2A\u5B69\u5B50\u6765\u8BF4\uFF0C\u53EF\u4EE5\u8BF4\u662F\u597D\u4E8B\uFF0C\u4E5F\u53EF\u4EE5\u8BF4\u662F\u574F\u4E8B\u3002\u597D\u4E8B\u662F Oracle \u6709\u94B1\uFF0C\u80FD\u591F\u6CE8\u5165\u8D44\u91D1\u63A8\u52A8 Java \u7684\u53D1\u5C55\uFF1B\u574F\u5904\u5C31\u662F Oracle \u662F\u540E\u7238\uFF0C\u5BF9 Java \u80AF\u5B9A\u6CA1\u6709 Sun \u90A3\u4E48\u4EB2\uFF0C\u8D70\u7684\u662F\u6781\u5177\u5546\u4E1A\u5316\u7684\u9053\u8DEF\u3002</p><p>2014 \u5E74 3 \u6708 18 \u65E5\uFF0CJDK 8 \u7EC8\u4E8E\u6765\u4E86\uFF0C\u6B65\u4F10\u662F\u90A3\u4E48\u8E52\u8DDA\uFF0C\u4F46\u7EC8\u7A76\u8FD8\u662F\u6765\u4E86\u3002\u5E26\u7740\u6700\u5F3A\u6709\u529B\u7684\u6B66\u5668\u2014\u2014Lambda \u8868\u8FBE\u5F0F\u800C\u6765\u3002\u867D\u7136 JDK 19 \u9A6C\u4E0A\u5C31\u53D1\u5E03\u4E86\uFF0C\u4F46\u201C\u65B0\u7248\u4EFB\u4F60\u53D1\uFF0C\u6211\u7528 Java 8\u201D\u7684\u6897\u81F3\u4ECA\u8FD8\u6D41\u4F20\u7740\u3002</p><p>2017 \u5E74 9 \u6708 21 \u65E5\uFF0CJDK 9 \u53D1\u5E03\u3002\u4ECE\u6B64\u4EE5\u540E\uFF0CJDK \u66F4\u65B0\u7248\u672C\u7684\u901F\u5EA6\u4EE4\u5F00\u53D1\u8005\u5E94\u63A5\u4E0D\u6687\uFF0C\u534A\u5E74\u4E00\u4E2A\u7248\u672C\uFF0C\u867D\u7136 Oracle \u7684\u76EE\u7684\u662F\u597D\u7684\uFF0C\u4E3A\u4E86\u907F\u514D\u56E0\u529F\u80FD\u589E\u52A0\u800C\u5F15\u53D1\u7684\u8DF3\u7968\u98CE\u9669\uFF0C\u4F46\u4E0D\u5F97\u4E0D\u627F\u8BA4\uFF0C\u7248\u672C\u66F4\u65B0\u7684\u8282\u594F\u5B9E\u5728\u662F\u6709\u70B9\u8FC7\u4E8E\u9891\u7E41\u3002</p><p>\u8FD9\u5C31\u5BFC\u81F4\u4E00\u4E2A\u95EE\u9898\uFF0C\u597D\u4E0D\u5BB9\u6613\u66F4\u65B0\u4E00\u4E2A\u7248\u672C\uFF0C\u7528\u4E86\u516D\u4E2A\u6708\u540E\uFF0COracle \u4E0D\u7EF4\u62A4\u4E86\u3002\u9488\u5BF9\u8FD9\u4E2A\u95EE\u9898\uFF0COracle \u7ED9\u51FA\u7684\u89E3\u51B3\u65B9\u6848\u633A\u5947\u8469\u7684\uFF0C\u6BCF\u516D\u4E2A JDK \u5927\u7248\u672C\u624D\u4F1A\u88AB\u957F\u671F\u652F\u6301\uFF08Long Term Support\uFF0CLTS\uFF09\u3002</p><p>JDK 8 \u662F LTS \u7248\uFF0C2018 \u5E74 9 \u6708 25 \u65E5\u53D1\u5E03\u7684 JDK 11 \u662F LTS \u7248\uFF0C 2018 \u5E74 3 \u6708 20 \u65E5\u53D1\u5E03\u7684 JDK 10 \u5C31\u53EF\u4EE5\u4E00\u7B14\u5E26\u8FC7\u4E86\u3002</p><p>2021 \u5E74\u53D1\u5E03\u7684 JDK 17 \u662F\u76EE\u524D\u6700\u65B0\u7684 LTS \u7248\u672C\u3002</p><p>JDK 12\u3001JDK 13\u3001JDK 14\u3001JDK 15\u3001JDK 16\u3001JDK 18\u3001JDK 19 \u90FD\u662F\u8FC7\u6E21\u4EA7\u54C1\uFF0C\u5C31\u597D\u50CF\u662F\u8BD5\u9A8C\u54C1\u4E00\u6837\uFF0C\u4E0D\u592A\u53D7\u5F00\u53D1\u8005\u5F85\u89C1\u3002</p><p>Java \u53D1\u5C55\u5230\u4ECA\u5929\u5DF2\u7ECF 20 \u591A\u5E74\u4E86\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u7F16\u7A0B\u8BED\u8A00\u786E\u5B9E\u4E0D\u7B80\u5355\uFF0CJava \u4EE3\u8868\u7684\u9762\u5411\u5BF9\u8C61\u601D\u60F3\u786E\u5B9E\u7ED9\u5DE5\u7A0B\u9886\u57DF\u5E26\u6765\u4E86\u9769\u547D\u6027\u7684\u53D8\u5316\uFF0C\u5173\u952E\u662F Java \u4E00\u76F4\u5728\u62E5\u62B1\u53D8\u5316\u3002</p><p>\u5927\u6570\u636E\u65B9\u9762\uFF0C\u6709 Apache Kafka\u3001Apache Samza\u3001Apache Storm\u3001Apache Spark\u3001Apache Flink\uFF0C\u9664\u4E86 Spark \u662F\u57FA\u4E8E JVM \u7684\u51FD\u6570\u8BED\u8A00 Scala \u7F16\u5199\u7684\uFF0C\u5176\u4F59\u90FD\u662F Java \u7F16\u5199\u7684\u3002</p><p>Java \u5728\u4E91\u65F6\u4EE3\u9762\u4E34\u7740\u4EE5 Go \u8BED\u8A00\u4E3A\u4E3B\u7684\u5BB9\u5668\uFF08Docker \u7B49\u6280\u672F\uFF09\u751F\u6001\u5708\u7684\u6311\u6218\uFF0C\u4F46\u662F\uFF0CJava \u7684\u5927\u578B\u5206\u5E03\u5F0F\u7CFB\u7EDF\u8D8A\u6765\u8D8A\u591A\uFF0CJava \u5728\u4E91\u8BA1\u7B97\u4E0E\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u8FD8\u662F\u626E\u6F14\u7740\u4E3B\u8981\u89D2\u8272\uFF0C\u5E76\u4E14\u5F62\u6210\u4E86\u4E00\u4E2A\u5927\u578B\u7684\u751F\u6001\u5708\u3002</p><p>\u867D\u7136 Java \u548C C++\uFF0CC \u4E00\u6837\uFF0C\u90FD\u201C\u8001\u201D\u4E86\uFF0C\u88AB\u5176\u4ED6\u8BED\u8A00\u4E0D\u65AD\u5730\u6311\u6218\uFF0C\u4F46\u53EA\u6709\u5F3A\u8005\u624D\u6709\u673A\u4F1A\u63A5\u53D7\u6311\u6218\uFF0C\u5BF9\u5427\uFF1F\u6211\u76F8\u4FE1\uFF0CJava \u7684\u672A\u6765\u4F9D\u7136\u5F88\u5149\u660E\u3002</p><hr>',37),v=[r];function n(o,c){return p(),e("div",null,v)}const s=a(t,[["render",n],["__file","java-history.html.vue"]]);export{s as default};
