import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as s,c as t,a,b as l,d as r,f as p,r as c}from"./app.55a3f9ac.js";const d={},i=p('<h1 id="tomcat\u7C7B\u52A0\u8F7D\u5668" tabindex="-1"><a class="header-anchor" href="#tomcat\u7C7B\u52A0\u8F7D\u5668" aria-hidden="true">#</a> tomcat\u7C7B\u52A0\u8F7D\u5668</h1><p>\u901A\u8FC7\u524D\u6587\u6211\u4EEC\u5DF2\u7ECF\u4E86\u89E3\u4E86\u7C7B\u52A0\u8F7D\u5668\u4EE5\u53CA\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\uFF0C\u5E76\u4E14\u4E86\u89E3\u4E3A\u4EC0\u4E48\u4F7F\u7528\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u3002\u4F46\u6211\u4EEC\u8003\u8651\u4E00\u4E0B\u6211\u4EECtomcat \u4E2D\u7684\u573A\u666F</p><ul><li>\u4E00\u4E2Aweb\u5BB9\u5668\u53EF\u80FD\u9700\u8981\u90E8\u7F72\u4E24\u4E2A\u5E94\u7528\u7A0B\u5E8F\uFF0C\u4E0D\u540C\u7684\u5E94\u7528\u7A0B\u5E8F\u53EF\u80FD\u4F1A<strong>\u4F9D\u8D56\u540C\u4E00\u4E2A\u7B2C\u4E09\u65B9\u7C7B\u5E93\u7684\u4E0D\u540C\u7248\u672C</strong>\u3002\u4E0D\u80FD\u8981\u6C42\u540C\u4E00\u4E2A\u7C7B\u5E93\u5728\u540C\u4E00\u4E2A\u670D\u52A1\u5668\u53EA\u6709\u4E00\u4EFD\uFF0C\u56E0\u6B64\u8981\u4FDD\u8BC1\u6BCF\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7684\u7C7B\u5E93\u90FD\u662F\u72EC\u7ACB\u7684\uFF0C\u4FDD\u8BC1\u76F8\u4E92\u9694\u79BB\u3002</li></ul><p>\u5982\u679C\u6211\u4EEC\u4F7F\u7528\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u5C31\u4E0D\u80FD\u4FDD\u8BC1\u4ED6\u4EEC\u7684\u9694\u79BB\u6027\u4E86\uFF0C\u90A3\u4E48\u6211\u4EEC\u5982\u679C\u8981\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B</p><h2 id="_1-\u5982\u4F55\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#_1-\u5982\u4F55\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B" aria-hidden="true">#</a> 1. \u5982\u4F55\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B</h2><p>\u53CC\u4EB2\u59D4\u4EFB\u6A21\u578B<strong>\u4E0D\u662F\u4E00\u4E2A\u5F3A\u5236\u6027\u7684\u7EA6\u675F\u6A21\u578B</strong>\uFF0C\u800C\u662F\u4E00\u4E2A\u5EFA\u8BAE\u578B\u7684\u7C7B\u52A0\u8F7D\u5668\u5B9E\u73B0\u65B9\u5F0F\u3002</p><p>\u5386\u53F2\u4E0A\u51FA\u73B0\u7684\u4E09\u6B21\u7834\u574F</p><ul><li><p>\u7B2C\u4E00\u6B21\uFF1A\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u51FA\u73B0\u4E4B\u524D\uFF0C\u65E2jdk 1.2 \u53D1\u5E03\u4E4B\u524D</p></li><li><p>\u7B2C\u4E8C\u6B21\uFF1A\u56E0\u4E3A\u81EA\u8EAB\u6A21\u578B\u7F3A\u9677</p><p>\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u89E3\u51B3\u4E86\u5404\u4E2A\u7C7B\u52A0\u8F7D\u5668\u7684\u57FA\u7840\u7C7B\u7684\u540C\u4E00\u95EE\u9898\uFF08\u8D8A\u57FA\u7840\u7684\u7C7B\u7531\u8D8A\u4E0A\u5C42\u7684\u52A0\u8F7D\u5668\u8FDB\u884C\u52A0\u8F7D\uFF09\uFF0C\u57FA\u7840\u7C7B\u4E4B\u6240\u4EE5\u79F0\u4E3A\u201C\u57FA\u7840\u201D\uFF0C\u662F\u56E0\u4E3A\u5B83\u4EEC\u603B\u662F\u4F5C\u4E3A\u88AB\u7528\u6237\u4EE3\u7801\u8C03\u7528\u7684API\uFF0C \u4F46\u6CA1\u6709\u7EDD\u5BF9\uFF0C<strong>\u5982\u679C\u57FA\u7840\u7C7B\u8C03\u7528\u4F1A\u7528\u6237\u7684\u4EE3\u7801</strong>\u600E\u4E48\u529E\u5462\uFF1F</p><blockquote><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0CJava\u8BBE\u8BA1\u56E2\u961F\u53EA\u597D\u5F15\u5165\u4E86\u4E00\u4E2A\u4E0D\u592A\u4F18\u96C5\u7684\u8BBE\u8BA1\uFF1A<strong>\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u7C7B\u52A0\u8F7D\u5668\uFF08Thread Context ClassLoader\uFF09</strong>\u3002\u8FD9\u4E2A\u7C7B\u52A0\u8F7D\u5668\u53EF\u4EE5\u901A\u8FC7java.lang.Thread\u7C7B\u7684setContextClassLoader\u65B9\u6CD5\u8FDB\u884C\u8BBE\u7F6E\u3002\u5982\u679C\u521B\u5EFA\u7EBF\u7A0B\u65F6\u8FD8\u672A\u8BBE\u7F6E\uFF0C\u5B83\u5C06\u4F1A\u4ECE\u7236\u7EBF\u7A0B\u4E2D\u7EE7\u627F\u4E00\u4E2A\uFF0C\u5982\u679C\u5728\u5E94\u7528\u7A0B\u5E8F\u7684\u5168\u5C40\u8303\u56F4\u5185\u90FD\u6CA1\u6709\u8BBE\u7F6E\u8FC7\u591A\u7684\u8BDD\uFF0C\u90A3\u8FD9\u4E2A\u7C7B\u52A0\u8F7D\u5668\u9ED8\u8BA4\u5373\u4F7F\u5E94\u7528\u7A0B\u5E8F\u7C7B\u52A0\u8F7D\u5668\u3002</p><p>\u563F\u563F\uFF0C\u6709\u4E86\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u52A0\u8F7D\u5668\uFF0CJNDI\u670D\u52A1\u4F7F\u7528\u8FD9\u4E2A\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u52A0\u8F7D\u5668\u53BB\u52A0\u8F7D\u6240\u9700\u8981\u7684SPI\u4EE3\u7801\uFF0C\u4E5F\u5C31\u662F\u7236\u7C7B\u52A0\u8F7D\u5668\u8BF7\u6C42\u5B50\u7C7B\u52A0\u8F7D\u5668\u53BB\u5B8C\u6210\u7C7B\u52A0\u8F7D\u7684\u52A8\u4F5C\uFF0C\u8FD9\u79CD\u884C\u4E3A\u5B9E\u9645\u4E0A\u5C31\u662F\u6253\u901A\u4E86\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u7684\u5C42\u6B21\u7ED3\u6784\u6765\u9006\u5411\u4F7F\u7528\u7C7B\u52A0\u8F7D\u5668\uFF0C\u5B9E\u9645\u4E0A\u5DF2\u7ECF\u8FDD\u80CC\u4E86\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u7684\u4E00\u822C\u6027\u539F\u5219\u3002\u4F46\u8FD9\u65E0\u53EF\u5948\u4F55\uFF0CJava\u4E2D\u6240\u6709\u6D89\u53CASPI\u7684\u52A0\u8F7D\u52A8\u4F5C\u57FA\u672C\u80DC\u90FD\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u3002\u4F8B\u5982JNDI\uFF0C<strong>JDBC</strong>\uFF0CJCE\uFF0CJAXB\uFF0CJBI\u7B49\u3002</p></blockquote></li><li><p><strong>\u7B2C\u4E09\u6B21</strong>\uFF1A\u4E3A\u4E86\u5B9E\u73B0\u70ED\u63D2\u62D4\uFF0C\u70ED\u90E8\u7F72\uFF0C\u6A21\u5757\u5316\uFF0C\u610F\u601D\u662F\u6DFB\u52A0\u4E00\u4E2A\u529F\u80FD\u6216\u51CF\u53BB\u4E00\u4E2A\u529F\u80FD\u4E0D\u7528\u91CD\u542F\uFF0C\u53EA\u9700\u8981\u628A\u8FD9\u6A21\u5757\u8FDE\u540C\u7C7B\u52A0\u8F7D\u5668\u4E00\u8D77\u6362\u6389\u5C31\u5B9E\u73B0\u4E86\u4EE3\u7801\u7684\u70ED\u66FF\u6362\u3002</p></li></ul><h2 id="_2-tomcat-\u7684\u7C7B\u52A0\u8F7D\u5668\u662F\u600E\u4E48\u8BBE\u8BA1\u7684" tabindex="-1"><a class="header-anchor" href="#_2-tomcat-\u7684\u7C7B\u52A0\u8F7D\u5668\u662F\u600E\u4E48\u8BBE\u8BA1\u7684" aria-hidden="true">#</a> 2. Tomcat \u7684\u7C7B\u52A0\u8F7D\u5668\u662F\u600E\u4E48\u8BBE\u8BA1\u7684\uFF1F</h2><h3 id="_2-1-tomcat\u662F\u4E2Aweb\u5BB9\u5668-\u90A3\u4E48\u4ED6\u8981\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_2-1-tomcat\u662F\u4E2Aweb\u5BB9\u5668-\u90A3\u4E48\u4ED6\u8981\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898" aria-hidden="true">#</a> 2.1 tomcat\u662F\u4E2Aweb\u5BB9\u5668\uFF0C\u90A3\u4E48\u4ED6\u8981\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898</h3><ol><li><p>\u4E00\u4E2Aweb\u5BB9\u5668\u53EF\u80FD\u9700\u8981\u90E8\u7F72\u4E24\u4E2A\u5E94\u7528\u7A0B\u5E8F\uFF0C\u4E0D\u540C\u7684\u5E94\u7528\u7A0B\u5E8F\u53EF\u80FD\u4F1A\u4F9D\u8D56<strong>\u540C\u4E00\u4E2A\u7B2C\u4E09\u65B9\u7C7B\u5E93\u7684\u4E0D\u540C\u7248\u672C</strong>\uFF0C\u4E0D\u80FD\u8981\u6C42\u540C\u4E00\u4E2A\u7C7B\u5E93\u5728\u540C\u4E00\u4E2A\u670D\u52A1\u5668\u53EA\u6709\u4E00\u4EFD\uFF0C\u56E0\u6B64\u8981\u4FDD\u8BC1\u6BCF\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7684\u7C7B\u5E93\u90FD\u662F\u72EC\u7ACB\u7684\uFF0C\u4FDD\u8BC1\u76F8\u4E92\u9694\u79BB\u3002</p></li><li><p>\u90E8\u7F72\u5728\u540C\u4E00\u4E2Aweb\u5BB9\u5668\u4E2D\u76F8\u540C\u7684\u7C7B\u5E93\u76F8\u540C\u7684\u7248\u672C\u53EF\u4EE5\u5171\u4EAB\u3002\u5426\u5219\uFF0C\u5982\u679C\u670D\u52A1\u5668\u670910\u4E2A\u5E94\u7528\u7A0B\u5E8F\uFF0C\u90A3\u4E48\u8981\u670910\u4EFD\u76F8\u540C\u7684\u7C7B\u5E93\u52A0\u8F7D\u8FDB\u865A\u62DF\u673A\uFF0C\u8FD9\u662F\u626F\u6DE1\u7684\u3002</p></li><li><p>web\u5BB9\u5668\u4E5F\u6709\u81EA\u5DF1\u4F9D\u8D56\u7684\u7C7B\u5E93\uFF0C\u4E0D\u80FD\u4E8E\u5E94\u7528\u7A0B\u5E8F\u7684\u7C7B\u5E93\u6DF7\u6DC6\u3002\u57FA\u4E8E\u5B89\u5168\u8003\u8651\uFF0C\u5E94\u8BE5\u8BA9\u5BB9\u5668\u7684\u7C7B\u5E93\u548C\u7A0B\u5E8F\u7684\u7C7B\u5E93\u9694\u79BB\u5F00\u6765\u3002</p></li><li><p>web\u5BB9\u5668\u8981\u652F\u6301jsp\u7684\u4FEE\u6539\uFF0C\u6211\u4EEC\u77E5\u9053\uFF0Cjsp \u6587\u4EF6\u6700\u7EC8\u4E5F\u662F\u8981\u7F16\u8BD1\u6210class\u6587\u4EF6\u624D\u80FD\u5728\u865A\u62DF\u673A\u4E2D\u8FD0\u884C\uFF0C\u4F46\u7A0B\u5E8F\u8FD0\u884C\u540E\u4FEE\u6539jsp\u5DF2\u7ECF\u662F\u53F8\u7A7A\u89C1\u60EF\u7684\u4E8B\u60C5\uFF0C\u5426\u5219\u8981\u4F60\u4F55\u7528\uFF1F \u6240\u4EE5\uFF0Cweb\u5BB9\u5668\u9700\u8981\u652F\u6301 jsp \u4FEE\u6539\u540E\u4E0D\u7528\u91CD\u542F\u3002</p></li></ol><p>\u518D\u770B\u770B\u6211\u4EEC\u7684\u95EE\u9898\uFF1ATomcat \u5982\u679C\u4F7F\u7528\u9ED8\u8BA4\u7684\u7C7B\u52A0\u8F7D\u673A\u5236\u884C\u4E0D\u884C\uFF1F \u7B54\u6848\u662F\u4E0D\u884C\u7684\u3002\u4E3A\u4EC0\u4E48\uFF1F\u6211\u4EEC\u770B\uFF0C</p><ul><li>\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF0C\u5982\u679C\u4F7F\u7528\u9ED8\u8BA4\u7684\u7C7B\u52A0\u8F7D\u5668\u673A\u5236\uFF0C\u90A3\u4E48\u662F\u65E0\u6CD5\u52A0\u8F7D\u4E24\u4E2A\u76F8\u540C\u7C7B\u5E93\u7684\u4E0D\u540C\u7248\u672C\u7684\uFF0C\u9ED8\u8BA4\u7684\u7D2F\u52A0\u5668\u662F\u4E0D\u7BA1\u4F60\u662F\u4EC0\u4E48\u7248\u672C\u7684\uFF0C\u53EA\u5728\u4E4E\u4F60\u7684\u5168\u9650\u5B9A\u7C7B\u540D\uFF0C\u5E76\u4E14\u53EA\u6709\u4E00\u4EFD\u3002</li><li>\u7B2C\u4E8C\u4E2A\u95EE\u9898\uFF0C\u9ED8\u8BA4\u7684\u7C7B\u52A0\u8F7D\u5668\u662F\u80FD\u591F\u5B9E\u73B0\u7684\uFF0C\u56E0\u4E3A\u4ED6\u7684\u804C\u8D23\u5C31\u662F\u4FDD\u8BC1\u552F\u4E00\u6027\u3002</li><li>\u7B2C\u4E09\u4E2A\u95EE\u9898\u548C\u7B2C\u4E00\u4E2A\u95EE\u9898\u4E00\u6837\u3002</li><li>\u56DB\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u60F3\u6211\u4EEC\u8981\u600E\u4E48\u5B9E\u73B0jsp\u6587\u4EF6\u7684\u70ED\u4FEE\u6539\uFF08\u697C\u4E3B\u8D77\u7684\u540D\u5B57\uFF09\uFF0Cjsp \u6587\u4EF6\u5176\u5B9E\u4E5F\u5C31\u662Fclass\u6587\u4EF6\uFF0C\u90A3\u4E48\u5982\u679C\u4FEE\u6539\u4E86\uFF0C\u4F46\u7C7B\u540D\u8FD8\u662F\u4E00\u6837\uFF0C\u7C7B\u52A0\u8F7D\u5668\u4F1A\u76F4\u63A5\u53D6\u65B9\u6CD5\u533A\u4E2D\u5DF2\u7ECF\u5B58\u5728\u7684\uFF0C\u4FEE\u6539\u540E\u7684jsp\u662F\u4E0D\u4F1A\u91CD\u65B0\u52A0\u8F7D\u7684\u3002\u90A3\u4E48\u600E\u4E48\u529E\u5462\uFF1F\u6211\u4EEC\u53EF\u4EE5\u76F4\u63A5\u5378\u8F7D\u6389\u8FD9jsp\u6587\u4EF6\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u6240\u4EE5\u4F60\u5E94\u8BE5\u60F3\u5230\u4E86\uFF0C\u6BCF\u4E2Ajsp\u6587\u4EF6\u5BF9\u5E94\u4E00\u4E2A\u552F\u4E00\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u5F53\u4E00\u4E2Ajsp\u6587\u4EF6\u4FEE\u6539\u4E86\uFF0C\u5C31\u76F4\u63A5\u5378\u8F7D\u8FD9\u4E2Ajsp\u7C7B\u52A0\u8F7D\u5668\u3002\u91CD\u65B0\u521B\u5EFA\u7C7B\u52A0\u8F7D\u5668\uFF0C\u91CD\u65B0\u52A0\u8F7Djsp\u6587\u4EF6\u3002</li></ul><h3 id="_2-2-tomcat-\u5982\u4F55\u5B9E\u73B0\u81EA\u5DF1\u72EC\u7279\u7684\u7C7B\u52A0\u8F7D\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#_2-2-tomcat-\u5982\u4F55\u5B9E\u73B0\u81EA\u5DF1\u72EC\u7279\u7684\u7C7B\u52A0\u8F7D\u673A\u5236" aria-hidden="true">#</a> 2.2 Tomcat \u5982\u4F55\u5B9E\u73B0\u81EA\u5DF1\u72EC\u7279\u7684\u7C7B\u52A0\u8F7D\u673A\u5236\uFF1F</h3><p>\u6240\u4EE5\uFF0CTomcat \u662F\u600E\u4E48\u5B9E\u73B0\u7684\u5462\uFF1F\u725B\u903C\u7684Tomcat\u56E2\u961F\u5DF2\u7ECF\u8BBE\u8BA1\u597D\u4E86\u3002\u6211\u4EEC\u770B\u770B\u4ED6\u4EEC\u7684\u8BBE\u8BA1\u56FE\uFF1A</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/blogimage-master/img/image-20190930101804101.png" alt="image-20190930101804101"></p><p>\u6211\u4EEC\u770B\u5230\uFF0C\u524D\u97623\u4E2A\u7C7B\u52A0\u8F7D\u548C\u9ED8\u8BA4\u7684\u4E00\u81F4\uFF0CCommonClassLoader\u3001CatalinaClassLoader\u3001SharedClassLoader\u548CWebappClassLoader\u5219\u662FTomcat\u81EA\u5DF1\u5B9A\u4E49\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u5B83\u4EEC\u5206\u522B\u52A0\u8F7D<code>/common/*</code>\u3001<code>/server/*</code>\u3001<code>/shared/*</code>\uFF08\u5728tomcat 6\u4E4B\u540E\u5DF2\u7ECF\u5408\u5E76\u5230\u6839\u76EE\u5F55\u4E0B\u7684lib\u76EE\u5F55\u4E0B\uFF09\u548C<code>/WebApp/WEB-INF/*</code>\u4E2D\u7684Java\u7C7B\u5E93\u3002\u5176\u4E2DWebApp\u7C7B\u52A0\u8F7D\u5668\u548CJsp\u7C7B\u52A0\u8F7D\u5668\u901A\u5E38\u4F1A\u5B58\u5728\u591A\u4E2A\u5B9E\u4F8B\uFF0C<strong>\u6BCF\u4E00\u4E2AWeb\u5E94\u7528\u7A0B\u5E8F</strong>\u5BF9\u5E94\u4E00\u4E2AWebApp\u7C7B\u52A0\u8F7D\u5668\uFF0C\u6BCF\u4E00\u4E2AJSP\u6587\u4EF6\u5BF9\u5E94\u4E00\u4E2AJsp\u7C7B\u52A0\u8F7D\u5668\u3002</p><ul><li>commonLoader\uFF1ATomcat\u6700\u57FA\u672C\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u52A0\u8F7D\u8DEF\u5F84\u4E2D\u7684class\u53EF\u4EE5\u88ABTomcat\u5BB9\u5668\u672C\u8EAB\u4EE5\u53CA\u5404\u4E2AWebapp\u8BBF\u95EE\uFF1B</li><li>catalinaLoader\uFF1ATomcat\u5BB9\u5668\u79C1\u6709\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u52A0\u8F7D\u8DEF\u5F84\u4E2D\u7684class\u5BF9\u4E8EWebapp\u4E0D\u53EF\u89C1\uFF1B</li><li>sharedLoader\uFF1A\u5404\u4E2AWebapp\u5171\u4EAB\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u52A0\u8F7D\u8DEF\u5F84\u4E2D\u7684class\u5BF9\u4E8E\u6240\u6709Webapp\u53EF\u89C1\uFF0C\u4F46\u662F\u5BF9\u4E8ETomcat\u5BB9\u5668\u4E0D\u53EF\u89C1\uFF1B</li><li>WebappClassLoader\uFF1A\u5404\u4E2AWebapp\u79C1\u6709\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u52A0\u8F7D\u8DEF\u5F84\u4E2D\u7684class\u53EA\u5BF9\u5F53\u524DWebapp\u53EF\u89C1\uFF1B</li></ul><p>\u4ECE\u56FE\u4E2D\u7684\u59D4\u6D3E\u5173\u7CFB\u4E2D\u53EF\u4EE5\u770B\u51FA\uFF1A</p><blockquote><p>CommonClassLoader\u80FD\u52A0\u8F7D\u7684\u7C7B\u90FD\u53EF\u4EE5\u88ABCatalina ClassLoader\u548CSharedClassLoader\u4F7F\u7528\uFF0C\u4ECE\u800C\u5B9E\u73B0\u4E86\u516C\u6709\u7C7B\u5E93\u7684\u5171\u7528\uFF0C\u800CCatalinaClassLoader\u548CShared ClassLoader\u81EA\u5DF1\u80FD\u52A0\u8F7D\u7684\u7C7B\u5219\u4E0E\u5BF9\u65B9\u76F8\u4E92\u9694\u79BB\u3002</p></blockquote><blockquote><p>WebAppClassLoader\u53EF\u4EE5\u4F7F\u7528SharedClassLoader\u52A0\u8F7D\u5230\u7684\u7C7B\uFF0C\u4F46\u5404\u4E2AWebAppClassLoader\u5B9E\u4F8B\u4E4B\u95F4\u76F8\u4E92\u9694\u79BB\u3002</p></blockquote><blockquote><p>\u800CJasperLoader\u7684\u52A0\u8F7D\u8303\u56F4\u4EC5\u4EC5\u662F\u8FD9\u4E2AJSP\u6587\u4EF6\u6240\u7F16\u8BD1\u51FA\u6765\u7684\u90A3\u4E00\u4E2A.Class\u6587\u4EF6\uFF0C\u5B83\u51FA\u73B0\u7684\u76EE\u7684\u5C31\u662F\u4E3A\u4E86\u88AB\u4E22\u5F03\uFF1A\u5F53Web\u5BB9\u5668\u68C0\u6D4B\u5230JSP\u6587\u4EF6\u88AB\u4FEE\u6539\u65F6\uFF0C\u4F1A\u66FF\u6362\u6389\u76EE\u524D\u7684JasperLoader\u7684\u5B9E\u4F8B\uFF0C\u5E76\u901A\u8FC7\u518D\u5EFA\u7ACB\u4E00\u4E2A\u65B0\u7684Jsp\u7C7B\u52A0\u8F7D\u5668\u6765\u5B9E\u73B0JSP\u6587\u4EF6\u7684HotSwap\u529F\u80FD\u3002</p></blockquote><p>\u5F88\u663E\u7136\uFF0Ctomcat \u4E0D\u662F\u8FD9\u6837\u5B9E\u73B0\uFF0Ctomcat \u4E3A\u4E86\u5B9E\u73B0\u9694\u79BB\u6027\uFF0C\u6CA1\u6709\u9075\u5B88\u8FD9\u4E2A\u7EA6\u5B9A\uFF0C\u6BCF\u4E2AwebappClassLoader\u52A0\u8F7D\u81EA\u5DF1\u7684\u76EE\u5F55\u4E0B\u7684class\u6587\u4EF6\uFF0C\u4E0D\u4F1A\u4F20\u9012\u7ED9\u7236\u7C7B\u52A0\u8F7D\u5668\u3002</p><h5 id="\u6211\u4EEC\u6269\u5C55\u51FA\u4E00\u4E2A\u95EE\u9898-\u5982\u679Ctomcat-\u7684-common-classloader-\u60F3\u52A0\u8F7D-webapp-classloader-\u4E2D\u7684\u7C7B-\u8BE5\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u6269\u5C55\u51FA\u4E00\u4E2A\u95EE\u9898-\u5982\u679Ctomcat-\u7684-common-classloader-\u60F3\u52A0\u8F7D-webapp-classloader-\u4E2D\u7684\u7C7B-\u8BE5\u600E\u4E48\u529E" aria-hidden="true">#</a> \u6211\u4EEC\u6269\u5C55\u51FA\u4E00\u4E2A\u95EE\u9898\uFF1A\u5982\u679Ctomcat \u7684 Common ClassLoader \u60F3\u52A0\u8F7D WebApp ClassLoader \u4E2D\u7684\u7C7B\uFF0C\u8BE5\u600E\u4E48\u529E\uFF1F</h5><p>\u770B\u4E86\u524D\u9762\u7684\u5173\u4E8E\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u7684\u5185\u5BB9\uFF0C\u6211\u4EEC\u5FC3\u91CC\u6709\u6570\u4E86\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u7C7B\u52A0\u8F7D\u5668\u5B9E\u73B0\uFF0C\u4F7F\u7528\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u52A0\u8F7D\u5668\uFF0C\u53EF\u4EE5\u8BA9\u7236\u7C7B\u52A0\u8F7D\u5668\u8BF7\u6C42\u5B50\u7C7B\u52A0\u8F7D\u5668\u53BB\u5B8C\u6210\u7C7B\u52A0\u8F7D\u7684\u52A8\u4F5C\u3002\u725B\u903C\u5427\u3002</p><h3 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h3>',26),n={href:"https://juejin.im/post/5a59f2296fb9a01ca871eb8c",target:"_blank",rel:"noopener noreferrer"};function m(h,b){const e=c("ExternalLinkIcon");return s(),t("div",null,[i,a("p",null,[a("a",n,[l("\u6DF1\u5165\u7406\u89E3 Tomcat\uFF08\u56DB\uFF09Tomcat \u7C7B\u52A0\u8F7D\u5668\u4E4B\u4E3A\u4F55\u8FDD\u80CC\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B"),r(e)])])])}const g=o(d,[["render",m],["__file","java-jvm-classload-tomcat.html.vue"]]);export{g as default};
