import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as t,f as e}from"./app.adc08052.js";const l={},o=e('<h1 id="\u7C7B\u52A0\u8F7D\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u52A0\u8F7D\u8FC7\u7A0B" aria-hidden="true">#</a> \u7C7B\u52A0\u8F7D\u8FC7\u7A0B</h1><p>Class \u6587\u4EF6\u9700\u8981\u52A0\u8F7D\u5230\u865A\u62DF\u673A\u4E2D\u4E4B\u540E\u624D\u80FD\u8FD0\u884C\u548C\u4F7F\u7528\uFF0C\u90A3\u4E48\u865A\u62DF\u673A\u662F\u5982\u4F55\u52A0\u8F7D\u8FD9\u4E9BClass\u6587\u4EF6\u5462\uFF1F</p><p>\u7CFB\u7EDF\u52A0\u8F7DClass\u7C7B\u578B\u7684\u6587\u4EF6\u4E3B\u8981\u4E09\u6B65\uFF1A\u52A0\u8F7D-&gt;\u8FDE\u63A5 -&gt;\u521D\u59CB\u5316\u3002\u8FDE\u63A5\u8FC7\u7A0B\u53C8\u53EF\u4EE5\u5206\u4E3A\u4E09\u6B65\uFF1A\u9A8C\u8BC1-&gt;\u51C6\u5907-&gt;\u89E3\u6790\u3002</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20190928000659249.png" alt="image-20190928000659249"></p><h2 id="_1-\u52A0\u8F7D" tabindex="-1"><a class="header-anchor" href="#_1-\u52A0\u8F7D" aria-hidden="true">#</a> 1. \u52A0\u8F7D</h2><p>\u7C7B\u52A0\u8F7D\u8FC7\u7A0B\u7684\u7B2C\u4E00\u6B65\uFF0C\u4E3B\u8981\u5B8C\u6210\u4E0B\u97623\u4EF6\u4E8B\u60C5</p><ol><li>\u901A\u8FC7\u5168\u7C7B\u540D\u83B7\u53D6\u5B9A\u4E49\u6B64\u7C7B\u7684\u4E8C\u8FDB\u5236\u5B57\u8282\u6D41\uFF08<strong>\u8FD9\u4E2A\u6B65\u9AA4\u5C31\u662F\u7C7B\u52A0\u8F7D\u5668</strong>\uFF09</li><li>\u5C06\u5B57\u8282\u6D41\u6240\u4EE3\u8868\u7684\u9759\u6001\u5B58\u50A8\u7ED3\u6784\u8F6C\u6362\u4E3A\u65B9\u6CD5\u533A\u7684\u8FD0\u884C\u65F6\u6570\u636E\u7ED3\u6784</li><li>\u5728\u5185\u5B58\u4E2D\u751F\u6210\u4E00\u4E2A\u4EE3\u8868\u8BE5\u7C7B\u7684 Class \u5BF9\u8C61,\u4F5C\u4E3A\u65B9\u6CD5\u533A\u8FD9\u4E9B\u6570\u636E\u7684\u8BBF\u95EE\u5165\u53E3</li></ol><p>\u865A\u62DF\u673A\u89C4\u8303\u591A\u4E0A\u9762\u8FD93\u70B9\u5E76\u4E0D\u5177\u4F53\uFF0C\u56E0\u6B64\u662F\u975E\u5E38\u7075\u6D3B\u7684\u3002\u6BD4\u5982\uFF1A&quot;\u901A\u8FC7\u5168\u7C7B\u540D\u83B7\u53D6\u5B9A\u4E49\u6B64\u7C7B\u7684\u4E8C\u8FDB\u5236\u5B57\u8282\u6D41&quot; \u5E76\u6CA1\u6709\u6307\u660E\u5177\u4F53\u4ECE\u54EA\u91CC\u83B7\u53D6\u3001\u600E\u6837\u83B7\u53D6\u3002\u6BD4\u5982\uFF1A\u6BD4\u8F83\u5E38\u89C1\u7684\u5C31\u662F\u4ECE ZIP \u5305\u4E2D\u8BFB\u53D6\uFF08\u65E5\u540E\u51FA\u73B0\u7684JAR\u3001EAR\u3001WAR\u683C\u5F0F\u7684\u57FA\u7840\uFF09\u3001\u5176\u4ED6\u6587\u4EF6\u751F\u6210\uFF08\u5178\u578B\u5E94\u7528\u5C31\u662FJSP\uFF09\u7B49\u7B49\u3002</p><p><strong>\u4E00\u4E2A\u975E\u6570\u7EC4\u7C7B\u7684\u52A0\u8F7D\u9636\u6BB5\uFF08\u52A0\u8F7D\u9636\u6BB5\u83B7\u53D6\u7C7B\u7684\u4E8C\u8FDB\u5236\u5B57\u8282\u6D41\u7684\u52A8\u4F5C\uFF09\u662F\u53EF\u63A7\u6027\u6700\u5F3A\u7684\u9636\u6BB5\uFF0C\u8FD9\u4E00\u6B65\u6211\u4EEC\u53EF\u4EE5\u53BB\u5B8C\u6210\u8FD8\u53EF\u4EE5\u81EA\u5B9A\u4E49\u7C7B\u52A0\u8F7D\u5668\u53BB\u63A7\u5236\u5B57\u8282\u6D41\u7684\u83B7\u53D6\u65B9\u5F0F\uFF08\u91CD\u5199\u4E00\u4E2A\u7C7B\u52A0\u8F7D\u5668\u7684 loadClass() \u65B9\u6CD5\uFF09\u3002\u6570\u7EC4\u7C7B\u578B\u4E0D\u901A\u8FC7\u7C7B\u52A0\u8F7D\u5668\u521B\u5EFA\uFF0C\u5B83\u7531 Java \u865A\u62DF\u673A\u76F4\u63A5\u521B\u5EFA\u3002</strong></p><p>\u7C7B\u52A0\u8F7D\u5668\u3001\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\u4E5F\u662F\u975E\u5E38\u91CD\u8981\u7684\u77E5\u8BC6\u70B9\uFF0C\u8FD9\u90E8\u5206\u5185\u5BB9\u4F1A\u5728\u540E\u9762\u7684\u6587\u7AE0\u4E2D\u5355\u72EC\u4ECB\u7ECD\u5230\u3002</p><p>\u52A0\u8F7D\u9636\u6BB5\u548C\u8FDE\u63A5\u9636\u6BB5\u7684\u90E8\u5206\u5185\u5BB9\u662F\u4EA4\u53C9\u8FDB\u884C\u7684\uFF0C\u52A0\u8F7D\u9636\u6BB5\u5C1A\u672A\u7ED3\u675F\uFF0C\u8FDE\u63A5\u9636\u6BB5\u53EF\u80FD\u5C31\u5DF2\u7ECF\u5F00\u59CB\u4E86\u3002</p><h2 id="_2-\u8FDE\u63A5" tabindex="-1"><a class="header-anchor" href="#_2-\u8FDE\u63A5" aria-hidden="true">#</a> 2.\u8FDE\u63A5</h2><h3 id="_2-1-\u9A8C\u8BC1" tabindex="-1"><a class="header-anchor" href="#_2-1-\u9A8C\u8BC1" aria-hidden="true">#</a> 2.1 \u9A8C\u8BC1</h3><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20190928001109063.png" alt="image-20190928001109063"></p><h3 id="_2-2-\u51C6\u5907" tabindex="-1"><a class="header-anchor" href="#_2-2-\u51C6\u5907" aria-hidden="true">#</a> 2.2 \u51C6\u5907</h3><p><strong>\u51C6\u5907\u9636\u6BB5\u662F\u6B63\u5F0F\u4E3A\u7C7B\u53D8\u91CF\u5206\u914D\u5185\u5B58\u5E76\u8BBE\u7F6E\u7C7B\u53D8\u91CF\u521D\u59CB\u503C\u7684\u9636\u6BB5</strong>\uFF0C\u8FD9\u4E9B\u5185\u5B58\u90FD\u5C06\u5728\u65B9\u6CD5\u533A\u4E2D\u5206\u914D\u3002\u5BF9\u4E8E\u8BE5\u9636\u6BB5\u6709\u4EE5\u4E0B\u51E0\u70B9\u9700\u8981\u6CE8\u610F\uFF1A</p><ol><li>\u8FD9\u65F6\u5019\u8FDB\u884C\u5185\u5B58\u5206\u914D\u7684\u4EC5\u5305\u62EC\u7C7B\u53D8\u91CF\uFF08static\uFF09\uFF0C\u800C\u4E0D\u5305\u62EC\u5B9E\u4F8B\u53D8\u91CF\uFF0C\u5B9E\u4F8B\u53D8\u91CF\u4F1A\u5728\u5BF9\u8C61\u5B9E\u4F8B\u5316\u65F6\u968F\u7740\u5BF9\u8C61\u4E00\u5757\u5206\u914D\u5728 Java \u5806\u4E2D\u3002</li><li>\u8FD9\u91CC\u6240\u8BBE\u7F6E\u7684\u521D\u59CB\u503C&quot;\u901A\u5E38\u60C5\u51B5&quot;\u4E0B\u662F\u6570\u636E\u7C7B\u578B\u9ED8\u8BA4\u7684\u96F6\u503C\uFF08\u59820\u30010L\u3001null\u3001false\u7B49\uFF09\uFF0C\u6BD4\u5982\u6211\u4EEC\u5B9A\u4E49\u4E86<code>public static int value=111</code> \uFF0C\u90A3\u4E48 value \u53D8\u91CF\u5728\u51C6\u5907\u9636\u6BB5\u7684\u521D\u59CB\u503C\u5C31\u662F 0 \u800C\u4E0D\u662F111\uFF08\u521D\u59CB\u5316\u9636\u6BB5\u624D\u4F1A\u590D\u5236\uFF09\u3002\u7279\u6B8A\u60C5\u51B5\uFF1A\u6BD4\u5982\u7ED9 value \u53D8\u91CF\u52A0\u4E0A\u4E86 fianl \u5173\u952E\u5B57<code>public static final int value=111</code> \uFF0C\u90A3\u4E48\u51C6\u5907\u9636\u6BB5 value \u7684\u503C\u5C31\u88AB\u590D\u5236\u4E3A 111\u3002</li></ol><p><strong>\u57FA\u672C\u6570\u636E\u7C7B\u578B\u7684\u96F6\u503C\uFF1A</strong></p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20190928001409383.png" alt="image-20190928001409383"></p><h3 id="_2-3-\u89E3\u6790" tabindex="-1"><a class="header-anchor" href="#_2-3-\u89E3\u6790" aria-hidden="true">#</a> 2.3 \u89E3\u6790</h3><p>\u89E3\u6790\u9636\u6BB5\u662F\u865A\u62DF<strong>\u673A\u5C06\u5E38\u91CF\u6C60\u5185</strong>\u7684<strong>\u7B26\u53F7\u5F15\u7528</strong>\u66FF\u6362\u4E3A<strong>\u76F4\u63A5\u5F15\u7528</strong>\u7684\u8FC7\u7A0B\u3002</p><blockquote><p>\u89E3\u6790\u52A8\u4F5C\u4E3B\u8981\u9488\u5BF9\u7C7B\u6216\u63A5\u53E3\u3001\u5B57\u6BB5\u3001\u7C7B\u65B9\u6CD5\u3001\u63A5\u53E3\u65B9\u6CD5\u3001\u65B9\u6CD5\u7C7B\u578B\u3001\u65B9\u6CD5\u53E5\u67C4\u548C\u8C03\u7528\u9650\u5B9A\u7B267\u7C7B\u7B26\u53F7\u5F15\u7528\u8FDB\u884C\u3002</p></blockquote><ul><li><p><strong>\u7B26\u53F7\u5F15\u7528\uFF1A</strong></p><p>\u7B26\u53F7\u5F15\u7528\u5C31\u662F\u4E00\u7EC4\u7B26\u53F7\u6765\u63CF\u8FF0\u76EE\u6807\uFF0C\u53EF\u4EE5\u662F\u4EFB\u4F55\u5B57\u9762\u91CF</p></li><li><p>\u76F4\u63A5\u5F15\u7528</p><p><strong>\u76F4\u63A5\u5F15\u7528</strong>\u5C31\u662F\u76F4\u63A5\u6307\u5411\u76EE\u6807\u7684\u6307\u9488\u3001\u76F8\u5BF9\u504F\u79FB\u91CF\u6216\u4E00\u4E2A\u95F4\u63A5\u5B9A\u4F4D\u5230\u76EE\u6807\u7684\u53E5\u67C4</p></li></ul><p>\u5728\u7A0B\u5E8F\u5B9E\u9645\u8FD0\u884C\u65F6\uFF0C\u53EA\u6709\u7B26\u53F7\u5F15\u7528\u662F\u4E0D\u591F\u7684\uFF0C\u4E3E\u4E2A\u4F8B\u5B50\uFF1A\u5728\u7A0B\u5E8F\u6267\u884C\u65B9\u6CD5\u65F6\uFF0C\u7CFB\u7EDF\u9700\u8981\u660E\u786E\u77E5\u9053\u8FD9\u4E2A\u65B9\u6CD5\u6240\u5728\u7684\u4F4D\u7F6E\u3002Java \u865A\u62DF\u673A\u4E3A\u6BCF\u4E2A\u7C7B\u90FD\u51C6\u5907\u4E86\u4E00\u5F20\u65B9\u6CD5\u8868\u6765\u5B58\u653E\u7C7B\u4E2D\u6240\u6709\u7684\u65B9\u6CD5\u3002\u5F53\u9700\u8981\u8C03\u7528\u4E00\u4E2A\u7C7B\u7684\u65B9\u6CD5\u7684\u65F6\u5019\uFF0C\u53EA\u8981\u77E5\u9053\u8FD9\u4E2A\u65B9\u6CD5\u5728\u65B9\u53D1\u8868\u4E2D\u7684\u504F\u79FB\u91CF\u5C31\u53EF\u4EE5\u76F4\u63A5\u8C03\u7528\u8BE5\u65B9\u6CD5\u4E86\u3002\u901A\u8FC7\u89E3\u6790\u64CD\u4F5C\u7B26\u53F7\u5F15\u7528\u5C31\u53EF\u4EE5\u76F4\u63A5\u8F6C\u53D8\u4E3A\u76EE\u6807\u65B9\u6CD5\u5728\u7C7B\u4E2D\u65B9\u6CD5\u8868\u7684\u4F4D\u7F6E\uFF0C\u4ECE\u800C\u4F7F\u5F97\u65B9\u6CD5\u53EF\u4EE5\u88AB\u8C03\u7528\u3002</p><p>\u7EFC\u4E0A\uFF0C<strong>\u89E3\u6790\u9636\u6BB5\u662F\u865A\u62DF\u673A\u5C06\u5E38\u91CF\u6C60\u5185\u7684\u7B26\u53F7\u5F15\u7528\u66FF\u6362\u4E3A\u76F4\u63A5\u5F15\u7528\u7684\u8FC7\u7A0B</strong>\uFF0C\u4E5F\u5C31\u662F\u5F97\u5230\u7C7B\u6216\u8005\u5B57\u6BB5\u3001\u65B9\u6CD5\u5728\u5185\u5B58\u4E2D\u7684\u6307\u9488\u6216\u8005\u504F\u79FB\u91CF\u3002</p><h2 id="_3-\u521D\u59CB\u5316" tabindex="-1"><a class="header-anchor" href="#_3-\u521D\u59CB\u5316" aria-hidden="true">#</a> 3. \u521D\u59CB\u5316</h2><p>\u521D\u59CB\u5316\u662F\u7C7B\u52A0\u8F7D\u7684\u6700\u540E\u4E00\u6B65\uFF0C\u4E5F\u662F\u771F\u6B63\u6267\u884C\u7C7B\u4E2D\u5B9A\u4E49\u7684 Java \u7A0B\u5E8F\u4EE3\u7801(\u5B57\u8282\u7801)\uFF0C\u521D\u59CB\u5316\u9636\u6BB5\u662F<strong>\u6267\u884C\u7C7B\u6784\u9020\u5668 <code>&lt;clinit&gt; ()</code>\u65B9\u6CD5\u7684\u8FC7\u7A0B</strong>\u3002</p><p>\u5BF9\u4E8E<code>&lt;clinit&gt;\uFF08\uFF09</code> \u65B9\u6CD5\u7684\u8C03\u7528\uFF0C\u865A\u62DF\u673A\u4F1A\u81EA\u5DF1\u786E\u4FDD\u5176\u5728\u591A\u7EBF\u7A0B\u73AF\u5883\u4E2D\u7684\u5B89\u5168\u6027\u3002\u56E0\u4E3A <code>&lt;clinit&gt;\uFF08\uFF09</code> \u65B9\u6CD5\u662F\u5E26\u9501\u7EBF\u7A0B\u5B89\u5168\uFF0C\u6240\u4EE5\u5728\u591A\u7EBF\u7A0B\u73AF\u5883\u4E0B\u8FDB\u884C\u7C7B\u521D\u59CB\u5316\u7684\u8BDD\u53EF\u80FD\u4F1A\u5F15\u8D77\u6B7B\u9501\uFF0C\u5E76\u4E14\u8FD9\u79CD\u6B7B\u9501\u5F88\u96BE\u88AB\u53D1\u73B0\u3002</p><p>\u5BF9\u4E8E\u521D\u59CB\u5316\u9636\u6BB5\uFF0C\u865A\u62DF\u673A\u4E25\u683C\u89C4\u8303\u4E86\u6709\u4E14\u53EA\u67095\u4E2D\u60C5\u51B5\u4E0B\uFF0C\u5FC5\u987B\u5BF9\u7C7B\u8FDB\u884C\u521D\u59CB\u5316\uFF1A</p><ol><li>\u5F53\u9047\u5230 new \u3001 getstatic\u3001putstatic\u6216invokestatic \u8FD94\u6761\u76F4\u63A5\u7801\u6307\u4EE4\u65F6\uFF0C\u6BD4\u5982 new \u4E00\u4E2A\u7C7B\uFF0C\u8BFB\u53D6\u4E00\u4E2A\u9759\u6001\u5B57\u6BB5(\u672A\u88AB final \u4FEE\u9970)\u3001\u6216\u8C03\u7528\u4E00\u4E2A\u7C7B\u7684\u9759\u6001\u65B9\u6CD5\u65F6\u3002</li><li>\u4F7F\u7528 <code>java.lang.reflect</code> \u5305\u7684\u65B9\u6CD5\u5BF9\u7C7B\u8FDB\u884C\u53CD\u5C04\u8C03\u7528\u65F6 \uFF0C\u5982\u679C\u7C7B\u6CA1\u521D\u59CB\u5316\uFF0C\u9700\u8981\u89E6\u53D1\u5176\u521D\u59CB\u5316\u3002</li><li>\u521D\u59CB\u5316\u4E00\u4E2A\u7C7B\uFF0C\u5982\u679C\u5176\u7236\u7C7B\u8FD8\u672A\u521D\u59CB\u5316\uFF0C\u5219\u5148\u89E6\u53D1\u8BE5\u7236\u7C7B\u7684\u521D\u59CB\u5316\u3002</li><li>\u5F53\u865A\u62DF\u673A\u542F\u52A8\u65F6\uFF0C\u7528\u6237\u9700\u8981\u5B9A\u4E49\u4E00\u4E2A\u8981\u6267\u884C\u7684\u4E3B\u7C7B (\u5305\u542B main \u65B9\u6CD5\u7684\u90A3\u4E2A\u7C7B)\uFF0C\u865A\u62DF\u673A\u4F1A\u5148\u521D\u59CB\u5316\u8FD9\u4E2A\u7C7B\u3002</li><li>\u5F53\u4F7F\u7528 JDK1.7 \u7684\u52A8\u6001\u52A8\u6001\u8BED\u8A00\u65F6\uFF0C\u5982\u679C\u4E00\u4E2A MethodHandle \u5B9E\u4F8B\u7684\u6700\u540E\u89E3\u6790\u7ED3\u6784\u4E3A REF_getStatic\u3001REF_putStatic\u3001REF_invokeStatic\u3001\u7684\u65B9\u6CD5\u53E5\u67C4\uFF0C\u5E76\u4E14\u8FD9\u4E2A\u53E5\u67C4\u6CA1\u6709\u521D\u59CB\u5316\uFF0C\u5219\u9700\u8981\u5148\u89E6\u53D1\u5668\u521D\u59CB\u5316\u3002</li></ol><h2 id="_4-\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#_4-\u603B\u7ED3" aria-hidden="true">#</a> 4. \u603B\u7ED3</h2><p>\u7C7B\u52A0\u8F7D\u4E3B\u8981\u5305\u542B\u4E09\u4E2A\u6B65\u9AA4\uFF1A\u52A0\u8F7D-&gt;\u8FDE\u63A5-&gt;\u521D\u59CB\u5316\uFF0C\u8FDE\u63A5\u53C8\u53EF\u4EE5\u5206\u4E3A\uFF1A\u9A8C\u8BC1-&gt;\u51C6\u5907-\u89E3\u6790\u3002</p><ul><li><p>\u52A0\u8F7D\uFF1A</p><ul><li>\u901A\u8FC7\u5168\u7C7B\u540D\u83B7\u53D6\u6B64\u7C7B\u4E8C\u8FDB\u5236\u5B57\u8282\u6D41</li><li>\u5C06\u5B57\u8282\u6D41\u9759\u6001\u5B58\u50A8\u7ED3\u6784\u8F6C\u4E3A\u65B9\u6CD5\u533A\u8FD0\u884C\u65F6\u6570\u636E\u7ED3\u6784</li><li>\u5185\u5B58\u751F\u6210\u4E00\u4E2A\u4EE3\u8868\u8BE5\u7C7B\u7684class\u5BF9\u8C61\uFF0C\u4F5C\u4E3A\u65B9\u6CD5\u533A\u8FD9\u4E9B\u6570\u636E\u7684\u8BBF\u95EE\u5165\u53E3</li></ul></li><li><p>\u8FDE\u63A5</p><ul><li><p>\u9A8C\u8BC1</p></li><li><p>\u51C6\u5907</p><p>\u4E3A\u7C7B\u53D8\u91CF\uFF08static\uFF09\u5206\u914D\u5185\u5B58\uFF0C\u8BBE\u7F6E\u521D\u59CB\u5316\u503C\uFF08\u662F0\uFF0C\u4E0D\u662F111\uFF09</p></li><li><p>\u89E3\u6790</p><p>\u5C06\u5E38\u91CF\u6C60\u7684\u7B26\u53F7\u5F15\u7528\u66FF\u6362\u4E3A\u76F4\u63A5\u5F15\u7528</p></li></ul></li><li><p>\u521D\u59CB\u5316</p><p>\u6267\u884C\u7C7B\u6784\u9020\u5668</p></li></ul>',33),n=[o];function p(s,r){return i(),t("div",null,n)}const h=a(l,[["render",p],["__file","jvm.html.vue"]]);export{h as default};
