import{_ as s}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as a,a as e,b as n,d as r,e as i,r as l}from"./app.b2391a7a.js";const c={},p=i(`<p><strong>\u6587\u4EF6\u7684IO\u5C31\u544A\u4E00\u6BB5\u843D\u4E86</strong>\uFF0C\u6211\u4EEC\u6765\u5B66\u4E60\u7F51\u7EDC\u4E2D\u7684IO~~~\u4E3A\u4E86\u66F4\u597D\u5730\u7406\u89E3NIO\uFF0C<strong>\u6211\u4EEC\u5148\u6765\u5B66\u4E60\u4E00\u4E0BIO\u7684\u6A21\u578B</strong>~</p><p>\u6839\u636EUNIX\u7F51\u7EDC\u7F16\u7A0B\u5BF9I/O\u6A21\u578B\u7684\u5206\u7C7B\uFF0C<strong>\u5728UNIX\u53EF\u4EE5\u5F52\u7EB3\u62105\u79CDI/O\u6A21\u578B</strong>\uFF1A</p><ul><li><strong>\u963B\u585EI/O</strong></li><li><strong>\u975E\u963B\u585EI/O</strong></li><li><strong>I/O\u591A\u8DEF\u590D\u7528</strong></li><li>\u4FE1\u53F7\u9A71\u52A8I/O</li><li>\u5F02\u6B65I/O</li></ul><h2 id="\u5B66\u4E60i-o\u6A21\u578B\u9700\u8981\u7684\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60i-o\u6A21\u578B\u9700\u8981\u7684\u57FA\u7840" aria-hidden="true">#</a> \u5B66\u4E60I/O\u6A21\u578B\u9700\u8981\u7684\u57FA\u7840</h2><h3 id="\u6587\u4EF6\u63CF\u8FF0\u7B26" tabindex="-1"><a class="header-anchor" href="#\u6587\u4EF6\u63CF\u8FF0\u7B26" aria-hidden="true">#</a> \u6587\u4EF6\u63CF\u8FF0\u7B26</h3><p>Linux \u7684\u5185\u6838\u5C06\u6240\u6709\u5916\u90E8\u8BBE\u5907<strong>\u90FD\u770B\u505A\u4E00\u4E2A\u6587\u4EF6\u6765\u64CD\u4F5C</strong>\uFF0C\u5BF9\u4E00\u4E2A\u6587\u4EF6\u7684\u8BFB\u5199\u64CD\u4F5C\u4F1A<strong>\u8C03\u7528\u5185\u6838\u63D0\u4F9B\u7684\u7CFB\u7EDF\u547D\u4EE4(api)</strong>\uFF0C\u8FD4\u56DE\u4E00\u4E2A<code>file descriptor</code>\uFF08fd\uFF0C\u6587\u4EF6\u63CF\u8FF0\u7B26\uFF09\u3002\u800C\u5BF9\u4E00\u4E2Asocket\u7684\u8BFB\u5199\u4E5F\u4F1A\u6709\u54CD\u5E94\u7684\u63CF\u8FF0\u7B26\uFF0C\u79F0\u4E3A<code>socket fd</code>\uFF08socket\u6587\u4EF6\u63CF\u8FF0\u7B26\uFF09\uFF0C\u63CF\u8FF0\u7B26\u5C31\u662F\u4E00\u4E2A\u6570\u5B57\uFF0C<strong>\u6307\u5411\u5185\u6838\u4E2D\u7684\u4E00\u4E2A\u7ED3\u6784\u4F53</strong>\uFF08\u6587\u4EF6\u8DEF\u5F84\uFF0C\u6570\u636E\u533A\u7B49\u4E00\u4E9B\u5C5E\u6027\uFF09\u3002</p><ul><li>\u6240\u4EE5\u8BF4\uFF1A\u5728Linux\u4E0B\u5BF9\u6587\u4EF6\u7684\u64CD\u4F5C\u662F<strong>\u5229\u7528\u6587\u4EF6\u63CF\u8FF0\u7B26(file descriptor)\u6765\u5B9E\u73B0\u7684</strong>\u3002</li></ul><h3 id="\u7528\u6237\u7A7A\u95F4\u548C\u5185\u6838\u7A7A\u95F4" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u7A7A\u95F4\u548C\u5185\u6838\u7A7A\u95F4" aria-hidden="true">#</a> \u7528\u6237\u7A7A\u95F4\u548C\u5185\u6838\u7A7A\u95F4</h3><p>\u4E3A\u4E86\u4FDD\u8BC1\u7528\u6237\u8FDB\u7A0B\u4E0D\u80FD\u76F4\u63A5\u64CD\u4F5C\u5185\u6838\uFF08kernel\uFF09\uFF0C<strong>\u4FDD\u8BC1\u5185\u6838\u7684\u5B89\u5168</strong>\uFF0C\u64CD\u5FC3\u7CFB\u7EDF\u5C06\u865A\u62DF\u7A7A\u95F4\u5212\u5206\u4E3A\u4E24\u90E8\u5206</p><ul><li><strong>\u4E00\u90E8\u5206\u4E3A\u5185\u6838\u7A7A\u95F4</strong>\u3002</li><li><strong>\u4E00\u90E8\u5206\u4E3A\u7528\u6237\u7A7A\u95F4</strong>\u3002</li></ul><h3 id="i-o\u8FD0\u884C\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#i-o\u8FD0\u884C\u8FC7\u7A0B" aria-hidden="true">#</a> I/O\u8FD0\u884C\u8FC7\u7A0B</h3><p>\u6211\u4EEC\u6765\u770B\u770BIO\u5728\u7CFB\u7EDF\u4E2D\u7684\u8FD0\u884C\u662F\u600E\u4E48\u6837\u7684(\u6211\u4EEC<strong>\u4EE5read\u4E3A\u4F8B</strong>)</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nio/moxing-54ee4738-b689-4026-863f-13e456b374de.jpg" alt=""></p><p>\u53EF\u4EE5\u53D1\u73B0\u7684\u662F\uFF1A\u5F53\u5E94\u7528\u7A0B\u5E8F\u8C03\u7528read\u65B9\u6CD5\u65F6\uFF0C\u662F\u9700\u8981<strong>\u7B49\u5F85</strong>\u7684---&gt;\u4ECE\u5185\u6838\u7A7A\u95F4\u4E2D\u627E\u6570\u636E\uFF0C\u518D\u5C06\u5185\u6838\u7A7A\u95F4\u7684\u6570\u636E\u62F7\u8D1D\u5230\u7528\u6237\u7A7A\u95F4\u7684\u3002</p><ul><li><strong>\u8FD9\u4E2A\u7B49\u5F85\u662F\u5FC5\u8981\u7684\u8FC7\u7A0B</strong>\uFF01</li></ul><p>\u4E0B\u9762\u53EA\u8BB2\u89E3\u7528\u5F97\u6700\u591A\u76843\u4E2AI/0\u6A21\u578B\uFF1A</p><ul><li><strong>\u963B\u585EI/O</strong></li><li><strong>\u975E\u963B\u585EI/O</strong></li><li><strong>I/O\u591A\u8DEF\u590D\u7528</strong></li></ul><h2 id="\u963B\u585Ei-o\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u963B\u585Ei-o\u6A21\u578B" aria-hidden="true">#</a> \u963B\u585EI/O\u6A21\u578B</h2><p>\u5728\u8FDB\u7A0B(\u7528\u6237)\u7A7A\u95F4\u4E2D\u8C03\u7528<code>recvfrom</code>\uFF0C\u5176\u7CFB\u7EDF\u8C03\u7528\u76F4\u5230\u6570\u636E\u5305\u5230\u8FBE\u4E14<strong>\u88AB\u590D\u5236\u5230\u5E94\u7528\u8FDB\u7A0B\u7684\u7F13\u51B2\u533A\u4E2D\u6216\u8005\u53D1\u751F\u9519\u8BEF\u65F6\u624D\u8FD4\u56DE</strong>\uFF0C\u5728\u6B64\u671F\u95F4<strong>\u4E00\u76F4\u7B49\u5F85</strong>\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nio/moxing-8a1cb207-6c56-4bd8-8489-c21d5a76e1ca.jpg" alt=""></p><h2 id="\u975E\u963B\u585Ei-o\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u975E\u963B\u585Ei-o\u6A21\u578B" aria-hidden="true">#</a> \u975E\u963B\u585EI/O\u6A21\u578B</h2><p><code>recvfrom</code>\u4ECE\u5E94\u7528\u5C42\u5230\u5185\u6838\u7684\u65F6\u5019\uFF0C\u5982\u679C\u6CA1\u6709\u6570\u636E\u5C31<strong>\u76F4\u63A5\u8FD4\u56DE</strong>\u4E00\u4E2AEWOULDBLOCK\u9519\u8BEF\uFF0C\u4E00\u822C\u90FD\u5BF9\u975E\u963B\u585EI/O\u6A21\u578B<strong>\u8FDB\u884C\u8F6E\u8BE2\u68C0\u67E5\u8FD9\u4E2A\u72B6\u6001</strong>\uFF0C\u770B\u5185\u6838\u662F\u4E0D\u662F\u6709\u6570\u636E\u5230\u6765\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nio/moxing-6590a3de-0e7c-4ce2-aa1c-815625095e62.jpg" alt=""></p><h2 id="i-o\u590D\u7528\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#i-o\u590D\u7528\u6A21\u578B" aria-hidden="true">#</a> I/O\u590D\u7528\u6A21\u578B</h2><p>\u524D\u9762\u4E5F\u5DF2\u7ECF\u8BF4\u4E86\uFF1A\u5728Linux\u4E0B\u5BF9\u6587\u4EF6\u7684\u64CD\u4F5C\u662F<strong>\u5229\u7528\u6587\u4EF6\u63CF\u8FF0\u7B26(file descriptor)\u6765\u5B9E\u73B0\u7684</strong>\u3002</p><p>\u5728Linux\u4E0B\u5B83\u662F\u8FD9\u6837\u5B50\u5B9E\u73B0I/O\u590D\u7528\u6A21\u578B\u7684\uFF1A</p><ul><li>\u8C03\u7528<code>select/poll/epoll/pselect</code>\u5176\u4E2D\u4E00\u4E2A\u51FD\u6570\uFF0C<strong>\u4F20\u5165\u591A\u4E2A\u6587\u4EF6\u63CF\u8FF0\u7B26</strong>\uFF0C\u5982\u679C\u6709\u4E00\u4E2A\u6587\u4EF6\u63CF\u8FF0\u7B26<strong>\u5C31\u7EEA\uFF0C\u5219\u8FD4\u56DE</strong>\uFF0C\u5426\u5219\u963B\u585E\u76F4\u5230\u8D85\u65F6\u3002</li></ul><p>\u6BD4\u5982<code>poll()</code>\u51FD\u6570\u662F\u8FD9\u6837\u5B50\u7684\uFF1A<code>int poll(struct pollfd *fds,nfds_t nfds, int timeout);</code></p><p>\u5176\u4E2D <code>pollfd</code> \u7ED3\u6784\u5B9A\u4E49\u5982\u4E0B\uFF1A</p><div class="language-c ext-c line-numbers-mode"><pre class="language-c"><code><span class="token keyword">struct</span> <span class="token class-name">pollfd</span> <span class="token punctuation">{</span>
    <span class="token keyword">int</span> fd<span class="token punctuation">;</span>         <span class="token comment">/* \u6587\u4EF6\u63CF\u8FF0\u7B26 */</span>
    <span class="token keyword">short</span> events<span class="token punctuation">;</span>         <span class="token comment">/* \u7B49\u5F85\u7684\u4E8B\u4EF6 */</span>
    <span class="token keyword">short</span> revents<span class="token punctuation">;</span>       <span class="token comment">/* \u5B9E\u9645\u53D1\u751F\u4E86\u7684\u4E8B\u4EF6 */</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nio/moxing-aec90e84-33c5-4f5b-997e-8db54d6bce88.jpg" alt=""></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nio/moxing-62def8ad-3ca3-467b-81f6-5d0a31dd7fdc.jpg" alt=""></p><ul><li>\uFF081\uFF09\u5F53\u7528\u6237\u8FDB\u7A0B\u8C03\u7528\u4E86select\uFF0C\u90A3\u4E48\u6574\u4E2A\u8FDB\u7A0B\u4F1A\u88ABblock\uFF1B</li><li>\uFF082\uFF09\u800C\u540C\u65F6\uFF0Ckernel\u4F1A\u201C\u76D1\u89C6\u201D\u6240\u6709select\u8D1F\u8D23\u7684socket\uFF1B</li><li>\uFF083\uFF09\u5F53\u4EFB\u4F55\u4E00\u4E2Asocket\u4E2D\u7684\u6570\u636E\u51C6\u5907\u597D\u4E86\uFF0Cselect\u5C31\u4F1A\u8FD4\u56DE\uFF1B</li><li>\uFF084\uFF09\u8FD9\u4E2A\u65F6\u5019\u7528\u6237\u8FDB\u7A0B\u518D\u8C03\u7528read\u64CD\u4F5C\uFF0C\u5C06\u6570\u636E\u4ECEkernel\u62F7\u8D1D\u5230\u7528\u6237\u8FDB\u7A0B(\u7A7A\u95F4)\u3002</li><li>\u6240\u4EE5\uFF0CI/O \u591A\u8DEF\u590D\u7528\u7684\u7279\u70B9\u662F<strong>\u901A\u8FC7\u4E00\u79CD\u673A\u5236\u4E00\u4E2A\u8FDB\u7A0B\u80FD\u540C\u65F6\u7B49\u5F85\u591A\u4E2A\u6587\u4EF6\u63CF\u8FF0\u7B26</strong>\uFF0C\u800C\u8FD9\u4E9B\u6587\u4EF6\u63CF\u8FF0\u7B26<strong>\u5176\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\u8FDB\u5165\u8BFB\u5C31\u7EEA\u72B6\u6001</strong>\uFF0Cselect()\u51FD\u6570<strong>\u5C31\u53EF\u4EE5\u8FD4\u56DE</strong>\u3002</li></ul><p>select/epoll\u7684\u4F18\u52BF\u5E76\u4E0D\u662F\u5BF9\u4E8E\u5355\u4E2A\u8FDE\u63A5\u80FD\u5904\u7406\u5F97\u66F4\u5FEB\uFF0C\u800C\u662F<strong>\u5728\u4E8E\u80FD\u5904\u7406\u66F4\u591A\u7684\u8FDE\u63A5</strong>\u3002</p><h2 id="i-o\u6A21\u578B\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#i-o\u6A21\u578B\u603B\u7ED3" aria-hidden="true">#</a> I/O\u6A21\u578B\u603B\u7ED3</h2><p>\u6B63\u7ECF\u7684\u63CF\u8FF0\u90FD\u5728\u4E0A\u9762\u7ED9\u51FA\u4E86\uFF0C\u4E0D\u77E5\u9053\u5927\u5BB6\u7406\u89E3\u4E86\u6CA1\u6709\u3002\u4E0B\u9762\u6211\u4E3E\u51E0\u4E2A\u4F8B\u5B50\u603B\u7ED3\u4E00\u4E0B\u8FD9\u4E09\u79CD\u6A21\u578B\uFF1A</p><p><strong>\u963B\u585EI/O\uFF1A</strong></p><ul><li><p>Java3y\u8DDF\u5973\u670B\u53CB\u53BB\u4E70\u559C\u8336\uFF0C\u6392\u4E86\u5F88\u4E45\u7684\u961F\u7EC8\u4E8E\u53EF\u4EE5\u70B9\u996E\u6599\u4E86\u3002\u6211\u8981\u7EFF\u7814\uFF0C\u8C22\u8C22\u3002\u53EF\u662F\u559C\u8336\u4E0D\u662F\u70B9\u4E86\u5355\u5C31\u80FD\u7ACB\u5373\u62FF\uFF0C\u4E8E\u662F\u6211<strong>\u5728\u559C\u8336\u95E8\u53E3\u7B49\u4E86\u4E00\u5C0F\u65F6\u624D\u62FF\u5230</strong>\u7EFF\u7814\u3002</p></li><li><p>\u5728\u95E8\u53E3\u5E72\u7B49\u4E00\u5C0F\u65F6</p></li></ul><p><strong>\u975E\u963B\u585EI/O\uFF1A</strong></p><ul><li><p>Java3y\u8DDF\u5973\u670B\u53CB\u53BB\u4E70\u4E00\u70B9\u70B9\uFF0C\u6392\u4E86\u5F88\u4E45\u7684\u961F\u7EC8\u4E8E\u53EF\u4EE5\u70B9\u996E\u6599\u4E86\u3002\u6211\u8981\u6CE2\u9738\u5976\u8336\uFF0C\u8C22\u8C22\u3002\u53EF\u662F\u4E00\u70B9\u70B9\u4E0D\u662F\u70B9\u4E86\u5355\u5C31\u80FD\u7ACB\u5373\u62FF\uFF0C<strong>\u540C\u65F6</strong>\u670D\u52A1\u5458\u544A\u8BC9\u6211\uFF1A\u4F60\u5927\u6982\u8981\u7B49\u534A\u5C0F\u65F6\u54E6\u3002\u4F60\u4EEC\u5148\u53BB\u901B\u901B\u5427~\u4E8E\u662FJava3y\u8DDF\u5973\u670B\u53CB\u53BB\u73A9\u4E86\u51E0\u628A\u6597\u5730\u4E3B\uFF0C\u611F\u89C9\u65F6\u95F4\u5DEE\u4E0D\u591A\u4E86\u3002\u4E8E\u662F<strong>\u53C8\u53BB\u4E00\u70B9\u70B9\u95EE</strong>\uFF1A\u8BF7\u95EE\u5230\u6211\u4E86\u5417\uFF1F\u6211\u7684\u5355\u53F7\u662Fxxx\u3002\u670D\u52A1\u5458\u544A\u8BC9Java3y\uFF1A\u8FD8\u6CA1\u5230\u5462\uFF0C\u73B0\u5728\u7684\u5355\u53F7\u662FXXX\uFF0C\u4F60\u8FD8\u8981\u7B49\u4E00\u4F1A\uFF0C\u53EF\u4EE5\u53BB\u9644\u8FD1\u800D\u800D\u3002\u95EE\u4E86\u597D\u51E0\u6B21\u540E\uFF0C\u7EC8\u4E8E\u62FF\u5230\u6211\u7684\u6CE2\u9738\u5976\u8336\u4E86\u3002</p></li><li><p>\u53BB\u901B\u4E86\u4E0B\u8857\u3001\u6597\u4E86\u4E0B\u5730\u4E3B\uFF0C\u65F6\u4E0D\u65F6\u95EE\u95EE\u5230\u6211\u4E86\u6CA1\u6709</p></li></ul><p><strong>I/O\u590D\u7528\u6A21\u578B\uFF1A</strong></p><ul><li><p>Java3y\u8DDF\u5973\u670B\u53CB\u53BB\u9EA6\u5F53\u52B3\u5403\u6C49\u5821\u5305\uFF0C\u73B0\u5728\u5C31\u5389\u5BB3\u4E86\u53EF\u4EE5\u4F7F\u7528\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u70B9\u9910\u4E86\u3002\u4E8E\u662F\u8DDF\u5973\u670B\u53CB\u627E\u4E86\u4E2A\u5730\u65B9\u5750\u4E0B\u5C31\u7528\u5C0F\u7A0B\u5E8F\u70B9\u9910\u4E86\u3002\u70B9\u9910\u4E86\u4E4B\u540E\u73A9\u73A9\u6597\u5730\u4E3B\u3001\u804A\u804A\u5929\u4EC0\u4E48\u7684\u3002<strong>\u65F6\u4E0D\u65F6\u542C\u5230\u5E7F\u64AD\u5728\u590D\u8FF0XXX\u8BF7\u53D6\u9910</strong>\uFF0C\u53CD\u6B63\u6211\u7684\u5355\u53F7\u8FD8\u6CA1\u5230\uFF0C\u5C31\u7EE7\u7EED\u73A9\u5457\u3002~~<strong>\u7B49\u542C\u5230\u5E7F\u64AD\u7684\u65F6\u5019\u518D\u53D6\u9910\u5C31\u662F\u4E86</strong>\u3002\u65F6\u95F4\u8FC7\u5F97\u633A\u5FEB\u7684\uFF0C\u6B64\u65F6\u4F20\u6765\uFF1AJava3y\u8BF7\u8FC7\u6765\u53D6\u9910\u3002\u4E8E\u662F\u6211\u5C31\u80FD\u62FF\u5230\u6211\u7684\u9EA6\u8FA3\u9E21\u7FC5\u6C49\u5821\u4E86\u3002</p></li><li><p>\u542C\u5E7F\u64AD\u53D6\u9910\uFF0C<strong>\u5E7F\u64AD\u4E0D\u662F\u4E3A\u6211\u4E00\u4E2A\u4EBA\u670D\u52A1</strong>\u3002\u5E7F\u64AD\u558A\u5230\u6211\u4E86\uFF0C\u6211\u8FC7\u53BB\u53D6\u5C31Ok\u4E86\u3002</p></li></ul>`,42),d={href:"https://www.zhihu.com/question/29005375/answer/667616386",target:"_blank",rel:"noopener noreferrer"},g=e("hr",null,null,-1);function u(h,m){const t=l("ExternalLinkIcon");return o(),a("div",null,[p,e("blockquote",null,[e("p",null,[n("\u53C2\u8003\u94FE\u63A5\uFF1A"),e("a",d,[n("https://www.zhihu.com/question/29005375/answer/667616386"),r(t)]),n("\uFF0C\u6574\u7406\uFF1Amusk")])]),g])}const v=s(c,[["render",u],["__file","moxing.html.vue"]]);export{v as default};
