import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as o,a as p,b as e,d as n,e as s,r as h}from"./app.b2391a7a.js";const i={},c=s('<h2 id="\u6389\u5355\u5F02\u5E38" tabindex="-1"><a class="header-anchor" href="#\u6389\u5355\u5F02\u5E38" aria-hidden="true">#</a> \u6389\u5355\u5F02\u5E38</h2><p>\u4E00\u4E2A\u6700\u5E38\u89C1\u7684\u652F\u4ED8\u5E73\u53F0\u67B6\u6784\u5173\u7CFB\u5982\u4E0B\u6240\u793A\uFF1A</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859085-1227418818.jpg" alt=""></p><blockquote><p>\u4E0A\u56FE\u6211\u4EEC\u662F\u7AD9\u5728\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u652F\u4ED8\u89D2\u5EA6\uFF0C\u5982\u679C\u662F\u81EA\u5DF1\u516C\u53F8\u7684\u5185\u90E8\u652F\u4ED8\u7CFB\u7EDF\uFF0C\u90A3\u4E48\u5916\u90E8\u5546\u6237\u8FD9\u4E00\u5757\u5176\u5B9E\u5C31\u662F\u516C\u53F8\u5185\u90E8\u4E00\u4E9B\u7CFB\u7EDF\uFF0C\u6BD4\u5982\u8BF4\u8BA2\u5355\u7CFB\u7EDF\uFF0C\u800C\u5916\u90E8\u652F\u4ED8\u6E20\u9053\u5176\u5B9E\u5C31\u662F\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8</p></blockquote><p>\u6211\u4EEC\u4EE5\u643A\u7A0B\u4E3A\u4F8B\uFF0C\u5728\u5176\u4E0A\u9762\u53D1\u8D77\u4E00\u7B14\u8BA2\u5355\u652F\u4ED8\uFF0C\u5C06\u4F1A\u7ECF\u8FC7\u4E09\u4E2A\u7CFB\u7EDF\uFF1A</p><ol><li>\u643A\u7A0B\u521B\u5EFA\u8BA2\u5355\uFF0C\u5411\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u53D1\u8D77\u652F\u4ED8\u8BF7\u6C42</li><li>\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u521B\u5EFA\u8BA2\u5355\uFF0C\u5E76\u5411\u5DE5\u884C\u53D1\u8D77\u652F\u4ED8\u8BF7\u6C42</li><li>\u5DE5\u884C\u5B8C\u6210\u6263\u6B3E\u64CD\u4F5C\uFF0C\u8FD4\u56DE\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8</li><li>\u7B2C\u4E09\u65B9\u652F\u4ED8\u5B8C\u6210\u8BA2\u5355\u66F4\u65B0\u5E76\u8FD4\u56DE\u643A\u7A0B</li><li>\u643A\u7A0B\u53D8\u66F4\u8BA2\u5355\u72B6\u6001</li></ol><p>\u4E0A\u9762\u7684\u6D41\u7A0B\uFF0C\u7B80\u5355\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859313-868801941.jpg" alt=""></p><p>\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u53EF\u80FD\u4F1A\u78B0\u5230\uFF0C\u7528\u6237\u5DE5\u884C\u5361\u5DF2\u7ECF\u6263\u6B3E\uFF0C\u4F46\u662F\u643A\u7A0B\u8BA2\u5355\u5374\u8FD8\u662F\u5F85\u652F\u4ED8\uFF0C\u6211\u4EEC\u901A\u5E38\u5C06\u8FD9\u79CD\u60C5\u51B5\u79F0\u4E3A<strong>\u6389\u5355</strong>\u3002</p><p>\u4E0A\u8FF0\u6389\u5355\u7684\u573A\u666F\uFF0C\u591A\u6570\u662F\u56E0\u4E3A<strong>\u2462\u3001\u2464</strong>\u73AF\u8282\u4FE1\u606F\u4E22\u5931\u5BFC\u81F4\uFF0C\u8FD9\u79CD\u6389\u5355\u6211\u4EEC\u5C06\u5176\u79F0\u4E3A<strong>\u5916\u90E8\u6389\u5355</strong>\u3002</p><p>\u8FD8\u6709\u4E00\u79CD\u6781\u5C11\u6570\u7684\u60C5\u51B5\uFF0C\u6536\u5230 <strong>\u2462\u3001\u2464</strong>\u73AF\u8282\u8FD4\u56DE\u4FE1\u606F\uFF0C\u4F46\u662F\u5728<strong>\u2463\u3001\u2465</strong>\u73AF\u8282\u5185\u90E8\u7CFB\u7EDF\u66F4\u65B0\u8BA2\u5355\u72B6\u6001\u5931\u8D25\uFF0C\u4ECE\u800C\u5BFC\u81F4\u4E22\u5931\u652F\u4ED8\u6210\u529F\u7684\u4FE1\u606F\uFF0C\u8FD9\u7C7B\u6389\u5355\u7531\u4E8E\u662F\u5185\u90E8\u95EE\u9898\uFF0C\u6211\u4EEC\u901A\u5E38\u5C06\u5176\u79F0\u4E4B\u4E3A<strong>\u5185\u90E8\u6389\u5355</strong>\u3002</p><h2 id="\u5916\u90E8\u6389\u5355" tabindex="-1"><a class="header-anchor" href="#\u5916\u90E8\u6389\u5355" aria-hidden="true">#</a> \u5916\u90E8\u6389\u5355</h2><p>\u5916\u90E8\u6389\u5355\u662F\u56E0\u4E3A\u6CA1\u6709\u6536\u5230\u5BF9\u7AEF\u8FD4\u56DE\u4FE1\u606F\uFF0C\u8FD9\u79CD\u60C5\u51B5\u6781\u6709\u53EF\u80FD\u662F\u7F51\u7EDC\u95EE\u9898\uFF0C\u4E5F\u6709\u53EF\u80FD\u5BF9\u7AEF\u5904\u7406\u903B\u8F91\u592A\u6162\uFF0C\u5BFC\u81F4\u6211\u65B9\u8BF7\u6C42\u8D85\u65F6\uFF0C\u76F4\u63A5\u65AD\u5F00\u4E86\u7F51\u7EDC\u8BF7\u6C42\u3002</p><h3 id="\u589E\u52A0\u8D85\u65F6\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#\u589E\u52A0\u8D85\u65F6\u65F6\u95F4" aria-hidden="true">#</a> \u589E\u52A0\u8D85\u65F6\u65F6\u95F4</h3><p>\u5BF9\u4E8E\u8FD9\u79CD\u60C5\u51B5\uFF0C\u7B2C\u4E00\u4E2A\u6700\u7B80\u5355\u7684\u89E3\u51B3\u529E\u6CD5\uFF0C<strong>\u9002\u5F53\u7684\u589E\u52A0\u8D85\u65F6\u65F6\u95F4</strong>\u3002</p><p>\u4E0D\u8FC7\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u4E86\uFF0C\u5728\u6211\u4EEC\u589E\u52A0\u7F51\u7EDC\u8D85\u65F6\u65F6\u95F4\u4E4B\u540E\uFF0C\u6211\u4EEC\u53EF\u80FD\u8FD8\u9700\u8981\u8C03\u6574\u6574\u4E2A\u94FE\u8DEF\u7684\u8D85\u65F6\u65F6\u95F4\uFF0C\u4E0D\u7136\u6709\u53EF\u80FD\u5BFC\u81F4\u6574\u4E2A\u94FE\u8DEF\u5185\u90E8\u5DEE\u4E8B\u4ECE\u800C\u5F15\u8D77\u5185\u90E8\u6389\u5355\u3002</p><blockquote><p>\u753B\u5916\u97F3\uFF1A\u5BF9\u63A5\u5916\u90E8\u6E20\u9053\uFF0C\u4E00\u5B9A\u8981<strong>\u8BBE\u7F6E\u7F51\u7EDC\u8FDE\u63A5\u8D85\u65F6\u65F6\u95F4\u4E0E\u8BFB\u53D6\u8D85\u65F6\u65F6\u95F4</strong>\u3002</p></blockquote><h3 id="\u63A5\u6536\u5F02\u6B65\u901A\u77E5" tabindex="-1"><a class="header-anchor" href="#\u63A5\u6536\u5F02\u6B65\u901A\u77E5" aria-hidden="true">#</a> \u63A5\u6536\u5F02\u6B65\u901A\u77E5</h3><p>\u7B2C\u4E8C\u4E2A\u529E\u6CD5\uFF0C\u63A5\u6536\u6E20\u9053\u5F02\u6B65\u56DE\u6267\u901A\u77E5\u4FE1\u606F\u3002</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u73B0\u5728\u652F\u4ED8\u6E20\u9053\u63A5\u53E3\u6211\u4EEC\u90FD\u53EF\u4EE5\u4E0A\u9001\u4E00\u4E2A\u5F02\u6B65\u56DE\u8C03\u5730\u5740\uFF0C\u5F53\u6E20\u9053\u7AEF\u5904\u7406\u6210\u529F\uFF0C\u5C06\u4F1A\u628A\u6210\u529F\u4FE1\u606F\u901A\u77E5\u5230\u8FD9\u4E2A\u56DE\u8C03\u5730\u5740\u4E0A\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u63A5\u6536\u901A\u77E5\u4FE1\u606F\uFF0C\u7136\u540E\u89E3\u6790\uFF0C\u518D\u66F4\u65B0\u5185\u90E8\u8BA2\u5355\u72B6\u6001\u3002</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859492-1898161292.jpg" alt="\u652F\u4ED8\u7CFB\u7EDF\u5F02\u5E38\u5904\u7406-\u652F\u4ED8\u5F02\u6B65\u901A\u77E5"></p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u9700\u8981\u6CE8\u610F\u51E0\u70B9\uFF1A</p><ol><li>\u5BF9\u4E8E\u5F02\u6B65\u8BF7\u6C42\u4FE1\u606F\uFF0C\u4E00\u5B9A\u9700\u8981\u5BF9\u901A\u77E5\u5185\u5BB9\u8FDB\u884C\u7B7E\u540D\u9A8C\u8BC1\uFF0C\u5E76\u6821\u9A8C\u8FD4\u56DE\u7684\u8BA2\u5355\u91D1\u989D\u662F\u5426\u4E0E\u5546\u6237\u4FA7\u7684\u8BA2\u5355\u91D1\u989D\u4E00\u81F4\uFF0C\u9632\u6B62\u6570\u636E\u6CC4\u6F0F\u5BFC\u81F4\u51FA\u73B0\u201C\u5047\u901A\u77E5\u201D\uFF0C\u9020\u6210\u8D44\u91D1\u635F\u5931\u3002</li><li>\u5F02\u6B65\u901A\u77E5\u5C06\u4F1A\u53D1\u9001\u591A\u6B21\uFF0C\u6240\u4EE5\u5F02\u6B65\u901A\u77E5\u5904\u7406\u9700\u8981\u5E42\u7B49\u3002</li></ol><h3 id="\u6389\u5355\u67E5\u8BE2" tabindex="-1"><a class="header-anchor" href="#\u6389\u5355\u67E5\u8BE2" aria-hidden="true">#</a> \u6389\u5355\u67E5\u8BE2</h3><p>\u6709\u7684\u6E20\u9053\u53EF\u80FD\u6CA1\u6709\u63D0\u4F9B\u5F02\u6B65\u901A\u77E5\u7684\u529F\u80FD\uFF0C\u53EA\u63D0\u4F9B\u4E86\u8BA2\u5355\u67E5\u8BE2\u7684\u63A5\u53E3\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u53EA\u80FD\u4F7F\u7528\u7B2C\u4E09\u79CD\u89E3\u51B3\u529E\u6CD5\uFF0C\u5B9A\u65F6\u6389\u5355\u67E5\u8BE2\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u5C06\u8FD9\u7C7B\u8D85\u65F6\u672A\u77E5\u7684\u8BA2\u5355\u7684\u5355\u72EC\u4FDD\u5B58\u5230\u6389\u5355\u8868\uFF0C\u7136\u540E\u5B9A\u65F6\u5411\u6E20\u9053\u7AEF\u67E5\u8BE2\u8BA2\u5355\u7684\u72B6\u6001\u3002</p><p>\u82E5\u67E5\u8BE2\u6210\u529F\u6216\u8005\u660E\u786E\u5931\u8D25\uFF08\u6BD4\u5982\u8BA2\u5355\u4E0D\u5B58\u5728\u7B49\uFF09\uFF0C\u53EF\u4EE5\u66F4\u65B0\u8BA2\u5355\u72B6\u6001\uFF0C\u5E76\u4E14\u5220\u9664\u6389\u5355\u8868\u8BB0\u5F55\u3002</p><p>\u82E5\u67E5\u8BE2\u4F9D\u65E7\u672A\u77E5\uFF0C\u8FD9\u65F6\u6211\u4EEC\u9700\u8981\u7B49\u5F85\u4E0B\u6B21\u67E5\u8BE2\u7684\u7ED3\u679C\u3002</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859615-1762843903.jpg" alt="\u652F\u4ED8\u7CFB\u7EDF\u5F02\u5E38\u5904\u7406-\u5B9A\u65F6\u67E5\u8BE2"></p><p>\u8FD9\u91CC\u6211\u4EEC\u9700\u8981\u6CE8\u610F\u4E86\uFF0C\u6709\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u6709\u53EF\u80FD\u65E0\u6CD5\u67E5\u8BE2\u8FD4\u56DE\u8BA2\u5355\u7684\u72B6\u6001\uFF0C\u6240\u4EE5\u6211\u4EEC\u9700\u8981\u8BBE\u7F6E\u8BA2\u5355\u67E5\u8BE2\u7684\u6700\u5927\u6B21\u6570\uFF0C\u9632\u6B62\u65E0\u9650\u67E5\u8BE2\u6D6A\u8D39\u6027\u80FD\u3002</p><h3 id="\u5BF9\u8D26" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u8D26" aria-hidden="true">#</a> \u5BF9\u8D26</h3><p>\u6700\u540E\uFF0C\u6781\u5C11\u6570\u7684\u60C5\u51B5\u4E0B\uFF0C\u8BA2\u5355\u67E5\u8BE2\u4E0E\u5F02\u6B65\u901A\u77E5\u90FD\u65E0\u6CD5\u83B7\u53D6\u7684\u652F\u4ED8\u7ED3\u679C\uFF0C\u8FD9\u5C31\u8FD8\u5269\u4E0B\u6700\u540E\u4E00\u79CD\u515C\u5E95\u7684\u89E3\u51B3\u529E\u6CD5\uFF0C\u5BF9\u8D26\u3002</p><p>\u5982\u679C\u7B2C\u4E8C\u5929\u6E20\u9053\u7AEF\u7ED9\u7684\u5BF9\u8D26\u6587\u4EF6\u6709\u8FD9\u4E00\u7B14\u652F\u4ED8\u7ED3\u679C\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EF\u4EE5\u6839\u636E\u8FD9\u4E2A\u8BB0\u5F55\u66F4\u65B0\u76F4\u63A5\u66F4\u65B0\u6211\u4EEC\u5185\u90E8\u652F\u4ED8\u8BB0\u5F55\u3002</p><blockquote><p>\u753B\u5916\u97F3\uFF1A\u7A33\u59A5\u4E00\u70B9\uFF0C\u53EF\u4EE5\u5148\u53D1\u8D77\u67E5\u8BE2\uFF0C\u7136\u540E\u6839\u636E\u67E5\u8BE2\u7ED3\u679C\u66F4\u65B0\u8BA2\u5355\u8BB0\u5F55\u3002</p><p>\u4E0D\u8FC7\u6709\u4E9B\u6781\u7AEF\u60C5\u51B5\uFF0C\u67E5\u8BE2\u65E0\u6CD5\u83B7\u53D6\u7ED3\u679C\uFF0C\u90A3\u4E48\u76F4\u63A5\u66F4\u65B0\u5185\u90E8\u8BB0\u5F55\u5373\u53EF\u3002</p></blockquote><p>\u90A3\u5982\u679C\u7B2C\u4E8C\u5929\u4E5F\u6CA1\u6709\u8FD9\u7B14\u8BB0\u5F55\u7684\u7ED3\u679C\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA4\u4E3A\u8FD9\u7B14\u662F\u5931\u8D25\u7684\u3002\u5982\u679C\u7528\u6237\u88AB\u6263\u6B3E\uFF0C\u6E20\u9053\u7AEF\u5185\u90E8\u5C06\u4F1A\u53D1\u8D77\u9000\u6B3E\uFF0C\u5C06\u652F\u4ED8\u91D1\u989D\u8FD4\u56DE\u7ED9\u7528\u6237\u3002\u6240\u4EE5\u8FD9\u79CD\u60C5\u51B5\u53EF\u4EE5\u65E0\u9700\u5904\u7406\u3002</p><h2 id="\u5185\u90E8\u6389\u5355\u5F02\u5E38" tabindex="-1"><a class="header-anchor" href="#\u5185\u90E8\u6389\u5355\u5F02\u5E38" aria-hidden="true">#</a> \u5185\u90E8\u6389\u5355\u5F02\u5E38</h2><h3 id="\u652F\u4ED8\u516C\u53F8\u5185\u90E8\u8BA2\u5355\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#\u652F\u4ED8\u516C\u53F8\u5185\u90E8\u8BA2\u5355\u5173\u7CFB" aria-hidden="true">#</a> \u652F\u4ED8\u516C\u53F8\u5185\u90E8\u8BA2\u5355\u5173\u7CFB</h3><p>\u63A5\u4E0B\u6765\u6211\u4EEC\u8BB2\u4E0B\u5185\u90E8\u6389\u5355\u5F02\u5E38\uFF0C\u9996\u5148\u6211\u4EEC\u6765\u770B\u4E0B\u4E3A\u4EC0\u4E48\u4F1A\u53D1\u751F\u5185\u90E8\u6389\u5355\u7684\u5F02\u5E38\uFF0C\u8FD9\u5176\u5B9E\u8DDF\u6211\u4EEC\u7CFB\u7EDF\u67B6\u6784\u6709\u5173\u3002</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859756-1011257296.jpg" alt=""></p><p>\u5982\u4E0A\u56FE\u968F\u6240\u793A\uFF0C\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u5185\u90E8\u8868\u901A\u5E38\u4E3A\u652F\u4ED8\u8BA2\u5355\u4E0E\u6E20\u9053\u8BA2\u5355\u8FD9\u6837\u4E00\u79CD 1 \u6BD4 N \u7684\u5173\u7CFB\u3002</p><p>\u652F\u4ED8\u8BA2\u5355\u4FDD\u5B58\u7740\u5916\u90E8\u5546\u6237\u7CFB\u7EDF\u7684\u8BA2\u5355\u53F7\uFF0C\u4EE3\u8868\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u5185\u90E8\u8BA2\u5355\u4E0E\u5916\u90E8\u5546\u6237\u7684\u8BA2\u5355\u7684\u5173\u7CFB\u3002</p><p>\u800C\u6E20\u9053\u8BA2\u5355\u4EE3\u8868\u7740\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u4E0E\u5916\u90E8\u6E20\u9053\u7684\u5173\u7CFB\uFF0C\u5176\u5B9E\u5BF9\u4E8E\u5916\u90E8\u6E20\u9053\u7CFB\u7EDF\u6765\u8BB2\uFF0C\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u5C31\u662F\u4E00\u4E2A\u5916\u90E8\u5546\u6237\u3002</p><p>\u4E3A\u4EC0\u4E48\u9700\u8981\u8BBE\u8BA1\u8FD9\u79CD\u5173\u7CFB\u90A3\uFF1F\u800C\u4E0D\u662F\u4F7F\u7528\u4E0B\u9762\u8FD9\u79CD 1 \u5BF9 1 \u5173\u7CFB\u7684\u90A3\uFF1F</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083859966-155602254.jpg" alt=""></p><p>\u5982\u679C\u6211\u4EEC\u4F7F\u7528\u4E0A\u56FE 1 \u5BF91 \u7684\u8BA2\u5355\u5173\u7CFB\uFF0C\u5982\u679C\u7B2C\u4E00\u6B21\u652F\u4ED8\u652F\u4ED8\u5931\u8D25\uFF0C\u5916\u90E8\u5546\u6237\u53EF\u80FD\u4F1A\u518D\u6B21\u4F7F\u7528\u76F8\u540C\u8BA2\u5355\u53F7\u5BF9\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u53D1\u8D77\u652F\u4ED8\u3002</p><p>\u8FD9\u65F6\u5982\u679C\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u4E5F\u62FF\u76F8\u540C\u7684\u5185\u90E8\u8BA2\u5355\u53BB\u8BF7\u6C42\u5916\u90E8\u6E20\u9053\u7CFB\u7EDF\uFF0C\u6709\u53EF\u80FD\u5916\u90E8\u6E20\u9053\u7CFB\u7EDF\u5E76\u4E0D\u652F\u6301\u540C\u4E00\u8BA2\u5355\u53F7\u518D\u6B21\u8BF7\u6C42\u3002</p><p>\u90A3\u5176\u5B9E\u6211\u4EEC\u4E5F\u6709\u5176\u4ED6\u529E\u6CD5\uFF0C\u751F\u6210\u4E00\u4E2A\u65B0\u7684\u5185\u90E8\u5355\u53F7\uFF0C\u66F4\u65B0\u539F\u6709\u652F\u4ED8\u8BA2\u5355\u4E0A\u5185\u90E8\u8BB0\u5F55\uFF0C\u7136\u540E\u53BB\u8BF7\u6C42\u5916\u90E8\u6E20\u9053\u7CFB\u7EDF\u3002\u4F46\u662F\u8FD9\u6837\u7684\u8BDD\u5C31\u4F1A\u4E22\u5931\u4E0A\u6B21\u652F\u4ED8\u5931\u8D25\u8BB0\u5F55\uFF0C\u8FD9\u5C31\u4E0D\u5229\u4E8E\u6211\u4EEC\u505A\u4E00\u4E9B\u4E8B\u540E\u7EDF\u8BA1\u4E86\u3002</p><p>\u90A3\u5176\u5B9E\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u4E5F\u53EF\u4EE5\u4E0D\u652F\u6301\u76F8\u540C\u7684\u8BA2\u5355\u53F7\u518D\u6B21\u53D1\u8D77\u8BF7\u6C42\uFF0C\u4F46\u662F\u8FD9\u6837\u7684\u8BDD\uFF0C\u5C31\u9700\u8981\u5916\u90E8\u5546\u6237\u91CD\u65B0\u751F\u6210\u7684\u65B0\u7684\u8BA2\u5355\u53F7\u3002</p><p>\u8FD9\u6837\u7684\u8BDD\uFF0C\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u662F\u7CFB\u7EDF\u662F\u7B80\u5355\u4E86\uFF0C\u5168\u90E8\u590D\u6742\u5EA6\u90FD\u4EA4\u7ED9\u4E86\u5916\u90E8\u5546\u6237\u3002</p><p>\u4F46\u662F\u73B0\u5B9E\u7684\u60C5\u51B5\uFF0C\u5F88\u591A\u5916\u90E8\u5546\u6237\u5E76\u4E0D\u662F\u90A3\u4E48\u5BB9\u6613\u66F4\u6362\u751F\u6210\u65B0\u7684\u8BA2\u5355\u53F7\uFF0C\u6240\u4EE5\u4E00\u822C\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u90FD\u9700\u8981\u652F\u6301\u540C\u4E00\u5916\u90E8\u5546\u6237\u8BA2\u5355\u53F7\u5728\u672A\u6210\u529F\u7684\u60C5\u51B5\u4E0B\uFF0C\u652F\u6301\u91CD\u590D\u652F\u4ED8\u3002</p><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5C31\u9700\u8981\u6211\u4EEC\u4E0A\u9762\u7684 1:N \u7684\u8BA2\u5355\u5173\u7CFB\u56FE\u4E86\u3002</p><h3 id="\u5185\u90E8\u6389\u5355\u5F02\u5E38\u7684\u539F\u56E0" tabindex="-1"><a class="header-anchor" href="#\u5185\u90E8\u6389\u5355\u5F02\u5E38\u7684\u539F\u56E0" aria-hidden="true">#</a> \u5185\u90E8\u6389\u5355\u5F02\u5E38\u7684\u539F\u56E0</h3><p>\u5F53\u6211\u4EEC\u6536\u5230\u5916\u90E8\u6E20\u9053\u7CFB\u7EDF\u7684\u6210\u529F\u7684\u8FD4\u56DE\u4FE1\u606F\uFF0C\u6210\u529F\u66F4\u65B0\u4E86\u6E20\u9053\u8BA2\u5355\u8868\u7684\u8BB0\u5F55\u3002\u4F46\u662F\u7531\u4E8E\u6E20\u9053\u8BA2\u5355\u8868\u4E0E\u652F\u4ED8\u8BA2\u5355\u8868\u53EF\u80FD\u4E0D\u662F\u540C\u4E00\u4E2A\u6570\u636E\u5E93\uFF0C\u4E5F\u6709\u53EF\u80FD\u4E24\u8005\u5E76\u4E0D\u5728\u540C\u4E00\u4E2A\u5E94\u7528\u4E2D\uFF0C\u8FD9\u5C31\u6709\u53EF\u80FD\u5BFC\u81F4\u66F4\u65B0\u652F\u4ED8\u8BA2\u5355\u8868\u7684\u66F4\u65B0\u5931\u8D25\u3002</p><p><img src="https://img2020.cnblogs.com/other/1419561/202010/1419561-20201012083900191-2087810427.jpg" alt=""></p><p>\u7531\u4E8E\u652F\u4ED8\u8BA2\u5355\u662F\u8868\u4FDD\u5B58\u7740\u5916\u90E8\u5546\u6237\u8BA2\u5355\u4E0E\u5185\u90E8\u8BA2\u5355\u5173\u7CFB\uFF0C\u652F\u4ED8\u8BA2\u5355\u672A\u6210\u529F\uFF0C\u6240\u4EE5\u5916\u90E8\u5546\u6237\u4E5F\u65E0\u6CD5\u67E5\u8BE2\u5F97\u5230\u6210\u529F\u7684\u652F\u4ED8\u7ED3\u679C\u3002</p><p>\u6B64\u65F6\u6E20\u9053\u8BA2\u5355\u8868\u5DF2\u7ECF\u6210\u529F\uFF0C\u6240\u4EE5\u4E0A\u9762\u5916\u90E8\u6389\u5355\u7684\u65B9\u6CD5\u5E76\u4E0D\u9002\u7528\u5185\u90E8\u6389\u5355\u3002</p><h3 id="\u5185\u90E8\u6389\u5355\u5F02\u5E38\u89E3\u51B3\u529E\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5185\u90E8\u6389\u5355\u5F02\u5E38\u89E3\u51B3\u529E\u6CD5" aria-hidden="true">#</a> \u5185\u90E8\u6389\u5355\u5F02\u5E38\u89E3\u51B3\u529E\u6CD5</h3><p><strong>\u7B2C\u4E00\u79CD\u89E3\u51B3\u529E\u6CD5\uFF0C\u5206\u5E03\u5F0F\u4E8B\u52A1\u3002</strong></p><p>\u5185\u90E8\u6389\u5355\u5F02\u5E38\uFF0C\u8BF4\u767D\u5C31\u662F\u56E0\u4E3A\u652F\u4ED8\u8BA2\u5355\u8868\u4E0E\u6E20\u9053\u8BA2\u5355\u8868\u65E0\u6CD5\u4F7F\u7528\u6570\u636E\u5E93\u4E8B\u52A1\u4FDD\u8BC1\u4E24\u8005\u540C\u65F6\u66F4\u65B0\u6210\u529F\u6216\u5931\u8D25\u3002</p><p>\u90A3\u4E48\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u5176\u5B9E\u5C31\u9700\u8981\u4F7F\u7528\u5206\u5E03\u5F0F\u4E8B\u52A1\u4E86\u3002</p><p>\u4E0D\u8FC7\u6211\u4EEC\u6CA1\u6709\u91C7\u7528\u8FD9\u79CD\u5206\u5E03\u5F0F\u4E8B\u52A1\uFF0C\u4E00\u662F\u56E0\u4E3A\u4E4B\u524D\u5F00\u53D1\u7684\u65F6\u5019\u5E02\u9762\u4E0A\u5E76\u6CA1\u6709\u5F00\u6E90\u6210\u719F\u5206\u5E03\u5F0F\u4E8B\u52A1\u6846\u67B6\uFF0C\u7B2C\u4E8C\u81EA\u5DF1\u81EA\u5DF1\u5F00\u53D1\u96BE\u5EA6\u53C8\u5F88\u5927\u3002</p><p>\u6240\u4EE5\u5BF9\u4E8E\u5206\u5E03\u5F0F\u4E8B\u52A1\u8FD9\u4E00\u5757\uFF0C\u5E76\u6CA1\u6709\u4EC0\u4E48\u4F7F\u7528\u7ECF\u9A8C\u3002\u5982\u679C\u6709\u4F7F\u7528\u5206\u5E03\u5F0F\u4E8B\u52A1\u89E3\u51B3\u8FD9\u7C7B\u7684\u95EE\u9898\u540C\u5B66\uFF0C\u7559\u8A00\u53BB\u53EF\u4EE5\u8BC4\u8BBA\u4E00\u4E0B\u3002</p><p><strong>\u7B2C\u4E8C\u79CD\u89E3\u51B3\u529E\u6CD5\uFF0C\u5F02\u6B65\u8865\u507F\u66F4\u65B0\u3002</strong></p><p>\u5F53\u53D1\u751F\u5185\u90E8\u6389\u5355\u7684\u60C5\u51B5\uFF0C\u5373\u66F4\u65B0\u652F\u4ED8\u8BA2\u5355\u5931\u8D25\u7B49\u60C5\u51B5\uFF0C\u53EF\u4EE5\u5C06\u8FD9\u91CC\u652F\u4ED8\u8BA2\u5355\u4FDD\u5B58\u5230\u4E00\u5F20\u5185\u90E8\u6389\u5355\u8868\u3002</p><p>\u4F46\u662F\u8FD9\u91CC\u53EF\u80FD\u4F1A\u6709\u4E00\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u65E0\u6CD5\u4FDD\u8BC1\u4FDD\u5B58\u5230\u5185\u90E8\u6389\u5355\u8868\u8FD9\u4E00\u6B65\u9AA4\u4E5F\u4E00\u5B9A\u6210\u529F\u3002</p><p>\u6240\u4EE5\u8BF4\uFF0C\u6211\u4EEC\u8FD8\u9700\u8981\u5B9A\u65F6\u67E5\u8BE2\uFF0C\u67E5\u8BE2\u4E00\u6BB5\u65F6\u95F4\u5185\u652F\u4ED8\u8BA2\u5355\u672A\u6210\u529F\uFF0C\u800C\u6E20\u9053\u8BA2\u5355\u8868\u5DF2\u6210\u529F\u7684\u652F\u4ED8\u8BA2\u5355\u8BB0\u5F55\uFF0C\u7136\u540E\u4E5F\u5C06\u5176\u63D2\u5165\u5230\u5185\u90E8\u6389\u5355\u8868\u3002</p><p>\u53E6\u4E00\u4E2A\u7CFB\u7EDF\u5E94\u7528\uFF0C\u53EA\u9700\u8981\u5B9A\u65F6\u626B\u63CF\u5185\u90E8\u6389\u5355\u8868\uFF0C\u5C06\u652F\u4ED8\u8BA2\u5355\u6210\u529F\uFF0C\u7136\u540E\u518D\u5220\u9664\u5185\u90E8\u6389\u5355\u8BB0\u5F55\u5373\u53EF\u3002</p><p>\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u4E86\uFF0C\u5F53\u652F\u4ED8\u8BA2\u5355\u8868\u6570\u636E\u91CF\u5F88\u5927\u4E4B\u540E\uFF0C\u5B9A\u65F6\u67E5\u8BE2\u53EF\u80FD\u4F1A\u6162\uFF0C\u4E3A\u4E86\u9632\u6B62\u5F71\u54CD\u4E3B\u5E93\uFF0C\u6240\u4EE5\u8FD9\u7C7B\u67E5\u8BE2\u53EF\u4EE5\u5728\u5907\u5E93\u8FDB\u884C\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u4ECA\u5929\u4E3B\u8981\u4ECB\u7ECD\u4E86\u652F\u4ED8\u7CFB\u7EDF\u4E2D\u7684\u6389\u5355\u5F02\u5E38\uFF0C\u8FD9\u7C7B\u5F02\u5E38\u5F80\u5F80\u4F1A\u5BFC\u81F4\u7528\u6237\u5B9E\u9645\u5DF2\u7ECF\u88AB\u6263\u94B1\uFF0C\u4F46\u662F\u5546\u6237\u8BA2\u5355\u8FD8\u662F\u7B49\u5F85\u652F\u4ED8\u7684\u60C5\u51B5\u3002</p><p>\u8FD9\u4E2A\u5F02\u5E38\u5982\u679C\u6CA1\u6709\u5F88\u597D\u5904\u7406\uFF0C\u5C06\u4F1A\u5BFC\u81F4\u5BA2\u6237\u7528\u6237\u4F53\u9A8C\u5F88\u4E0D\u597D\uFF0C\u8FD8\u6709\u53EF\u80FD\u6536\u5230\u5BA2\u6237\u7684\u6295\u8BC9\u3002</p><p>\u6389\u5355\u7684\u5F02\u5E38\uFF0C\u901A\u5E38\u53EF\u4EE5\u5916\u90E8\u7CFB\u7EDF\u4E0E\u5185\u90E8\u7CFB\u7EDF\u3002\u800C\u5927\u90E8\u5206\u7684\u6389\u5355\u90FD\u662F\u56E0\u4E3A\u5916\u90E8\u7CFB\u7EDF\u5BFC\u81F4\uFF0C\u6211\u4EEC\u53EF\u4EE5\u589E\u52A0\u8D85\u65F6\u65F6\u95F4\uFF0C\u6389\u5355\u67E5\u8BE2\uFF0C\u4EE5\u53CA\u63A5\u53D7\u5F02\u6B65\u901A\u77E5\u89E3\u51B3 99% \u7684\u95EE\u9898\uFF0C\u5269\u4E0B 1% \u7684\u6389\u5355\u53EA\u80FD\u901A\u8FC7\u6B21\u65E5\u7684\u5BF9\u8D26\u6765\u515C\u5E95\u3002</p><p>\u5185\u90E8\u7CFB\u7EDF\u5BFC\u81F4\u6389\u5355\u5F02\u5E38\u662F\u5178\u578B\u7684\u5206\u5E03\u5F0F\u73AF\u5883\u6570\u636E\u4E00\u81F4\u6027\u7684\u95EE\u9898\uFF0C\u8FD9\u7C7B\u95EE\u9898\u6211\u4EEC\u53EF\u4EE5\u4E0D\u9700\u8981\u8FFD\u6C42\u5F3A\u4E00\u81F4\u6027\uFF0C\u53EA\u8981\u4FDD\u8BC1\u6700\u7EC8\u4E00\u81F4\u6027\u5373\u53EF\u3002\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u5206\u5E03\u5F0F\u4E8B\u52A1\u89E3\u51B3\u8FD9\u7C7B\u95EE\u9898\uFF0C\u4E5F\u53EF\u4EE5\u5B9A\u65F6\u626B\u63CF\u72B6\u6001\u4E0D\u4E00\u81F4\u7684\u8BA2\u5355\uFF0C\u7136\u540E\u5728\u505A\u6279\u91CF\u66F4\u65B0\u3002</p>',74),d={href:"https://www.cnblogs.com/goodAndyxublog/p/13800796.html",target:"_blank",rel:"noopener noreferrer"};function l(g,m){const a=h("ExternalLinkIcon");return t(),o("div",null,[c,p("blockquote",null,[p("p",null,[e("\u53C2\u8003\u94FE\u63A5\uFF1A"),p("a",d,[e("https://www.cnblogs.com/goodAndyxublog/p/13800796.html"),n(a)]),e("\uFF0C\u6574\u7406\uFF1Amusk")])])])}const f=r(i,[["render",l],["__file","qianbkzldsddqwcgzfdcyczqxjfa.html.vue"]]);export{f as default};
