import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as s,f as r}from"./app.41a23dcf.js";const a={},d=r(`<h1 id="redis\u9762\u8BD5-\u96C6\u7FA4-\u4E3B\u4ECE\u590D\u5236" tabindex="-1"><a class="header-anchor" href="#redis\u9762\u8BD5-\u96C6\u7FA4-\u4E3B\u4ECE\u590D\u5236" aria-hidden="true">#</a> Redis\u9762\u8BD5 - \u96C6\u7FA4-\u4E3B\u4ECE\u590D\u5236</h1><h2 id="_1-redis\u96C6\u7FA4\u7684\u4E3B\u4ECE\u590D\u5236\u6A21\u578B\u662F\u600E\u6837\u7684" tabindex="-1"><a class="header-anchor" href="#_1-redis\u96C6\u7FA4\u7684\u4E3B\u4ECE\u590D\u5236\u6A21\u578B\u662F\u600E\u6837\u7684" aria-hidden="true">#</a> 1 Redis\u96C6\u7FA4\u7684\u4E3B\u4ECE\u590D\u5236\u6A21\u578B\u662F\u600E\u6837\u7684\uFF1F</h2><p>\u4E3B\u4ECE\u590D\u5236\uFF0C\u662F\u6307\u5C06\u4E00\u53F0Redis\u670D\u52A1\u5668\u7684\u6570\u636E\uFF0C\u590D\u5236\u5230\u5176\u4ED6\u7684Redis\u670D\u52A1\u5668\u3002\u524D\u8005\u79F0\u4E3A\u4E3B\u8282\u70B9(master)\uFF0C\u540E\u8005\u79F0\u4E3A\u4ECE\u8282\u70B9(slave)\uFF1B\u6570\u636E\u7684\u590D\u5236\u662F\u5355\u5411\u7684\uFF0C\u53EA\u80FD\u7531\u4E3B\u8282\u70B9\u5230\u4ECE\u8282\u70B9\u3002</p><p><strong>\u4E3B\u4ECE\u590D\u5236\u7684\u4F5C\u7528</strong>\u4E3B\u8981\u5305\u62EC\uFF1A</p><ul><li><strong>\u6570\u636E\u5197\u4F59</strong>\uFF1A\u4E3B\u4ECE\u590D\u5236\u5B9E\u73B0\u4E86\u6570\u636E\u7684\u70ED\u5907\u4EFD\uFF0C\u662F\u6301\u4E45\u5316\u4E4B\u5916\u7684\u4E00\u79CD\u6570\u636E\u5197\u4F59\u65B9\u5F0F\u3002</li><li><strong>\u6545\u969C\u6062\u590D</strong>\uFF1A\u5F53\u4E3B\u8282\u70B9\u51FA\u73B0\u95EE\u9898\u65F6\uFF0C\u53EF\u4EE5\u7531\u4ECE\u8282\u70B9\u63D0\u4F9B\u670D\u52A1\uFF0C\u5B9E\u73B0\u5FEB\u901F\u7684\u6545\u969C\u6062\u590D\uFF1B\u5B9E\u9645\u4E0A\u662F\u4E00\u79CD\u670D\u52A1\u7684\u5197\u4F59\u3002</li><li><strong>\u8D1F\u8F7D\u5747\u8861</strong>\uFF1A\u5728\u4E3B\u4ECE\u590D\u5236\u7684\u57FA\u7840\u4E0A\uFF0C\u914D\u5408\u8BFB\u5199\u5206\u79BB\uFF0C\u53EF\u4EE5\u7531\u4E3B\u8282\u70B9\u63D0\u4F9B\u5199\u670D\u52A1\uFF0C\u7531\u4ECE\u8282\u70B9\u63D0\u4F9B\u8BFB\u670D\u52A1\uFF08\u5373\u5199Redis\u6570\u636E\u65F6\u5E94\u7528\u8FDE\u63A5\u4E3B\u8282\u70B9\uFF0C\u8BFBRedis\u6570\u636E\u65F6\u5E94\u7528\u8FDE\u63A5\u4ECE\u8282\u70B9\uFF09\uFF0C\u5206\u62C5\u670D\u52A1\u5668\u8D1F\u8F7D\uFF1B\u5C24\u5176\u662F\u5728\u5199\u5C11\u8BFB\u591A\u7684\u573A\u666F\u4E0B\uFF0C\u901A\u8FC7\u591A\u4E2A\u4ECE\u8282\u70B9\u5206\u62C5\u8BFB\u8D1F\u8F7D\uFF0C\u53EF\u4EE5\u5927\u5927\u63D0\u9AD8Redis\u670D\u52A1\u5668\u7684\u5E76\u53D1\u91CF\u3002</li><li><strong>\u9AD8\u53EF\u7528\u57FA\u77F3</strong>\uFF1A\u9664\u4E86\u4E0A\u8FF0\u4F5C\u7528\u4EE5\u5916\uFF0C\u4E3B\u4ECE\u590D\u5236\u8FD8\u662F\u54E8\u5175\u548C\u96C6\u7FA4\u80FD\u591F\u5B9E\u65BD\u7684\u57FA\u7840\uFF0C\u56E0\u6B64\u8BF4\u4E3B\u4ECE\u590D\u5236\u662FRedis\u9AD8\u53EF\u7528\u7684\u57FA\u7840\u3002</li></ul><p>\u4E3B\u4ECE\u5E93\u4E4B\u95F4\u91C7\u7528\u7684\u662F<strong>\u8BFB\u5199\u5206\u79BB</strong>\u7684\u65B9\u5F0F\u3002</p><ul><li>\u8BFB\u64CD\u4F5C\uFF1A\u4E3B\u5E93\u3001\u4ECE\u5E93\u90FD\u53EF\u4EE5\u63A5\u6536\uFF1B</li><li>\u5199\u64CD\u4F5C\uFF1A\u9996\u5148\u5230\u4E3B\u5E93\u6267\u884C\uFF0C\u7136\u540E\uFF0C\u4E3B\u5E93\u5C06\u5199\u64CD\u4F5C\u540C\u6B65\u7ED9\u4ECE\u5E93\u3002</li></ul><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220628224139614.png" alt="image-20220628224139614"></p><p>\u6CE8\u610F\uFF1A\u57282.8\u7248\u672C\u4E4B\u524D\u53EA\u6709\u5168\u91CF\u590D\u5236\uFF0C\u800C2.8\u7248\u672C\u540E\u6709\u5168\u91CF\u548C\u589E\u91CF\u590D\u5236\uFF1A</p><ul><li><strong>\u5168\u91CF\uFF08\u540C\u6B65\uFF09\u590D\u5236</strong>\uFF1A\u6BD4\u5982\u7B2C\u4E00\u6B21\u540C\u6B65\u65F6</li><li><strong>\u589E\u91CF\uFF08\u540C\u6B65\uFF09\u590D\u5236</strong>\uFF1A\u53EA\u4F1A\u628A\u4E3B\u4ECE\u5E93\u7F51\u7EDC\u65AD\u8FDE\u671F\u95F4\u4E3B\u5E93\u6536\u5230\u7684\u547D\u4EE4\uFF0C\u540C\u6B65\u7ED9\u4ECE\u5E93</li></ul><h2 id="_2-redis-\u5168\u91CF\u590D\u5236\u7684\u4E09\u4E2A\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_2-redis-\u5168\u91CF\u590D\u5236\u7684\u4E09\u4E2A\u9636\u6BB5" aria-hidden="true">#</a> 2 Redis \u5168\u91CF\u590D\u5236\u7684\u4E09\u4E2A\u9636\u6BB5\uFF1F</h2><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220628224222691.png" alt="image-20220628224222691"></p><p><strong>\u7B2C\u4E00\u9636\u6BB5\u662F\u4E3B\u4ECE\u5E93\u95F4\u5EFA\u7ACB\u8FDE\u63A5\u3001\u534F\u5546\u540C\u6B65\u7684\u8FC7\u7A0B</strong>\uFF0C\u4E3B\u8981\u662F\u4E3A\u5168\u91CF\u590D\u5236\u505A\u51C6\u5907\u3002\u5728\u8FD9\u4E00\u6B65\uFF0C\u4ECE\u5E93\u548C\u4E3B\u5E93\u5EFA\u7ACB\u8D77\u8FDE\u63A5\uFF0C\u5E76\u544A\u8BC9\u4E3B\u5E93\u5373\u5C06\u8FDB\u884C\u540C\u6B65\uFF0C\u4E3B\u5E93\u786E\u8BA4\u56DE\u590D\u540E\uFF0C\u4E3B\u4ECE\u5E93\u95F4\u5C31\u53EF\u4EE5\u5F00\u59CB\u540C\u6B65\u4E86\u3002</p><p>\u5177\u4F53\u6765\u8BF4\uFF0C\u4ECE\u5E93\u7ED9\u4E3B\u5E93\u53D1\u9001 psync \u547D\u4EE4\uFF0C\u8868\u793A\u8981\u8FDB\u884C\u6570\u636E\u540C\u6B65\uFF0C\u4E3B\u5E93\u6839\u636E\u8FD9\u4E2A\u547D\u4EE4\u7684\u53C2\u6570\u6765\u542F\u52A8\u590D\u5236\u3002psync \u547D\u4EE4\u5305\u542B\u4E86\u4E3B\u5E93\u7684 runID \u548C\u590D\u5236\u8FDB\u5EA6 offset \u4E24\u4E2A\u53C2\u6570\u3002runID\uFF0C\u662F\u6BCF\u4E2A Redis \u5B9E\u4F8B\u542F\u52A8\u65F6\u90FD\u4F1A\u81EA\u52A8\u751F\u6210\u7684\u4E00\u4E2A\u968F\u673A ID\uFF0C\u7528\u6765\u552F\u4E00\u6807\u8BB0\u8FD9\u4E2A\u5B9E\u4F8B\u3002\u5F53\u4ECE\u5E93\u548C\u4E3B\u5E93\u7B2C\u4E00\u6B21\u590D\u5236\u65F6\uFF0C\u56E0\u4E3A\u4E0D\u77E5\u9053\u4E3B\u5E93\u7684 runID\uFF0C\u6240\u4EE5\u5C06 runID \u8BBE\u4E3A\u201C\uFF1F\u201D\u3002offset\uFF0C\u6B64\u65F6\u8BBE\u4E3A -1\uFF0C\u8868\u793A\u7B2C\u4E00\u6B21\u590D\u5236\u3002\u4E3B\u5E93\u6536\u5230 psync \u547D\u4EE4\u540E\uFF0C\u4F1A\u7528 FULLRESYNC \u54CD\u5E94\u547D\u4EE4\u5E26\u4E0A\u4E24\u4E2A\u53C2\u6570\uFF1A\u4E3B\u5E93 runID \u548C\u4E3B\u5E93\u76EE\u524D\u7684\u590D\u5236\u8FDB\u5EA6 offset\uFF0C\u8FD4\u56DE\u7ED9\u4ECE\u5E93\u3002\u4ECE\u5E93\u6536\u5230\u54CD\u5E94\u540E\uFF0C\u4F1A\u8BB0\u5F55\u4E0B\u8FD9\u4E24\u4E2A\u53C2\u6570\u3002\u8FD9\u91CC\u6709\u4E2A\u5730\u65B9\u9700\u8981\u6CE8\u610F\uFF0CFULLRESYNC \u54CD\u5E94\u8868\u793A\u7B2C\u4E00\u6B21\u590D\u5236\u91C7\u7528\u7684\u5168\u91CF\u590D\u5236\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E3B\u5E93\u4F1A\u628A\u5F53\u524D\u6240\u6709\u7684\u6570\u636E\u90FD\u590D\u5236\u7ED9\u4ECE\u5E93\u3002</p><p><strong>\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u4E3B\u5E93\u5C06\u6240\u6709\u6570\u636E\u540C\u6B65\u7ED9\u4ECE\u5E93</strong>\u3002\u4ECE\u5E93\u6536\u5230\u6570\u636E\u540E\uFF0C\u5728\u672C\u5730\u5B8C\u6210\u6570\u636E\u52A0\u8F7D\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u4F9D\u8D56\u4E8E\u5185\u5B58\u5FEB\u7167\u751F\u6210\u7684 RDB \u6587\u4EF6\u3002</p><p>\u5177\u4F53\u6765\u8BF4\uFF0C\u4E3B\u5E93\u6267\u884C bgsave \u547D\u4EE4\uFF0C\u751F\u6210 RDB \u6587\u4EF6\uFF0C\u63A5\u7740\u5C06\u6587\u4EF6\u53D1\u7ED9\u4ECE\u5E93\u3002\u4ECE\u5E93\u63A5\u6536\u5230 RDB \u6587\u4EF6\u540E\uFF0C\u4F1A\u5148\u6E05\u7A7A\u5F53\u524D\u6570\u636E\u5E93\uFF0C\u7136\u540E\u52A0\u8F7D RDB \u6587\u4EF6\u3002\u8FD9\u662F\u56E0\u4E3A\u4ECE\u5E93\u5728\u901A\u8FC7 replicaof \u547D\u4EE4\u5F00\u59CB\u548C\u4E3B\u5E93\u540C\u6B65\u524D\uFF0C\u53EF\u80FD\u4FDD\u5B58\u4E86\u5176\u4ED6\u6570\u636E\u3002\u4E3A\u4E86\u907F\u514D\u4E4B\u524D\u6570\u636E\u7684\u5F71\u54CD\uFF0C\u4ECE\u5E93\u9700\u8981\u5148\u628A\u5F53\u524D\u6570\u636E\u5E93\u6E05\u7A7A\u3002\u5728\u4E3B\u5E93\u5C06\u6570\u636E\u540C\u6B65\u7ED9\u4ECE\u5E93\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4E3B\u5E93\u4E0D\u4F1A\u88AB\u963B\u585E\uFF0C\u4ECD\u7136\u53EF\u4EE5\u6B63\u5E38\u63A5\u6536\u8BF7\u6C42\u3002\u5426\u5219\uFF0CRedis \u7684\u670D\u52A1\u5C31\u88AB\u4E2D\u65AD\u4E86\u3002\u4F46\u662F\uFF0C\u8FD9\u4E9B\u8BF7\u6C42\u4E2D\u7684\u5199\u64CD\u4F5C\u5E76\u6CA1\u6709\u8BB0\u5F55\u5230\u521A\u521A\u751F\u6210\u7684 RDB \u6587\u4EF6\u4E2D\u3002\u4E3A\u4E86\u4FDD\u8BC1\u4E3B\u4ECE\u5E93\u7684\u6570\u636E\u4E00\u81F4\u6027\uFF0C\u4E3B\u5E93\u4F1A\u5728\u5185\u5B58\u4E2D\u7528\u4E13\u95E8\u7684 replication buffer\uFF0C\u8BB0\u5F55 RDB \u6587\u4EF6\u751F\u6210\u540E\u6536\u5230\u7684\u6240\u6709\u5199\u64CD\u4F5C\u3002</p><p><strong>\u7B2C\u4E09\u4E2A\u9636\u6BB5\uFF0C\u4E3B\u5E93\u4F1A\u628A\u7B2C\u4E8C\u9636\u6BB5\u6267\u884C\u8FC7\u7A0B\u4E2D\u65B0\u6536\u5230\u7684\u5199\u547D\u4EE4\uFF0C\u518D\u53D1\u9001\u7ED9\u4ECE\u5E93</strong>\u3002\u5177\u4F53\u7684\u64CD\u4F5C\u662F\uFF0C\u5F53\u4E3B\u5E93\u5B8C\u6210 RDB \u6587\u4EF6\u53D1\u9001\u540E\uFF0C\u5C31\u4F1A\u628A\u6B64\u65F6 replication buffer \u4E2D\u7684\u4FEE\u6539\u64CD\u4F5C\u53D1\u7ED9\u4ECE\u5E93\uFF0C\u4ECE\u5E93\u518D\u91CD\u65B0\u6267\u884C\u8FD9\u4E9B\u64CD\u4F5C\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u4E3B\u4ECE\u5E93\u5C31\u5B9E\u73B0\u540C\u6B65\u4E86\u3002</p><h2 id="_3-redis-\u4E3A\u4EC0\u4E48\u4F1A\u8BBE\u8BA1\u589E\u91CF\u590D\u5236" tabindex="-1"><a class="header-anchor" href="#_3-redis-\u4E3A\u4EC0\u4E48\u4F1A\u8BBE\u8BA1\u589E\u91CF\u590D\u5236" aria-hidden="true">#</a> 3 Redis \u4E3A\u4EC0\u4E48\u4F1A\u8BBE\u8BA1\u589E\u91CF\u590D\u5236\uFF1F</h2><p>\u5982\u679C\u4E3B\u4ECE\u5E93\u5728\u547D\u4EE4\u4F20\u64AD\u65F6\u51FA\u73B0\u4E86\u7F51\u7EDC\u95EA\u65AD\uFF0C\u90A3\u4E48\uFF0C\u4ECE\u5E93\u5C31\u4F1A\u548C\u4E3B\u5E93\u91CD\u65B0\u8FDB\u884C\u4E00\u6B21\u5168\u91CF\u590D\u5236\uFF0C\u5F00\u9500\u975E\u5E38\u5927\u3002\u4ECE Redis 2.8 \u5F00\u59CB\uFF0C\u7F51\u7EDC\u65AD\u4E86\u4E4B\u540E\uFF0C\u4E3B\u4ECE\u5E93\u4F1A\u91C7\u7528\u589E\u91CF\u590D\u5236\u7684\u65B9\u5F0F\u7EE7\u7EED\u540C\u6B65\u3002</p><h2 id="_4-redis-\u589E\u91CF\u590D\u5236\u7684\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_4-redis-\u589E\u91CF\u590D\u5236\u7684\u6D41\u7A0B" aria-hidden="true">#</a> 4 Redis \u589E\u91CF\u590D\u5236\u7684\u6D41\u7A0B\uFF1F</h2><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220628225658343.png" alt="image-20220628225658343"></p><p>\u5148\u770B\u4E24\u4E2A\u6982\u5FF5\uFF1A <code>replication buffer</code> \u548C <code>repl_backlog_buffer</code></p><p><code>repl_backlog_buffer</code>\uFF1A\u5B83\u662F\u4E3A\u4E86\u4ECE\u5E93\u65AD\u5F00\u4E4B\u540E\uFF0C\u5982\u4F55\u627E\u5230\u4E3B\u4ECE\u5DEE\u5F02\u6570\u636E\u800C\u8BBE\u8BA1\u7684\u73AF\u5F62\u7F13\u51B2\u533A\uFF0C\u4ECE\u800C\u907F\u514D\u5168\u91CF\u590D\u5236\u5E26\u6765\u7684\u6027\u80FD\u5F00\u9500\u3002\u5982\u679C\u4ECE\u5E93\u65AD\u5F00\u65F6\u95F4\u592A\u4E45\uFF0Crepl_backlog_buffer\u73AF\u5F62\u7F13\u51B2\u533A\u88AB\u4E3B\u5E93\u7684\u5199\u547D\u4EE4\u8986\u76D6\u4E86\uFF0C\u90A3\u4E48\u4ECE\u5E93\u8FDE\u4E0A\u4E3B\u5E93\u540E\u53EA\u80FD\u4E56\u4E56\u5730\u8FDB\u884C\u4E00\u6B21\u5168\u91CF\u590D\u5236\uFF0C\u6240\u4EE5<strong>repl_backlog_buffer\u914D\u7F6E\u5C3D\u91CF\u5927\u4E00\u4E9B\uFF0C\u53EF\u4EE5\u964D\u4F4E\u4E3B\u4ECE\u65AD\u5F00\u540E\u5168\u91CF\u590D\u5236\u7684\u6982\u7387</strong>\u3002\u800C\u5728repl_backlog_buffer\u4E2D\u627E\u4E3B\u4ECE\u5DEE\u5F02\u7684\u6570\u636E\u540E\uFF0C\u5982\u4F55\u53D1\u7ED9\u4ECE\u5E93\u5462\uFF1F\u8FD9\u5C31\u7528\u5230\u4E86replication buffer\u3002</p><p><code>replication buffer</code>\uFF1ARedis\u548C\u5BA2\u6237\u7AEF\u901A\u4FE1\u4E5F\u597D\uFF0C\u548C\u4ECE\u5E93\u901A\u4FE1\u4E5F\u597D\uFF0CRedis\u90FD\u9700\u8981\u7ED9\u5206\u914D\u4E00\u4E2A \u5185\u5B58buffer\u8FDB\u884C\u6570\u636E\u4EA4\u4E92\uFF0C\u5BA2\u6237\u7AEF\u662F\u4E00\u4E2Aclient\uFF0C\u4ECE\u5E93\u4E5F\u662F\u4E00\u4E2Aclient\uFF0C\u6211\u4EEC\u6BCF\u4E2Aclient\u8FDE\u4E0ARedis\u540E\uFF0CRedis\u90FD\u4F1A\u5206\u914D\u4E00\u4E2Aclient buffer\uFF0C\u6240\u6709\u6570\u636E\u4EA4\u4E92\u90FD\u662F\u901A\u8FC7\u8FD9\u4E2Abuffer\u8FDB\u884C\u7684\uFF1ARedis\u5148\u628A\u6570\u636E\u5199\u5230\u8FD9\u4E2Abuffer\u4E2D\uFF0C\u7136\u540E\u518D\u628Abuffer\u4E2D\u7684\u6570\u636E\u53D1\u5230client socket\u4E2D\u518D\u901A\u8FC7\u7F51\u7EDC\u53D1\u9001\u51FA\u53BB\uFF0C\u8FD9\u6837\u5C31\u5B8C\u6210\u4E86\u6570\u636E\u4EA4\u4E92\u3002\u6240\u4EE5\u4E3B\u4ECE\u5728\u589E\u91CF\u540C\u6B65\u65F6\uFF0C\u4ECE\u5E93\u4F5C\u4E3A\u4E00\u4E2Aclient\uFF0C\u4E5F\u4F1A\u5206\u914D\u4E00\u4E2Abuffer\uFF0C\u53EA\u4E0D\u8FC7\u8FD9\u4E2Abuffer\u4E13\u95E8\u7528\u6765\u4F20\u64AD\u7528\u6237\u7684\u5199\u547D\u4EE4\u5230\u4ECE\u5E93\uFF0C\u4FDD\u8BC1\u4E3B\u4ECE\u6570\u636E\u4E00\u81F4\uFF0C\u6211\u4EEC\u901A\u5E38\u628A\u5B83\u53EB\u505Areplication buffer\u3002</p><h2 id="_5-\u589E\u91CF\u590D\u5236\u5982\u679C\u5728\u7F51\u7EDC\u65AD\u5F00\u671F\u95F4-repl-backlog-size\u73AF\u5F62\u7F13\u51B2\u533A\u5199\u6EE1\u4E4B\u540E-\u4ECE\u5E93\u662F\u4F1A\u4E22\u5931\u6389\u90A3\u90E8\u5206\u88AB\u8986\u76D6\u6389\u7684\u6570\u636E-\u8FD8\u662F\u76F4\u63A5\u8FDB\u884C\u5168\u91CF\u590D\u5236\u5462" tabindex="-1"><a class="header-anchor" href="#_5-\u589E\u91CF\u590D\u5236\u5982\u679C\u5728\u7F51\u7EDC\u65AD\u5F00\u671F\u95F4-repl-backlog-size\u73AF\u5F62\u7F13\u51B2\u533A\u5199\u6EE1\u4E4B\u540E-\u4ECE\u5E93\u662F\u4F1A\u4E22\u5931\u6389\u90A3\u90E8\u5206\u88AB\u8986\u76D6\u6389\u7684\u6570\u636E-\u8FD8\u662F\u76F4\u63A5\u8FDB\u884C\u5168\u91CF\u590D\u5236\u5462" aria-hidden="true">#</a> 5 \u589E\u91CF\u590D\u5236\u5982\u679C\u5728\u7F51\u7EDC\u65AD\u5F00\u671F\u95F4\uFF0Crepl_backlog_size\u73AF\u5F62\u7F13\u51B2\u533A\u5199\u6EE1\u4E4B\u540E\uFF0C\u4ECE\u5E93\u662F\u4F1A\u4E22\u5931\u6389\u90A3\u90E8\u5206\u88AB\u8986\u76D6\u6389\u7684\u6570\u636E\uFF0C\u8FD8\u662F\u76F4\u63A5\u8FDB\u884C\u5168\u91CF\u590D\u5236\u5462\uFF1F</h2><p>\u5BF9\u4E8E\u8FD9\u4E2A\u95EE\u9898\u6765\u8BF4\uFF0C\u6709\u4E24\u4E2A\u5173\u952E\u70B9\uFF1A</p><ol><li>\u4E00\u4E2A\u4ECE\u5E93\u5982\u679C\u548C\u4E3B\u5E93\u65AD\u8FDE\u65F6\u95F4\u8FC7\u957F\uFF0C\u9020\u6210\u5B83\u5728\u4E3B\u5E93repl_backlog_buffer\u7684slave_repl_offset\u4F4D\u7F6E\u4E0A\u7684\u6570\u636E\u5DF2\u7ECF\u88AB\u8986\u76D6\u6389\u4E86\uFF0C\u6B64\u65F6\u4ECE\u5E93\u548C\u4E3B\u5E93\u95F4\u5C06\u8FDB\u884C\u5168\u91CF\u590D\u5236\u3002</li><li>\u6BCF\u4E2A\u4ECE\u5E93\u4F1A\u8BB0\u5F55\u81EA\u5DF1\u7684slave_repl_offset\uFF0C\u6BCF\u4E2A\u4ECE\u5E93\u7684\u590D\u5236\u8FDB\u5EA6\u4E5F\u4E0D\u4E00\u5B9A\u76F8\u540C\u3002\u5728\u548C\u4E3B\u5E93\u91CD\u8FDE\u8FDB\u884C\u6062\u590D\u65F6\uFF0C\u4ECE\u5E93\u4F1A\u901A\u8FC7psync\u547D\u4EE4\u628A\u81EA\u5DF1\u8BB0\u5F55\u7684slave_repl_offset\u53D1\u7ED9\u4E3B\u5E93\uFF0C\u4E3B\u5E93\u4F1A\u6839\u636E\u4ECE\u5E93\u5404\u81EA\u7684\u590D\u5236\u8FDB\u5EA6\uFF0C\u6765\u51B3\u5B9A\u8FD9\u4E2A\u4ECE\u5E93\u53EF\u4EE5\u8FDB\u884C\u589E\u91CF\u590D\u5236\uFF0C\u8FD8\u662F\u5168\u91CF\u590D\u5236\u3002</li></ol><h2 id="_6-redis-\u4E3A\u4EC0\u4E48\u4E0D\u6301\u4E45\u5316\u7684\u4E3B\u670D\u52A1\u5668\u81EA\u52A8\u91CD\u542F\u975E\u5E38\u5371\u9669\u5462" tabindex="-1"><a class="header-anchor" href="#_6-redis-\u4E3A\u4EC0\u4E48\u4E0D\u6301\u4E45\u5316\u7684\u4E3B\u670D\u52A1\u5668\u81EA\u52A8\u91CD\u542F\u975E\u5E38\u5371\u9669\u5462" aria-hidden="true">#</a> 6 Redis \u4E3A\u4EC0\u4E48\u4E0D\u6301\u4E45\u5316\u7684\u4E3B\u670D\u52A1\u5668\u81EA\u52A8\u91CD\u542F\u975E\u5E38\u5371\u9669\u5462?</h2><ul><li>\u6211\u4EEC\u8BBE\u7F6E\u8282\u70B9A\u4E3A\u4E3B\u670D\u52A1\u5668\uFF0C\u5173\u95ED\u6301\u4E45\u5316\uFF0C\u8282\u70B9B\u548CC\u4ECE\u8282\u70B9A\u590D\u5236\u6570\u636E\u3002</li><li>\u8FD9\u65F6\u51FA\u73B0\u4E86\u4E00\u4E2A\u5D29\u6E83\uFF0C\u4F46Redis\u5177\u6709\u81EA\u52A8\u91CD\u542F\u7CFB\u7EDF\uFF0C\u91CD\u542F\u4E86\u8FDB\u7A0B\uFF0C\u56E0\u4E3A\u5173\u95ED\u4E86\u6301\u4E45\u5316\uFF0C\u8282\u70B9\u91CD\u542F\u540E\u53EA\u6709\u4E00\u4E2A\u7A7A\u7684\u6570\u636E\u96C6\u3002</li><li>\u8282\u70B9B\u548CC\u4ECE\u8282\u70B9A\u8FDB\u884C\u590D\u5236\uFF0C\u73B0\u5728\u8282\u70B9A\u662F\u7A7A\u7684\uFF0C\u6240\u4EE5\u8282\u70B9B\u548CC\u4E0A\u7684\u590D\u5236\u6570\u636E\u4E5F\u4F1A\u88AB\u5220\u9664\u3002</li><li>\u5F53\u5728\u9AD8\u53EF\u7528\u7CFB\u7EDF\u4E2D\u4F7F\u7528Redis Sentinel\uFF0C\u5173\u95ED\u4E86\u4E3B\u670D\u52A1\u5668\u7684\u6301\u4E45\u5316\uFF0C\u5E76\u4E14\u5141\u8BB8\u81EA\u52A8\u91CD\u542F\uFF0C\u8FD9\u79CD\u60C5\u51B5\u662F\u5F88\u5371\u9669\u7684\u3002\u6BD4\u5982\u4E3B\u670D\u52A1\u5668\u53EF\u80FD\u5728\u5F88\u77ED\u7684\u65F6\u95F4\u5C31\u5B8C\u6210\u4E86\u91CD\u542F\uFF0C\u4EE5\u81F3\u4E8ESentinel\u90FD\u65E0\u6CD5\u68C0\u6D4B\u5230\u8FD9\u6B21\u5931\u8D25\uFF0C\u90A3\u4E48\u4E0A\u9762\u8BF4\u7684\u8FD9\u79CD\u5931\u8D25\u7684\u60C5\u51B5\u5C31\u53D1\u751F\u4E86\u3002</li></ul><p>\u5982\u679C\u6570\u636E\u6BD4\u8F83\u91CD\u8981\uFF0C\u5E76\u4E14\u5728\u4F7F\u7528\u4E3B\u4ECE\u590D\u5236\u65F6\u5173\u95ED\u4E86\u4E3B\u670D\u52A1\u5668\u6301\u4E45\u5316\u529F\u80FD\u7684\u573A\u666F\u4E2D\uFF0C\u90FD\u5E94\u8BE5\u7981\u6B62\u5B9E\u4F8B\u81EA\u52A8\u91CD\u542F\u3002</p><h2 id="_7-redis-\u4E3A\u4EC0\u4E48\u4E3B\u4ECE\u5168\u91CF\u590D\u5236\u4F7F\u7528rdb\u800C\u4E0D\u4F7F\u7528aof" tabindex="-1"><a class="header-anchor" href="#_7-redis-\u4E3A\u4EC0\u4E48\u4E3B\u4ECE\u5168\u91CF\u590D\u5236\u4F7F\u7528rdb\u800C\u4E0D\u4F7F\u7528aof" aria-hidden="true">#</a> 7 Redis \u4E3A\u4EC0\u4E48\u4E3B\u4ECE\u5168\u91CF\u590D\u5236\u4F7F\u7528RDB\u800C\u4E0D\u4F7F\u7528AOF\uFF1F</h2><p>1\u3001RDB\u6587\u4EF6\u5185\u5BB9\u662F\u7ECF\u8FC7\u538B\u7F29\u7684\u4E8C\u8FDB\u5236\u6570\u636E\uFF08\u4E0D\u540C\u6570\u636E\u7C7B\u578B\u6570\u636E\u505A\u4E86\u9488\u5BF9\u6027\u4F18\u5316\uFF09\uFF0C\u6587\u4EF6\u5F88\u5C0F\u3002\u800CAOF\u6587\u4EF6\u8BB0\u5F55\u7684\u662F\u6BCF\u4E00\u6B21\u5199\u64CD\u4F5C\u7684\u547D\u4EE4\uFF0C\u5199\u64CD\u4F5C\u8D8A\u591A\u6587\u4EF6\u4F1A\u53D8\u5F97\u5F88\u5927\uFF0C\u5176\u4E2D\u8FD8\u5305\u62EC\u5F88\u591A\u5BF9\u540C\u4E00\u4E2Akey\u7684\u591A\u6B21\u5197\u4F59\u64CD\u4F5C\u3002\u5728\u4E3B\u4ECE\u5168\u91CF\u6570\u636E\u540C\u6B65\u65F6\uFF0C\u4F20\u8F93RDB\u6587\u4EF6\u53EF\u4EE5\u5C3D\u91CF\u964D\u4F4E\u5BF9\u4E3B\u5E93\u673A\u5668\u7F51\u7EDC\u5E26\u5BBD\u7684\u6D88\u8017\uFF0C\u4ECE\u5E93\u5728\u52A0\u8F7DRDB\u6587\u4EF6\u65F6\uFF0C\u4E00\u662F\u6587\u4EF6\u5C0F\uFF0C\u8BFB\u53D6\u6574\u4E2A\u6587\u4EF6\u7684\u901F\u5EA6\u4F1A\u5F88\u5FEB\uFF0C\u4E8C\u662F\u56E0\u4E3ARDB\u6587\u4EF6\u5B58\u50A8\u7684\u90FD\u662F\u4E8C\u8FDB\u5236\u6570\u636E\uFF0C\u4ECE\u5E93\u76F4\u63A5\u6309\u7167RDB\u534F\u8BAE\u89E3\u6790\u8FD8\u539F\u6570\u636E\u5373\u53EF\uFF0C\u901F\u5EA6\u4F1A\u975E\u5E38\u5FEB\uFF0C\u800CAOF\u9700\u8981\u4F9D\u6B21\u91CD\u653E\u6BCF\u4E2A\u5199\u547D\u4EE4\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u4F1A\u7ECF\u5386\u5197\u957F\u7684\u5904\u7406\u903B\u8F91\uFF0C\u6062\u590D\u901F\u5EA6\u76F8\u6BD4RDB\u4F1A\u6162\u5F97\u591A\uFF0C\u6240\u4EE5\u4F7F\u7528RDB\u8FDB\u884C\u4E3B\u4ECE\u5168\u91CF\u590D\u5236\u7684\u6210\u672C\u6700\u4F4E\u3002</p><p>2\u3001\u5047\u8BBE\u8981\u4F7F\u7528AOF\u505A\u5168\u91CF\u590D\u5236\uFF0C\u610F\u5473\u7740\u5FC5\u987B\u6253\u5F00AOF\u529F\u80FD\uFF0C\u6253\u5F00AOF\u5C31\u8981\u9009\u62E9\u6587\u4EF6\u5237\u76D8\u7684\u7B56\u7565\uFF0C\u9009\u62E9\u4E0D\u5F53\u4F1A\u4E25\u91CD\u5F71\u54CDRedis\u6027\u80FD\u3002\u800CRDB\u53EA\u6709\u5728\u9700\u8981\u5B9A\u65F6\u5907\u4EFD\u548C\u4E3B\u4ECE\u5168\u91CF\u590D\u5236\u6570\u636E\u65F6\u624D\u4F1A\u89E6\u53D1\u751F\u6210\u4E00\u6B21\u5FEB\u7167\u3002\u800C\u5728\u5F88\u591A\u4E22\u5931\u6570\u636E\u4E0D\u654F\u611F\u7684\u4E1A\u52A1\u573A\u666F\uFF0C\u5176\u5B9E\u662F\u4E0D\u9700\u8981\u5F00\u542FAOF\u7684\u3002</p><h2 id="_8-redis-\u4E3A\u4EC0\u4E48\u8FD8\u6709\u65E0\u78C1\u76D8\u590D\u5236\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_8-redis-\u4E3A\u4EC0\u4E48\u8FD8\u6709\u65E0\u78C1\u76D8\u590D\u5236\u6A21\u5F0F" aria-hidden="true">#</a> 8 Redis \u4E3A\u4EC0\u4E48\u8FD8\u6709\u65E0\u78C1\u76D8\u590D\u5236\u6A21\u5F0F\uFF1F</h2><p>Redis \u9ED8\u8BA4\u662F\u78C1\u76D8\u590D\u5236\uFF0C\u4F46\u662F<strong>\u5982\u679C\u4F7F\u7528\u6BD4\u8F83\u4F4E\u901F\u7684\u78C1\u76D8\uFF0C\u8FD9\u79CD\u64CD\u4F5C\u4F1A\u7ED9\u4E3B\u670D\u52A1\u5668\u5E26\u6765\u8F83\u5927\u7684\u538B\u529B</strong>\u3002Redis\u4ECE2.8.18\u7248\u672C\u5F00\u59CB\u5C1D\u8BD5\u652F\u6301\u65E0\u78C1\u76D8\u7684\u590D\u5236\u3002\u4F7F\u7528\u8FD9\u79CD\u8BBE\u7F6E\u65F6\uFF0C\u5B50\u8FDB\u7A0B\u76F4\u63A5\u5C06RDB\u901A\u8FC7\u7F51\u7EDC\u53D1\u9001\u7ED9\u4ECE\u670D\u52A1\u5668\uFF0C\u4E0D\u4F7F\u7528\u78C1\u76D8\u4F5C\u4E3A\u4E2D\u95F4\u5B58\u50A8\u3002</p><p><strong>\u65E0\u78C1\u76D8\u590D\u5236\u6A21\u5F0F</strong>\uFF1Amaster\u521B\u5EFA\u4E00\u4E2A\u65B0\u8FDB\u7A0B\u76F4\u63A5dump RDB\u5230slave\u7684socket\uFF0C\u4E0D\u7ECF\u8FC7\u4E3B\u8FDB\u7A0B\uFF0C\u4E0D\u7ECF\u8FC7\u786C\u76D8\u3002\u9002\u7528\u4E8Edisk\u8F83\u6162\uFF0C\u5E76\u4E14\u7F51\u7EDC\u8F83\u5FEB\u7684\u65F6\u5019\u3002</p><p>\u4F7F\u7528<code>repl-diskless-sync</code>\u914D\u7F6E\u53C2\u6570\u6765\u542F\u52A8\u65E0\u78C1\u76D8\u590D\u5236\u3002</p><p>\u4F7F\u7528<code>repl-diskless-sync-delay</code> \u53C2\u6570\u6765\u914D\u7F6E\u4F20\u8F93\u5F00\u59CB\u7684\u5EF6\u8FDF\u65F6\u95F4\uFF1Bmaster\u7B49\u5F85\u4E00\u4E2A<code>repl-diskless-sync-delay</code>\u7684\u79D2\u6570\uFF0C\u5982\u679C\u6CA1slave\u6765\u7684\u8BDD\uFF0C\u5C31\u76F4\u63A5\u4F20\uFF0C\u540E\u6765\u7684\u5F97\u6392\u961F\u7B49\u4E86; \u5426\u5219\u5C31\u53EF\u4EE5\u4E00\u8D77\u4F20\u3002</p><h2 id="_9-redis-\u4E3A\u4EC0\u4E48\u8FD8\u4F1A\u6709\u4ECE\u5E93\u7684\u4ECE\u5E93\u7684\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#_9-redis-\u4E3A\u4EC0\u4E48\u8FD8\u4F1A\u6709\u4ECE\u5E93\u7684\u4ECE\u5E93\u7684\u8BBE\u8BA1" aria-hidden="true">#</a> 9 Redis \u4E3A\u4EC0\u4E48\u8FD8\u4F1A\u6709\u4ECE\u5E93\u7684\u4ECE\u5E93\u7684\u8BBE\u8BA1\uFF1F</h2><p>\u901A\u8FC7\u5206\u6790\u4E3B\u4ECE\u5E93\u95F4\u7B2C\u4E00\u6B21\u6570\u636E\u540C\u6B65\u7684\u8FC7\u7A0B\uFF0C\u4F60\u53EF\u4EE5\u770B\u5230\uFF0C\u4E00\u6B21\u5168\u91CF\u590D\u5236\u4E2D\uFF0C\u5BF9\u4E8E\u4E3B\u5E93\u6765\u8BF4\uFF0C\u9700\u8981\u5B8C\u6210\u4E24\u4E2A\u8017\u65F6\u7684\u64CD\u4F5C\uFF1A<strong>\u751F\u6210 RDB \u6587\u4EF6\u548C\u4F20\u8F93 RDB \u6587\u4EF6</strong>\u3002</p><p>\u5982\u679C\u4ECE\u5E93\u6570\u91CF\u5F88\u591A\uFF0C\u800C\u4E14\u90FD\u8981\u548C\u4E3B\u5E93\u8FDB\u884C\u5168\u91CF\u590D\u5236\u7684\u8BDD\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u4E3B\u5E93\u5FD9\u4E8E fork \u5B50\u8FDB\u7A0B\u751F\u6210 RDB \u6587\u4EF6\uFF0C\u8FDB\u884C\u6570\u636E\u5168\u91CF\u590D\u5236\u3002fork \u8FD9\u4E2A\u64CD\u4F5C\u4F1A\u963B\u585E\u4E3B\u7EBF\u7A0B\u5904\u7406\u6B63\u5E38\u8BF7\u6C42\uFF0C\u4ECE\u800C\u5BFC\u81F4\u4E3B\u5E93\u54CD\u5E94\u5E94\u7528\u7A0B\u5E8F\u7684\u8BF7\u6C42\u901F\u5EA6\u53D8\u6162\u3002\u6B64\u5916\uFF0C\u4F20\u8F93 RDB \u6587\u4EF6\u4E5F\u4F1A\u5360\u7528\u4E3B\u5E93\u7684\u7F51\u7EDC\u5E26\u5BBD\uFF0C\u540C\u6837\u4F1A\u7ED9\u4E3B\u5E93\u7684\u8D44\u6E90\u4F7F\u7528\u5E26\u6765\u538B\u529B\u3002\u90A3\u4E48\uFF0C\u6709\u6CA1\u6709\u597D\u7684\u89E3\u51B3\u65B9\u6CD5\u53EF\u4EE5\u5206\u62C5\u4E3B\u5E93\u538B\u529B\u5462\uFF1F</p><p>\u5176\u5B9E\u662F\u6709\u7684\uFF0C\u8FD9\u5C31\u662F\u201C\u4E3B - \u4ECE - \u4ECE\u201D\u6A21\u5F0F\u3002</p><p>\u5728\u521A\u624D\u4ECB\u7ECD\u7684\u4E3B\u4ECE\u5E93\u6A21\u5F0F\u4E2D\uFF0C\u6240\u6709\u7684\u4ECE\u5E93\u90FD\u662F\u548C\u4E3B\u5E93\u8FDE\u63A5\uFF0C\u6240\u6709\u7684\u5168\u91CF\u590D\u5236\u4E5F\u90FD\u662F\u548C\u4E3B\u5E93\u8FDB\u884C\u7684\u3002\u73B0\u5728\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u201C\u4E3B - \u4ECE - \u4ECE\u201D\u6A21\u5F0F<strong>\u5C06\u4E3B\u5E93\u751F\u6210 RDB \u548C\u4F20\u8F93 RDB \u7684\u538B\u529B\uFF0C\u4EE5\u7EA7\u8054\u7684\u65B9\u5F0F\u5206\u6563\u5230\u4ECE\u5E93\u4E0A</strong>\u3002</p><p>\u7B80\u5355\u6765\u8BF4\uFF0C\u6211\u4EEC\u5728\u90E8\u7F72\u4E3B\u4ECE\u96C6\u7FA4\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u624B\u52A8\u9009\u62E9\u4E00\u4E2A\u4ECE\u5E93\uFF08\u6BD4\u5982\u9009\u62E9\u5185\u5B58\u8D44\u6E90\u914D\u7F6E\u8F83\u9AD8\u7684\u4ECE\u5E93\uFF09\uFF0C\u7528\u4E8E\u7EA7\u8054\u5176\u4ED6\u7684\u4ECE\u5E93\u3002\u7136\u540E\uFF0C\u6211\u4EEC\u53EF\u4EE5\u518D\u9009\u62E9\u4E00\u4E9B\u4ECE\u5E93\uFF08\u4F8B\u5982\u4E09\u5206\u4E4B\u4E00\u7684\u4ECE\u5E93\uFF09\uFF0C\u5728\u8FD9\u4E9B\u4ECE\u5E93\u4E0A\u6267\u884C\u5982\u4E0B\u547D\u4EE4\uFF0C\u8BA9\u5B83\u4EEC\u548C\u521A\u624D\u6240\u9009\u7684\u4ECE\u5E93\uFF0C\u5EFA\u7ACB\u8D77\u4E3B\u4ECE\u5173\u7CFB\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>replicaof \u6240\u9009\u4ECE\u5E93\u7684IP <span class="token number">6379</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u8FD9\u6837\u4E00\u6765\uFF0C\u8FD9\u4E9B\u4ECE\u5E93\u5C31\u4F1A\u77E5\u9053\uFF0C\u5728\u8FDB\u884C\u540C\u6B65\u65F6\uFF0C\u4E0D\u7528\u518D\u548C\u4E3B\u5E93\u8FDB\u884C\u4EA4\u4E92\u4E86\uFF0C\u53EA\u8981\u548C\u7EA7\u8054\u7684\u4ECE\u5E93\u8FDB\u884C\u5199\u64CD\u4F5C\u540C\u6B65\u5C31\u884C\u4E86\uFF0C\u8FD9\u5C31\u53EF\u4EE5\u51CF\u8F7B\u4E3B\u5E93\u4E0A\u7684\u538B\u529B\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://abelsun-1256449468.cos.ap-beijing.myqcloud.com/image/image-20220628225901289.png" alt="image-20220628225901289"></p><p>\u7EA7\u8054\u7684\u201C\u4E3B-\u4ECE-\u4ECE\u201D\u6A21\u5F0F\u597D\u4E86\uFF0C\u5230\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4E86\u89E3\u4E86\u4E3B\u4ECE\u5E93\u95F4\u901A\u8FC7\u5168\u91CF\u590D\u5236\u5B9E\u73B0\u6570\u636E\u540C\u6B65\u7684\u8FC7\u7A0B\uFF0C\u4EE5\u53CA\u901A\u8FC7\u201C\u4E3B - \u4ECE - \u4ECE\u201D\u6A21\u5F0F\u5206\u62C5\u4E3B\u5E93\u538B\u529B\u7684\u65B9\u5F0F\u3002\u90A3\u4E48\uFF0C\u4E00\u65E6\u4E3B\u4ECE\u5E93\u5B8C\u6210\u4E86\u5168\u91CF\u590D\u5236\uFF0C\u5B83\u4EEC\u4E4B\u95F4\u5C31\u4F1A\u4E00\u76F4\u7EF4\u62A4\u4E00\u4E2A\u7F51\u7EDC\u8FDE\u63A5\uFF0C\u4E3B\u5E93\u4F1A\u901A\u8FC7\u8FD9\u4E2A\u8FDE\u63A5\u5C06\u540E\u7EED\u9646\u7EED\u6536\u5230\u7684\u547D\u4EE4\u64CD\u4F5C\u518D\u540C\u6B65\u7ED9\u4ECE\u5E93\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u4E5F\u79F0\u4E3A\u57FA\u4E8E\u957F\u8FDE\u63A5\u7684\u547D\u4EE4\u4F20\u64AD\uFF0C\u53EF\u4EE5\u907F\u514D\u9891\u7E41\u5EFA\u7ACB\u8FDE\u63A5\u7684\u5F00\u9500\u3002</p>`,48),l=[d];function n(o,t){return i(),s("div",null,l)}const f=e(a,[["render",n],["__file","redis-z-interview-master-salve.html.vue"]]);export{f as default};
