import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as p,a as e,b as a,d as c,e as l,r as n}from"./app.b2391a7a.js";const o={},s=l('<p>\u5728\u52A0\u73ED\u6587\u5316\u76DB\u884C\u7684\u4E92\u8054\u7F51\u6587\u5316\u4E2D\uFF0C\u5F88\u591A\u5F00\u53D1\u8005\u4E3A\u81EA\u5DF1\u7684\u4E2A\u4EBA\u6210\u957F\u611F\u5230\u8FF7\u832B\u3002\u8FD9\u7BC7\u6587\u7AE0\u5C06\u4F1A\u4ECE\u6211\u7684\u4E2A\u4EBA\u7ECF\u5386\u51FA\u53D1\uFF0C\u4ECE\u6253\u7834\u6210\u957F\u7684\u8BEF\u533A\u3001\u5982\u4F55\u9AD8\u6548\u7684\u5229\u7528\u65F6\u95F4\u3001\u4EE5\u53CA\u575A\u6301\u6C89\u6DC0\u548C\u5199\u4F5C\u4E09\u4E2A\u65B9\u9762\u4ECB\u7ECD\uFF1A<strong>\u5982\u4F55\u5728\u7E41\u91CD\u7684\u5DE5\u4F5C\u4E2D\u4E5F\u53EF\u4EE5\u505A\u5230\u6301\u7EED\u6210\u957F</strong>\u3002</p><h2 id="\u8BA4\u77E5-\u6253\u7834\u6210\u957F\u8BEF\u533A" tabindex="-1"><a class="header-anchor" href="#\u8BA4\u77E5-\u6253\u7834\u6210\u957F\u8BEF\u533A" aria-hidden="true">#</a> \u8BA4\u77E5\uFF1A\u6253\u7834\u6210\u957F\u8BEF\u533A</h2><p>\u9996\u5148\uFF0C\u6211\u60F3\u8DDF\u5927\u5BB6\u8C08\u51E0\u4E2A\u5173\u4E8E\u6210\u957F\u7684\u8BA4\u77E5\u3002\u5305\u62EC\u6211\u5728\u6211\u7684\u804C\u4E1A\u751F\u6DAF\u7684\u524D\u671F\uFF0C\u4EE5\u53CA\u6211\u8EAB\u8FB9\u7684\u4E00\u4E9B\u540C\u4E8B\u548C\u8BFB\u8005\uFF0C\u5BF9\u6210\u957F\u7684\u4E00\u4E9B\u8BEF\u533A\uFF1A</p><ul><li>\u4E0D\u77E5\u9053\u8BE5\u505A\u70B9\u4EC0\u4E48</li><li>\u505A\u5230\u505A\u4EC0\u4E48\uFF0C\u4E5F\u6709\u52A8\u529B\uFF0C\u6B63\u5728\u6301\u7EED\u63D0\u5347\u81EA\u5DF1</li><li>\u505A\u4EC0\u4E48\u90FD\u6CA1\u52A8\u529B</li></ul><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-77bf7c8d-5df8-461b-92b6-14258f617af8.jpg" alt=""></p><p>\u4F60\u89C9\u5F97\u81EA\u5DF1\u5904\u5728\u4E0B\u9762\u54EA\u4E2A\u72B6\u6001\u91CC\u9762\u5462\uFF1F</p><p>\u5982\u679C\u4F60\u4E0D\u77E5\u9053\u8BE5\u505A\u70B9\u4EC0\u4E48\uFF0C\u53EA\u662F\u673A\u68B0\u7684\u91CD\u590D\u7740\u6BCF\u5929\u7684\u5DE5\u4F5C\uFF0C\u90A3\u5F88\u6709\u53EF\u80FD\u5904\u5728\u4E86\u8FF7\u832B\u671F\u3002</p><p>\u5982\u679C\u4F60\u611F\u89C9\u505A\u4EC0\u4E48\u4E8B\u90FD\u4EC0\u4E48\u52A8\u529B\uFF0C\u4E0A\u73ED\u7684\u65F6\u5019\u5C31\u60F3\u7740\u4E0B\u73ED\uFF0C\u90A3\u4F60\u53EF\u80FD\u5904\u5728\u4E86\u8212\u9002\u533A\u3002</p><p>\u8FF7\u832B\u548C\u8212\u9002\u53EF\u80FD\u90FD\u4F1A\u8BA9\u6211\u4EEC\u5904\u4E8E\u4E00\u4E2A\u505C\u6EDE\u4E0D\u524D\u7684\u72B6\u6001\uFF0C\u6700\u597D\u7684\u72B6\u6001\u5C31\u662F\u4E24\u8005\u4E4B\u95F4\uFF0C\u6301\u7EED\u63D0\u5347\u81EA\u5DF1\u3001\u6301\u7EED\u6210\u957F\u3002\u8FD9\u4E2A\u65F6\u5019\u6709\u7684\u7403\u53CB\u53EF\u80FD\u4F1A\u8BF4\u4E86\uFF0C\u5DE5\u4F5C\u771F\u7684\u592A\u5FD9\u4E86\uFF0C\u771F\u7684\u6CA1\u65F6\u95F4\u987E\u53CA\u4E2A\u4EBA\u7684\u6210\u957F\u554A\u3002</p><h3 id="\u8BEF\u533A-1-\u4E2A\u4EBA\u6210\u957F\u548C\u5DE5\u4F5C\u76F8\u4E92\u51B2\u7A81" tabindex="-1"><a class="header-anchor" href="#\u8BEF\u533A-1-\u4E2A\u4EBA\u6210\u957F\u548C\u5DE5\u4F5C\u76F8\u4E92\u51B2\u7A81" aria-hidden="true">#</a> \u8BEF\u533A 1\uFF1A\u4E2A\u4EBA\u6210\u957F\u548C\u5DE5\u4F5C\u76F8\u4E92\u51B2\u7A81</h3><p>\u8FD9\u5C31\u662F\u6211\u60F3\u8BF4\u7684\u7B2C\u4E00\u4E2A\u8BEF\u533A\uFF0C\u5F88\u591A\u7403\u53CB\u89C9\u5F97\u4E2A\u4EBA\u7684\u6210\u957F\u548C\u5DE5\u4F5C\u4E92\u76F8\u51B2\u7A81\u7684\u3002\u5176\u5B9E\u6070\u6070\u76F8\u53CD\uFF0C\u56E0\u4E3A\u5DE5\u4F5C\u548C\u6210\u957F\u672C\u8EAB\u5C31\u662F\u5BC6\u4E0D\u53EF\u5206\u7684\u3002\u6BD4\u5982\uFF1A</p><ul><li>\u4E1A\u52A1\u80FD\u529B\uFF1A\u5BF9\u5DE5\u4F5C\u4E2D\u4E1A\u52A1\u7684\u6DF1\u5165\u7406\u89E3\uFF0C\u53EF\u4EE5\u8BA9\u4F60\u5728\u5927\u90E8\u5206\u6CA1\u6709\u6DF1\u5165\u7406\u89E3\u4E1A\u52A1\u7684\u540C\u4E8B\u91CC\u8131\u9896\u800C\u51FA\uFF0C\u751A\u81F3\u53EF\u4EE5\u8BA9\u4F60\u6210\u4E3A\u67D0\u4E2A\u9886\u57DF\u7684\u4E13\u5BB6\uFF0C\u8FD9\u5C31\u662F\u6700\u76F4\u63A5\u7684\u6210\u957F\u3002</li></ul><ul><li>\u7F16\u7801\u80FD\u529B\uFF1A\u6709\u7684\u7403\u53CB\u53EF\u80FD\u4F1A\u53BB\u523B\u610F\u7684\u5B66\u4E60\u4E00\u4E9B\uFF0C\u770B\u8D77\u6765\u5F88\u9AD8\u6DF1\u7684\u7B97\u6CD5\u53BB\u63D0\u5347\u7F16\u7801\u80FD\u529B\u3002\u5176\u5B9E\u4ED4\u7EC6\u60F3\u4E00\u60F3\uFF0C\u4F60\u80FD\u591F\u5728\u5DE5\u4F5C\u91CC\u9762\u628A\u4F60\u7684\u4E1A\u52A1\u4EE3\u7801\u91CD\u6784\u7684\u66F4\u6E05\u6670\u4E00\u70B9\uFF0C\u6216\u8005\u8BF4\u5728\u5F00\u59CB\u7F16\u5199\u4E1A\u52A1\u4EE3\u7801\u4E4B\u524D\uFF0C\u5C31\u628A\u4F60\u7684\u4EE3\u7801\u67B6\u6784\uFF0C\u8BBE\u8BA1\u7684\u66F4\u5408\u7406\u4E00\u70B9\uFF0C\u8FD9\u53EF\u80FD\u662F\u4E2A\u66F4\u597D\u7684\u9009\u62E9\u3002</li></ul><ul><li>\u7F16\u7801\u6280\u5DE7\uFF1A\u6709\u7684\u7403\u53CB\u53EF\u80FD\u4F1A\u53BB\u60F3\u505A\u4E00\u4E9B\u5F00\u6E90\u5E93\u7684\u6E90\u7801\u7684\u89E3\u8BFB\u5206\u6790\uFF0C\u5E0C\u671B\u4ECE\u91CC\u9762\u80FD\u83B7\u5F97\u4E00\u4E9B\u7F16\u7801\u6280\u5DE7\uFF0C\u5176\u5B9E\u5728\u5DE5\u4F5C\u4E2D\uFF0C\u5982\u679C\u80FD\u628A\u5E73\u65F6\u7684 Code Review \u505A\u597D\uFF0C\u4F60\u4E5F\u53EF\u80FD\u4F1A\u4ECE\u5F88\u591A\u7ECF\u9A8C\u4E30\u5BCC\u7684\u540C\u4E8B\u4E2D\u83B7\u5F97\u5F88\u591A\u6536\u83B7\uFF0C\u800C\u4E14\u8FD9\u4E9B\u6536\u83B7\u90FD\u662F\u53EF\u4EE5\u76F4\u63A5\u8FD0\u7528\u5230\u5DE5\u4F5C\u91CC\u9762\u7684\u3002</li></ul><ul><li>\u5F71\u54CD\u529B\uFF1A\u5728\u5E73\u65F6\u5DE5\u4F5C\u4E2D\u7684\uFF0C\u5404\u9879\u6280\u672F\u65B9\u6848\u7684\u8BBE\u8BA1\u548C\u8BC4\u5BA1\u4E5F\u53EF\u4EE5\u65E0\u5F62\u4E2D\u63D0\u5347\u4F60\u5728\u56E2\u961F\u5185\u7684\u5F71\u54CD\u529B\uFF0C\u5F53\u7136\u4EE5\u4E0A\u7684\u524D\u63D0\u662F\uFF0C\u4F60\u4E00\u5B9A\u8981\u8BA4\u771F\u5BF9\u5F85\u3002</li></ul><p>\u6240\u4EE5\u6211\u4E00\u76F4\u8BA4\u4E3A\u4E2A\u4EBA\u7684\u6210\u957F\u6709\u5F88\u5927\u4E00\u90E8\u5206\u90FD\u6765\u6E90\u4E8E\u5DE5\u4F5C\uFF0C\u5173\u952E\u662F\u4F60\u600E\u4E48\u770B\u5F85\u6210\u957F\u548C\u5DE5\u4F5C\u7684\u5173\u7CFB\u3002\u5207\u5FCC\u4E3A\u4E86\u6240\u8C13\u4E86\u4E2A\u4EBA\u6210\u957F\u800C\u6020\u6162\u4E86\u5E73\u65F6\u7684\u5DE5\u4F5C\uFF0C<strong>\u4ECE\u5DE5\u4F5C\u4E2D\u8C0B\u6C42\u6210\u957F\u624D\u662F\u6700\u4F73\u7684\u9009\u62E9</strong>\u3002</p><h3 id="\u8BEF\u533A-2-\u505A\u81EA\u5DF1\u6CA1\u4E86\u89E3\u8FC7\u7684\u4E8B\u60C5\u624D\u662F\u6210\u957F" tabindex="-1"><a class="header-anchor" href="#\u8BEF\u533A-2-\u505A\u81EA\u5DF1\u6CA1\u4E86\u89E3\u8FC7\u7684\u4E8B\u60C5\u624D\u662F\u6210\u957F" aria-hidden="true">#</a> \u8BEF\u533A 2\uFF1A\u505A\u81EA\u5DF1\u6CA1\u4E86\u89E3\u8FC7\u7684\u4E8B\u60C5\u624D\u662F\u6210\u957F</h3><p>\u7B2C\u4E8C\u4E2A\u8BEF\u533A\uFF1A\u5F88\u591A\u7403\u53CB\u89C9\u5F97\u4E00\u5B9A\u8981\u505A\u81EA\u5DF1\u6CA1\u4E86\u89E3\u8FC7\u7684\u4E8B\u60C5\u624D\u662F\u6210\u957F\u3002\u5176\u5B9E\u5728\u5E73\u65F6\u7684\u5DE5\u4F5C\u4E2D\uFF0C\u53EA\u662F\u88AB\u52A8\u6267\u884C\u7740\u4E00\u4EF6\u4E8B\u60C5\uFF0C\u548C\u5C06\u4E00\u4EF6\u4E8B\u60C5\u505A\u5230\u6781\u81F4\uFF0C\u6700\u7EC8\u53D6\u5F97\u7684\u7ED3\u679C\u662F\u5B8C\u5168\u4E0D\u540C\u7684\u3002<strong>\u4E0E\u5176\u6D6A\u8D39\u4E00\u4E9B\u65F6\u95F4\u53BB\u4E86\u89E3\u4E00\u4E9B\u53EF\u80FD\u672A\u6765\u4E0D\u53EF\u80FD\u5728\u5DE5\u4F5C\u4E2D\u7528\u5230\u7684\u6280\u672F\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u9009\u62E9\u628A\u73B0\u5728\u624B\u4E0A\u8D1F\u8D23\u7684\u4E8B\u60C5\u505A\u5230\u6781\u81F4\u5462\uFF1F</strong></p><h3 id="\u8BEF\u533A-3-\u5B66\u7684\u4E1C\u897F\u8D8A\u591A-\u6210\u957F\u8D8A\u5FEB" tabindex="-1"><a class="header-anchor" href="#\u8BEF\u533A-3-\u5B66\u7684\u4E1C\u897F\u8D8A\u591A-\u6210\u957F\u8D8A\u5FEB" aria-hidden="true">#</a> \u8BEF\u533A 3\uFF1A\u5B66\u7684\u4E1C\u897F\u8D8A\u591A\uFF0C\u6210\u957F\u8D8A\u5FEB</h3><p>\u7B2C\u4E09\u4E2A\u8BEF\u533A\uFF1A\u548C\u524D\u9762\u7684\u95EE\u9898\u5F88\u7C7B\u4F3C\uFF0C\u5B66\u7684\u4E1C\u897F\u8D8A\u591A\u4E00\u5B9A\u6210\u957F\u8D8A\u5FEB\u5417\uFF1F\u5B9E\u9645\u4E0A\u5F80\u5F80\u4F1A\u8D77\u5230\u76F8\u53CD\u7684\u4F5C\u7528\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-aa05d34e-93e9-4fd3-a134-785e71057390.jpg" alt=""></p><p>\u4E00\u4E2A\u4EBA\u7684\u8111\u5BB9\u91CF\u548C\u8BB0\u5FC6\u529B\u662F\u6709\u9650\u7684\uFF0C\u62D3\u5BBD\u81EA\u5DF1\u7684\u773C\u754C\u548C\u77E5\u8BC6\u5E7F\u5EA6\u662F\u5B8C\u5168\u6CA1\u95EE\u9898\u7684\uFF0C\u8FD9\u53EF\u4EE5\u8BA9\u6211\u4EEC\u5728\u601D\u8003\u95EE\u9898\u7684\u65F6\u5019\u601D\u7EF4\u66F4\u52A0\u6D3B\u8DC3\uFF0C\u4F46\u662F\u62D3\u5BBD\u77E5\u8BC6\u5E7F\u5EA6\u7684\u524D\u63D0\u4E00\u5B9A\u662F\u4F60\u5DF2\u7ECF\u6709\u4E86\u4E00\u9879\u975E\u5E38\u64C5\u957F\u7684\u65B9\u5411\uFF0C\u524D\u9762\u7684 15% \u5E26\u7ED9\u4F60\u7684\u6536\u76CA\u8981\u6BD4\u540E\u9762\u7684 85% \u5927\u5F88\u591A\u3002</p><p>\u90A3\u4E48\u600E\u4E48\u624D\u80FD\u8BA9\u6211\u4EEC\u6709\u9650\u7684\u8111\u5BB9\u91CF\u548C\u8BB0\u5FC6\u529B\u53D1\u6325\u51FA\u66F4\u5927\u7684\u4F5C\u7528\u5462\uFF1F\u9009\u62E9\u5408\u9002\u7684\u65B9\u5411\u662F\u6700\u91CD\u8981\u7684\u3002</p><p>\u4EE5\u6211\u4E2A\u4EBA\u7684\u7ECF\u9A8C\u6765\u770B\uFF0C<strong>\u4E00\u5B9A\u8981\u9009\u62E9\u4F60\u5DE5\u4F5C\u4E2D\u53EF\u80FD\u4F1A\u7528\u5230\u7684\u65B9\u5411\uFF0C\u6216\u8005\u8BF4\u53EF\u4EE5\u843D\u5730\u5230\u5DE5\u4F5C\u4E2D\u7684\u65B9\u5411</strong>\u3002\u6BD4\u5982\uFF0C\u4F60\u60F3\u8981\u7814\u7A76\u6027\u80FD\u4F18\u5316\uFF0C\u4F60\u73B0\u5728\u7684\u5DE5\u4F5C\u662F TOB \u65B9\u5411\uFF0C\u90A3\u4E48\u4E00\u5B9A\u8981\u628A\u91CD\u70B9\u653E\u5728\u7814\u7A76 PC \u573A\u666F\u4E0B\u7684\u6027\u80FD\u4F18\u5316\u3002\u5982\u679C\u4F60\u60F3\u7814\u7A76 TS\uFF0C\u90A3\u4E00\u5B9A\u8981\u4E89\u53D6\u628A\u5DE5\u4F5C\u4E2D\u9879\u76EE\u7684 TS \u8986\u76D6\u7387\u548C\u7F16\u5199\u8D28\u91CF\u63D0\u5347\u4E0A\u53BB\u3002</p><p>\u8FD9\u6837\uFF0C\u4F60\u7684\u4E2A\u4EBA\u6210\u957F\u624D\u80FD\u66F4\u597D\u7684\u878D\u5165\u5230\u5DE5\u4F5C\u4E2D\uFF0C\u624D\u53EF\u4EE5\u8BA9\u4F60\u53D7\u76CA\u6700\u5927\u5316\u3002\u5927\u5BB6\u4E00\u5B9A\u8981\u660E\u767D\u8FD9\u4E00\u70B9\uFF0C\u5426\u5219\u4F60\u53EF\u80FD\u4ED8\u51FA\u518D\u591A\u7684\u52AA\u529B\u90FD\u662F\u5F92\u52B3\u7684\u3002</p><h2 id="\u5B9E\u65BD-\u5982\u4F55\u9AD8\u6548\u7684\u5229\u7528\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u65BD-\u5982\u4F55\u9AD8\u6548\u7684\u5229\u7528\u65F6\u95F4" aria-hidden="true">#</a> \u5B9E\u65BD\uFF1A\u5982\u4F55\u9AD8\u6548\u7684\u5229\u7528\u65F6\u95F4</h2><p>\u5728\u524D\u9762\uFF0C\u6211\u4EEC\u7EA0\u6B63\u4E86\u51E0\u4E2A\u5173\u4E8E\u6210\u957F\u7684\u8BEF\u533A\uFF0C\u4F46\u662F\u5F53\u6211\u4EEC\u6EE1\u6000\u671F\u671B\u7684\u53BB\u6295\u5165\u5230\u6211\u4EEC\u9009\u62E9\u7684\u65B9\u5411\u4E2D\u53BB\u7684\u65F6\u5019\uFF0C\u53EF\u80FD\u5C31\u8981\u9762\u4E34\u4E00\u4E2A\u65B0\u7684\u95EE\u9898\u3002\u65F6\u95F4\u4E0D\u591F\u7528\u554A\uFF0C\u7279\u522B\u662F\u5728\u4E92\u8054\u7F51\u516C\u53F8\u4E0A\u73ED\u7684\u7403\u53CB\uFF0C\u56E0\u4E3A\u5927\u73AF\u5883\u7684\u5F71\u54CD\u53EF\u80FD\u5DE5\u4F5C\u90FD\u975E\u5E38\u7684\u5377\uFF0C\u600E\u4E48\u624D\u80FD\u9AD8\u6548\u7684\u5229\u7528\u65F6\u95F4\u5462\uFF1F</p><p>\u6211\u4E00\u76F4\u5728\u575A\u6301\u5BF9\u5916\u8F93\u51FA\u4E00\u4E9B\u6280\u672F\u6587\u7AE0\u548C\u5206\u4EAB\uFF0C\u5728\u793E\u533A\u4E5F\u6BD4\u8F83\u6D3B\u8DC3\uFF0C\u6240\u4EE5\u6211\u7ECF\u5E38\u542C\u5230\u8BFB\u8005\u5BF9\u6211\u63D0\u8FD9\u6837\u7684\u95EE\u9898\uFF1A</p><blockquote><p>\u4E3A\u4EC0\u4E48\u5DE5\u4F5C\u8FD9\u4E48\u5FD9\uFF0C\u4F60\u8FD8\u80FD\u6301\u7EED\u8F93\u51FA\u8FD9\u4E48\u591A\u5185\u5BB9\uFF1F</p></blockquote><p>\u4F60\u53EF\u80FD\u4F1A\u89C9\u5F97\uFF0C\u6211\u662F\u4E0D\u662F\u53EF\u80FD\u4F1A\u5728\u5DE5\u4F5C\u4E2D\u5077\u61D2\uFF0C\u6216\u8005\u8BF4\u653E\u6C34\u5462\uFF1F\u6211\u8EAB\u8FB9\u6BD4\u8F83\u719F\u6089\u6211\u7684\u540C\u4E8B\u53EF\u80FD\u90FD\u77E5\u9053\uFF0C\u6211\u5728\u5DE5\u4F5C\u4E2D\u57FA\u672C\u4E0A\u4E5F\u662F\u6700\u5377\u7684\u90A3\u4E00\u4E2A\uFF0C\u6211\u4E5F\u7ECF\u5E38\u542C\u5230\u540C\u4E8B\u5BF9\u6211\u8FD9\u6837\u7684\u8BC4\u4EF7\uFF1A</p><blockquote><p>\u611F\u89C9\u4ED6\u53EF\u80FD\u662F\u665A\u4E0A\u4E0D\u7761\u89C9\u3002</p><p>\u4E3A\u4EC0\u4E48\u611F\u89C9\u4ED6\u7684\u4E00\u5929\u53EF\u4EE5\u5F53\u4E24\u5929\u7528\uFF1F</p></blockquote><p>\u6211\u5F53\u7136\u4E0D\u53EF\u80FD\u4E0D\u7761\u89C9\u554A\uFF0C\u6211\u66F4\u6CA1\u6709\u201C\u65F6\u95F4\u8F6C\u6362\u5668\u201D\u8FD9\u6837\u7684\u795E\u5668\u3002\u4EBA\u7684\u4E00\u5929\u53EA\u6709 24 \u5C0F\u65F6\uFF0C\u6211\u5176\u5B9E\u53EA\u662F\u201C\u65F6\u95F4\u7BA1\u7406\u201D\u65B9\u9762\u505A\u7684\u597D\u4E00\u70B9\u800C\u5DF2\u3002</p><h3 id="\u5173\u4E8E\u65F6\u95F4\u7684\u56F0\u60D1" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u65F6\u95F4\u7684\u56F0\u60D1" aria-hidden="true">#</a> \u5173\u4E8E\u65F6\u95F4\u7684\u56F0\u60D1</h3><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-77559ce6-25f8-421d-82f5-69b91a6fe0b8.jpg" alt=""></p><p>\u4E3A\u4EC0\u4E48\u505A\u597D\u201C\u65F6\u95F4\u7BA1\u7406\u201D\u8FD9\u4E48\u91CD\u8981\u5462\uFF0C\u6211\u4EEC\u5148\u6765\u770B\u770B\u4E0B\u9762\u51E0\u4E2A\u573A\u666F\uFF0C\u5927\u5BB6\u662F\u4E0D\u662F\u4E5F\u6709\u8FC7\u8FD9\u6837\u7684\u60F3\u6CD5\uFF1F</p><ul><li><p>\u5F53\u6211\u4EEC\u9762\u524D\u6709\u4E00\u5927\u5806\u9700\u8981\u5904\u7406\u7684\u4E8B\u60C5\u7684\u65F6\u5019\uFF0C\u5F88\u591A\u7403\u53CB\u7684\u7B2C\u4E00\u60F3\u6CD5\u5C31\u662F\u5148\u628A\u6700\u7B80\u5355\u7684\u5B8C\u6210\u4E86</p><ul><li>\u6DF7\u4E71\u578B\uFF0C\u7F3A\u4E4F\u5BF9\u65F6\u95F4\u548C\u7CBE\u529B\u6700\u57FA\u672C\u7684\u5B89\u6392</li></ul></li></ul><ul><li><p>\u6709\u65F6\u5019\u6211\u4EEC\u4ECE\u65E9\u5230\u665A\u5FD9\u4E86\u4E00\u5929\uFF0C\u5F88\u7D2F\uFF0C\u4F46\u662F\u53C8\u611F\u89C9\u81EA\u5DF1\u4EC0\u4E48\u4E5F\u6CA1\u505A</p><ul><li>\u7F3A\u4E4F\u5BF9\u4E8B\u60C5\u4F18\u5148\u7EA7\u7684\u5224\u65AD\u548C\u590D\u76D8\uFF0C\u4F1A\u8BA9\u6211\u4EEC\u4E27\u5931\u5BF9\u5DE5\u4F5C\u7684\u6210\u5C31\u611F</li></ul></li></ul><ul><li><p>\u8FD8\u6CA1\u6709\u5230 DeadLine\uFF0C\u518D\u8EBA\u4E00\u4F1A\u5427</p><ul><li>\u62D6\u5EF6\u75C7\u561B\uFF0C\u7684\u786E\u77E5\u9053\u6211\u8981\u5E72\u4EC0\u4E48\uFF0C\u5C31\u662F\u63D0\u4E0D\u8D77\u52A8\u529B</li></ul></li></ul><p>\u5176\u5B9E\uFF0C\u5982\u679C\u6211\u4EEC\u4E0D\u53BB\u5BF9\u65F6\u95F4\u505A\u4E00\u4E9B\u601D\u8003\uFF0C\u4E0D\u5F15\u5165\u4E00\u4E9B\u9002\u5408\u81EA\u5DF1\u7684\u65B9\u6CD5\u7684\u8BDD\uFF0C\u4E0A\u9762\u8FD9\u4E9B\u95EE\u9898\u6211\u4EEC\u662F\u5F88\u96BE\u89C4\u907F\u6389\u7684\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-27260bfc-0a51-4e30-8dc5-719f41664a59.jpg" alt=""></p><p>\u8FD9\u53EF\u4EE5\u7528\u6211\u4EEC\u7269\u7406\u5B66\u91CC\u9762\u975E\u5E38\u91CD\u8981\u7684\u4E00\u6761\u5B9A\u5F8B\u6765\u89E3\u91CA\uFF0C\u5C31\u662F\u71B5\u589E\u5B9A\u5F8B\u3002</p><blockquote><p>\u5728\u4E00\u4E2A<strong>\u5B64\u7ACB</strong>\u7CFB\u7EDF\u91CC\uFF0C\u5982\u679C\u6CA1\u6709<strong>\u5916\u529B</strong>\u505A\u529F\uFF0C\u5176\u603B\u6DF7\u4E71\u5EA6\uFF08\u5373\u71B5\uFF09\u4F1A\u4E0D\u65AD\u589E\u5927\u3002</p></blockquote><p>\u56E0\u4E3A\u4E8B\u60C5\u603B\u662F\u5411\u7740\u71B5\u589E\u7684\u65B9\u5411\u53D1\u5C55\u7684\uFF0C\u6240\u4EE5\u6240\u6709\u7B26\u5408\u71B5\u589E\u7684\uFF0C\u90FD\u975E\u5E38\u5BB9\u6613\u548C\u8212\u9002\u3002\u6BD4\u5982\u6211\u4EEC\u559C\u6B22\u505A\u7B80\u5355\u7684\u4E8B\u60C5\u3001\u4E0D\u559C\u6B22\u628A\u4E8B\u60C5\u5B89\u6392\u7684\u5F88\u6709\u5E8F\uFF1B\u559C\u6B22\u62D6\u5EF6\u3001\u4E0D\u559C\u6B22\u81EA\u5F8B\u3002\u4F46\u662F\uFF0C\u71B5\u589E\u5B9A\u5F8B\u4E5F\u662F\u4E00\u76F4\u88AB\u8BA4\u4E3A\u662F\u8BA9\u5168\u5B87\u5B99\u90FD\u5F88\u7EDD\u671B\u7684\u5B9A\u5F8B\uFF0C\u56E0\u4E3A\u6301\u7EED\u7684\u71B5\u589E\u4F1A\u8BA9\u4E8B\u7269\u8D70\u5411\u8870\u9000\u3002\u8FD9\u4E2A\u6982\u5FF5\u5F80\u5927\u4E86\u8BB2\uFF0C\u5C31\u662F\u5730\u7403\u5FC5\u5B9A\u4F1A\u8D70\u5411\u706D\u4EA1\u3002\u56DE\u5230\u6211\u4EEC\u73B0\u5B9E\u751F\u6D3B\u4E2D\u6765\uFF0C\u5C31\u662F\u4EBA\u4E00\u5B9A\u4F1A\u5728\u71B5\u589E\u7684\u8FC7\u7A0B\u4E2D\u8D70\u5411\u8870\u9000\u3002\u6240\u4EE5\u859B\u5B9A\u8C14\u5728\u4ED6\u8457\u540D\u7684\u751F\u7269\u5B66\u8457\u4F5C\u300A\u751F\u547D\u662F\u4EC0\u4E48\u300B\u91CC\u9762\u4E5F\u6709\u63D0\u5230\u8FC7\uFF1A</p><blockquote><p>\u4EBA\u6D3B\u7740\u5C31\u662F\u5728\u5BF9\u6297\u71B5\u589E\u5B9A\u5F8B\u3002</p></blockquote><p>\u600E\u4E48\u5BF9\u6297\u5462\uFF1F\u5176\u5B9E\u4ECE\u5B83\u7684\u6982\u5FF5\u91CC\u9762\u6211\u4EEC\u5C31\u53EF\u4EE5\u77E5\u9053\uFF0C<strong>\u60F3\u8981\u907F\u514D\u71B5\u589E\uFF0C\u5149\u9760\u81EA\u7136\u7684\u53D8\u5316\u662F\u5F88\u96BE\u7684\uFF0C\u6211\u4EEC\u5FC5\u987B\u5F15\u5165\u4E00\u4E9B\u5916\u529B\u3002</strong></p><h3 id="\u5E38\u89C1\u7684\u65F6\u95F4\u7BA1\u7406\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5E38\u89C1\u7684\u65F6\u95F4\u7BA1\u7406\u6CD5" aria-hidden="true">#</a> \u5E38\u89C1\u7684\u65F6\u95F4\u7BA1\u7406\u6CD5</h3><p>\u56DE\u5230\u65F6\u95F4\u7BA1\u7406\uFF0C\u6709\u54EA\u4E9B\u5916\u529B\u5462\uFF1F\u6211\u4EEC\u53EF\u4EE5\u5148\u6765\u770B\u51E0\u4E2A\u65B9\u6CD5\u8BBA\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-c0db05b7-0ef9-4541-a399-5bdafb16cc7b.jpg" alt=""></p><ul><li><p><strong>GTD</strong> <strong>\u65F6\u95F4\u7BA1\u7406\u6CD5</strong>\uFF1A\u901A\u8FC7\u8BB0\u5F55\u7684\u65B9\u5F0F\u628A\u5934\u8111\u4E2D\u7684\u5404\u79CD\u4E8B\u60C5\u79FB\u51FA\u6765\uFF0C\u7136\u540E\u6574\u7406\u5B89\u6392\u81EA\u5DF1\u53BB\u6267\u884C\u3002\u5B83\u7684\u6838\u5FC3\u662F\u56F4\u7ED5 \u6536\u96C6\u3001\u6E05\u7406\u3001\u7EC4\u7EC7\u3001\u56DE\u987E\u3001\u6267\u884C\uFF0C\u4E94\u4E2A\u70B9\u6765\u7BA1\u7406\u65F6\u95F4\uFF1A</p><ul><li>\u6536\u96C6\uFF1A\u6240\u6709\u9700\u8981\u6D88\u8017\u65F6\u95F4\u7684\u4E8B\u60C5\u6536\u96C6\u5230\u4E00\u4E2A\u5730\u65B9\uFF0C\u6BD4\u5982\u8BF4\u6211\u4EEC\u770B\u5230\u4E86\u4E00\u7BC7\u6587\u7AE0\uFF0C\u73B0\u5728\u6CA1\u65F6\u95F4\u770B\uFF0C\u8981\u5148\u6536\u85CF\u8D77\u6765\uFF1B\u8001\u677F\u4E34\u65F6\u5B89\u6392\u4E86\u4E00\u4EF6\u4E8B\u60C5\uFF0C\u4F46\u662F\u73B0\u5728\u6709\u5176\u4ED6\u66F4\u9AD8\u4F18\u7684\u4E8B\u60C5\uFF0C\u8981\u5148\u8BB0\u5F55\u4E0B\u6765\u3002\u8FD9\u90FD\u662F\u6536\u96C6\u7684\u8FC7\u7A0B\u3002</li><li>\u6E05\u7406\uFF1A\u8FD9\u4E2A\u8FC7\u7A0B\u5176\u5B9E\u5C31\u662F\u6E05\u7406\u6211\u4EEC\u7684\u6536\u96C6\u7BB1\uFF0C\u56E0\u4E3A\u6536\u96C6\u7684\u8FC7\u7A0B\u5F80\u5F80\u90FD\u662F\u5F88\u5306\u5FD9\u7684\uFF0C\u4E0D\u4F1A\u7ECF\u8FC7\u8FC7\u591A\u7684\u601D\u8003\u3002\u6211\u6267\u884C\u4E4B\u524D\u5F88\u96BE\u4ECE\u4E00\u5927\u5806\u6536\u96C6\u6765\u7684\u4E8B\u60C5\u91CC\u9762\u53BB\u6311\u51FA\u6765\u73B0\u5728\u8981\u505A\u7684\u90A3\u4E00\u4EF6\u3002\u6240\u4EE5\u8981\u6709\u4E00\u4E2A\u5B9A\u671F\u6E05\u7406\u7684\u8FC7\u7A0B\u3002</li><li>\u7EC4\u7EC7\uFF1A\u6E05\u7406\u51FA\u6765\u7684\u4E8B\u9879\uFF0C\u6211\u4EEC\u9700\u8981\u628A\u5B83\u4EEC\u8FDB\u884C\u66F4\u7EC6\u7C92\u5EA6\u7684\u5B89\u6392\uFF0C\u6BD4\u5982\u8F83\u5927\u7684\u4E8B\u9879\u6211\u4EEC\u8981\u8FDB\u884C\u5408\u7406\u62C6\u5206\u3002\u6839\u636E\u4E8B\u60C5\u4E0D\u540C\u7684\u4F18\u5148\u7EA7\u548C\u91CD\u8981\u7A0B\u5EA6\uFF0C\u6211\u4EEC\u8981\u628A\u5B83\u4EEC\u5206\u914D\u5230\u4E0D\u540C\u7684\u65E5\u7A0B\u91CC\u9762\u53BB\u3002</li><li>\u6267\u884C\uFF1A\u6839\u636E\u6211\u4EEC\u4E4B\u524D\u5DF2\u7ECF\u7EC4\u7EC7\u597D\u7684\u4E8B\u9879\u6309\u7167\u5177\u4F53\u7684\u65F6\u95F4\u548C\u5730\u70B9\u53BB\u6267\u884C\u5C31\u597D\u4E86\u3002</li><li>\u590D\u76D8\uFF1A\u5B9A\u671F\u8FDB\u884C\u56DE\u987E\u68C0\u67E5\uFF0C\u56E0\u4E3A\u8BA1\u5212\u80AF\u5B9A\u8D76\u4E0D\u4E0A\u53D8\u5316\uFF0C\u6211\u4EEC\u8981\u968F\u65F6\u5BF9\u6211\u4EEC\u5DF2\u7ECF\u5B89\u6392\u597D\u7684\u4E8B\u9879\u8FDB\u884C\u8C03\u6574\u3002</li></ul></li></ul><ul><li><strong>\u4E09\u53EA\u9752\u86D9\u65F6\u95F4\u7BA1\u7406\u6CD5\uFF1A</strong> \u5982\u679C\u73B0\u5728\u6709\u4E09\u53EA\u9752\u86D9\uFF0C\u5148\u628A\u6700\u5927\u6700\u4E11\u7684\u90A3\u53EA\u5403\u6389\u3002\u4E5F\u5C31\u662F\u8BF4\u5F53\u4F60\u9762\u4E34\u4E09\u4E2A\u91CD\u8981\u4EFB\u52A1\u7684\u65F6\u5019\uFF0C\u5148\u505A\u66F4\u8270\u5DE8\u3001\u66F4\u91CD\u8981\u7684\u90A3\u4E00\u4E2A\u3002\u8FD9\u4E2A\u5176\u5B9E\u672C\u8D28\u4E0A\u5C31\u662F\u5BF9\u4E8B\u60C5\u4F18\u5148\u7EA7\u7684\u7BA1\u7406\u3002</li></ul><ul><li><strong>\u756A\u8304\u65F6\u95F4\u7BA1\u7406\u6CD5</strong>\uFF1A\u628A\u6267\u884C\u7684\u65F6\u95F4\u5212\u5206\u4E3A\u591A\u4E2A\u756A\u8304\u65F6\u95F4\u3002\u4E00\u4E2A\u756A\u8304\u65F6\u95F4\u5305\u542B\u4E24\u4E2A\u90E8\u5206\uFF1A25 \u5206\u949F\u7684\u5DE5\u4F5C\u5B66\u4E60\u548C 5 \u5206\u949F\u7684\u4F11\u606F\u3002\u5F53\u6BCF\u56DB\u4E2A\u756A\u8304\u65F6\u95F4\u4E4B\u540E\u5462\uFF0C\u53EF\u4EE5\u6682\u65F6\u505C\u6B62\u4E00\u4E0B\u5DE5\u4F5C\uFF0C\u8FDB\u884C\u4E00\u6B21\u8F83\u957F\u65F6\u95F4\u7684\u4F11\u606F\uFF0C\u5927\u7EA6 15 \u5230 30 \u5206\u949F\u3002\u8FD9\u91CC\u6709\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u70B9\uFF0C\u5C31\u662F\u4E00\u4E2A\u756A\u8304\u65F6\u95F4\u662F\u4E0D\u53EF\u518D\u5206\u5272\u7684\u3002\u8FD9\u4E2A\u6CD5\u5219\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u4E13\u6CE8\u7684\u6267\u884C\uFF0C\u540C\u65F6\u4E5F\u80FD\u505A\u5230\u5F20\u5F1B\u6709\u5EA6\u3002</li></ul><p><strong>\u65F6\u95F4\u7BA1\u7406\u7684\u65B9\u6CD5\u8BBA\u8FD8\u6709\u5F88\u591A\uFF0C\u4F46\u662F\u5927\u5BB6\u4E00\u5B9A\u8981\u907F\u514D\u8FDB\u884C\u751F\u642C\u786C\u5957</strong>\u3002\u6BCF\u4E2A\u4EBA\u53EF\u80FD\u5B9E\u9645\u60C5\u51B5\u90FD\u4E0D\u4E00\u6837\uFF0C\u9002\u5408\u7684\u65B9\u6CD5\u4E5F\u4E0D\u4E00\u6837\uFF0C\u6BD4\u5982\u6211\u5C31\u89C9\u5F97 GTD \u592A\u590D\u6742\u4E86\uFF0C\u6240\u4EE5\u6211\u6839\u636E\u81EA\u5DF1\u7684\u5B9E\u9645\u60C5\u51B5\u5BF9\u5B83\u8FDB\u884C\u4E86\u4E00\u4E9B\u5220\u51CF\uFF0C\u540C\u65F6\u5728\u4E0D\u540C\u7684\u6B65\u9AA4\u91CC\u53C8\u878D\u5165\u4E86\u5176\u4ED6\u7684\u65B9\u6CD5\u3002\u6211\u628A\u5B83\u5206\u4E3A\u8BB0\u5F55\u3001\u6574\u7406\u3001\u6267\u884C\u4E09\u4E2A\u6B65\u9AA4\u3002</p><h3 id="\u8BB0\u5F55-\u63D0\u9192\u4E8B\u9879" tabindex="-1"><a class="header-anchor" href="#\u8BB0\u5F55-\u63D0\u9192\u4E8B\u9879" aria-hidden="true">#</a> \u8BB0\u5F55\uFF1A\u63D0\u9192\u4E8B\u9879</h3><p>\u7B2C\u4E00\u6B65\uFF0C\u662F\u8BB0\u5F55\u3002\u6211\u4F1A\u5C06\u6211\u6240\u6709\u73B0\u5728\u4E0D\u80FD\u7ACB\u523B\u53BB\u505A\uFF0C\u4F46\u662F\u9700\u8981\u505A\u7684\u4E8B\u60C5\u8BB0\u5F55\u4E0B\u6765\u3002\u4F60\u53EF\u4EE5\u628A\u5B83\u7C7B\u6BD4\u4E8E GTD \u91CC\u9762\u7684\u6536\u96C6\u9636\u6BB5\uFF0C\u4F46\u662F\u53C8\u4E0D\u5B8C\u5168\u4E00\u6837\u3002</p><p><strong>\u6211\u5728\u8BB0\u5F55\u7684\u65F6\u5019\u4F1A\u660E\u786E\u7684\u5C06\u8FD9\u4EF6\u4E8B\u60C5\u6253\u4E0A\u6807\u7B7E</strong>\uFF0C\u6BD4\u5982\uFF1A</p><ul><li><strong>\u5DE5\u4F5C\u6267\u884C\u4E0A\u7684</strong>\uFF1A\u8001\u677F\u4E34\u65F6\u5B89\u6392\u4E86\u4E00\u4EF6\u4EC0\u4E48\u5DE5\u4F5C\u3001\u6628\u5929\u8BC4\u5BA1\u4E86\u4E00\u4E2A\u9700\u6C42\uFF1B</li></ul><ul><li><strong>\u6280\u672F\u5199\u4F5C\u4E0A\u7684</strong>\uFF1A\u521A\u521A\u6536\u5230\u90AE\u4EF6\uFF0CChrome \u66F4\u65B0\u4E86 104 \u7248\u672C\uFF0C\u8981\u7814\u7A76\u4E00\u4E0B\u6709\u54EA\u4E9B\u7B56\u7565\u53D8\u5316\uFF1B</li></ul><ul><li><strong>\u65E5\u5E38\u751F\u6D3B\u4E0A\u7684</strong>\uFF1A\u9171\u6CB9\u5FEB\u5403\u5B8C\u4E86\uFF0C\u4E0B\u6B21\u53BB\u8D85\u5E02\u4E70\u4E00\u74F6\u3002</li></ul><p>\u6309\u7167\u6807\u7B7E\u5F52\u7C7B\u53EF\u4EE5\u8BA9\u6211\u66F4\u6E05\u6670\u7684\u77E5\u9053\u6211\u9700\u8981\u5728\u4F55\u65F6\u4F55\u5730\u53BB\u5B8C\u6210\u8FD9\u4E9B\u4E8B\u60C5\uFF0C\u4F1A\u8BA9\u6211\u540E\u7EED\u7684\u6B65\u9AA4\u66F4\u52A0\u8F7B\u677E\u3002\u5F53\u7136\uFF0C\u6211\u5B9E\u9645\u7684\u6807\u7B7E\u8981\u6BD4\u8FD9\u4E2A\u66F4\u590D\u6742\u4E00\u70B9\uFF0C\u6BCF\u4E2A\u4EBA\u53EF\u80FD\u9700\u8981\u6839\u636E\u81EA\u5DF1\u7684\u5B9E\u9645\u60C5\u51B5\u53BB\u8FDB\u884C\u4E0D\u540C\u7684\u5F52\u7C7B\u3002</p><p><strong>\u5982\u679C\u8FD9\u4EF6\u4E8B\u60C5\u6709\u660E\u786E\u8981\u5B8C\u6210\u7684\u65F6\u95F4\uFF0C\u4E00\u5B9A\u8981\u6302\u4E0A DeadLine \u7684\u65F6\u95F4\u63D0\u9192</strong>\u3002\u6CE8\u610F\u8FD9\u91CC\u4E0D\u662F\u8981\u6267\u884C\u8FD9\u4EF6\u7684\u65F6\u95F4\uFF0C\u800C\u662F\u8FD9\u4EF6\u4E8B\u60C5\u5B8C\u6210\u7684\u622A\u6B62\u65F6\u95F4\uFF0C\u8FD9\u53EF\u4EE5\u8BA9\u6211\u4EEC\u5728\u540E\u7EED\u7684\u4F18\u5148\u7EA7\u5224\u5B9A\u4E2D\u66F4\u8F7B\u677E\u3002</p><blockquote><p>\u6CE8\u610F\uFF1A\u6267\u884C\u65F6\u95F4\u9700\u8981\u6839\u636E\u6211\u4EEC\u5F53\u524D\u5DF2\u6709\u7684\u4E8B\u9879\u5B89\u6392\u4EE5\u53CA\u8FD9\u4EF6\u4E8B\u60C5\u7684\u7D27\u6025\u91CD\u8981\u7A0B\u5EA6\u7EFC\u5408\u5224\u5B9A\uFF0C\u800C\u622A\u6B62\u65F6\u95F4\u5F80\u5F80\u662F\u5F88\u660E\u786E\u7684\u3002</p></blockquote><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-c61ce1ac-5094-4cf4-ab29-94debef209eb.jpg" alt=""></p><p>\u90A3\u8BB0\u5F55\u8FD9\u4E00\u6B65\u7528\u5230\u7684\u5DE5\u5177\u6709\u54EA\u4E9B\u5462\uFF1F\u5176\u5B9E\u6211\u4EEC\u7684\u9700\u6C42\u975E\u5E38\u660E\u786E\uFF1A</p><ul><li>\u6709\u4E00\u4E2A TODO List \u529F\u80FD</li></ul><ul><li>\u53EF\u4EE5\u5BF9\u6BCF\u6761 TODO \u8FDB\u884C\u6807\u7B7E\u5F52\u7C7B</li></ul><ul><li>\u53EF\u4EE5\u6709\u65F6\u95F4\u63D0\u9192\u529F\u80FD</li></ul><ul><li>\u53EF\u4EE5\u8DE8\u5E73\u53F0\uFF0C\u5728 PC\u3001App \u4E0A\u90FD\u53EF\u4EE5\u540C\u6B65\u64CD\u4F5C</li></ul><p>\u4E4B\u524D\u6211\u4E5F\u5C1D\u8BD5\u4E86\u5F88\u591A\u4E2A App\uFF0C\u6700\u540E\uFF0C\u6211\u53D1\u73B0<strong>\u82F9\u679C\u7684\u63D0\u9192\u4E8B\u9879</strong>\u7B80\u76F4\u662F\u4E3A\u6211\u91CF\u8EAB\u5B9A\u505A\u7684\uFF0C\u53EF\u4EE5\u6EE1\u8DB3\u4E0A\u9762\u6211\u63D0\u5230\u7684\u6240\u6709\u8981\u6C42\uFF0C\u800C\u4E14\u529F\u80FD\u975E\u5E38\u7B80\u6D01\uFF0C\u5C31\u53EA\u6709\u6211\u8981\u7684\u8FD9\u51E0\u4E2A\u529F\u80FD\u3002</p><p>\u9664\u4E86\u8FD9\u4E2A\uFF0C\u6211\u8FD8\u4F1A\u7528\u5907\u5FD8\u5F55\u3002\u6309\u7167\u4E0D\u540C\u7684\u7C7B\u578B\u7EC4\u7EC7\u5206\u7C7B\uFF0C\u7136\u540E\u6BCF\u5468\u7684\u4E8B\u60C5\u653E\u5728\u4E00\u4E2A memo \u91CC\uFF0C\u5E76\u4E14\u4F18\u5148\u7EA7\u4F1A\u6309\u7167 TODO \u7684\u5F62\u5F0F\u8BB0\u5F55\u4E0B\u6765\uFF0C\u76F4\u63A5\u6253\u94A9\u8868\u793A\u5B8C\u6210\u3002\u540C\u65F6\uFF0C\u4E00\u4E9B\u91CD\u8981\u7684\u4E8B\u60C5\uFF0C\u6BD4\u5982\u8BF4\u7403\u53CB\u7684\u63D0\u95EE\uFF0C\u6211\u4F1A\u5728\u8FD9\u91CC\u505A\u4E00\u4E2A\u5907\u4EFD\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-15daa890-85e4-423f-8195-a1216d952522.png" alt=""></p><p>\u597D\uFF0C\u73B0\u5728\u6211\u4EEC\u5DF2\u7ECF\u628A\u6240\u6709\u9700\u8981\u505A\u7684\u4E8B\u60C5\u8BB0\u5F55\u4E0B\u6765\u4E86\uFF0C\u6211\u4EEC\u8981\u5728\u4EC0\u4E48\u5730\u70B9\u4EC0\u4E48\u65F6\u95F4\u628A\u8FD9\u4E9B\u4E8B\u60C5\u5B8C\u6210\u6389\u5462\uFF1F\u4E4B\u524D\u5B57\u8282\u8DF3\u52A8\u7684 Boss \u5F20\u4E00\u9E23\u5728\u4ED6\u7684\u4E00\u6B21\u91C7\u8BBF\u91CC\u66FE\u7ECF\u6709\u8FD9\u6837\u4E00\u53E5\u8BDD\uFF0C\u6211\u5370\u8C61\u975E\u5E38\u6DF1\u523B\uFF1A</p><blockquote><p>\u66F4\u5927\u7684\u6548\u7387\u6765\u81EA\u4E8E\u91CD\u8981\u4E8B\u60C5\u4E0A\u505A\u5F97\u597D\uFF0C\u800C\u4E0D\u662F\u5728\u5904\u5904\u505A\u5F97\u597D\u3002</p></blockquote><p>\u4E8B\u60C5\u662F\u6C38\u8FDC\u505A\u4E0D\u5B8C\u7684\uFF0C\u4F46\u662F\u65F6\u95F4\u662F\u6052\u5B9A\u6709\u9650\u7684\uFF0C\u6211\u4EEC\u5728\u63D0\u9192\u4E8B\u9879\u91CC\u8BB0\u5F55\u7684\u4E8B\u9879\u4F1A\u8D8A\u6765\u8D8A\u591A\uFF0C\u4E0B\u9762\u6700\u91CD\u8981\u7684\u5C31\u662F\u5982\u4F55\u5728\u6709\u9650\u7684\u65F6\u95F4\u91CC\u628A\u8FD9\u5176\u4E2D\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u5B8C\u6210\u6389\uFF0C\u4EE5\u53D6\u5F97\u6700\u5927\u7684\u6536\u76CA\u4E86\u3002</p><h3 id="\u6574\u7406-\u4F18\u5148\u7EA7-\u5236\u5B9A\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u6574\u7406-\u4F18\u5148\u7EA7-\u5236\u5B9A\u8BA1\u5212" aria-hidden="true">#</a> \u6574\u7406\uFF1A\u4F18\u5148\u7EA7 &amp; \u5236\u5B9A\u8BA1\u5212</h3><p>\u4E0B\u4E00\u6B65\u662F\u6574\u7406\uFF0C\u4E5F\u5C31\u662F\u628A\u6211\u8BB0\u5F55\u7684\u4E8B\u9879\u6309\u7167\u4F18\u5148\u7EA7\u548C\u91CD\u8981\u7A0B\u5EA6\u5B89\u6392\u5230\u5177\u4F53\u7684\u6267\u884C\u8BA1\u5212\u4E2D\u53BB\u3002\u8FD9\u4E00\u6B65\u5B9E\u9645\u4E0A\u662F\u6211\u5BF9 GTD \u4E2D\u6E05\u7406\u548C\u7EC4\u7EC7\u4E24\u4E2A\u6B65\u9AA4\u7684\u878D\u5408\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-fe1147ad-a234-458b-8cf0-ceac27ca9e23.jpg" alt=""></p><p>\u8FD9\u91CC\u7528\u5230\u4E86\u4E00\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u6CD5\u5219\uFF0C\u5C31\u662F\u56DB\u8C61\u9650\u6CD5\u5219\uFF0C\u6211\u4EEC\u6839\u636E\u4E8B\u60C5\u7684\u91CD\u8981\u548C\u7D27\u6025\u7A0B\u5EA6\u5206\u6210\u56DB\u7C7B\uFF1A</p><ul><li><strong>\u91CD\u8981\u4E14\u7D27\u6025\u7684\u4E8B\u60C5</strong>\uFF1A\u5982\u679C\u6211\u73B0\u5728\u6CA1\u6709\u5176\u4ED6\u91CD\u8981\u7D27\u6025\u7684\u4E8B\u60C5\uFF0C\u6211\u4E00\u822C\u4F1A\u9009\u62E9\u7ACB\u523B\u53BB\u505A\u3002\u5982\u679C\u73B0\u5728\u6CA1\u65F6\u95F4\u7684\u8BDD\uFF0C\u90A3\u6211\u5C31\u4F1A\u628A\u5B83\u5B89\u6392\u5230\u6700\u8FD1\u7684\u65F6\u95F4\u4E2D\u53BB\u3002</li></ul><ul><li><strong>\u91CD\u8981\u4E0D\u7D27\u6025\u7684\u4E8B\u60C5</strong>\uFF1A\u65E2\u7136\u4E8B\u60C5\u5F88\u91CD\u8981\uFF0C\u6211\u4EEC\u4E00\u5B9A\u8981\u628A\u5B83\u505A\u597D\u3002\u5982\u679C\u4E8B\u60C5\u6BD4\u8F83\u5927\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u5B83\u62C6\u5206\u6210\u4E00\u4E9B\u5C0F\u7684\u4EFB\u52A1\uFF0C\u6709\u8BA1\u5212\u7684\u5B89\u6392\u5230\u65E5\u7A0B\u4E2D\u53BB\u3002\u8FD9\u91CC\u6211\u53EF\u80FD\u4F1A\u7528\u5230\u4E24\u4E2A\u5DE5\u5177\uFF0C\u7B2C\u4E00\u4E2A\u662F\u9700\u6C42\u7BA1\u7406\u5DE5\u5177\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u5E2E\u52A9\u6211\u66F4\u597D\u7684\u8FDB\u884C\u4EFB\u52A1\u62C6\u5206\u3001\u56E2\u961F\u534F\u4F5C\u3001\u8DDF\u8FDB\u8FDB\u5EA6\u7B49\u7B49\uFF0C\u5728\u8FD9\u4E00\u6B65\u5B9E\u9645\u4E0A\u5C31\u5B8C\u6210\u4E86\u4ECE\u4EE3\u529E\u4E8B\u9879\u5230\u9700\u6C42\u7BA1\u7406\u7684\u8F6C\u6362\uFF0C\u76F8\u4FE1\u6BCF\u4E2A\u516C\u53F8\u90FD\u6709\u81EA\u5DF1\u7684\u9700\u6C42\u7BA1\u7406\u5DE5\u5177\uFF0C\u4F60\u4EEC\u4E5F\u53EF\u4EE5\u81EA\u5DF1\u9009\u62E9\uFF1B\u7B2C\u4E8C\u4E2A\u662F\u65E5\u7A0B\u7BA1\u7406\u5DE5\u5177\uFF0C\u5B83\u53EF\u4EE5\u66F4\u7EC6\u7C92\u5EA6\u7684\u5B89\u6392\u6211\u4EEC\u5728\u4EC0\u4E48\u65F6\u95F4\u6267\u884C\u4EC0\u4E48\u4E8B\u60C5\uFF0C\u907F\u514D\u4E8B\u60C5\u8FC7\u591A\u7684\u65F6\u5019\u4E0D\u77E5\u9053\u8BE5\u505A\u4EC0\u4E48\uFF0C\u6BD4\u5982\u5B57\u8282\u7684\u540C\u5B66\u5927\u90E8\u5206\u5E94\u8BE5\u90FD\u4F1A\u7528\u98DE\u4E66\u7684\u65E5\u7A0B\u7BA1\u7406\uFF0C<strong>\u5B83\u4E0D\u5E94\u8BE5\u53EA\u4F5C\u4E3A\u4E00\u4E2A\u7EA6\u4F1A\u7684\u5DE5\u4F5C\uFF0C\u66F4\u5EFA\u8BAE\u5927\u5BB6\u628A\u81EA\u5DF1\u7684\u4E8B\u60C5\u548C\u8BA1\u5212\u4E5F\u8BB0\u5F55\u4E0A\u53BB\u3002</strong></li></ul><ul><li><strong>\u4E0D\u91CD\u8981\u4F46\u7D27\u6025\u7684\u4E8B\u60C5</strong>\uFF1A\u8FD9\u79CD\u4E8B\u60C5\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u5B83\u8BA9\u7ED9\u522B\u4EBA\u53BB\u505A\uFF0C\u5BF9\u4F60\u6765\u8BB2\u53EF\u80FD\u4E0D\u91CD\u8981\uFF0C\u5BF9\u5176\u4ED6\u4EBA\u6765\u8BB2\u53EF\u80FD\u5C31\u662F\u4E00\u6B21\u673A\u4F1A\uFF0C\u6240\u4EE5\u8FD9\u91CC\u5176\u5B9E\u4E5F\u662F\u60F3\u8BF4\uFF0C\u4E0D\u4E00\u5B9A\u6240\u6709\u7684\u4E8B\u60C5\u90FD\u8981\u4EB2\u529B\u4EB2\u4E3A\uFF0C\u5176\u4ED6\u4EBA\u4E0D\u4E00\u5B9A\u4F1A\u6BD4\u4F60\u5B8C\u6210\u7684\u5DEE\uFF0C\u6216\u8005\u8BF4<strong>\u5728\u4F60\u6709\u5176\u4ED6\u4E8B\u60C5\u5E76\u884C\u7684\u524D\u63D0\u4E0B\uFF0C\u505A\u5BF9 \u201C\u53D6\u820D\u201D \u662F\u5F88\u91CD\u8981\u7684</strong>\u3002</li></ul><ul><li><strong>\u4E0D\u91CD\u8981\u4E5F\u4E0D\u7D27\u6025\u7684\u4E8B\u60C5\uFF1A</strong> \u8FD9\u79CD\u4E8B\u5E72\u8106\u8FDE\u8BA1\u5212\u4E5F\u4E0D\u8981\u505A\u4E86\uFF0C\u4EC0\u4E48\u65F6\u5019\u524D\u9762\u4E09\u79CD\u4E8B\u6CA1\u6709\u4E86\uFF0C\u518D\u62FF\u51FA\u6765\u770B\u8FD9\u4E9B\u4E8B\u60C5\u3002\u53E6\u5916\u4E5F\u4E0D\u5EFA\u8BAE\u5C31\u628A\u5B83\u4EEC\u653E\u5728\u5F85\u529E\u4E8B\u9879\u672C\u6765\u7684\u4F4D\u7F6E\uFF0C\u56E0\u4E3A\u968F\u7740\u65F6\u95F4\u7684\u79EF\u7D2F\u8FD9\u79CD\u5806\u79EF\u4F1A\u8BA9\u4F60\u7684\u8BB0\u5F55\u5F88\u96BE\u6574\u7406\u3002\u5EFA\u8BAE\u628A\u5B83\u4EEC\u5355\u72EC\u5F52\u7F6E\u5230\u4E00\u4E2A\u6216\u591A\u4E2A\u5F52\u6863\u533A\u57DF\uFF0C\u4E0B\u6B21\u5728\u505A\u6574\u7406\u7684\u65F6\u5019\u4E5F\u8981\u62FF\u51FA\u6765\u770B\u770B\uFF0C\u56E0\u4E3A\u4E0D\u7D27\u6025\u7684\u4E8B\u60C5\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\u4E5F\u6709\u53EF\u80FD\u53D8\u5F97\u7D27\u6025\u3002</li></ul><p>\u4F46\u662F\uFF0C\u8BA1\u5212\u6C38\u8FDC\u8D76\u4E0D\u4E0A\u53D8\u5316\uFF0C\u4F60\u53EF\u80FD\u5DF2\u7ECF\u5B89\u6392\u597D\u4E86\u4F60\u4E00\u5929\u7684\u89C4\u5212\uFF0C\u4F46\u662F\u7A81\u7136\u63A5\u5230\u4E86\u5176\u4ED6\u7684\u4E8B\u60C5\uFF0C\u8FD9\u662F\u975E\u5E38\u6B63\u5E38\u7684\uFF0C\u8FD9\u79CD\u573A\u666F\u57FA\u672C\u4E0A\u4E5F\u53D1\u751F\u5728\u6211\u6BCF\u5929\u7684\u751F\u6D3B\u5DE5\u4F5C\u91CC\u9762\uFF0C\u6211\u76F8\u4FE1\u5927\u5BB6\u4E5F\u90FD\u4E00\u6837\u3002</p><p>\u7ED3\u679C\u5176\u5B9E\u53EA\u6709\u4E24\u4E2A\uFF0C\u505A\u8FD8\u662F\u4E0D\u505A\u3002\u5982\u679C\u505A\uFF0C\u662F\u73B0\u5728\u505A\uFF0C\u8FD8\u662F\u4EE5\u540E\u505A\u3002\u6211\u4EEC\u540C\u6837\u53EF\u4EE5\u7528\u56DB\u8C61\u9650\u6CD5\u5219\u601D\u8003\uFF1A</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-97e767ce-dc89-4054-a84b-85005f574aa7.jpg" alt=""></p><p><strong>\u5982\u679C\u662F\u4E0D\u91CD\u8981\u4E5F\u4E0D\u7D27\u6025\u7684\u4E8B\u60C5\uFF0C\u73B0\u5728\u5C31\u76F4\u63A5\u548C\u5BF9\u65B9\u8BF4 No\uFF0C\u5982\u679C\u4E8B\u60C5\u5F88\u91CD\u8981\uFF0C\u4F46\u662F\u4E0D\u7D27\u6025\uFF0C\u4F60\u4F9D\u7136\u53EF\u4EE5\u73B0\u5728\u548C\u5BF9\u65B9\u8BF4 NO\uFF0C\u4F46\u662F\u4F60\u53EF\u4EE5\u548C\u5BF9\u65B9\u660E\u786E DeadLine\uFF0C\u7136\u540E\u5C06\u5B83\u8BB0\u5F55\u5230\u5F85\u529E\u4E8B\u9879\u4E2D\u53BB</strong>\u3002</p><p>\u6211\u89C1\u8FC7\u5F88\u591A\u7403\u53CB\u4E0D\u592A\u4F1A\u62D2\u7EDD\uFF0C\u53EF\u80FD\u9047\u5230\u4E86\u4E34\u65F6\u7684\u4E8B\u60C5\u7ACB\u523B\u5C31\u53BB\u505A\u4E86\uFF0C\u5B9E\u9645\u4E0A\u4F60\u73B0\u5728\u505A\u548C\u4EE5\u540E\u505A\u8FD9\u4EF6\u4E8B\u53EF\u80FD\u6CA1\u6709\u4EC0\u4E48\u533A\u522B\uFF0C\u5BF9\u65B9\u53EF\u80FD\u4E5F\u4E0D\u7740\u6025\uFF0C\u53EA\u662F\u9700\u8981\u4F60\u4E00\u4E2A\u6392\u671F\uFF0C\u4F46\u662F\u8FD9\u53EF\u80FD\u4F1A\u5BFC\u81F4\u4F60\u73B0\u5728\u9AD8\u4F18\u5148\u7EA7\u7684\u4E8B\u60C5 Delay\u3002\u8FD9\u4E5F\u6709\u53EF\u80FD\u662F\u5BFC\u81F4\u4F60\u56DB\u5904\u6551\u706B\uFF0C\u4F46\u662F\u4E00\u5929\u4E0B\u6765\u5374\u53C8\u89C9\u5F97\u4EC0\u4E48\u4E5F\u6CA1\u5E72\u7684\u539F\u56E0\u3002</p><p>\u5982\u679C\u662F\u91CD\u8981\u7D27\u6025\u7684\u4E8B\u60C5\uFF0C\u90A3\u9700\u8981\u548C\u4F60\u73B0\u5728\u5728\u505A\u7684\u4E8B\u60C5\u5BF9\u6BD4\u4E00\u4E0B\uFF0C<strong>\u5982\u679C\u786E\u5B9E\u6BD4\u73B0\u5728\u7684\u4E8B\u60C5\u4F18\u5148\u7EA7\u8981\u9AD8\uFF0C\u5C31\u4E50\u89C2\u7684\u9762\u5BF9\u8BA1\u5212\u8C03\u6574\u5C31\u597D\u4E86\uFF0C\u4F60\u8981\u660E\u767D\uFF0C\u8FD9\u4F9D\u7136\u53EA\u662F\u4F60\u5728\u5728\u91CD\u590D\u6267\u884C\u4F60\u7684 \u201C\u6574\u7406\u201D \u8FD9\u4E2A\u8FC7\u7A0B</strong>\u3002</p><p>\u8FD8\u662F\u90A3\u53E5\u8BDD\uFF0C\u4E8B\u60C5\u662F\u505A\u4E0D\u5B8C\u7684\uFF0C\u4F46\u662F\u65F6\u95F4\u662F\u6709\u9650\u7684\uFF0C\u6211\u4EEC\u53EA\u8981\u6309\u7167\u6211\u4EEC\u7684\u6807\u51C6\u5BF9\u4E8B\u60C5\u8FDB\u884C\u8BB0\u5F55\u3001\u6574\u7406\u3001\u6267\u884C\uFF0C\u4E0D\u7BA1\u662F\u4E4B\u524D\u8BA1\u5212\u597D\u7684\u4E8B\u60C5\uFF0C\u8FD8\u662F\u4E34\u65F6\u65B0\u589E\u7684\u4E8B\u60C5\uFF0C\u6839\u636E\u6211\u4EEC\u7684\u65B9\u6CD5\u6700\u7EC8\u90FD\u53EF\u4EE5\u88AB\u5B89\u6392\u5230\u5177\u4F53\u7684\u8BA1\u5212\u4E2D\u3002\u4E25\u683C\u6267\u884C\u6211\u4EEC\u65E2\u5B9A\u7684\u6807\u51C6\uFF0C\u4E50\u89C2\u7684\u9762\u5BF9\u8BA1\u5212\u8C03\u6574\u3002</p><p>\u5C31\u6BD4\u5982\u8BF4\uFF0C\u6211\u73B0\u5728\u5C31\u4F1A\u4F18\u5148\u5904\u7406\u7403\u53CB\u7684\u95EE\u9898\uFF0C\u5FAE\u4FE1\u597D\u53CB\u7684\u63D0\u95EE\u5C31\u4F1A\u653E\u5230\u540E\u9762\u4E00\u4E9B\u3002</p><h3 id="\u6267\u884C-\u5F20\u5F1B\u6709\u5EA6\u7684\u4E13\u6CE8" tabindex="-1"><a class="header-anchor" href="#\u6267\u884C-\u5F20\u5F1B\u6709\u5EA6\u7684\u4E13\u6CE8" aria-hidden="true">#</a> \u6267\u884C\uFF1A\u5F20\u5F1B\u6709\u5EA6\u7684\u4E13\u6CE8</h3><p>\u6700\u540E\uFF0C\u5C31\u662F\u6267\u884C\u9636\u6BB5\u4E86\u3002\u5BF9\u6211\u800C\u8A00\uFF0C\u756A\u8304\u65F6\u95F4\u7BA1\u7406\u6CD5\u8FD8\u662F\u5F88\u4E0D\u9519\u7684\uFF0C\u53EA\u4E0D\u8FC7\u6211\u53EF\u80FD\u4F1A\u9002\u5F53\u7684\u8C03\u6574\u4E00\u4E2A\u756A\u8304\u949F\u7684\u65F6\u95F4\uFF0C\u6211\u4F1A\u628A\u4E00\u4E2A\u756A\u8304\u949F\u7684\u65F6\u95F4\u8C03\u7684\u66F4\u957F\u4E00\u70B9\uFF0C\u8FD9\u4E2A\u56E0\u4EBA\u800C\u5F02\uFF0C\u5927\u5BB6\u53EF\u4EE5\u591A\u5C1D\u8BD5\u4E00\u4E0B\uFF0C\u627E\u5230\u81EA\u5DF1\u66F4\u8212\u670D\u7684\u65F6\u95F4\u3002</p><p>\u5BF9\u4E8E\u77ED\u65F6\u95F4\u4F11\u606F\uFF0C\u65B9\u5F0F\u6709\u5F88\u591A\u79CD\u3002\u6BD4\u5982\uFF0C\u8FDC\u773A\u653E\u677E\u4E00\u4E0B\u773C\u775B\u5C5E\u4E8E\u4F11\u606F\u3001\u7AD9\u7ACB\u6D3B\u52A8\u4E00\u4E0B\u810A\u690E\u5C5E\u4E8E\u4F11\u606F\uFF0C\u56DE\u590D\u4E00\u4E9B\u6D88\u606F\u4E5F\u5C5E\u4E8E\u4F11\u606F\u3002\u5982\u679C\u53EA\u662F\u5750\u5728\u5EA7\u4F4D\u4E0A\u5237\u5237\u624B\u673A\uFF0C\u8FD9\u79CD\u4F11\u606F\u53EF\u80FD\u6CA1\u6709\u4EC0\u4E48\u610F\u4E49\u3002\u957F\u65F6\u95F4\u4F11\u606F\uFF0C\u6211\u4F1A\u9009\u62E9\u5904\u7406\u4E00\u4E9B\u4E34\u65F6\u63D2\u4EF6\u6765\u7684\u4E8B\u60C5\u3001\u5305\u62EC\u91CD\u65B0\u8FDB\u884C\u8BB0\u5F55\u3001\u5B89\u6392\u7684\u6B65\u9AA4\u3001\u6216\u8005\u89E3\u7B54\u4E00\u4E9B\u95EE\u9898\u3001\u505A\u4E00\u4E9B CodeReview \u7B49\u7B49\u3002</p><p>\u756A\u8304\u949F\u6709\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u7279\u70B9\uFF0C\u5C31\u662F<strong>\u6267\u884C\u65F6\u4E00\u5B9A\u8981\u4FDD\u6301\u4E13\u6CE8\uFF0C\u5982\u679C\u4E0D\u662F\u4EC0\u4E48\u7279\u522B\u91CD\u8981\u3001\u7D27\u6025\u7684\u4E8B\u60C5\u4E00\u5B9A\u4E0D\u80FD\u6253\u65AD\u8FD9\u4E2A\u79CD</strong>\uFF0C\u5C3D\u91CF\u5230\u8FBE\u4F11\u606F\u65F6\u95F4\u518D\u53BB\u5904\u7406\u5176\u4ED6\u4E8B\u60C5\uFF0C\u517B\u6210\u8FD9\u6837\u7684\u4E60\u60EF\uFF0C\u5C31\u7B97\u65F6\u95F4\u5F88\u5C11\uFF0C\u6267\u884C\u4E00\u4EF6\u4E8B\u7684\u6548\u7387\u4E5F\u4F1A\u975E\u5E38\u9AD8\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-72050c1d-a3fa-43d1-8a92-22c13119eb96.jpg" alt=""></p><p>\u5E02\u9762\u4E0A\u6709\u975E\u5E38\u591A\u4F7F\u7528\u756A\u8304\u65F6\u95F4\u7BA1\u7406\u6CD5\u7684\u5DE5\u5177\uFF0C\u505A\u7684\u82B1\u91CC\u80E1\u54E8\u7684\uFF0C\u9009\u62E9\u4F7F\u7528\u4E5F\u662F\u56E0\u4EBA\u800C\u5F02\u3002\u5982\u679C\u4F60\u81EA\u63A7\u80FD\u529B\u8F83\u5DEE\uFF0C\u4F60\u53EF\u4EE5\u9009\u62E9\u4E00\u4E2A\u5E2E\u4F60\u81EA\u52A8\u9501\u5C4F\u3001\u5C4F\u853D\u5176\u4ED6\u5E94\u7528\u7684 App\u3002\u4E0D\u8FC7\u6211\u66F4\u503E\u5411\u4E8E\u66F4\u7B80\u6D01\u4E00\u70B9\u7684\uFF0C\u53EA\u9700\u8981\u5E26\u6709\u756A\u8304\u949F\u548C\u4F11\u606F\u65F6\u95F4\u7684\u5012\u8BA1\u65F6\u63D0\u9192\u4EE5\u53CA\u7B80\u5355\u7684\u7EDF\u8BA1\u529F\u80FD\u5C31\u53EF\u4EE5\u4E86\u3002</p><p>\u4EE5\u4E0A\u5C31\u662F\u6211\u7684\u65F6\u95F4\u7BA1\u7406\u65B9\u6CD5\uFF0C\u5176\u5B9E\u53EF\u4EE5\u770B\u4F5C\u662F\u4E00\u4E2A\u7CBE\u7B80\u7248\u7684 GTD\uFF0C\u6211\u628A GTD \u4E2D\u7684\u6E05\u7406\u3001\u7EC4\u7EC7\u548C\u590D\u76D8\u8FDB\u884C\u4E86\u5408\u5E76\u3002\u540C\u65F6\u5728\u6BCF\u4E2A\u6B65\u9AA4\u91CC\u6839\u636E\u6211\u7684\u4E2A\u4EBA\u60C5\u51B5\u878D\u5165\u4E86\u4E00\u4E9B\u5176\u4ED6\u65B9\u6CD5\u3002\u5927\u5BB6\u4E5F\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u5B9E\u9645\u60C5\u51B5\uFF0C\u78E8\u5408\u51FA\u5C5E\u4E8E\u81EA\u5DF1\u7684\u4E00\u5957\u65B9\u6CD5\u3002</p><p>\u73B0\u5728\u65B9\u6CD5\u6709\u4E86\uFF0C\u5982\u4F55\u80FD\u628A\u8FD9\u5957\u65B9\u6CD5\u5B9E\u65BD\u4E0B\u53BB\uFF0C\u505A\u5230\u77E5\u884C\u5408\u4E00\uFF0C\u5C31\u5F88\u5173\u952E\u4E86\u3002\u6211\u8FD9\u91CC\u4E5F\u6709\u4E00\u4E9B\u81EA\u5DF1\u603B\u7ED3\u51FA\u7684\u4E00\u4E9B\u6280\u5DE7\u5206\u4EAB\u7ED9\u5927\u5BB6\u3002</p><h3 id="\u6280\u5DE7-\u627E\u51FA\u4E8B\u60C5\u7684\u4EA4\u96C6" tabindex="-1"><a class="header-anchor" href="#\u6280\u5DE7-\u627E\u51FA\u4E8B\u60C5\u7684\u4EA4\u96C6" aria-hidden="true">#</a> \u6280\u5DE7\uFF1A\u627E\u51FA\u4E8B\u60C5\u7684\u4EA4\u96C6</h3><p>\u6211\u4EEC\u4F1A\u5728\u8BB0\u5F55\u7684\u9636\u6BB5\u5806\u79EF\u51FA\u975E\u5E38\u591A\u9700\u8981\u505A\u7684\u4E8B\u60C5\uFF0C\u867D\u7136\u662F\u4E00\u4EF6\u4E00\u4EF6\u8BB0\u7684\uFF0C\u4F46\u662F\u6CA1\u5FC5\u8981\u4E00\u4EF6\u4E00\u4EF6\u7684\u53BB\u6267\u884C\u3002<strong>\u6211\u4EEC\u53EA\u8981\u5728\u6574\u7406\u9636\u6BB5\u66F4\u597D\u7684\u627E\u5230\u8FD9\u4E9B\u4E8B\u60C5\u7684\u4EA4\u96C6\uFF0C\u5C31\u53EF\u4EE5\u66F4\u9AD8\u6548\u7684\u5229\u7528\u6709\u9650\u7684\u65F6\u95F4\u3002</strong></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-f6c0887b-f901-460b-b4fb-61281a9a9e94.jpg" alt=""></p><p>\u6BD4\u5982\uFF0C\u6211\u4EEC\u5728\u5199\u4F5C\u7684 tag \u91CC\u8BB0\u5F55\u4E86\u6700\u8FD1\u8981\u8F93\u51FA\u4E00\u7BC7\u6280\u672F\u6587\u7AE0\uFF0C\u53E6\u5916\u5728\u5DE5\u4F5C\u7684 tag \u91CC\u8BB0\u5F55\u4E86\u7CFB\u7EDF\u7A33\u5B9A\u6027\u5F88\u5DEE\uFF0C\u9700\u8981\u8FDB\u884C\u4E00\u6B21\u4F18\u5316\u3002\u90A3\u4E48\u4E24\u8005\u7684\u4EA4\u96C6\u5C31\u53EF\u4EE5\u662F\uFF1A\u7814\u7A76\u5E76\u843D\u5730\u7CFB\u7EDF\u7A33\u5B9A\u6027\u65B9\u6848\uFF0C\u5E76\u5C06\u5B9E\u8DF5\u7ECF\u9A8C\u6574\u7406\u6210\u4E00\u7BC7\u6280\u672F\u6587\u7AE0\u3002</p><h3 id="\u6280\u5DE7-\u9AD8\u6548\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u6280\u5DE7-\u9AD8\u6548\u6C9F\u901A" aria-hidden="true">#</a> \u6280\u5DE7\uFF1A\u9AD8\u6548\u6C9F\u901A</h3><p>\u5728\u73B0\u9636\u6BB5\u7684\u5DE5\u4F5C\u4E2D\uFF0C\u548C\u522B\u4EBA\u4EA4\u6D41\u6C9F\u901A\uFF0C\u4F1A\u5360\u7528\u6389\u6211\u975E\u5E38\u5927\u4E00\u90E8\u5206\u65F6\u95F4\u3002\u6211\u4EEC\u9700\u8981\u8BC4\u9700\u6C42\u3001\u8DDF\u8FDB\u5EA6\u3001\u63A8\u4E13\u9879\u3001\u89E3\u7B54\u95EE\u9898\u7B49\u7B49\uFF0C\u5982\u679C\u8FD9\u4E9B\u4E8B\u60C5\u90FD\u8981\u9760\u6253\u5B57\u6765\u5B8C\u6210\uFF0C\u6548\u7387\u662F\u975E\u5E38\u4F4E\u7684\u3002\u4E00\u822C\u6765\u8BB2\uFF0C<strong>\u9762\u5BF9\u9762\u6C9F\u901A\u7684\u6548\u7387\u8981\u5927\u4E8E\u8BED\u97F3\u6C9F\u901A\uFF0C\u800C\u8BED\u97F3\u6C9F\u901A\u7684\u6548\u7387\u8981\u5927\u4E8E\u6253\u5B57\u3002</strong></p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-a422f9d3-40ac-4fd9-9294-b87b089c5399.jpg" alt=""></p><p>\u56E0\u4E3A\u6211\u4EEC\u5728\u6253\u5B57\u65F6\uFF0C\u5C31\u7B97\u52A0\u4E86\u518D\u591A\u7684\u8868\u60C5\u548C\u8BED\u6C14\u8BCD\u4E5F\u5F88\u96BE\u6E05\u6670\u7684\u611F\u53D7\u5230\u5BF9\u65B9\u7684\u8BED\u6C14\u3001\u6001\u5EA6\u3001\u5FC3\u60C5\uFF0C\u66F4\u662F\u5F88\u96BE\u63A2\u5BFB\u51FA\u4E00\u4E9B\u6DF1\u5C42\u6B21\u7684\u60F3\u6CD5\uFF0C\u6253\u5B57\u65F6\u7684\u65E0\u6548\u6C9F\u901A\u4F1A\u6D6A\u8D39\u6389\u5927\u91CF\u7684\u65F6\u95F4\u3002\u6240\u4EE5\uFF0C\u5F53\u6211\u8981\u6C9F\u901A\u4E00\u4EF6\u7A0D\u5FAE\u590D\u6742\u4E00\u70B9\u7684\u4E8B\u60C5\u7684\u65F6\u5019\uFF0C\u5982\u679C\u9700\u8981\u6C9F\u901A\u7684\u4EBA\u79BB\u6211\u5F88\u8FD1\uFF0C\u6211\u4E00\u5B9A\u4F1A\u9009\u62E9\u9762\u5BF9\u9762\u6C9F\u901A\uFF1B\u5982\u679C\u548C\u6211\u4E0D\u5728\u4E00\u4E2A\u4F4D\u7F6E\uFF0C\u6211\u4F1A\u5C1D\u8BD5\u8BED\u97F3\u6C9F\u901A\uFF1B\u5982\u679C\u5BF9\u65B9\u77ED\u65F6\u95F4\u5185\u65E0\u6CD5\u8BED\u97F3\u4EA4\u6D41\uFF0C\u518D\u9009\u62E9\u6253\u5B57\u6C9F\u901A\u3002</p><p>\u5F53\u7136\uFF0C\u8FD9\u91CC\u53EA\u9488\u5BF9\u4E00\u4E9B\u6BD4\u8F83\u590D\u6742\u7684\u5185\u5BB9\u6C9F\u901A\uFF0C\u5982\u679C\u4F60\u9700\u8981\u6C9F\u901A\u7684\u5185\u5BB9\u6709\u660E\u786E\u7684\u7ED3\u8BBA\u6BD4\u5982\uFF1A\u6709\u3001\u6CA1\u6709\u3001\u5BF9\u3001\u9519\uFF0C\u8FD9\u6837\u7684\u7B54\u6848\uFF0C\u6253\u5B57\u53CD\u800C\u66F4\u7701\u4E8B\u4E00\u70B9\u3002</p><h3 id="\u6280\u5DE7-\u788E\u7247\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#\u6280\u5DE7-\u788E\u7247\u65F6\u95F4" aria-hidden="true">#</a> \u6280\u5DE7\uFF1A\u788E\u7247\u65F6\u95F4</h3><p>\u6700\u540E\u4E00\u4E2A\uFF0C\u5C31\u662F\u788E\u7247\u65F6\u95F4\u4E86\u3002\u8FD9\u4E2A\u4E5F\u662F\u56E0\u4EBA\u800C\u5F02\u7684\uFF0C\u5728\u6211\u7684\u65E5\u5E38\u751F\u6D3B\u91CC\u6709\u4E24\u6BB5\u975E\u5E38\u56FA\u5B9A\u7684\u788E\u7247\u65F6\u95F4\u3002\u56E0\u4E3A\u6211\u4F4F\u7684\u79BB\u516C\u53F8\u6BD4\u8F83\u8FDC\uFF0C\u6240\u4EE5\u6211\u6BCF\u5929\u901A\u52E4\u65F6\u95F4\u662F\u5F88\u957F\u7684\u3002\u5927\u6982\u4E0A\u73ED\u8981\u4E00\u4E2A\u5C0F\u65F6\u3001\u4E0B\u73ED\u8981 40 \u5206\u949F\u3002\u4F46\u662F\u8FD9\u4E24\u6BB5\u65F6\u95F4\u5BF9\u6211\u662F\u975E\u5E38\u91CD\u8981\u7684\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-09958f95-ac75-494f-a52a-ca0294edff4b.jpg" alt=""></p><p>\u6211\u4E00\u822C\u4F1A\u5728\u4E0A\u73ED\u901A\u52E4\u4E2D\u8FDB\u884C \u201C\u6574\u7406\u201D \u8FD9\u4E2A\u6B65\u9AA4\uFF0C\u5B9A\u5236\u6211\u4E00\u5929\u7684\u8BA1\u5212\u3002\u5982\u679C\u5F53\u5929\u4E8B\u60C5\u5806\u79EF\u8F83\u591A\uFF0C\u6211\u4F1A\u5728\u665A\u4E0A\u901A\u52E4\u7684\u65F6\u95F4\u8FDB\u884C\u9002\u5F53\u8C03\u6574\uFF0C\u8865\u5145\u5230\u7B2C\u4E8C\u5929\u7684\u8BA1\u5212\u4E2D\u3002\u5269\u4E0B\u7684\u65F6\u95F4\u5C31\u662F\u9605\u8BFB\u4E86\uFF0C\u6211\u5728\u4E00\u5468\u4E2D\u5927\u90E8\u5206\u7684\u9605\u8BFB\u65F6\u95F4\u5C31\u6765\u81EA\u4E8E\u8FD9\u4E24\u6BB5\u901A\u52E4\u3002\u8FD9\u91CC\u7684\u9605\u8BFB\u4E0D\u4EC5\u4EC5\u4F1A\u5305\u62EC\u4E00\u4E9B\u6280\u672F\u6587\u7AE0\u548C\u4E66\u7C4D\uFF0C\u4E5F\u4F1A\u5305\u62EC\u5F53\u5929\u5DE5\u4F5C\u4E2D\u8981\u6D89\u53CA\u7684\u4E00\u4E9B\u6587\u6863\u3002\u8FD9\u4E0D\u4EC5\u8BA9\u6211\u53EF\u4EE5\u5FEB\u901F\u8C03\u6574\u8FDB\u5165\u5DE5\u4F5C\u72B6\u6001\uFF0C\u4E5F\u80FD\u8BA9\u6211\u6301\u7EED\u4FDD\u6301\u63A5\u6536\u65B0\u9C9C\u4E8B\u7269\u3002\u6240\u4EE5\uFF0C\u5728\u6211\u4E4B\u524D\u7684\u5DE5\u4F5C\u7ECF\u5386\u91CC\u9762\uFF0C\u5BF9\u901A\u52E4\u8DDD\u79BB\u4E00\u76F4\u90FD\u662F\u5F88\u5305\u5BB9\u7684\u3002\u751A\u81F3\u4E2D\u95F4\u6709\u4E00\u6BB5\u65F6\u95F4\u6211\u642C\u5230\u4E86\u79BB\u516C\u53F8\u5F88\u8FD1\u7684\u5730\u65B9\uFF0C\u8FD8\u8BA9\u6211\u975E\u5E38\u4E0D\u9002\u5E94\u3002</p><p>\u6211\u76F8\u4FE1\u5927\u5BB6\u6216\u591A\u6216\u5C11\u5728\u5E73\u65F6\u4E5F\u90FD\u4F1A\u6709\u8FD9\u6837\u7684\u788E\u7247\u65F6\u95F4\uFF0C\u56E0\u4E3A\u4F60\u4E0D\u53EF\u80FD\u6240\u6709\u7684\u65F6\u95F4\u90FD\u82B1\u8D39\u5728\u7D27\u5F20\u7684\u5DE5\u4F5C\u4E2D\uFF0C\u6BD5\u7ADF\u4EBA\u7684\u7CBE\u529B\u662F\u6709\u9650\u7684\uFF0C\u4E0D\u53EF\u80FD\u6240\u6709\u65F6\u95F4\u90FD\u5728\u4FDD\u6301\u4E13\u6CE8\u3002<strong>\u6240\u4EE5\u600E\u4E48\u53BB\u5B89\u6392\u597D\u8FD9\u4E9B\u788E\u7247\u65F6\u95F4\uFF0C\u53EF\u80FD\u4F1A\u6210\u4E3A\u4F60\u80FD\u8131\u9896\u800C\u51FA\u7684\u4E00\u4E2A\u5236\u80DC\u6CD5\u5B9D</strong>\u3002</p><h2 id="\u79D8\u8BC0-\u575A\u6301\u6C89\u6DC0\u548C\u5199\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u79D8\u8BC0-\u575A\u6301\u6C89\u6DC0\u548C\u5199\u4F5C" aria-hidden="true">#</a> \u79D8\u8BC0\uFF1A\u575A\u6301\u6C89\u6DC0\u548C\u5199\u4F5C</h2><p>\u5173\u4E8E\u65F6\u95F4\u7684\u5206\u4EAB\u90FD\u662F\u8FD9\u4E48\u591A\uFF0C\u4E0B\u9762\u6211\u4EEC\u6765\u804A\u804A\u6C89\u6DC0\u548C\u5199\u4F5C\u3002\u6211\u8BA4\u4E3A\u5B83\u662F\u80FD\u8BA9\u6211\u4EEC\u4FDD\u6301\u6301\u7EED\u6210\u957F\u7684\u79D8\u8BC0\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-6f432f38-7404-4662-93dd-2a89fcc338b7.jpg" alt=""></p><p>\u6280\u672F\u5199\u4F5C\u4E0D\u4E00\u5B9A\u662F\u5BF9\u5916\u53D1\u8868\u7684\u4E00\u7BC7\u6587\u7AE0\uFF0C\u6211\u4E2A\u4EBA\u628A\u5199\u4F5C\u5206\u6210\u4E86\u4E24\u7C7B\uFF1A</p><ul><li>\u4E2A\u4EBA\u7684\u6C89\u6DC0\uFF1A\u53EF\u4EE5\u662F\u5E73\u65F6\u7684\u5DE5\u4F5C\u8BB0\u5F55\u3001\u4E00\u4E9B\u77E5\u8BC6\u788E\u7247\u3001\u4F60\u7684\u4E2A\u4EBA\u77E5\u8BC6\u4F53\u7CFB\u7B49\u7B49\uFF1B</li></ul><ul><li>\u5BF9\u5916\u7684\u5206\u4EAB\uFF1A\u5305\u62EC\u5DE5\u4F5C\u4E2D\u7684\u6280\u672F\u65B9\u6848\u3001\u5BF9\u5916\u53D1\u8868\u7684\u6280\u672F\u6587\u7AE0\u7B49\u7684\u3002</li></ul><p>\u4E24\u8005\u5404\u6709\u5404\u7684\u597D\u5904\uFF0C\u6211\u5EFA\u8BAE\u5927\u5BB6\u4E24\u7C7B\u5199\u4F5C\u90FD\u8981\u6267\u884C\u8D77\u6765\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5148\u4ECE\u6700\u7B80\u5355\u7684\u4E2A\u4EBA\u6C89\u6DC0\u5F00\u59CB\u3002</p><h3 id="\u4E2A\u4EBA\u6C89\u6DC0" tabindex="-1"><a class="header-anchor" href="#\u4E2A\u4EBA\u6C89\u6DC0" aria-hidden="true">#</a> \u4E2A\u4EBA\u6C89\u6DC0</h3><h4 id="\u6709\u4EC0\u4E48\u597D\u5904" tabindex="-1"><a class="header-anchor" href="#\u6709\u4EC0\u4E48\u597D\u5904" aria-hidden="true">#</a> \u6709\u4EC0\u4E48\u597D\u5904\uFF1F</h4><p>\u505A\u4E2A\u4EBA\u6C89\u6DC0\u6709\u4EC0\u4E48\u597D\u5904\u5462\uFF0C\u9996\u5148\u5C31\u662F\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5DE9\u56FA\u8BB0\u5FC6\u3002\u5173\u4E8E\u8BB0\u5FC6\uFF0C\u6211\u76F8\u4FE1\u5927\u5BB6\u5728\u4E0A\u5B66\u7684\u65F6\u5019\u90FD\u5B66\u8FC7\u4E00\u4E2A\u7406\u8BBA\uFF0C\u5982\u679C\u6211\u4EEC\u8BB0\u4F4F\u4E86\u4E00\u4E2A\u77E5\u8BC6\u70B9\uFF0C\u90A3\u4E48\u4E00\u4E2A\u6708\u8FC7\u540E\uFF0C\u4F60\u5BF9\u5B83\u7684\u8BB0\u5FC6\u53EF\u80FD\u53EA\u4F1A\u5269\u4E0B 20%\u3002\u5F53\u7136\u5982\u679C\u662F\u5929\u624D\u7684\u8BDD\u6211\u4EEC\u5C31\u4E0D\u8BF4\u4E86\u54C8\uFF0C\u6211\u76F8\u4FE1\u5927\u90E8\u5206\u4EBA\uFF0C\u5305\u62EC\u6211\u4E5F\u662F\u4E00\u6837\u7684\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-f86c4a8c-8b7c-4884-ace8-4e47c62551da.jpg" alt=""></p><p>\u6BD4\u5982\uFF0C\u6709\u65F6\u5019\u6211\u4EEC\u5B8C\u6210\u4E86\u4E00\u4EF6\u5DE5\u4F5C\uFF0C\u53EF\u80FD\u5F53\u65F6\u6210\u5C31\u611F\u633A\u5F3A\u7684\uFF0C\u4F46\u662F\u5982\u679C\u4E0D\u8FDB\u884C\u603B\u7ED3\u548C\u6C89\u6DC0\uFF0C\u8FC7\u4E86\u4E00\u6BB5\u65F6\u95F4\u4F60\u4F1A\u53D1\u73B0\u8111\u5B50\u91CC\u5173\u4E8E\u5B83\u7684\u8BB0\u5FC6\u5DF2\u7ECF\u5269\u4E0D\u4E0B\u4EC0\u4E48\u4E86\uFF0C\u53EF\u80FD\u5230\u8FBE\u4E00\u5B9A\u7684\u65F6\u95F4\u4E4B\u540E\uFF0C\u8FD9\u4EF6\u4E8B\u662F\u4E0D\u662F\u4F60\u505A\u7684\u597D\u50CF\u6CA1\u6709\u4EC0\u4E48\u533A\u522B\u4E86\u3002\u6240\u4EE5\u5F88\u591A\u540C\u5B66\u5DE5\u4F5C\u5F88\u957F\u65F6\u95F4\u4E86\uFF0C\u4F46\u662F\u53C8\u60F3\u4E0D\u660E\u767D\u81EA\u5DF1\u505A\u4E86\u4EC0\u4E48\uFF0C\u867D\u7136\u8868\u9762\u4E0A\u5DE5\u4F5C\u7ECF\u9A8C\u591A\u4E00\u70B9\uFF0C\u4F46\u662F\u8DDF\u521A\u6BD5\u4E1A\u7684\u540C\u5B66\u6709\u4EC0\u4E48\u533A\u522B\u5462\uFF1F\u76F8\u53CD\uFF0C\u5982\u679C\u4F60\u6BCF\u5B8C\u6210\u4E00\u4EF6\u5DE5\u4F5C\u5C31\u8FDB\u884C\u9002\u5F53\u7684\u6C89\u6DC0\u548C\u603B\u7ED3\uFF0C\u90A3\u7ED3\u679C\u53EF\u80FD\u5C31\u5B8C\u5168\u4E0D\u4E00\u6837\u4E86\u3002\u4E00\u6BB5\u65F6\u95F4\u4E4B\u540E\u4F60\u4F1A\u6E05\u6670\u7684\u77E5\u9053\u4F60\u5B66\u5230\u4E86\u4EC0\u4E48\uFF0C\u7559\u4E0B\u4E86\u4EC0\u4E48\uFF0C\u751A\u81F3\u5BF9\u4F60\u5DE5\u4F5C\u4E2D\u9057\u7559\u7684\u75DB\u70B9\u4EE5\u53CA\u5BF9\u672A\u6765\u7684\u89C4\u5212\u90FD\u4F1A\u975E\u5E38\u6E05\u6670\u3002</p><p>\u53E6\u5916\u4E00\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u70B9\uFF0C\u5C31\u662F\u6211\u4EEC\u53EF\u4EE5\u628A\u5DE5\u4F5C\u4E2D\u6C89\u6DC0\u4E0B\u6765\u7684\u5DE5\u4F5C\u603B\u7ED3\u3001\u77E5\u8BC6\u788E\u7247\uFF0C\u5B8C\u5584\u5230\u6211\u4EEC\u7684\u77E5\u8BC6\u4F53\u7CFB\u4E2D\u53BB\u3002\u6211\u8BA4\u4E3A<strong>\u4E00\u5957\u81EA\u5DF1\u7684\u77E5\u8BC6\u4F53\u7CFB\u662F\u6BCF\u4E2A\u4EBA\u5728\u6210\u957F\u8FC7\u7A0B\u4E2D\u4E0D\u53EF\u6216\u7F3A\u7684\u4E00\u90E8\u5206</strong>\u3002\u5B83\u7684\u4F5C\u7528\u975E\u5E38\u5927\uFF0C\u9996\u5148\u5B83\u53EF\u4EE5\u8BA9\u6211\u4EEC\u5BF9\u81EA\u5DF1\u7684\u8BA4\u77E5\u66F4\u52A0\u6E05\u6670\u3002\u6BD4\u5982\uFF1A\u6211\u5230\u5E95\u4E86\u89E3\u591A\u5C11\u5185\u5BB9\u3001\u6211\u6700\u64C5\u957F\u4EC0\u4E48\u3001\u6211\u6709\u54EA\u4E9B\u80FD\u529B\u6BD4\u8F83\u6B20\u7F3A\u3001\u6B20\u7F3A\u7684\u8FD9\u4E9B\u80FD\u529B\u54EA\u4E9B\u662F\u5DE5\u4F5C\u4E2D\u9700\u8981\u7528\u5230\u7684\uFF1F</p><p>\u5176\u5B9E\u5C31\u8FD9\u7B80\u5355\u7684\u51E0\u70B9\u597D\u591A\u540C\u5B66\u90FD\u8BF4\u4E0D\u6E05\u695A\u3002\u53E6\u5916\u5B83\u4E5F\u53EF\u4EE5\u4F5C\u4E3A\u6211\u4EEC\u7684\u77E5\u8BC6\u7D22\u5F15\uFF0C\u5C31\u50CF\u524D\u9762\u63D0\u5230\u7684\uFF1A\u5C31\u7B97\u6211\u4EEC\u53CA\u65F6\u8FDB\u884C\u4E86\u6C89\u6DC0\u548C\u603B\u7ED3\uFF0C\u4E5F\u4E0D\u53EF\u80FD\u4FDD\u7559 100% \u7684\u8BB0\u5FC6\uFF0C\u4F46\u662F\u5F53\u6211\u4EEC\u7528\u5230\u8FD9\u4E9B\u77E5\u8BC6\u7684\u65F6\u5019\uFF0C\u6709\u4E00\u4E2A\u5730\u65B9\u80FD\u591F\u8FDB\u884C\u5FEB\u901F\u7D22\u5F15\u5E2E\u6211\u4EEC\u627E\u5230\u5B83\u4EEC\uFF0C\u5C31\u53EF\u4EE5\u5E2E\u6211\u4EEC\u8282\u7701\u5927\u91CF\u7684\u65F6\u95F4\u3002</p><h4 id="\u6784\u5EFA\u4E2A\u4EBA\u77E5\u8BC6\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u6784\u5EFA\u4E2A\u4EBA\u77E5\u8BC6\u7CFB\u7EDF" aria-hidden="true">#</a> \u6784\u5EFA\u4E2A\u4EBA\u77E5\u8BC6\u7CFB\u7EDF</h4><p>\u6240\u4EE5\u4E0B\u9762\u6211\u4EEC\u6765\u770B\u770B\u600E\u4E48\u901A\u8FC7\u6C89\u5E95\u548C\u603B\u7ED3\u6784\u5EFA\u6211\u4EEC\u7684\u4E2A\u4EBA\u77E5\u8BC6\u7CFB\u7EDF\u3002\u4E00\u4E2A\u57FA\u672C\u7684\u4E2A\u4EBA\u77E5\u8BC6\u7CFB\u7EDF\u9700\u8981\u6EE1\u8DB3\u4E0B\u9762\u4E09\u4E2A\u8981\u70B9\uFF1A</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-833f1ba5-6ed1-47f7-902d-84607620f36c.jpg" alt=""></p><ul><li><strong>\u65B9\u4FBF\u7BA1\u7406\uFF1A</strong> \u6574\u4E2A\u6D41\u7A0B\u6211\u4EEC\u4E0D\u8981\u8BBE\u8BA1\u7684\u592A\u590D\u6742\u592A\u957F\uFF0C\u5426\u5219\u5F88\u96BE\u6301\u7EED\u4E0B\u53BB\u3002\u6211\u66FE\u7ECF\u4E5F\u6C89\u8FF7\u4E8E\u5404\u79CD\u82B1\u91CC\u80E1\u54E8\u7684\u7B14\u8BB0\u8F6F\u4EF6\u7684\u8FC1\u79FB\uFF0C\u540E\u6765\u53D1\u73B0\u5F88\u591A\u529F\u80FD\u90FD\u7528\u4E0D\u4E0A\u3001\u590D\u6742\u7684\u6D41\u7A0B\u4E5F\u6BD4\u8F83\u96BE\u575A\u6301\u4E0B\u53BB\uFF0C\u5176\u5B9E\u53EA\u8981\u80FD\u6EE1\u8DB3\u6211\u4EEC\u5BF9\u77E5\u8BC6\u7BA1\u7406\u6700\u57FA\u672C\u7684\u8981\u6C42\uFF0C\u6700\u7B80\u6D01\u7684\u5C31\u662F\u6700\u597D\u7684\u3002</li></ul><ul><li><strong>\u8981\u4F53\u7CFB\u5316\uFF1A</strong> \u6BD4\u5982\u6211\u4EEC\u53EF\u4EE5\u7528\u601D\u7EF4\u5BFC\u56FE\u7684\u65B9\u5F0F\u5C06\u4F60\u7684\u6574\u4E2A\u77E5\u8BC6\u4F53\u7CFB\u5448\u73B0\u51FA\u6765\uFF0C\u5426\u5219\u5982\u679C\u53EA\u662F\u6742\u4E71\u65E0\u7AE0\u7684\u8BB0\u5F55\uFF0C\u6CA1\u6709\u6210\u4F53\u7CFB\u7684\u6574\u7406\u3002\u90A3\u540E\u9762\u8FD9\u4E2A\u77E5\u8BC6\u7CFB\u7EDF\u80AF\u5B9A\u4F1A\u5E9F\u6389\uFF0C\u8FD9\u5176\u5B9E\u8FD8\u662F\u4E00\u4E2A\u71B5\u589E\u7684\u8FC7\u7A0B\u3002\u6211\u4EEC\u5FC5\u987B\u8981\u7ECF\u8FC7\u4F53\u7CFB\u5316\u7684\u6574\u7406\u624D\u80FD\u5C06\u77E5\u8BC6\u53D8\u5F97\u6709\u5E8F\uFF0C\u907F\u514D\u7CFB\u7EDF\u5D29\u6E83\u3002\u53E6\u5916\uFF0C\u4F53\u7CFB\u5316\u7684\u7EC4\u7EC7\u4F1A\u8BA9\u6211\u4EEC\u968F\u65F6\u770B\u5230\u6211\u4EEC\u7684\u77E5\u8BC6\u4F53\u7CFB\u662F\u4E0D\u65AD\u6269\u5145\u7684\uFF0C\u8FD9\u4F1A\u8BA9\u4F60\u975E\u5E38\u6709\u6210\u5C31\u611F\u3002\u540C\u65F6\u77E5\u8BC6\u4F53\u7CFB\u5B8C\u5584\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4F60\u4E5F\u80FD\u6E05\u6670\u7684\u770B\u5230\u4F60\u7684\u5F3A\u9879\u548C\u6B20\u7F3A\u4E4B\u5904\uFF0C\u8BA9\u6211\u4EEC\u5BF9\u81EA\u5DF1\u6709\u66F4\u6E05\u6670\u7684\u8BA4\u77E5\uFF0C\u4E5F\u80FD\u8BA9\u6211\u4EEC\u6709\u9488\u5BF9\u6027\u7684\u5236\u5B9A\u5B66\u4E60\u8BA1\u5212\u3002</li></ul><ul><li><strong>\u65B9\u4FBF\u63D0\u53D6\uFF1A</strong> \u5982\u679C\u77E5\u8BC6\u7684\u83B7\u53D6\u8DEF\u5F84\u5F88\u6DF1\uFF0C\u5C31\u7B97\u662F\u8BB0\u5F55\u4E0B\u6765\uFF0C\u90A3\u540E\u7EED\u53EF\u80FD\u4E5F\u8981\u5403\u7070\u4E86\uFF0C\u6240\u4EE5\u5FEB\u901F\u7D22\u5F15\u7684\u80FD\u529B\u8FD8\u662F\u5F88\u91CD\u8981\u7684\u3002</li></ul><p>\u5C31\u50CF\u4E0A\u9762\u7684\u65F6\u95F4\u7BA1\u7406\u4E00\u6837\uFF0C\u77E5\u8BC6\u7BA1\u7406\u4E5F\u662F\u56E0\u4EBA\u800C\u5F02\u7684\uFF0C\u5927\u5BB6\u9700\u8981\u6839\u636E\u81EA\u5DF1\u7684\u4E2A\u4EBA\u60C5\u51B5\u9009\u62E9\u9002\u5408\u81EA\u5DF1\u7684\u65B9\u6CD5\uFF0C\u4E0B\u9762\u8BB2\u8BB2\u6211\u5BF9\u77E5\u8BC6\u7BA1\u7406\u662F\u600E\u4E48\u505A\u7684\u3002</p><h4 id="\u5982\u4F55\u505A\u597D\u77E5\u8BC6\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u505A\u597D\u77E5\u8BC6\u7BA1\u7406" aria-hidden="true">#</a> \u5982\u4F55\u505A\u597D\u77E5\u8BC6\u7BA1\u7406</h4><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-d396eea7-19ea-42d9-8e42-f06b1b47d55b.jpg" alt=""></p><p>\u6700\u4E0B\u9762\u7684\u63D0\u9192\u4E8B\u9879\u548C\u98DE\u4E66\u65E5\u7A0B\u5176\u5B9E\u4E5F\u662F\u4E0A\u9762\u6211\u4EEC\u65F6\u95F4\u7BA1\u7406\u7684\u4E00\u90E8\u5206\uFF0C\u6240\u4EE5\u6211\u7684\u65F6\u95F4\u7BA1\u7406\u548C\u77E5\u8BC6\u7BA1\u7406\u4E5F\u662F\u6709\u4EA4\u96C6\u7684\u3002\u6700\u76F4\u63A5\u7684\u5C31\u662F\u5BF9\u5916\u90E8\u8F93\u5165\u7684\u8BB0\u5F55\u4E86\uFF0C\u6211\u628A\u5B83\u4EEC\u5206\u6210\u4E09\u7C7B\uFF1A</p><ul><li><strong>\u9700\u8981\u6267\u884C\u7684\u4E8B\u60C5\uFF0C\u6211\u4F1A\u8BB0\u5F55\u5728\u63D0\u9192\u4E8B\u9879\u4E2D</strong>\uFF1A\u63D0\u9192\u4E8B\u9879\u4E2D\u7684\u8BB0\u5F55\u6211\u4F1A\u901A\u8FC7\u6574\u7406\uFF0C\u5C06\u4ED6\u4EEC\u8F6C\u6362\u6210\u98DE\u4E66\u65E5\u5386\u4E2D\u7684\u65E5\u7A0B\u548C\u8BA1\u5212\uFF0C\u5728\u6267\u884C\u4E4B\u540E\uFF0C\u6211\u4F1A\u5BF9\u5B8C\u6210\u7684\u4E8B\u60C5\u8FDB\u884C\u590D\u76D8\u548C\u53CD\u601D\uFF0C\u6574\u7406\u6210\u6587\u7AE0\uFF0C\u5B8C\u5584\u5230\u77E5\u8BC6\u5E93\u91CC\u3002</li></ul><ul><li><strong>\u5E73\u65F6\u7684\u4E00\u4E9B\u7075\u611F\u3001\u60F3\u6CD5\u3001\u4EE5\u53CA\u4E00\u4E9B\u788E\u7247\u7684\u77E5\u8BC6\uFF0C\u6211\u4F1A\u901A\u8FC7\u8BED\u96C0\u5C0F\u8BB0\u8BB0\u5F55\u4E0B\u6765\uFF1A</strong> \u8BED\u96C0\u5C0F\u8BB0\u4E2D\u7684\u77E5\u8BC6\u788E\u7247\uFF0C\u6211\u4E5F\u4F1A\u901A\u8FC7\u5B9A\u671F\u7684\u5F52\u6863\u548C\u603B\u7ED3\u5B8C\u5584\u5230\u77E5\u8BC6\u5E93\u91CC\u3002</li></ul><ul><li><strong>\u5E73\u65F6\u770B\u5230\u7684\u4E00\u4E9B\u4E0D\u9519\u7684\u6587\u7AE0\uFF0C\u6211\u4F1A\u5728 Cubox \u4E2D\u6536\u85CF\u4E0B\u6765\uFF1A</strong> \u5728\u9605\u8BFB\u4E2D\u4EA7\u751F\u7684\u8BB0\u5F55\u548C\u601D\u8003\uFF0C\u6211\u4F1A\u8BB0\u5F55\u5728\u5C0F\u8BB0\u4E2D\uFF0C\u5982\u679C\u6709\u5B8C\u6574\u7684\u603B\u7ED3\u548C\u6C89\u6DC0\uFF0C\u6211\u4F1A\u76F4\u63A5\u8BB0\u5F55\u5230\u77E5\u8BC6\u5E93\u4E2D\u3002</li></ul><p>\u77E5\u8BC6\u5E93\u91CC\u7684\u5185\u5BB9\uFF0C\u5DF2\u7ECF\u7ECF\u8FC7\u4E86\u5206\u7C7B\u548C\u7CFB\u7EDF\u5316\u7684\u603B\u7ED3\uFF0C\u6211\u4F1A\u5C06\u65B0\u589E\u7684\u5185\u5BB9\u5B8C\u5584\u5230\u77E5\u8BC6\u7F51\u7EDC\u4E2D\u3002\u5176\u5B9E\u77E5\u8BC6\u7F51\u7EDC\u4E5F\u5C31\u662F\u5BF9\u77E5\u8BC6\u5E93\u4E2D\u5206\u7C7B\u548C\u76EE\u5F55\u7684\u7EC4\u7EC7\u3002\u7ECF\u8FC7\u957F\u65F6\u95F4\u7684\u6C89\u6DC0\u548C\u6574\u7406\uFF0C\u6211\u7684\u77E5\u8BC6\u7F51\u7EDC\u548C\u77E5\u8BC6\u5E93\u5DF2\u7ECF\u975E\u5E38\u590D\u6742\u4E86\uFF0C\u6240\u4EE5\u6211\u5BF9\u6211\u4E2A\u4EBA\u7684\u8BA4\u77E5\u662F\u975E\u5E38\u6E05\u6670\u7684\u3002\u6211\u77E5\u9053\u6211\u638C\u63E1\u4E86\u54EA\u4E9B\u6280\u80FD\u548C\u77E5\u8BC6\uFF0C\u6211\u5728\u5229\u7528\u7684\u65F6\u5019\u53EF\u4EE5\u968F\u65F6\u968F\u5730\u7684\u628A\u5B83\u4EEC\u63D0\u53D6\u51FA\u6765\uFF0C\u540C\u65F6\u6211\u4E5F\u77E5\u9053\u6211\u6709\u54EA\u4E9B\u5206\u652F\u662F\u6BD4\u8F83\u8584\u5F31\u7684\uFF0C\u5982\u679C\u8FD9\u4E9B\u5206\u652F\u6211\u6BD4\u8F83\u611F\u5174\u8DA3\uFF0C\u6216\u8005\u8BF4\u548C\u6211\u672A\u6765\u7684\u5DE5\u4F5C\u53EF\u80FD\u6709\u4EA4\u96C6\uFF0C\u6211\u5C31\u4F1A\u6709\u9488\u5BF9\u6027\u7684\u53BB\u5B66\u4E60\u4ED6\u4EEC\u3002</p><h3 id="\u5BF9\u5916\u5206\u4EAB" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u5916\u5206\u4EAB" aria-hidden="true">#</a> \u5BF9\u5916\u5206\u4EAB</h3><h4 id="\u6709\u4EC0\u4E48\u597D\u5904-1" tabindex="-1"><a class="header-anchor" href="#\u6709\u4EC0\u4E48\u597D\u5904-1" aria-hidden="true">#</a> \u6709\u4EC0\u4E48\u597D\u5904</h4><p>\u4E0B\u9762\u6211\u4EEC\u518D\u6765\u8BF4\u8BF4\u5BF9\u5916\u7684\u5206\u4EAB\uFF0C\u80FD\u591F\u5C06\u81EA\u5DF1\u7684\u5199\u4F5C\u5185\u5BB9\u5BF9\u5916\u8F93\u51FA\uFF0C\u5176\u5B9E\u597D\u5904\u5C31\u66F4\u5927\u4E86\uFF0C\u8FD9\u91CC\u6211\u8BF4\u4E09\u70B9\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-683b322e-90ea-4548-ae07-c97febfbf834.jpg" alt=""></p><p>\u9996\u5148\u5173\u4E8E\u4E2A\u4EBA\u5BF9\u77E5\u8BC6\u7684\u7406\u89E3\u65B9\u9762\uFF0C\u6709\u65F6\u81EA\u5DF1\u7406\u89E3\u4E00\u4E2A\u77E5\u8BC6\u5176\u5B9E\u662F\u6BD4\u8F83\u5BB9\u6613\u7684\uFF0C\u53EF\u80FD\u4F60\u8BFB\u5B8C\u4E00\u7BC7\u76F8\u5173\u7684\u6587\u7AE0\uFF0C\u5C31\u4F1A\u89C9\u5F97\u81EA\u5DF1\u5DF2\u7ECF\u7406\u89E3\u4E86\u3002\u4F46\u662F\u5F53\u4F60\u5C1D\u8BD5\u628A\u81EA\u5DF1\u7406\u89E3\u5230\u7684\u77E5\u8BC6\u7ED9\u522B\u4EBA\u4E5F\u8BB2\u660E\u767D\u7684\u65F6\u5019\uFF0C\u4F1A\u53D1\u73B0\u53EF\u80FD\u8FDC\u8FDC\u4E0D\u662F\u4F60\u60F3\u7684\u90A3\u4E48\u7B80\u5355\u3002<strong>\u4F60\u53EF\u80FD\u9700\u8981\u6570\u500D\u7684\u6DF1\u5EA6\u7406\u89E3\u624D\u80FD\u505A\u5230\u628A\u4E00\u4E2A\u77E5\u8BC6\u7ED9\u5176\u4ED6\u4EBA\u8BB2\u660E\u767D</strong>\u3002\u5F53\u7136\uFF0C\u5BF9\u5916\u7684\u5206\u4EAB\u9664\u4E86\u5E26\u7ED9\u4F60\u4E2A\u4EBA\u7684\u63D0\u5347\uFF0C\u8FD8\u4F1A\u7ED9\u4F60\u7684\u804C\u573A\u5E26\u6765\u6536\u76CA\uFF0C\u6BD4\u5982\u53EF\u4EE5\u63D0\u5347\u4F60\u4E2A\u4EBA\u5F71\u54CD\u529B\uFF0C\u5E26\u52A8\u56E2\u961F\u7684\u6574\u4F53\u6280\u672F\u6C1B\u56F4\u7B49\u7B49\u3002\u4E0B\u9762\u8DDF\u5927\u5BB6\u4ECB\u7ECD\u51E0\u4E2A\u6211\u7684\u5199\u4F5C\u65B9\u9762\u7684\u7ECF\u9A8C\u3002</p><h4 id="\u5199\u4F5C\u6280\u5DE7-\u5148\u679D\u540E\u53F6" tabindex="-1"><a class="header-anchor" href="#\u5199\u4F5C\u6280\u5DE7-\u5148\u679D\u540E\u53F6" aria-hidden="true">#</a> \u5199\u4F5C\u6280\u5DE7\uFF1A\u5148\u679D\u540E\u53F6</h4><p>\u4E2A\u4EBA\u7684\u6C89\u6DC0\uFF0C\u6700\u7EC8\u662F\u7ED9\u6211\u4EEC\u81EA\u5DF1\u770B\u7684\uFF0C\u6240\u4EE5\u5199\u7684\u518D\u4E71\uFF0C\u53EA\u8981\u6211\u4EEC\u81EA\u5DF1\u80FD\u7406\u89E3\u5C31\u6CA1\u95EE\u9898\u3002 \u4F46\u662F<strong>\u6587\u7AE0\u5982\u679C\u662F\u8981\u5BF9\u5916\u5206\u4EAB\u7684\uFF0C\u4E00\u5B9A\u6709\u62E5\u6709\u6E05\u6670\u7684\u6761\u7406\u548C\u7ED3\u6784\uFF0C\u6700\u597D\u662F\u5148\u679D\u540E\u53F6\uFF0C\u5148\u628A\u6587\u7AE0\u6574\u4F53\u7684\u7ED3\u6784\u548C\u5927\u7EB2\u5217\u597D\uFF0C\u518D\u5F80\u91CC\u586B\u5145\u5B8C\u5584</strong>\u3002\u5728\u7ED3\u6784\u4E0A\u6709\u5F88\u591A\u6CD5\u5219\u53EF\u4EE5\u5927\u5BB6\u53EF\u4EE5\u5957\u7528\uFF0C\u4E00\u4E2A\u6700\u7B80\u5355\u7684\u6CD5\u5219\u5C31\u662F 2W1H \u6CD5\u5219\uFF0C\u4E5F\u5C31\u662F\uFF1A\u662F\u4EC0\u4E48\u3001\u4E3A\u4EC0\u4E48\u3001\u600E\u4E48\u505A\u3002\u6BD4\u5982\u6211\u6700\u8FD1\u7684\u4E00\u7BC7\u8BB2\u89E3 Early Hints \u7684\u6587\u7AE0\u662F\u8FD9\u6837\u62C6\u89E3\u7684\uFF1A</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-903e06bf-440c-4ce6-abc1-739fd05faf99.jpg" alt=""></p><ul><li><strong>Why</strong>\uFF1A\u4E3A\u4EC0\u4E48\u9700\u8981 Early Hints\uFF1F\u8D44\u6E90\u52A0\u8F7D\u7684\u6027\u80FD\u95EE\u9898</li></ul><ul><li><strong>What</strong>\uFF1A Early Hints \u5230\u5E95\u662F\u4EC0\u4E48\uFF1FHTTP \u7684 103 \u72B6\u6001\u7801\u3001\u4EC0\u4E48\u6837\u7684\u7F51\u7AD9\u9002\u5408 Early Hints</li></ul><ul><li><strong>How</strong>\uFF1A\u5982\u4F55\u542F\u7528 Early Hints</li></ul><p>\u62E5\u6709\u4E00\u4E2A\u597D\u7684\u7ED3\u6784\u548C\u5927\u7EB2\u4E0D\u4EC5\u4F1A\u8BA9\u4F60\u7684\u5199\u4F5C\u66F4\u52A0\u8F7B\u677E\u4E5F\u4F1A\u8BA9\u8BFB\u8005\u66F4\u52A0\u8212\u670D\u3002</p><h4 id="\u5199\u4F5C\u6280\u5DE7-\u5584\u7528\u56FE\u7247" tabindex="-1"><a class="header-anchor" href="#\u5199\u4F5C\u6280\u5DE7-\u5584\u7528\u56FE\u7247" aria-hidden="true">#</a> \u5199\u4F5C\u6280\u5DE7\uFF1A\u5584\u7528\u56FE\u7247</h4><p>\u7B2C\u4E8C\u70B9\u5C31\u662F\u5584\u7528\u56FE\u7247\u3002<strong>\u6709\u65F6\u5019\u53EF\u80FD\u9700\u8981\u4E00\u5927\u5806\u6587\u5B57\u63CF\u8FF0\u624D\u80FD\u8BF4\u660E\u767D\u7684\u5185\u5BB9\uFF0C\u4E00\u5F20\u56FE\u7247\u5C31\u80FD\u641E\u5B9A</strong>\u3002</p><p>\u6BD4\u5982\uFF1A\u6211\u4EEC\u8981\u5728\u6587\u7AE0\u4E2D\u8BB2\u89E3\u600E\u4E48\u7406\u89E3\u8D1F\u8F7D\u5747\u8861\u8FD9\u4E2A\u6982\u5FF5\uFF1F</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-1a079f76-0e93-46e0-8784-1ef28bfa9cef.jpg" alt=""></p><p>\u6211\u4EEC\u53EF\u4EE5\u5728\u56FE\u4E2D\u53D1\u73B0\uFF1A\u524D\u9762\u662F\u4F17\u591A\u7684\u670D\u52A1\u7A97\u53E3\uFF0C\u4E0B\u9762\u6709\u5F88\u591A\u7528\u6237\u9700\u8981\u670D\u52A1\uFF0C\u6211\u4EEC\u9700\u8981\u4E00\u4E2A\u5DE5\u5177\u6216\u7B56\u7565\u6765\u5E2E\u52A9\u6211\u4EEC\u5C06\u5982\u6B64\u591A\u7684\u7528\u6237\u5206\u914D\u5230\u6BCF\u4E2A\u7A97\u53E3\uFF0C\u6765\u8FBE\u5230\u8D44\u6E90\u7684\u5145\u5206\u5229\u7528\u4EE5\u53CA\u66F4\u5C11\u7684\u6392\u961F\u65F6\u95F4\u3002</p><p>\u6211\u4EEC\u6709\u4E09\u79CD\u6D4B\u7B56\u7565\u53EF\u4EE5\u9009\u62E9\uFF1A</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/nice-article/juejin-ruhzfzdgzzcxcz-54cefc55-7f85-4e30-b0e9-9c1cb58810cc.jpg" alt=""></p><ul><li>\u7B2C\u4E00\u79CD\u662F\u9ED8\u8BA4\u7684\u8F6E\u8BE2\u7B56\u7565\uFF0C\u4E5F\u5C31\u662F\u5C06\u6240\u6709\u5BA2\u6237\u7AEF\u8BF7\u6C42\u8F6E\u8BE2\u5206\u914D\u7ED9\u670D\u52A1\u7AEF\u3002</li></ul><ul><li>\u7B2C\u4E8C\u79CD\u662F\u5C06\u8BF7\u6C42\u4F18\u5148\u5206\u914D\u7ED9\u538B\u529B\u8F83\u5C0F\u7684\u670D\u52A1\u5668\u3002</li></ul><ul><li>\u7B2C\u4E09\u79CD\u662F\u6709\u9650\u9009\u62E9\u54CD\u5E94\u65F6\u95F4\u6700\u5FEB\u7684\u670D\u52A1\u5668\u3002</li></ul><p>\u5927\u5BB6\u53EF\u4EE5\u770B\u4E00\u4E0B\uFF0C\u662F\u4E0D\u662F\u589E\u52A0\u4E86\u51E0\u5F20\u56FE\u7247\uFF0C\u5C31\u53EF\u4EE5\u80DC\u8FC7\u5927\u6279\u91CF\u7684\u6587\u5B57\u63CF\u8FF0\u5462\uFF1F</p><h4 id="\u5199\u4F5C\u6280\u5DE7-\u4ECE\u8BFB\u8005\u89D2\u5EA6\u601D\u8003" tabindex="-1"><a class="header-anchor" href="#\u5199\u4F5C\u6280\u5DE7-\u4ECE\u8BFB\u8005\u89D2\u5EA6\u601D\u8003" aria-hidden="true">#</a> \u5199\u4F5C\u6280\u5DE7\uFF1A\u4ECE\u8BFB\u8005\u89D2\u5EA6\u601D\u8003</h4><p>\u6700\u540E\u4E00\u4E2A\u6211\u60F3\u8BF4\u7684\u5C31\u662F\uFF0C\u591A\u4ECE\u8BFB\u8005\u7684\u89D2\u5EA6\u601D\u8003\u3002\u56E0\u4E3A<strong>\u6211\u4EEC\u5BF9\u5916\u7684\u6587\u7AE0\u7B2C\u4E00\u76EE\u7684\u8FD8\u662F\u8981\u7ED9\u8BFB\u8005\u53BB\u770B\u7684\uFF0C\u8BFB\u8005\u4E0D\u4E00\u5B9A\u6709\u548C\u6211\u4EEC\u4E00\u6837\u7684\u77E5\u8BC6\u80CC\u666F</strong>\u3002\u8BD5\u7740\u60F3\u4E00\u60F3\uFF0C\u5982\u679C\u4F60\u662F\u4E00\u540D\u6CA1\u6709\u76F8\u5173\u77E5\u8BC6\u80CC\u666F\u7684\u8BFB\u8005\uFF0C\u60F3\u8981\u83B7\u53D6\u76F8\u5173\u7684\u77E5\u8BC6\uFF0C\u4F60\u60F3\u8981\u770B\u5230\u4E00\u7BC7\u4EC0\u4E48\u6837\u7684\u6587\u7AE0\u5462\uFF1F\u5F53\u7136\u4E0D\u662F\u6EE1\u5C4F\u7684\u4E13\u4E1A\u672F\u8BED\u3001\u6216\u8005\u6742\u4E71\u65E0\u7AE0\u7684\u7B14\u8BB0\u3001\u751A\u81F3\u662F\u6BEB\u65E0\u4EFB\u4F55\u7684\u642C\u8FD0\u3002\u6211\u76F8\u4FE1\u5462\uFF0C\u5982\u679C\u5927\u5BB6\u80FD\u505A\u5230\u8FD9\u4E00\u70B9\uFF0C\u5199\u51FA\u7684\u6587\u7AE0\u4E00\u5B9A\u4E0D\u4F1A\u592A\u5DEE\u3002</p><p>\u4EE5\u4E0A\u5C31\u662F\u6211\u4E2A\u4EBA\u7684\u4E00\u4E9B\u7ECF\u9A8C\u4E4B\u8C08\uFF0C\u4EC5\u4F9B\u53C2\u8003\uFF0C\u672C\u6587\u5B8C\u3002</p>',165),d={href:"https://juejin.cn/post/7124180647958020104",target:"_blank",rel:"noopener noreferrer"},h=e("hr",null,null,-1);function g(u,b){const t=n("ExternalLinkIcon");return i(),p("div",null,[s,e("blockquote",null,[e("p",null,[a("\u53C2\u8003\u94FE\u63A5\uFF1A"),e("a",d,[a("https://juejin.cn/post/7124180647958020104"),c(t)]),a("\uFF0C\u6574\u7406\uFF1Amusk")])]),h])}const f=r(o,[["render",g],["__file","ruhzfzdgzzcxcz.html.vue"]]);export{f as default};
