import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as d,c as a,a as t,b as h,d as i,f as l,r as s}from"./app.62e9bec7.js";const n={},o=l('<h1 id="solr\u9AD8\u4EAE" tabindex="-1"><a class="header-anchor" href="#solr\u9AD8\u4EAE" aria-hidden="true">#</a> Solr\u9AD8\u4EAE</h1><h2 id="_1-\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#_1-\u7B80\u4ECB" aria-hidden="true">#</a> 1. \u7B80\u4ECB</h2><p>\u5927\u591A\u6570\u7684\u641C\u7D22\u5E94\u7528\u90FD\u5B58\u5728\u7C7B\u4F3C\u7684\u60C5\u51B5\uFF0C\u90A3\u5C31\u662F\u641C\u7D22\u7ED3\u679C\u663E\u793A\u7684\u5C4F\u5E55\u7A7A\u95F4\u6709\u9650\u3002</p><ul><li>\u5982\u679C\u6587\u6863\u5F88\u77ED\u5E76\u53EF\u4EE5\u5728\u7ED3\u679C\u5217\u8868\u4E2D\u663E\u793A\u5168\u90E8\u5185\u5BB9\uFF0C\u5BF9\u5C4F\u5E55\u7A7A\u95F4\u663E\u793A\u5C31\u4E0D\u4F1A\u6784\u6210\u592A\u5927\u7684\u95EE\u9898\u3002</li><li>\u4F46\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u90FD\u53EA\u80FD\u663E\u793A\u6BCF\u4E2A\u7ED3\u679C\u6587\u6863\u7684\u4E00\u5C0F\u90E8\u5206\u3002</li></ul><p>\u8FD9\u5C31\u63D0\u51FA\u4E86\u4E00\u4E2A\u95EE\u9898\uFF1A\u5982\u4F55\u51B3\u5B9A\u7ED3\u679C\u6587\u6863\u4E2D\u663E\u793A\u54EA\u4E00\u90E8\u5206\uFF1F\u7406\u60F3\u60C5\u51B5\u4E0B\uFF0C<strong>\u5E94\u8BE5\u662F\u57FA\u4E8E\u5404\u7247\u6BB5\u4E0E\u7528\u6237\u67E5\u8BE2\u7684\u5339\u914D\u7A0B\u5EA6\u6765\u51B3\u5B9A</strong>\u3002\u4E3A\u67E5\u8BE2\u7ED3\u679C\u9009\u62E9\u6700\u4F73\u7247\u6BB5\u8FDB\u884C\u663E\u793A\uFF0C\u8FD9\u662FSolr\u9AD8\u4EAE\u6846\u67B6\u63D0\u4F9B\u7684\u6838\u5FC3\u529F\u80FD\u3002</p><h2 id="_2-\u9AD8\u4EAE\u6240\u5904\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_2-\u9AD8\u4EAE\u6240\u5904\u6D41\u7A0B" aria-hidden="true">#</a> 2. \u9AD8\u4EAE\u6240\u5904\u6D41\u7A0B</h2><h3 id="_2-1-\u67E5\u8BE2\u5168\u6D41\u7A0B\u4E2D\u7684\u9AD8\u4EAE" tabindex="-1"><a class="header-anchor" href="#_2-1-\u67E5\u8BE2\u5168\u6D41\u7A0B\u4E2D\u7684\u9AD8\u4EAE" aria-hidden="true">#</a> 2.1 \u67E5\u8BE2\u5168\u6D41\u7A0B\u4E2D\u7684\u9AD8\u4EAE</h3><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220418164457501.png" alt="image-20220418164457501"></p><h3 id="_2-2-\u9AD8\u4EAE\u7EC4\u4EF6\u7684\u5185\u90E8\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_2-2-\u9AD8\u4EAE\u7EC4\u4EF6\u7684\u5185\u90E8\u6D41\u7A0B" aria-hidden="true">#</a> 2.2 \u9AD8\u4EAE\u7EC4\u4EF6\u7684\u5185\u90E8\u6D41\u7A0B</h3><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220418164555628.png" alt="image-20220418164555628"></p><h2 id="_3-\u56DB\u79CD\u9AD8\u4EAE\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#_3-\u56DB\u79CD\u9AD8\u4EAE\u5B9E\u73B0" aria-hidden="true">#</a> 3. \u56DB\u79CD\u9AD8\u4EAE\u5B9E\u73B0</h2><h3 id="_3-1-unified-highlighter" tabindex="-1"><a class="header-anchor" href="#_3-1-unified-highlighter" aria-hidden="true">#</a> 3.1 <strong>Unified Highlighter \uFF1A</strong></h3><p>\u8FD9\u662F\u6700\u7075\u6D3B\u548C\u6700\u5177\u9009\u62E9\u6027\u7684\u9009\u9879,\u5B83\u652F\u6301\u6700\u5E38\u89C1\u7684\u7A81\u51FA\u663E\u793A\u53C2\u6570\uFF0C\u5E76\u4E14\u53EF\u4EE5\u51C6\u786E\u5730\u5904\u7406\u4EFB\u4F55\u67E5\u8BE2\uFF0C\u751A\u81F3\u662FSpanQueries\uFF08\u4F8B\u5982\u4ECE\u73AF\u7ED5\u89E3\u6790\u5668\u4E2D\u53EF\u4EE5\u770B\u5230\uFF09\u3002 \u8FD9\u79CD\u65B9\u5F0F\u7684\u7684\u5F3A\u5927\u4F18\u52BF\u5728\u4E8E\uFF0C</p><p>\u60A8\u53EF\u4EE5\u9009\u62E9\u914D\u7F6ESolr\u4EE5\u5C06\u66F4\u591A\u4FE1\u606F\u653E\u5165\u5E95\u5C42\u7D22\u5F15\u4E2D\uFF0C\u4EE5\u52A0\u901F\u7A81\u51FA\u663E\u793A\u5927\u578B\u6587\u6863; \u5373\u4F7F\u5728\u6BCF\u4E2A\u5B57\u6BB5\u7684\u57FA\u7840\u4E0A\uFF0C\u4E5F\u652F\u6301\u591A\u79CD\u914D\u7F6E\u3002 \u5176\u4ED6\u65B9\u5F0F\u51E0\u4E4E\u6CA1\u6709\u8FD9\u79CD\u7075\u6D3B\u6027</p><h3 id="_3-2-original-highlighter-\u6700\u5E38\u7528" tabindex="-1"><a class="header-anchor" href="#_3-2-original-highlighter-\u6700\u5E38\u7528" aria-hidden="true">#</a> 3.2 <strong>Original Highlighter\uFF08\u6700\u5E38\u7528\uFF09\uFF1A</strong></h3><p>\u8FD9\u4E2AOriginal Highlighter\u662F\u9AD8\u4EAE\u7684\u4E00\u4E2A\u5178\u578B.\u5B83\u62E5\u6709\u4E09\u4E2A\u9AD8\u4EAE\u7684\u6700\u590D\u6742\u7684\u548C\u6700\u7EC6\u7C92\u5EA6\u7684\u67E5\u8BE2\u8868\u793A\u6CD5.\u6BD4\u5982,\u9AD8\u4EAE\u80FD\u591F\u63D0\u4F9B\u7CBE\u786E\u7684\u5339\u914D,\u751A\u81F3\u7528\u4E8E\u5982surround\u8FD9\u6837\u7684\u67E5\u8BE2\u89E3\u6790\u5668\u3002</p><p>\u5B83\u4E0D\u8981\u6C42\u4EFB\u4F55\u7684\u7279\u6B8A\u7684\u6570\u636E\u7ED3\u679C\u5982termVectors.\u5BF9\u4E8E\u4E00\u4E2A\u5BBD\u6CDB\u79CD\u7C7B\u7684\u641C\u7D22\u60C5\u51B5,\u8FD9\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u9009\u62E9.</p><h3 id="_3-3-fastvector-highlighter" tabindex="-1"><a class="header-anchor" href="#_3-3-fastvector-highlighter" aria-hidden="true">#</a> 3.3 <strong>FastVector Highlighter</strong>\uFF1A</h3><p>FastVector Highlighter\u8981\u6C42\u5B57\u6BB5\u7684term vector\u9009\u9879(termVectors,termPositions,termOffsets).\u5E76\u8003\u8651\u5230\u662F\u6700\u4F18\u5316\u7684,\u5BF9\u4E8E\u591A\u8BED\u8A00\u73AF\u5883\u5B83\u5F80\u5F80\u6BD4standard highlighter\u4F1A\u66F4\u597D\u7684\u5DE5\u4F5C\u3002</p><p>\u56E0\u4E3A\u5B83\u652F\u6301Unicode breakiterators(\u5206\u89E3\u8FED\u4EE3).\u53E6\u4E00\u65B9\u9762,\u5B83\u7684\u67E5\u8BE2\u8868\u793A\u6CA1\u6709standard highlighter\u90A3\u4E48\u9AD8\u7EA7.\u4F8B\u5982,\u5B83\u5C06\u4E0D\u4F1A\u5F88\u597D\u7684\u5DE5\u4F5C\u4E8Esurround\u89E3\u6790\u5668.\u5B83\u7ECF\u5E38\u4F7F\u7528\u4E8E\u5927\u6587\u6863\u548C\u591A\u79CD\u7C7B\u8BED\u8A00\u7684\u9AD8\u4EAE\u6587\u672C.</p><h3 id="_3-4-postings-highlighter" tabindex="-1"><a class="header-anchor" href="#_3-4-postings-highlighter" aria-hidden="true">#</a> 3.4 Postings Highlighter**:</h3><p>Postings Highlighter\u8981\u6C42\u5B58\u50A8storeOffsetsWithPositions(\u4F4D\u7F6E\u504F\u79FB\u91CF),\u9700\u8981\u5728\u5B57\u6BB5\u4E2D\u914D\u7F6E.\u8FD9\u662F\u4E00\u4E2A\u6BD4vectors\u66F4\u7B80\u5355\u6709\u6548\u7684\u7ED3\u679C,\u4F46\u662F\u4E0D\u9002\u5408\u4E8E\u5927\u6570\u91CF\u7684\u67E5\u8BE2terms\u3002</p><p>\u50CFFastVector Highlighter,\u5B83\u652F\u6301Unicode\u7B97\u6CD5\u6765\u5206\u5272\u6587\u6863.\u53E6\u4E00\u65B9\u9762,\u5B83\u6709\u5F88\u7C97\u7565\u7684\u67E5\u8BE2\u8868\u793A:\u5B83\u6CE8\u91CD\u4E8E\u6458\u8981\u7684\u8D28\u91CF,\u5B8C\u5168\u5FFD\u7565\u67E5\u8BE2\u7684\u7ED3\u6784,\u4EC5\u4EC5\u57FA\u4E8E\u67E5\u8BE2term\u548C\u7EDF\u8BA1\u6392\u5E8F.</p><h2 id="_4-original-highlighter" tabindex="-1"><a class="header-anchor" href="#_4-original-highlighter" aria-hidden="true">#</a> 4. Original Highlighter</h2><h3 id="_4-1-\u53C2\u6570" tabindex="-1"><a class="header-anchor" href="#_4-1-\u53C2\u6570" aria-hidden="true">#</a> 4.1 \u53C2\u6570</h3><table><thead><tr><th>\u53C2\u6570</th><th>\u9ED8\u8BA4</th><th>\u542B\u4E49</th></tr></thead><tbody><tr><td>hl :</td><td>\u9ED8\u8BA4\u4E3A\u7A7A\u767D(\u4E0D\u9AD8\u4EAE).</td><td>\u4E3Atrue\u65F6,\u5F00\u542F\u9AD8\u4EAE\u529F\u80FD,\u4E3Afalse\u6216\u8005\u7A7A\u767D\u6216\u8005\u7F3A\u5C11\u65F6,\u5173\u95ED\u9AD8\u4EAE\u529F\u80FD.</td></tr><tr><td>hl.fl:\u3002</td><td>\u9ED8\u8BA4\u7A7A\u767D\uFF0C</td><td>\u6307\u5B9A\u9AD8\u4EAE\u5B57\u6BB5\u5217\u8868.\u591A\u4E2A\u5B57\u6BB5\u4E4B\u95F4\u4EE5\u9017\u53F7\u6216\u7A7A\u683C\u5206\u5F00.\u5982\u679C\u4E3A\u7A7A\u767D,\u5BF9\u4E8EStandardRequestHandler,\u9AD8\u4EAE\u9ED8\u8BA4\u641C\u7D22\u5B57\u6BB5(\u6216\u8005\u6307\u5B9A\u7684df\u53C2\u6570).\u5BF9\u4E8EDisMaxRequestHandler,qf\u4F5C\u4E3A\u9ED8\u8BA4\u7684 \u3000\u3000\u3000\u3000\u3000\u3000&quot;<em>&quot;\u53EF\u4EE5\u7528\u4E8E\u5339\u914D\u5168\u5C40,\u5982&quot;text_</em>&quot;\u6216\u8005&quot;<em>&quot;,\u5F53\u4F7F\u7528&quot;</em>&quot;\u65F6,hl.requireFieldMatch=true\u5FC5\u586B.</td></tr><tr><td>hl.snippets :</td><td>\u9ED8\u8BA4\u4E3A1.</td><td>\u6307\u5B9A\u6BCF\u4E2A\u5B57\u6BB5\u751F\u6210\u7684\u9AD8\u4EAE\u5B57\u6BB5\u7684\u6700\u5927\u6570\u91CF.</td></tr><tr><td>hl.fragsize :</td><td>\u9ED8\u8BA4\u662F100.</td><td>\u6BCF\u4E2Asnippet\u8FD4\u56DE\u7684\u6700\u5927\u5B57\u7B26\u6570\u3002\u5982\u679C\u4E3A0\uFF0C\u90A3\u4E48\u8BE5\u5B57\u6BB5\u4E0D\u4F1A\u88ABfragmented\u4E14\u6574\u4E2A\u5B57\u6BB5\u7684\u503C\u4F1A\u88AB\u8FD4\u56DE\u3002</td></tr><tr><td>hl.mergeContiguous :</td><td>\u9ED8\u8BA4\u4E3Afalse.</td><td>\u77E5\u8BC6solr\u5C06\u90BB\u8FD1\u76F8\u8FDE\u7684\u7247\u6BB5\u5408\u5E76\u4E3A\u4E00\u4E2A\u5355\u72EC\u7684\u7247\u6BB5.true\u8868\u793A\u5408\u5E76.\u9ED8\u8BA4\u503C\u4E3Afalse,\u4E3A\u5411\u540E\u517C\u5BB9\u8BBE\u7F6E.</td></tr><tr><td><strong>hl.requireFieldMatch:</strong></td><td>\u9ED8\u8BA4\u4E3Afasle.</td><td>\u5982\u679C\u7F6E\u4E3Atrue\uFF0C\u9664\u975E\u8BE5\u5B57\u6BB5\u7684\u67E5\u8BE2\u7ED3\u679C\u4E0D\u4E3A\u7A7A\u624D\u4F1A\u88AB\u9AD8\u4EAE\u3002\u5B83\u7684\u9ED8\u8BA4\u503C\u662Ffalse\uFF0C\u610F\u5473 \u7740\u5B83\u53EF\u80FD\u5339\u914D\u67D0\u4E2A\u5B57\u6BB5\u5374\u9AD8\u4EAE\u4E00\u4E2A\u4E0D\u540C\u7684\u5B57\u6BB5\u3002\u5982\u679Chl.fl\u4F7F\u7528\u4E86\u901A\u914D\u7B26\uFF0C\u90A3\u4E48\u5C31\u8981\u542F\u7528\u8BE5\u53C2\u6570\u3002 \u5C3D\u7BA1\u5982\u6B64\uFF0C\u5982\u679C\u4F60\u7684\u67E5\u8BE2\u662Fall\u5B57\u6BB5\uFF08\u53EF\u80FD\u662F\u4F7F\u7528 copy-field \u6307\u4EE4\uFF09\uFF0C\u90A3\u4E48\u8FD8\u662F\u628A\u5B83\u8BBE\u4E3Afalse\uFF0C\u8FD9\u6837\u641C\u7D22\u7ED3\u679C\u80FD\u8868\u660E\u54EA\u4E2A\u5B57\u6BB5\u7684\u67E5\u8BE2\u6587\u672C\u672A\u88AB\u627E\u5230</td></tr><tr><td>hl.maxAnalyzedChars:</td><td>\u9ED8\u8BA451200.</td><td>\u4F1A\u641C\u7D22\u9AD8\u4EAE\u7684\u6700\u5927\u5B57\u7B26\uFF0C\u9ED8\u8BA4\u503C\u4E3A51200\uFF0C\u5982\u679C\u4F60\u60F3\u7981\u7528\uFF0C\u8BBE\u4E3A-1</td></tr><tr><td>hl.maxMultiValuedToExamine:</td><td>\u9ED8\u8BA4integer.MAX_VALUE.</td><td>\u5728\u505C\u6B62\u4E4B\u524D,\u6307\u5B9A\u68C0\u67E5\u7684\u591A\u503C\u5B57\u6BB5\u7684\u6700\u5927\u6761\u6728\u6570.\u5728\u4EFB\u4F55\u5339\u914D\u6CA1\u6709\u627E\u5230\u4E4B\u524D,\u5982\u679C\u8FBE\u5230\u9650\u5236,\u53EF\u80FD\u4F1A\u8FD4\u56DE0\u4E2A\u7ED3\u679C.</td></tr><tr><td>hl.maxMultiValuedToMatch:</td><td>\u9ED8\u8BA4integer.MAX_VALUE.</td><td>\u5728\u505C\u6B62\u4E4B\u524D,\u6307\u5B9A\u5728\u591A\u503C\u5B57\u6BB5\u4E2D\u627E\u5230\u7684\u6700\u5927\u5339\u914D\u6570.\u5982\u679Chl.maxMultiValuedToExamine\u4E5F\u5DF2\u7ECF\u5B9A\u4E49\u4E86,\u9996\u5148\u8FBE\u5230\u7684\u9650\u5236\u5C06\u51B3\u5B9A\u4F55\u65F6\u505C\u6B62\u67E5\u627E\u3002</td></tr><tr><td>hl.alternateField:</td><td>\u9ED8\u8BA4blank.</td><td>\u5982\u679C\u6CA1\u6709\u751F\u6210snippet\uFF08\u6CA1\u6709terms \u5339\u914D\uFF09\uFF0C\u90A3\u4E48\u4F7F\u7528\u53E6\u4E00\u4E2A\u5B57\u6BB5\u503C\u4F5C\u4E3A\u8FD4\u56DE\u3002</td></tr><tr><td>hl.maxAlternateFieldLength :</td><td>\u9ED8\u8BA4 unlimited.</td><td>\u5982\u679Chl.alternateField\u542F\u7528\uFF0C\u5219\u6709\u65F6\u9700\u8981\u5236\u5B9AalternateField\u7684\u6700\u5927\u5B57\u7B26\u957F\u5EA6\uFF0C\u9ED8\u8BA40\u662F\u5373\u6CA1\u6709\u9650\u5236\u3002\u6240\u4EE5\u5408\u7406\u7684\u503C\u662F\u5E94\u8BE5\u4E3A \u3000hl.snippets * hl.fragsize\u8FD9\u6837\u8FD4\u56DE\u7ED3\u679C\u7684\u5927\u5C0F\u5C31\u80FD\u4FDD\u6301\u4E00\u81F4\u3002</td></tr><tr><td>hl.formatter :</td><td>\u9ED8\u8BA4 simple.</td><td>\u4E00\u4E2A\u63D0\u4F9B\u53EF\u66FF\u6362\u7684formatting\u7B97\u6CD5\u7684\u6269\u5C55\u70B9\u3002\u9ED8\u8BA4\u503C\u662Fsimple\uFF0C\u8FD9\u662F\u76EE\u524D\u4EC5\u6709\u7684\u9009\u9879\u3002\u663E\u7136\u8FD9\u4E0D\u591F\u7528\uFF0C\u4F60\u53EF \u4EE5\u770B\u770Borg.apache.solr.highlight.HtmlFormatter.java \u548C \u3000solrconfig.xml\u4E2Dhighlighting\u5143\u7D20\u662F\u5982\u4F55\u914D\u7F6E\u7684\u3002 \u6CE8\u610F\u5728\u4E0D\u8BBA\u539F\u6587\u4E2D\u88AB\u9AD8\u4EAE\u4E86\u4EC0\u4E48\u503C\u7684\u60C5\u51B5\u4E0B\uFF0C\u5982\u9884\u5148\u5DF2\u5B58\u5728\u7684em tags\uFF0C\u4E5F\u4E0D\u4F1A\u88AB\u8F6C\u4E49\uFF0C\u6240\u4EE5\u5728\u6709\u65F6\u4F1A\u5BFC\u81F4\u5047\u7684\u9AD8\u4EAE\u3002</td></tr><tr><td>hl.simple.pre hl.simple.post :</td><td>\u9ED8\u8BA4<em> and </em> \uFF0E</td><td></td></tr><tr><td>hl.fragmenter :</td><td>\u9ED8\u8BA4gap\uFF0E</td><td>\u8FD9\u4E2A\u662Fsolr\u5236\u5B9Afragment\u7B97\u6CD5\u7684\u6269\u5C55\u70B9\u3002gap\u662F\u9ED8\u8BA4\u503C\u3002regex\u662F\u53E6\u4E00\u79CD\u9009\u9879\uFF0C\u8FD9\u79CD\u9009\u9879\u6307\u660E highlight\u7684\u8FB9\u754C\u7531\u4E00\u4E2A\u6B63\u5219\u8868\u8FBE\u5F0F\u786E \u5B9A\u3002\u8FD9\u662F\u4E00\u79CD\u975E\u5178\u578B \u7684\u9AD8\u7EA7\u9009\u9879\u3002 \u4E3A\u4E86\u77E5\u9053\u9ED8\u8BA4\u8BBE\u7F6E\u548Cfragmenters (and formatters)\u662F\u5982\u4F55\u914D\u7F6E\u7684\uFF0C\u53EF\u4EE5\u770B\u770Bsolrconfig.xml\u4E2D\u7684highlight\u6BB5\u3002</td></tr><tr><td>hl.usePhraseHighlighter :</td><td>\u9ED8\u8BA4true\uFF0E</td><td>\u5982\u679C\u4E00\u4E2A\u67E5\u8BE2\u4E2D\u542B\u6709\u77ED\u8BED\uFF08\u5F15\u53F7\u6846\u8D77\u6765\u7684\uFF09\u90A3\u4E48\u4F1A\u4FDD\u8BC1\u4E00\u5B9A\u8981\u5B8C\u5168\u5339\u914D\u77ED\u8BED\u7684\u624D\u4F1A\u88AB\u9AD8\u4EAE</td></tr><tr><td>hl.highlightMultiTerm : \u3002</td><td>\u9ED8\u8BA4true.</td><td>\u5982\u679C\u8BBE\u7F6E\u4E3Atrue,solr\u5C06\u4F1A\u9AD8\u4EAE\u51FA\u73B0\u5728\u591Aterms\u67E5\u8BE2\u4E2D\u7684\u77ED\u8BED</td></tr><tr><td>hl.regex.slop:</td><td>\u9ED8\u8BA40.6.</td><td>\u5728\u4F7F\u7528hl.fragmenter=regex\u65F6,\u610F\u601D\u662F\u5982\u679Chl.fragsize=100\u90A3\u4E48fragment\u7684\u5927\u5C0F\u4F1A\u4ECE40-160.</td></tr><tr><td>hl.regex.pattern:</td><td>\u9ED8\u8BA4\u7A7A\u767D.</td><td>\u4E3Afragmenting\u6307\u5B9A\u6B63\u5219\u8868\u8FBE\u5F0F.\u8FD9\u4E2A\u53EF\u4EE5\u7528\u4F5C\u63D0\u53D6\u53E5\u5B50\u3002</td></tr><tr><td>hl.regex.maxAnalyzedChars:</td><td>\u9ED8\u8BA410000.</td><td>\u641C\u7D22\u9AD8\u4EAE\u7684\u6700\u5927\u5B57\u7B26,\u5BF9\u4E00\u4E2A\u5927\u5B57\u6BB5\u4F7F\u7528\u4E00\u4E2A\u590D\u6742\u7684\u6B63\u5219\u8868\u8FBE\u5F0F\u662F\u975E\u5E38\u6602\u8D35\u7684\u3002</td></tr><tr><td>hl.preserveMulti :</td><td>\u9ED8\u8BA4false.</td><td>\u5982\u679C\u4E3Atrue,\u591A\u503C\u5B57\u6BB5\u5C06\u4F1A\u6309\u7167\u5B83\u4EEC\u5728\u7D22\u5F15\u4E2D\u987A\u5E8F\u8FD4\u56DE\u6240\u6709\u7684\u503C.\u5982\u679Cfalse,\u53EA\u6709\u5339\u914D\u9AD8\u4EAE\u8BF7\u6C42\u7684\u503C\u8FD4\u56DE\u3002</td></tr></tbody></table><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',27),g={href:"https://www.cnblogs.com/yszd/p/12867697.html",target:"_blank",rel:"noopener noreferrer"};function c(m,p){const e=s("ExternalLinkIcon");return d(),a("div",null,[o,t("p",null,[t("a",g,[h("Solr\u641C\u7D22\u7ED3\u679C\u9AD8\u4EAE\u53CA\u9AD8\u7EA7\u8BBE\u7F6E "),i(e)])])])}const _=r(n,[["render",c],["__file","solr-b-highlight.html.vue"]]);export{_ as default};
