import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as a,a as p,b as e,d as t,e as o,r as n}from"./app.0d488247.js";const h={},s=o('<p>\u9996\u5148\u795D\u5927\u5BB6\u8282\u65E5\u5FEB\u4E50\uFF0C\u8EAB\u4F53\u5065\u5EB7\u3001\u5FEB\u5FEB\u4E50\u4E50\u3001\u5F00\u5FC3\u7F16\u7801\u3002</p><p>\u4ECA\u5929\u662F\u4E2A\u5C5E\u4E8E\u7A0B\u5E8F\u5458\u7684\u65E5\u5B50\uFF0C\u521A\u597D\u548C\u592B\u4EBA\u6765\u56FE\u4E66\u9986\u770B\u4E66\uFF0C\u6709\u65F6\u95F4\u6574\u7406\u6574\u7406\u81EA\u5DF1\u6700\u8FD1\u7684\u601D\u7EEA,\u627E\u627E\u5199\u6587\u7684\u611F\u89C9\u3002</p><p>\u597D\u4E45\u6CA1\u6709\u8BA4\u771F\u5750\u4E0B\u6765\u5199\u6587\u7AE0\u4E86\uFF0C\u5F88\u96BE\u8FDB\u5165\u5FC3\u6D41\u72B6\u6001\u3002</p><blockquote><p>\u53D1\u73B0\u81EA\u5DF1\u8FD8\u662F\u8F93\u7ED9\u4E86\u81EA\u5DF1\u7684\u61D2\u6563\u4E0E\u62D6\u5EF6\u3002\u5176\u5B9E\u5907\u5FD8\u5F55\u91CC\u9762\u6709\u597D\u591A\u8981\u5199\u7684\u7075\u611F\uFF0C\u540E\u9762\u4F1A\u5B89\u6392\u65F6\u95F4\u90FD\u5206\u4EAB\u51FA\u6765\u3002</p></blockquote><p>1024\u662F\u4E00\u4E2A\u795E\u5947\u53C8\u5177\u6709\u7279\u6B8A\u542B\u4E49\u7684\u6570\u5B57\uFF0C\u6709\u5F88\u591A\u8BB2\u7A76\u3002\u5982\u679C\u8981\u7EC6\u8BF4\u53EF\u80FD\u4E92\u8054\u7F51\u4E0A\u7684\u5F88\u591A\u4E1C\u897F\uFF0C\u90FD\u53EF\u4EE5\u901A\u8FC7\u8FD9\u4E2A1024\u63A8\u6F14\u4EA7\u751F\u3002</p><p>\u5728\u8FD9\u4E00\u884C\u4E5F\u6709\u5C06\u8FD14\u5E74\u4E4B\u4E45\uFF0C\u867D\u8BF4\u90FD\u662F\u5728\u5C0F\u516C\u53F8\u5E72\u6D3B\uFF0C\u53EF\u5BF9\u4ECE\u4E8B\u8FD9\u4E00\u884C\u7684\u611F\u609F\u4E5F\u8D8A\u6765\u8D8A\u591A\uFF0C\u5F88\u591A\u65F6\u95F4\u5C31\u4F1A\u89C9\u5F97\u81EA\u5DF1\u5176\u5B9E\u4E0D\u9002\u5408\u505A\u7A0B\u5E8F\u5F00\u53D1\uFF0C\u53C8\u6709\u65F6\u5019\u89C9\u5F97\u81EA\u5DF1\u597D\u50CF\u53EA\u80FD\u505A\u8FD9\u4E2A\u3002</p><p>\u4E0B\u9762\u662F\u6700\u8FD1\u7684\u4E00\u4E9B\u601D\u8003\uFF0C\u4ECE\u5DE5\u4F5C\u4E60\u60EF\uFF0C\u6210\u957F\u7ECF\u5386\uFF0C\u804C\u4E1A\u53D1\u5C55\u7B49\u65B9\u5411\u8FDB\u884C\u9610\u8FF0\uFF0C\u662F\u81EA\u5DF1\u4ECE\u4E1A\u8FD9\u51E0\u5E74\u6765\u7684\u4E00\u4E9B\u5C0F\u603B\u7ED3\uFF0C\u4E5F\u5E0C\u671B\u80FD\u5BF9\u5927\u5BB6\u6709\u5E2E\u52A9\u3002</p><h2 id="\u4E00\u3001\u8FDB\u5165\u5FC3\u6D41\u7684\u72B6\u6001" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u8FDB\u5165\u5FC3\u6D41\u7684\u72B6\u6001" aria-hidden="true">#</a> \u4E00\u3001\u8FDB\u5165\u5FC3\u6D41\u7684\u72B6\u6001</h2><p>\u8FDB\u5165\u5FC3\u6D41\u7684\u72B6\u6001\uFF0C\u73B0\u5728\u7684\u6211\u4EEC\u592A\u96BE\u8FDB\u5165\u5FC3\u6D41\u4E86\u3002</p><p>\u5176\u5B9E\u5F88\u5927\u4E00\u90E8\u5206\u539F\u56E0\u662F\u624B\u673A\u5BF9\u6211\u4EEC\u7684\u63A7\u5236\u8D8A\u6765\u8D8A\u5927\u3002</p><p>\u4E0D\u77E5\u9053\u5927\u5BB6\u6709\u6CA1\u6709\u8FD9\u6837\u4E00\u79CD\u9519\u89C9\uFF0C\u597D\u50CF\u6211\u4EEC\u968F\u65F6\u968F\u5730\u7684\u90FD\u5728\u627E\u624B\u673A\uFF0C\u4E0D\u7BA1\u6211\u4EEC\u53BB\u5E72\u4EC0\u4E48\uFF0C\u90FD\u5FC5\u987B\u62FF\u7740\u624B\u673A\u3002\u597D\u50CF\u624B\u673A\u4E0D\u5728\u624B\u4E0A\u6216\u8005\u53E3\u888B\u91CC\uFF0C\u603B\u89C9\u5F97\u7F3A\u5C11\u70B9\u4EC0\u4E48\uFF1F</p><p>\u6628\u5929\u4E0A\u73ED(<strong>\u5DE5\u4F5C\u5355\u4F11</strong>)\u624B\u673A\u88AB\u6211\u9057\u5FD8\u5728\u5BB6\u91CC\uFF0C\u4E00\u6574\u5929\u90FD\u6CA1\u6709\u5E26\u624B\u673A\u3002\u521A\u5F00\u59CB\u7684\u65F6\u5019\uFF0C\u603B\u611F\u89C9\u6709\u4EBA\u4F1A\u7ED9\u6211\u6253\u7535\u8BDD\uFF0C\u53D1\u6D88\u606F\u3002\u751A\u81F3\u521A\u5230\u516C\u53F8\u6211\u90FD\u6709\u60F3\u6CD5\uFF0C\u8BA9\u592B\u4EBA\u5E2E\u5FD9\u53D1\u4E2A\u670B\u53CB\u5708\u8BF4\u4E0B\u672C\u4EBA\u4ECA\u5929\u6CA1\u5E26\u624B\u673A\uFF0C\u8BF7\u5927\u5BB6\u522B\u8054\u7CFB\u6211\u3002\u6700\u540E\u60F3\u4E86\u60F3\u8FD8\u662F\u7B97\u4E86\uFF0C\u6BD5\u7ADF\u81EA\u5DF1\u4E0D\u662F\u4EC0\u4E48\u5927\u5FD9\u4EBA\uFF0C\u6CA1\u5FC5\u8981\u90A3\u4E48\u641E\u3002</p><p>\u665A\u4E0A\u56DE\u53BB\uFF0C\u770B\u4E86\u770B\u624B\u673A\uFF0C\u4E00\u6574\u5929\u4E5F\u6CA1\u6709\u4EBA\u8054\u7CFB\u6211(<strong>\u81EA\u5DF1\u60F3\u591A\u4E86</strong>)\u3002</p><p>\u8BF4\u8BF4\u6628\u5929\u7684\u611F\u89C9\uFF1A</p><ul><li>\u5FC3\u91CC\u9762\u7A7A\u843D\u843D\u7684\uFF0C\u603B\u89C9\u5F97\u5C11\u70B9\u4EC0\u4E48</li><li>\u5DE5\u4F5C\u7684\u65F6\u5019\u5F88\u4E13\u6CE8</li><li>\u5DE5\u4F5C\u6548\u7387\u660E\u663E\u63D0\u9AD8</li><li>\u4E2D\u5348\u5348\u4F11\u7761\u4E8630\u5206\u949F</li></ul><p>\u4EE5\u524D\u5E26\u624B\u673A\u7684\u65F6\u5019\uFF0C\u4E0A\u73ED\u671F\u95F4\u5F88\u96BE\u8BA9\u81EA\u5DF1\u8FDB\u5165\u72B6\u6001\uFF0C\u65F6\u4E0D\u65F6\u7684\u5C31\u4F1A\u770B\u4E0B\u624B\u673A\uFF1A\u770B\u65F6\u95F4\u3001\u767B\u5F55\u5FAE\u4FE1\u3001QQ\uFF0C\u8FDB\u77E5\u8BC6\u661F\u7403\uFF0C\u5237\u5FAE\u535A\u70ED\u641C\u3002</p><p>\u4E0D\u7BA1\u6709\u6CA1\u6709\u4EBA\u8054\u7CFB\u6211\uFF0C@\u6211\uFF0C\u7ED9\u6211\u53D1\u6D88\u606F\uFF0C\u53CD\u6B63\u6211\u90FD\u8981\u8FDB\u53BB\u770B\u770B\uFF0C\u751F\u6015\u81EA\u5DF1\u9519\u8FC7\u4EC0\u4E48\u5927\u4E8B\u60C5\u3002</p><p>\u6CA1\u5E26\u624B\u673A\u540E\uFF0C\u5DE5\u4F5C\u6548\u7387\u63D0\u9AD8\u4E86\uFF0C\u5348\u4F11\u7684\u65F6\u5019\u4E0D\u770B\u89C6\u9891\uFF0C\u5348\u4F11\u7761\u7720\u8D28\u91CF\u5F88\u597D\u3002</p><p>\u4E00\u6574\u5929\u5F88\u5BB9\u6613\u8FDB\u5165\u5FC3\u6D41\u72B6\u6001(\u4E13\u6CE8\u3001\u4E0D\u5206\u5FC3\u3001\u6301\u7EED\u65F6\u95F4\u957F)\uFF0C\u5199\u4EE3\u7801\u601D\u8DEF\u4E5F\u5F88\u6E05\u6670\u3002</p><hr><p>\u901A\u8FC7\u6628\u5929\u7684\u4E8B\u60C5\uFF0C\u8BA9\u6211\u5728\u5DE5\u4F5C\u548C\u751F\u6D3B\u4E0A\u6709\u4E86\u4E00\u4E9B\u611F\u609F\uFF0C\u5728\u8FD9\u91CC\u548C\u5927\u5BB6\u5206\u4EAB\u4E0B\u3002</p><p>\u5728\u5E73\u65F6\u7684\u5DE5\u4F5C\u548C\u751F\u6D3B\u4E2D\uFF0C\u8981\u8BA9\u81EA\u5DF1\u591A\u591A\u8FDB\u5165\u5FC3\u6D41\uFF0C\u51CF\u5C11\u5916\u754C\u4E8B\u7269\u5BF9\u81EA\u5DF1\u7684\u5E72\u6270\u3002\u5C24\u5176\u662F\u4ECE\u4E8B\u7F16\u7801\u5DE5\u4F5C\u7684\u670B\u53CB\uFF0C\u8FDB\u5165\u5FC3\u6D41\u72B6\u6001\uFF0C\u80FD\u8BA9\u81EA\u5DF1\u9759\u4E0B\u5FC3\u6765\uFF0C\u601D\u8003\u95EE\u9898\u7684\u6DF1\u5EA6\u5C31\u4F1A\u52A0\u6DF1\uFF0C\u4F1A\u8BA9\u81EA\u5DF1\u6C89\u6D78\u5728\u4E00\u79CD\u72B6\u6001\u4E0B\uFF0C\u4E00\u79CD\u6301\u7EED\u7CBE\u8FDB\u7684\u72B6\u6001\u4E2D\u3002</p><p>\u6240\u4EE5\u5BF9\u4E8E\u5373\u5C06\u8E0F\u4E0A\u5DE5\u4F5C\u5C97\u4F4D\u7684\u670B\u53CB\u4EEC\uFF0C\u5728\u8FD9\u91CC\u6211\u5E0C\u671B\u5927\u5BB6\u80FD\u6E10\u6E10\u57F9\u517B\u81EA\u5DF1\u7684\u5FC3\u6D41\u72B6\u6001\uFF0C\u770B\u770B\u81EA\u5DF1\u5230\u5E95\u5728\u4EC0\u4E48\u6837\u7684\u73AF\u5883\uFF0C\u4EC0\u4E48\u573A\u666F\u4E0B\u80FD\u5FEB\u901F\u8FDB\u5165\u72B6\u6001\uFF0C\u6301\u7EED\u7684\u8FDB\u884C\u5185\u5BB9\u521B\u4F5C\u3002</p><blockquote><p>\u8BB0\u4F4F\uFF0C\u8FD9\u662F\u4E00\u79CD\u672C\u9886\uFF0C\u4E5F\u662F\u6211\u4EEC\u5728\u5DE5\u4F5C\u548C\u751F\u6D3B\u4E2D\u8981\u53CA\u65F6\u5B66\u4F1A\u7684\u6280\u80FD</p></blockquote><h2 id="\u4E8C\u3001\u8BA9\u81EA\u5DF1\u62E5\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u8BA9\u81EA\u5DF1\u62E5\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886" aria-hidden="true">#</a> \u4E8C\u3001\u8BA9\u81EA\u5DF1\u62E5\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886</h2><p>\u505A\u6280\u672F\u5230\u5E95\u80FD\u5E72\u591A\u4E45\uFF1F\u7A0B\u5E8F\u5458\u662F\u5403\u9752\u6625\u996D\u7684\u5417\uFF1F\u7A0B\u5E8F\u5458\u7ECF\u5E38\u52A0\u73ED\u538B\u529B\u5927\uFF0C\u5BB9\u6613\u79C3\u9876\uFF1F\u7B49\u7B49\u7B49\u7B49\uFF0C\u5173\u4E8E\u7A0B\u5E8F\u5458\u6709\u5F88\u591A\u5F88\u591A\u7684\u95EE\u9898\uFF0C\u6700\u8FD1\u5C45\u7136\u8FD8\u6709\u4E00\u4E2A\u8111\u6B8B\u5267&lt;\u7A0B\u5E8F\u5458\u90A3\u4E48\u53EF\u7231&gt;\u4E5F\u4E0A\u6620\u4E86\u3002</p><p>\u968F\u7740\u4E92\u8054\u7F51\u7684\u53D1\u5C55\uFF0C\u7A0B\u5E8F\u5458\u8FD9\u4E2A\u884C\u4E1A\u6162\u6162\u8FDB\u5165\u5927\u4F17\u5316\uFF0C\u57FA\u672C\u4E0A\u6BCF\u4E2A\u884C\u4E1A\u90FD\u4F1A\u6216\u591A\u6216\u5C11\u548C\u7A0B\u5E8F\u5458\u6253\u8FC7\u4EA4\u9053\u3001\u4E0D\u7BA1\u662F\u4E1A\u52A1\u5408\u4F5C\u8FD8\u662F\u670B\u53CB\u95F4\u4EA4\u8C08\uFF0C\u5927\u5BB6\u53EF\u80FD\u90FD\u89C9\u5F97\u7A0B\u5E8F\u5458\u5E72\u4E0D\u957F\u4E45\uFF0C\u4F46\u662F\u6709\u4E00\u70B9\u5927\u5BB6\u90FD\u6709\u5171\u9E23\u3002\u5C31\u662F\u6323\u5F97\u591A\u3002\u57FA\u672C\u521A\u6BD5\u4E1A\u5C31\u80FD\u62FF\u5230\u522B\u7684\u884C\u4E1A4~5\u5E74\u540E\u7684\u85AA\u8D44\u3002</p><p>\u786E\u5B9E\uFF0C\u8FD9\u4E2A\u4E0D\u53EF\u5426\u8BA4\u3002\u8FD9\u4E2A\u884C\u4E1A\u76F8\u6BD4\u8F83\u522B\u7684\u4F20\u7EDF\u884C\u4E1A\u786E\u5B9E\u85AA\u8D44\u5DEE\u8DDD\u7279\u522B\u5927\u3002\u5C24\u5176\u662F\u4ECA\u5E74\u7684\u5927\u5382\u6821\u62DB\uFF0C\u57FA\u672C\u90FD\u662F\u5012\u6302(\u592A\u7FA1\u6155\u4E86)\u3002</p><p>\u53EF\u662F\u96BE\u5EA6\u3001\u538B\u529B\u4E5F\u4E0D\u662F\u90A3\u4E48\u8F7B\u677E\u3002\u9762\u8BD5\u65F6\uFF0C\u5BF9\u8BA1\u7B97\u673A\u77E5\u8BC6\u3001\u5BF9\u7B97\u6CD5\u3001\u5BF9\u6570\u636E\u7ED3\u6784\u3001\u5BF9\u9879\u76EE\u7ECF\u9A8C\u7684\u8003\u5BDF\u90FD\u662F\u7279\u522B\u4E25\u683C\u7684\u3002\u5C24\u5176\u662F\u5927\u5382\uFF0C\u7279\u522B\u5173\u6CE8\u8FD9\u4E2A\u3002</p><p>\u8FD9\u4E2A\u884C\u4E1A\u4F60\u4E0D\u5F97\u4E0D\u968F\u65F6\u90FD\u51C6\u5907\u5B66\u4E60\uFF0C\u53EF\u4EE5\u8BF4\u662F\u628A\u7EC8\u8EAB\u5B66\u4E60\u7406\u5FF5\u8D2F\u5F7B\u6700\u5F7B\u5E95\u7684\u884C\u4E1A\u3002</p><p>\u662F\u7684\uFF0C\u6211\u4EEC\u8981\u8BA9\u81EA\u5DF1\u62E5\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886\uFF0C\u65F6\u523B\u7CBE\u8FDB\u81EA\u5DF1\u7684\u6280\u80FD\u70B9\uFF0C\u522B\u5DE5\u4F5C4~5\u5E74\u540E\u8BA9\u65B0\u6280\u672F\u628A\u81EA\u5DF1\u6DD8\u6C70\u6389\u3002</p><p>\u8FD9\u4E2A\u884C\u4E1A\u6280\u672F\u7684\u66F4\u65B0\u6362\u4EE3\u662F\u5DE8\u5FEB\u7684\uFF0C\u5F88\u96BE\u4E00\u62DB\u5403\u904D\u5929\u4E0B\u3002\u4F60\u8981\u65F6\u523B\u66F4\u65B0\u6280\u80FD\uFF0C\u65F6\u5E38\u51FA\u53BB\u9762\u8BD5\uFF0C\u770B\u770B\u81EA\u5DF1\u5728\u5E02\u9762\u4E0A\u7684\u4EF7\u683C(\u5F88\u96BE\u76F8\u4FE1\u5427\uFF0C\u662F\u4E0D\u662F\u89C9\u5F97\u5C31\u597D\u50CF\u5546\u54C1\u4E00\u6837)\u3002</p><p>\u4E0D\u7BA1\u662F\u5728\u5927\u516C\u53F8\u8FD8\u662F\u5C0F\u516C\u53F8\uFF0C\u90FD\u522B\u505A\u8868\u9762\u7684\u87BA\u4E1D\u9489\u3002\u8981\u8BA9\u81EA\u5DF1\u5177\u6709\u6838\u5FC3\u7ADE\u4E89\u529B\u548C\u4E0D\u53EF\u66FF\u4EE3\u6027\uFF0C\u4E5F\u8981\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886\u3002</p><p>\u60F3\u62E5\u6709\u968F\u65F6\u79BB\u5F00\u7684\u672C\u9886\uFF0C\u90A3\u4E48\u4F60\u5C31\u5F97\u4ECE\u4EE5\u4E0B\u51E0\u70B9\u505A\u8D77\uFF1A</p><ul><li>\u7CBE\u8FDB\u6280\u80FD</li><li>\u638C\u63E1\u6838\u5FC3\u6280\u672F</li><li>\u5728\u516C\u53F8\u522B\u53EA\u505A\u4E1A\u52A1\u5B9E\u73B0\u8005\uFF0C\u8981\u591A\u60F3\u60F3\u505A\u8FD9\u4E2A\u80FD\u5426\u5BF9\u81EA\u5DF1\u6709\u6240\u63D0\u9AD8</li><li>\u5E73\u65F6\u591A\u601D\u8003\u3001\u591A\u95EE\u4E9B\u4E3A\u4EC0\u4E48</li><li>\u522B\u5728\u6E29\u6C34\u91CC\u9762\u6CE1\u592A\u4E45</li><li>\u8981\u5411\u524D\u770B\uFF0C\u6709\u65F6\u5019\u5C31\u5F97\u52A0\u901F\u8DD1</li></ul><p>\u8FD9\u65B9\u9762\u81EA\u5DF1\u505A\u7684\u5F88\u4E0D\u8DB3\uFF0C\u6240\u5728\u516C\u53F8\u4E1A\u52A1\u592A\u5355\u4E00\uFF0C\u548C\u5E02\u573A\u8131\u8F68\u8FC7\u4E8E\u4E25\u91CD\u3002\u5E73\u65F6\u4E5F\u6CA1\u6709\u5728\u793E\u533A\u8DDF\u7740\u505A\u5F00\u6E90\u9879\u76EE\u3002\u6700\u8FD1\u5F00\u59CB\u6269\u5C55\u6280\u80FD\u6811\uFF0C\u5E0C\u671B\u81EA\u5DF1\u597D\u597D\u52AA\u529B\uFF0C\u5728\u628A\u5E73\u65F6\u57FA\u672C\u5DE5\u4F5C\u505A\u597D\u7684\u524D\u63D0\u4E0B\uFF0C\u591A\u591A\u590D\u76D8\u603B\u7ED3\uFF0C\u5B66\u4E60\u4E0A\u8FDB\u3002</p><h2 id="\u4E09\u3001\u5E74\u8F7B\u65F6\u522B\u628A\u94B1\u770B\u7684\u592A\u91CD" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u5E74\u8F7B\u65F6\u522B\u628A\u94B1\u770B\u7684\u592A\u91CD" aria-hidden="true">#</a> \u4E09\u3001\u5E74\u8F7B\u65F6\u522B\u628A\u94B1\u770B\u7684\u592A\u91CD</h2><p>\u521A\u6BD5\u4E1A\u4ECE\u4E8B\u5DE5\u4F5C\u7684\u65F6\u5019\uFF0C\u4EC0\u4E48\u90FD\u60F3\u8981\u3002</p><p>\u770B\u7740\u540C\u4E8B\u7ED3\u5A5A\u3001\u4E70\u623F\u3001\u4E70\u8F66\uFF0C\u81EA\u5DF1\u597D\u751F\u7FA1\u6155\u3002</p><p>\u4E5F\u66FE\u60F3\u8FC7\u7ED3\u5A5A\u3001\u60F3\u8FC7\u4E70\u623F\u3001\u60F3\u8FC7\u4E70\u8F66\uFF0C\u53EF\u6709\u5FC3\u65E0\u529B\u3002</p><p>\u53EF\u662F\u968F\u7740\u5E74\u9F84\u7684\u6E10\u957F\u4F60\u4F1A\u53D1\u73B0\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u90FD\u4F1A\u6162\u6162\u7684\u8DDF\u7740\u65F6\u95F4\u4E00\u70B9\u4E00\u6EF4\u7684\u6765\u5230\u4F60\u7684\u8EAB\u8FB9\uFF0C\u6709\u65F6\u5019\u90FD\u4E0D\u7528\u4F60\u53BB\u7740\u6025\uFF0C\u5B83\u5C31\u4E0D\u7BA1\u4E0D\u987E\u7684\u6765\u4E86\u3002</p><p>\u5E74\u8F7B\u7684\u65F6\u5019\u522B\u628A\u94B1\u770B\u7684\u592A\u91CD\u3002</p><p>\u5BF9\u4E8E\u94B1\u8D22\uFF0C\u6211\u4EEC\u5BF9\u5F85\u5B83\u7684\u5FC3\u6001\u8981\u597D\uFF0C\u522B\u529F\u5229\u5FC3\u592A\u91CD\uFF0C\u8981\u6709\u5F97\u4E4B\u6211\u547D\u5931\u4E4B\u6211\u5E78\u7684\u6001\u5EA6\uFF0C\u8981\u8BB0\u4F4F\u521A\u5F00\u59CB\u5148\u63D0\u5347\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u8FD9\u624D\u662F\u81F3\u5173\u91CD\u8981\u7684\u3002</p><p>\u5F53\u4F60\u7684\u80FD\u529B\u548C\u6280\u672F\u5230\u8FBE\u4E00\u5B9A\u9AD8\u5EA6\u540E\uFF0C\u8FD9\u4E9B\u90FD\u4F1A\u81EA\u7136\u800C\u7136\u7684\u5230\u6765\u3002</p><h2 id="\u56DB\u3001\u52B3\u9038\u7ED3\u5408\u6CE8\u610F\u4F11\u606F" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u52B3\u9038\u7ED3\u5408\u6CE8\u610F\u4F11\u606F" aria-hidden="true">#</a> \u56DB\u3001\u52B3\u9038\u7ED3\u5408\u6CE8\u610F\u4F11\u606F</h2><p>\u8FD9\u4E2A\u884C\u4E1A\u8981\u7ECF\u5E38\u52A0\u73ED\uFF0C\u8FD9\u662F\u6240\u6709\u4EBA\u5BF9\u4E8E\u7A0B\u5E8F\u5458\u7684\u8BA4\u77E5\u3002</p><p>\u521A\u8FDB\u5165\u8FD9\u884C\u7684\u65F6\u5019\uFF0C\u5728\u5B66\u6821\u5927\u5BB6\u7ECF\u5E38\u63A2\u8BA8\u5173\u4E8E\u7A0B\u5E8F\u5458\u52A0\u73ED\u5230\u5929\u4EAE\u7684\u4E8B\u60C5\uFF0C\u8FD8\u6709\u52A0\u73ED\u731D\u6B7B\u7684\u65B0\u95FB\uFF0C\u8BF4\u7684\u4E8B\u60C5\u7279\u522B\u591A\u3002</p><p>\u53EF\u662F\u6BD5\u4E1A\u4ECE\u4E8B\u7F16\u7801\u5DE5\u4F5C\u540E\uFF0C\u53D1\u73B0\u5E72\u7740\u5E72\u7740\u5C31\u6CA1\u5FC3\u52B2\u4E86\u3002</p><ul><li>\u957F\u65F6\u95F4\u9762\u5BF9\u7535\u8111\u4F1A\u5BFC\u81F4\u5934\u6655\u76EE\u7729(<strong>\u548CCF\u6253\u591A\u4E86\u5410\u662F\u4E00\u6837\u7684\u9053\u7406</strong>)</li><li>\u957F\u65F6\u95F4\u9AD8\u5F3A\u5EA6\u7CBE\u529B\u96C6\u4E2D\u4F1A\u5BFC\u81F4\u8EAB\u4F53\u673A\u80FD\u51FA\u73B0\u95EE\u9898</li><li>\u7ECF\u5E38\u548C\u7A0B\u5E8F\u6253\u4EA4\u9053\u548C\u4EBA\u4EA4\u6D41\u7684\u673A\u4F1A\u8D8A\u6765\u8D8A\u5C11(<strong>\u6728\u8BB7\uFF0C\u6CA1\u60C5\u8DA3</strong>)</li><li>\u9700\u8981\u7ECF\u5E38\u4E1A\u4F59\u65F6\u95F4\u5B66\u4E60\u65B0\u6280\u672F</li><li>\u5C45\u7136\u8FD8\u6709\u5355\u4F11\u7684\u4F01\u4E1A(<strong>\u5343\u4E07\u522B\u53BB\u5355\u4F11\u7684\u516C\u53F8\uFF0C\u9664\u975E\u94B1\u5230\u4F4D</strong>)</li><li>\u94B1\u786E\u5B9E\u5F88\u591A\u4F46\u4F11\u606F\u65F6\u95F4\u5F88\u5C11</li></ul><p>\u8FD9\u91CC\u6211\u60F3\u8BF4\u7684\u662F\u5927\u5BB6\u8981\u5B66\u4F1A\u52B3\u9038\u7ED3\u5408\uFF0C\u522B\u4E00\u5934\u624E\u8FDB\u5DE5\u4F5C\u4E2D\u3002\u8981\u5B66\u4F1A\u5DE5\u4F5C\uFF0C\u8BA9\u81EA\u5DF1\u6709\u6240\u63D0\u9AD8\u3002</p><p>\u5982\u679C\u611F\u5230\u7D2F\u4E86\u8BE5\u8BF7\u5047\u4F11\u606F\u5C31\u4F11\u606F\uFF0C\u522B\u89C9\u5F97\u516C\u53F8\u79BB\u5F00\u4F60\u5C31\u4F1A\u7834\u4EA7\uFF0C\u60F3\u591A\u4E86\u3002</p><p>\u7A0B\u5E8F\u5458\u5F88\u591A\u7684\uFF0C\u8001\u677F\u968F\u65F6\u90FD\u80FD\u62DB\u4E00\u4E2A\u66FF\u4EE3\u6389\u4F60\u3002</p><p>\u5982\u679C\u521A\u6BD5\u4E1A\u80FD\u53BB\u5927\u516C\u53F8\u5C31\u53BB\u5927\u516C\u53F8\uFF0C\u80FD\u53BB\u5927\u57CE\u5E02\u5C31\u53BB\u5927\u57CE\u5E02\uFF0C\u521A\u6BD5\u4E1A\u7684\u90A3\u51E0\u5E74\u662F\u4F60\u4EBA\u751F\u804C\u4E1A\u53D1\u5C55\u7684\u9EC4\u91D1\u65F6\u95F4\uFF0C\u803D\u8BEF\u4E0D\u5F97\u3002</p><p>\u8FD8\u6709\u522B\u53BB\u5355\u4F11\u7684\u4F01\u4E1A\uFF0C\u8981\u6709\u81EA\u5DF1\u7684\u65F6\u95F4\u63D0\u9AD8\u6280\u80FD\u548C\u5B89\u6392\u751F\u6D3B\u3002</p><h2 id="\u4E94\u3001\u61C2\u5F97\u8D23\u4EFB\u7684\u91CD\u8981\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001\u61C2\u5F97\u8D23\u4EFB\u7684\u91CD\u8981\u6027" aria-hidden="true">#</a> \u4E94\u3001\u61C2\u5F97\u8D23\u4EFB\u7684\u91CD\u8981\u6027</h2><p>\u8981\u8BA9\u81EA\u5DF1\u6709\u8D23\u4EFB\u5FC3\u3001\u5728\u5176\u4F4D\u8C0B\u5176\u653F\u3002</p><p>\u5982\u679C\u4F60\u8FD8\u5728\u5C97\u4F4D\u4E0A\uFF0C\u5C31\u8981\u5C3D\u81EA\u5DF1\u6700\u5927\u52AA\u529B\u5E72\u597D\u81EA\u5DF1\u7684\u5DE5\u4F5C\uFF0C\u7CBE\u8FDB\u6280\u672F\u6EE1\u8DB3\u8001\u677F\u7684\u8981\u6C42\u53CA\u65F6\u5B8C\u6210\u5DE5\u4F5C\u5185\u5BB9\u3002</p><p>\u8D23\u4EFB\u5FC3\u4E0D\u7BA1\u662F\u5728\u5DE5\u4F5C\u4E2D\u8FD8\u662F\u5728\u5E73\u65F6\u7684\u4E0E\u4EBA\u4EA4\u6D41\u76F8\u5904\u4E2D\uFF0C\u90FD\u81F3\u5173\u91CD\u8981\u3002</p><p>\u4F5C\u4E3A\u7537\u4EBA\uFF0C\u66F4\u8981\u6709\u8D23\u4EFB\u5FC3\u3002</p><p>\u4F60\u8981\u8BB0\u5F97\u4F60\u5C06\u6765\u4F1A\u6210\u4E3A\u4E00\u4E2A\u5BB6\u5EAD\u7684\u652F\u67F1\uFF0C\u4F60\u5F97\u6311\u8D77\u81EA\u5DF1\u80A9\u8180\u4E0A\u5E94\u6709\u7684\u8D23\u4EFB\u548C\u4E49\u52A1\uFF0C\u4F60\u5F97\u5BF9\u4F60\u7684\u592B\u4EBA\u3001\u5B69\u5B50\u6709\u6240\u4EA4\u4EE3\u3002</p><p>\u4F60\u5F97\u8BA9\u4ED6\u4EEC\u5FEB\u4E50\u5E78\u798F\u3002</p><hr><hr><hr><p>\u670B\u53CB\u4F60\u597D\uFF0C\u6211\u662F\u963F\u8F89\uFF0C\u5F88\u9AD8\u5174\u8BA4\u8BC6\u4F60\u3002</p><p>\u4E00\u4E2A\u957F\u671F\u5728\u5C0F\u516C\u53F8\u4E0A\u73ED\u7684\u7A0B\u5E8F\u5458\u3002\u4F7F\u7528C#\u5F00\u53D1\u8BBE\u8BA1\u8FC7\u7F51\u7AD9\u3001\u901A\u8FC7Android\u5F00\u53D1\u8FC7\u624B\u673AAPP\uFF0C\u4F1A\u4E00\u70B9\u524D\u7AEF\u6280\u672F\uFF0C\u52B1\u5FD7\u505A\u4E00\u4E2A\u7EC8\u8EAB\u5B66\u4E60\u8005\u3002\u5982\u679C\u4F60\u60F3\u4E86\u89E3\u5173\u4E8E\u5C0F\u516C\u53F8\u7A0B\u5E8F\u5458\u7684\u65E5\u5E38\u751F\u6D3B\uFF0C\u6B22\u8FCE\u52A0\u6211\u5FAE\u4FE1\uFF0C\u6211\u4EEC\u4E00\u8D77\u8FDB\u6B65\u3001\u4EA4\u6D41\u5B66\u4E60\u3002</p>',66),c={href:"https://www.cnblogs.com/netxiaohui/p/15468786.html",target:"_blank",rel:"noopener noreferrer"};function d(u,x){const l=n("ExternalLinkIcon");return i(),a("div",null,[s,p("blockquote",null,[p("p",null,[e("\u8F6C\u8F7D\u94FE\u63A5\uFF1A"),p("a",c,[e("https://www.cnblogs.com/netxiaohui/p/15468786.html"),t(l)]),e("\uFF0C\u6574\u7406\uFF1Amusk")])])])}const m=r(h,[["render",d],["__file","xjcxydyxjy.html.vue"]]);export{m as default};
